// added color.. o.O 
// Andrew Caudwell 2014
// @acaudwell

#define MAX_RAY_STEPS 100
#define PI 3.14159265359

#define DEGREES_TO_RADIANS 0.017453292

#define IFS_ITERATIONS 12

struct IFS {
    vec3  offset;
    float scale;
    vec3  axis;
    float angle;
    mat4  transform;
};

IFS IFS_constructor(vec3 offset, vec3 axis, float angle, float scale) {
	IFS ifs;
	ifs.offset = offset;
	ifs.axis   = axis;
	ifs.angle  = angle;
	ifs.scale  = scale;

	return ifs;
}

mat4 calc_transform(inout IFS ifs) {
    float angle = ifs.angle * DEGREES_TO_RADIANS;

    float c = cos(angle);
    float s = sin(angle);

    vec3 t = (1.0-c) * ifs.axis;

    return mat4(
        vec4(c + t.x * ifs.axis.x, t.y * ifs.axis.x - s * ifs.axis.z, t.z * ifs.axis.x + s * ifs.axis.y, 0.0) * ifs.scale,
        vec4(t.x * ifs.axis.y + s * ifs.axis.z, (c + t.y * ifs.axis.y),          t.z * ifs.axis.y - s * ifs.axis.x, 0.0) * ifs.scale,
        vec4(t.x * ifs.axis.z - s * ifs.axis.y, t.y * ifs.axis.z + s * ifs.axis.x, c + t.z * ifs.axis.z, 0.0) * ifs.scale,
        vec4(ifs.offset, 1.0)
    );
}

#define t iTime*0.6

int stage_no;
float stage_t;

IFS ifs_N;
IFS ifs_lerp;

void InitIFS() {

    stage_no  = int(fract(t/float(IFS_ITERATIONS*2)) * float(IFS_ITERATIONS*2));
    stage_t = smoothstep(0.0, 1.0, fract(t));


    if(stage_no >= IFS_ITERATIONS) {
        stage_no = IFS_ITERATIONS-(stage_no-IFS_ITERATIONS)-1;
        stage_t  = 1.0-stage_t;
    }

    // IFS to visualize
    ifs_N = IFS_constructor(vec3(-1.5), normalize(vec3(-1.0)), -36.0, 1.5);

	ifs_lerp.axis   = ifs_N.axis;
	ifs_lerp.angle  = ifs_N.angle;

	// interpolate scale and position offset
	ifs_lerp.offset = ifs_N.offset * stage_t;
	ifs_lerp.scale  = 1.0 + (ifs_N.scale-1.0) * stage_t;

	// left mouse button disables interpolation
	if(iMouse.z>0.0) {
		ifs_lerp = ifs_N;
	}

    ifs_N.transform    = calc_transform(ifs_N);
    ifs_lerp.transform = calc_transform(ifs_lerp);
}

// The definitive Fractal Forums thread about this class of fractals:
// http://www.fractalforums.com/ifs-iterated-function-systems/kaleidoscopic-%28escape-time-ifs%29/

float scene(vec3 p) {

	IFS ifs = ifs_N;

	float scale = 1.0;

	for(int i=0;i<IFS_ITERATIONS;i++) {

		if(i==stage_no) ifs = ifs_lerp;
		else if(i>stage_no) break;

		// mirror on 2 axis to get a tree shape
		p.xy = abs(p.xy);

		// apply transform
		p = (ifs.transform * vec4(p, 1.0)).xyz;

		scale *= ifs.scale;
	}

	// divide by scale preserve correct distance
	return (length(p)-2.0) / scale;
}

vec3 normal(vec3 p) {

    vec2 o = vec2(0.001,0.0);

	float d = scene(p);

    float d1 = d-scene(p+o.xyy);
    float d2 = d-scene(p+o.yxy);
    float d3 = d-scene(p+o.yyx);

    return normalize(vec3(d1,d2,d3));
}

float AO(vec3 p, vec3 normal) {

    float a = 1.0;

	float c = 0.0;
    float s = 0.25;

    for(int i=0; i<3; i++) {
	    c += s;
        a *= 1.0-max(0.0, (c -scene(p + normal*c)) * s / c);
    }

    return clamp(a,0.0,1.0);
}

float map( in vec3 p )
{
	float e = 1.0;//2.0*texture( iChannel0, vec2(0.01,0.25) ).x;
    return min( e +  length(p) - 1.0, p.y+1.0 );
}

vec3 calcNormal( in vec3 p )
{

	vec3 e = vec3(0.001,0.0,0.0);
	return normalize( vec3(map(p+e.xyy) - map(p-e.xyy),
						   map(p+e.yxy) - map(p-e.yxy),
						   map(p+e.yyx) - map(p-e.yyx) ) );

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	InitIFS();

    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;

    vec3 dir = normalize(vec3(uv.x, uv.y * (iResolution.y/iResolution.x), 1.0));

    vec3 p = vec3(0.0,0.0,-4.1);

	float d = 0.0;

	for(int i=0; i<MAX_RAY_STEPS; i++) {
        d = scene(p);		

		p += d * dir;
		if(d<0.001) break;
    }

	vec3 c = vec3(0.0);

	if(d<0.001) {
		vec3 l = vec3(-3.0, 3.0, 3.0);

		vec3 n = -normal(p-dir*0.001);

		c = vec3(0.4);

		c += 1.5 * (max(0.0, dot(n, normalize(l-p)))/length(l-p));

		c *= AO(p, 0.5*(n+normalize(n+l)));

		c *= calcNormal(dir);
	}

    fragColor = vec4(c,1.0);
}

// Created by Per Bloksgaard/2014
// Thanks to iq for letting me use his ray-marching, softshadow and ao code.

#define PI 3.14159265358979

float distPlane(in vec3 p)
{
	return p.y;
}

vec2 distTorus(in vec3 p, in vec2 t)
{
	vec2 q = vec2(length(p.xz)-t.x,p.y);
	float a = (atan(q.x,q.y)+PI)/PI*4.0;
	float b = (atan(p.z,p.x)+PI)/PI*8.0;
	float s = clamp(abs(sin(iTime*0.05))*1.8-0.7,0.0,1.0);
	float c = a * s;
	float d = b * s;	
	float m;
	if (fract(d)>0.5)
	{
		m = -s;
		if (fract(c)>0.5)
		{
			m = b*(1.0-s);
		}
	}
	else
	{
		m = a*(1.0-s);
		if (fract(c)>0.5)
		{
			m = -s;
		}
	}
	return vec2((length(q)-t.y)*0.5,m);
}

vec2 maxOfTwo(in vec2 a, in vec2 b)
{
	return (a.x<b.x)?a:b;
}

vec3 doTwist(in vec3 p)
{
	float f = sin(iTime)*12.0;
	float c = cos(f*p.y);
	float s = sin(f*p.y);
	mat2  m = mat2(c,-s,s,c);
	return vec3(p.y,m*p.xz);
}

vec2 map(in vec3 pos)
{
	return maxOfTwo( vec2(distPlane(pos),-2.0), distTorus(doTwist(pos-vec3(0.0,0.25,0.0)),vec2(0.20,0.05)) );
}

vec2 castRay(in vec3 ro, in vec3 rd, in float maxd)
{
	float precis = 0.0008;
	float h = precis*2.0;
	float t = 0.0;
	float m = -1.0;
	for(int i=0; i<50; i++)
	{
		if(abs(h)<precis||t>maxd)
		{
			continue;
		}
		t += h;
		vec2 res = map( ro+rd*t );
		h = res.x;
		m = res.y;
	}
	return vec2(t, m);
}

float softshadow(in vec3 ro, in vec3 rd, in float mint, in float maxt, in float k)
{
	float res = 1.0;
	float t = mint;
	for(int i=0; i<30; i++)
	{
		if(t<maxt)
		{
			float h = map(ro + rd*t).x;
			res = min(res, k*h/t);
			t += 0.02;
		}
	}
	return clamp(res,0.0,1.0);
}

vec3 calcNormal(in vec3 pos)
{
	vec3 eps = vec3( 0.001, 0.0, 0.0 );
	vec3 nor = vec3(
	map(pos+eps.xyy).x - map(pos-eps.xyy).x,
	map(pos+eps.yxy).x - map(pos-eps.yxy).x,
	map(pos+eps.yyx).x - map(pos-eps.yyx).x);
	return normalize(nor);
}

float calcAO(in vec3 pos, in vec3 nor)
{
	float totao = 0.0;
	float sca = 1.0;
	for(int aoi=0; aoi<5; aoi++)
	{
		float hr = 0.01+0.05*float(aoi);
		vec3 aopos = nor*hr+pos;
		float dd = map(aopos).x;
		totao += -(dd-hr)*sca;
		sca *= 0.75;
	}
	return clamp(1.0-4.0*totao,0.0,1.0);
}

vec3 render(in vec3 o, in vec3 d, in vec3 lig)
{ 
	vec3 col = vec3(0.0);
	vec2 res = castRay(o,d,7.0);
	float t = res.x;
	float m = res.y;
	vec3 pos = o + d*t;
	vec3 nor;
	if(m<-1.0)
	{
		nor = vec3(0.0,1.0,0.0);
		t = 7.0;	
		if (d.y<0.0)
		{
			t = -o.y/d.y;
			pos = o + d*t;
			m = (fract(pos.x)>0.5)?0.0:1.0;
			m = (fract(pos.z)>0.5)?1.0-m:m;
			m -= 1.0;
		}
	}
	else
	{
		nor = calcNormal(pos);
	}
	float factor = clamp(1.0+m,0.0,1.0);
	float ao = calcAO(pos,nor);
	col = vec3(0.5)+0.5*sin(vec3(PI,PI*0.25,PI*0.5)*m);
	float dif = clamp(dot(nor,lig),0.0,1.0);
	float bac = clamp(dot(nor,normalize(vec3(-lig.x,0.0,-lig.z))),0.0,1.0)*clamp(1.0-pos.y,0.0,1.0);
	float sh = softshadow(pos,lig,0.02,7.0,4.0); 
	dif *= sh; 
	vec3 brdf = dif*vec3(1.0);
	float pp = clamp(dot(reflect(d,nor),lig ),0.0,1.0);
	float fre = ao*pow(clamp(1.0+dot(nor,d),0.0,1.0),2.0);
	col = col*brdf+fre*(0.5+0.5*col);
	col *= factor;
	col += vec3(1.5)*sh*pow(pp,8.0);
	t = clamp(t-3.2,0.0,10.0);
	return mix(vec3(0.8,0.9,1.0),col,exp(-0.4*t*t));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 s = (-iResolution.xy+2.*gl_FragCoord.xy)/iResolution.y;
	float u = iTime*0.5;
	float v = -PI*0.4-cos(iTime*0.7)*PI*0.10;
	float f = 0.5+abs(cos(iTime*0.1)*0.5);
	float time = -iTime*0.5;
	vec3 target = vec3(0.0,0.25,0.0);
	vec3 origin = vec3(cos(u)*sin(v)*f,cos(v)*f,sin(u)*sin(v)*f);
	origin += target;
	vec3 camForward = normalize(target-origin);
	vec3 wldUp = vec3(0.0,1.0,0.0);
	vec3 camRight = normalize(cross(camForward,wldUp));
	vec3 camUp = normalize(cross(camRight,camForward));
	vec3 d = normalize(s.x*camRight+s.y*camUp+camForward*2.1);
	vec3 c = render(origin,d,normalize(origin));
	fragColor = vec4(c,1.0);
}

#define PI 3.1415

#define ENVIRONMENT 0
#if(ENVIRONMENT == 0)
	#define RESOLUTION iResolution.xy
	#define TIME iTime
	#define OFFSET iMouse.xy/iResolution.xy
	#define POSITION fragCoord.xy

	#define DEPTH 4
#elif(ENVIRONMENT == 1)
	#define RESOLUTION resolution.xy
	#define TIME time
	#define OFFSET offset
	#define POSITION fragCoord.yx
	uniform vec3 resolution;
	uniform float time;
	uniform vec2 offset;

	#define DEPTH 3
#endif

#define CHILDREN 3

vec2 rotate(vec2 v, float a) {
	return vec2(cos(a)*v.x+sin(a)*v.y, -sin(a)*v.x+cos(a)*v.y);
}

vec2 repeat_arc(vec2 pos, int n) {
	float a = atan(pos.y, pos.x);
	float s = 2.*PI/float(n);
	return rotate(vec2(length(pos),0), mod(a,s)-s/2.);
}

float cog_teeth (float a) {
	return 4.*clamp(2.*abs(mod(a/2./PI, 1.)-.5), .25, .75)-2.;
}

bool cog (vec2 pos, float r, float depth, int teeth) {
	return length(pos) < r - depth*cog_teeth(float(teeth)*atan(pos.y, pos.x));
}

bool cogs(vec2 pos, float theta, float r1, float r2, float r3, float cog_depth, int cog_teeth) {
	float rm = (r2+r3)/2.;
	float ri = (r2-r3)/2.;

	bool result = false;
	for(int i=0; i<DEPTH; i++) {
		float s_theta = (r2/rm)*(1.-r3/r2)*theta; // (r2/rm) is magic factor
		result = result || cog(pos, r1, cog_depth, cog_teeth);
		result = result &&!cog(pos, r2, cog_depth*ri/r1, int(float(cog_teeth)*r2/ri));
		result = result || cog(rotate(pos, theta), r3, cog_depth*ri/r1, int(float(cog_teeth)*r3/ri));
		pos = rotate(repeat_arc(rotate(pos, s_theta*(r3/r2)), CHILDREN)-vec2(rm,0.), s_theta*(r3/ri))*r1/ri;
	}
	return result;
}

bool cogs(vec2 pos, float theta) {
	return cogs(pos, theta, 1., .8, .4, .1, 9);
}

bool cog_array(vec2 pos, float theta, float r, float depth, int teeth) {
	pos = mod(pos,4.*r);
	if(pos.x < 2.*r != pos.y < 2.*r) {
		theta += PI/float(teeth);
	}
	pos = abs(pos-vec2(2.*r));
	pos = rotate(pos-vec2(r), theta);
	return cog(pos, r, depth, teeth);
}

vec2 get_pos(vec2 fragCoord) {
	return (POSITION-RESOLUTION/2.)/RESOLUTION.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
	fragColor = cogs(rotate(get_pos(fragCoord)+vec2(.4,0),-TIME/5.), TIME/3.) ? vec4(1) :
						cogs(rotate(get_pos(fragCoord)+vec2(-.5,+.4)-OFFSET/3., -TIME)*3., TIME) ? vec4(.5) : 
						cog_array(get_pos(fragCoord)-OFFSET, TIME/3., .1, .01, 9) ? vec4(.1) : vec4(0);
}

struct traceResult {
	float d;
	vec3 intersection;
	vec3 normal;
	vec3 color;
};

traceResult trace( vec3 orig, vec3 delta ) {
	vec3 spherea = vec3(0, 1, 00);
	float spherea_r = 20.0;
	vec3 sphereb = vec3(400, 250, 1500);
	float sphereb_r = 300.0;
	vec3 spherec = vec3(200, -300, 1400);
	float spherec_r = 300.0;

	float plane_y = -300.0;

	float bar, foo, d=100000.0, d2;
	vec3 intersection, norm, color;

	bar = dot(delta,orig-spherea);
	foo = ( bar*bar - dot(orig-spherea,orig-spherea) + spherea_r*spherea_r );
	d2 = - bar - sqrt(foo);
	if( foo>0.0 && d2 > 0.01 && d2<d ) {
		intersection = orig + delta*d2;
		norm = normalize(spherea-intersection);
		d=d2;
		color=vec3(0.8,0.2,0.3);
	}

	return traceResult( d, intersection, norm, color );
}

