	}
}

float dTiles(vec3 p)
{
	float subs = 16.0;
	float final = length(p) - 2.2;
	float a = atan(p.x, p.z);
	a /= 2.0*PI;
	a *= subs;
	a = abs((fract(a) - 0.5))*2.0;	// triangle wave from 0.0 to 1.0
	a -= 0.15;
	a *= 6.0;
	a = max(0.0, a);
	a = min(0.75, a);

	float b = atan(length(p.xz), p.y);
	b /= 2.0*PI;
	b *= subs;
	b = abs((fract(b) - 0.5))*2.0;	// triangle wave from 0.0 to 1.0
	b -= 0.15;
	b *= 6.0;
	b = max(0.0, b);
	b = min(0.75, b);

	a = a*b;

	a = a *0.2 + 0.8;
	b = b *0.2 + 0.8;

	final = final - a;
	final = max(final, 0.5-p.y);
	return final/1.414;
}

float GemCut(vec3 p)
{
	float size = 0.5;
	float f = length(p) - size;
	if (f <= 1.0)
	{
		marchingMultplier = 0.65;
		f = max(f, p.y - size * 0.25);

		f = max(f, p.y + p.x - size * 0.7);
		f = max(f, p.y - p.x - size * 0.7);
		f = max(f, p.y + p.z - size * 0.7);
		f = max(f, p.y - p.z - size * 0.7);

		f = max(f, -p.y + p.x - size * 0.6);
		f = max(f, -p.y - p.x - size * 0.6);
		f = max(f, -p.y + p.z - size * 0.6);
		f = max(f, -p.y - p.z - size * 0.6);

		f = max(f, p.y + p.x + p.z - size * 0.95);
		f = max(f, p.y - p.x + p.z - size * 0.95);
		f = max(f, p.y - p.x - p.z - size * 0.95);
		f = max(f, p.y + p.x - p.z - size * 0.95);

		f = max(f, -p.y + p.x + p.z - size * 0.85);
		f = max(f, -p.y - p.x + p.z - size * 0.85);
		f = max(f, -p.y - p.x - p.z - size * 0.85);
		f = max(f, -p.y + p.x - p.z - size * 0.85);
	} else marchingMultplier = 1.0;
	return f;
}

float atrium(vec3 p)
{
	vec3 c = vec3(1.0, 1.0, 1.0)* 4.0;
	float c2 = 5.2;
	vec3 q = mod(p,c)-0.5*c;
	float q2 = mod(p.x,c2)-0.5*c2;
	vec3 p2 = vec3(q.x, p.y, q.z);
	vec3 p3 = vec3(q2, p.y, p.z);

	float final = -sdCapsule(p, vec3(0.0,-0.5,0.0), vec3(0.0,2.25,0.0), 3.0);
	// This if condition is for a culling speedup and a cool bevel effect on the ceiling tiles.
	if (final < 0.01) final = max(final, -dTiles(p + vec3(0.0, -2.25, 0.0)));
	final = min(final, sdTorus82(p, vec2(2.75, 0.25), vec3(0.0, -0.795, 0.0), 12.0));
	final = max(final, -sdCapsule(p, vec3(-6.0,0.0,0.0), vec3(6.0,0.0,0.0), 2.0));
	//final = max(final, -sdCapsule(p, vec3(0.0,0.0,-16.0), vec3(0.0,0.0,16.0), 2.0));
	final = max(final, -dBoxSigned(p));
	final = max(final, -sdCapsule(p, vec3(0.0,0.0,0.0), vec3(0.0,5.5,0.0), 0.5));
	final = max(final, p.y - 5.3);
	//final = max(final, sdCapsule(p, vec3(0.0,-0.5,0.0), vec3(0.0,0.5,0.0), 3.05));
	//final = max(final, -dSphere(p2, 0.08));
	final = min(final, sdColumn(p2, vec3(0.0, 0.0, 0.25)));

	final = min(final, sdTorus82(p, vec2(3.0, 0.25), vec3(0.0, 2.7, 0.0), 8.0));
	final = min(final, sdTorus82(p, vec2(0.75, 0.25), vec3(0.0, -1.0, 0.0), 6.0));
	final = min(final, sdTorusArch(p3, vec2(2.125, 0.3), vec3(0.0, -0.1, 0.0), 6.0));
	//final = min(final, sdTorusArch(p, vec2(2.125, 0.3), vec3(2.6, -0.1, 0.0), 6.0));
	//final = min(final, sdTorusArch(p, vec2(2.125, 0.3), vec3(-2.6, -0.1, 0.0), 6.0));
	final = min(final, dFloor(p));
	material = 0.0;
	final = matMin(final, sdHexPrism(p, vec2(0.25, 1.0)), 2.0);

	//final = min(final, dGem(p));
	//final = matMin(final, sdHexPrismGem(p - vec3(0, 1.0, 0), vec2(0.25, 0.5)), 1.0);
	final = matMin(final, GemCut(p - vec3(0, 0.8, 0)), 1.0);

	//vec4 texX = texture(iChannel1, p.yz*0.2);
	//vec4 texY = texture(iChannel1, p.xz*0.2);
	//vec4 texZ = texture(iChannel1, p.xy*0.2);
	//vec4 noise = texX + texY + texZ;
	//final += noise.x * 0.05;

	return final;
}

float DistanceToObject(vec3 p)
{
	float tubeWidth = 0.15;
	//p = RotateY(p, p.y);
	p = RotateY(p, p.y);
	vec3 rp = p;
	vec3 lp = p;
	rp.x += (sin(rp.y+iTime)*0.5+0.5);
	lp.x += -(sin(rp.y+iTime)*0.5+0.5);
	float final = sdCapsule(lp, vec3(1.0,-5.5,0.0), vec3(1.0,5.5,0.0), tubeWidth);
	final = min(final, sdCapsule(rp, vec3(-1.0,-5.5,0.0), vec3(-1.0,5.5,0.0), tubeWidth));
	float cl = 0.5;
	float ql = mod(rp.y,cl)-0.5*cl;
	vec3 pl = vec3(rp.x, ql, rp.z);
	vec3 pr = vec3(lp.x, ql, lp.z);
	final = matMin(final, sdCapsule(pl, vec3(-1.0,0.0,0.0), vec3(0.0,0.0,0.0), tubeWidth), 1.0);
	final = matMin(final, sdCapsule(pr, vec3(-0.0,0.0,0.0), vec3(1.0,0.0,0.0), tubeWidth), 2.0);

	return final;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy/iResolution.xy * 2.0 - 1.0;// - 0.5;

	// Camera up vector.
	vec3 camUp=vec3(0,1,0); // vuv

	// Camera lookat.
	vec3 camLookat=vec3(0,1.0,0);	// vrp

	float mx=iMouse.x/iResolution.x*PI*2.0 + iTime * 0.5;
	float my=-iMouse.y/iResolution.y*10.0 + sin(iTime * 0.93)*0.32+0.02;//*PI/2.01;
	vec3 camPos=vec3(cos(my)*cos(mx),sin(my),cos(my)*sin(mx))*(3.75); 	// prp

	// Camera setup.
	vec3 camVec=normalize(camLookat - camPos);//vpn
	vec3 sideNorm=normalize(cross(camUp, camVec));	// u
	vec3 upNorm=cross(camVec, sideNorm);//v
	vec3 worldFacing=(camPos + camVec);//vcv
	vec3 worldPix = worldFacing + uv.x * sideNorm * (iResolution.x/iResolution.y) + uv.y * upNorm;//scrCoord
	vec3 relVec = normalize(worldPix - camPos);//scp

	float sunSpeed = -0.77;
	vec3 sunDir = normalize(vec3(sin(iTime*sunSpeed)*2.0, -3.0, sin(iTime*sunSpeed)*2.0));

	float dist = 0.02;
	float t = 0.1;
	float maxDepth = 40.0;
	vec3 pos = vec3(0,0,0);
	// ray marching time
	for (int i = 0; i < 150; i++)
	{
		if ((t > maxDepth) || (abs(dist) < 0.001)) continue;	// break DOESN'T WORK!!! ARRRGGG!
		material = 0.0;
		pos = camPos + relVec * t;
		dist = DistanceToObject(pos);
		t += dist * 0.5;	// because deformations mess up distance function.
	}
	float finalMaterial = material;

	//vec3 finalColor = vec3(0.0,0.0,0.0);// GetSunColorReflection(relVec, -sunDir) + vec3(0.1, 0.1, 0.1);
	vec3 finalColor = GetSunColorStupid(relVec, -sunDir) + vec3(0.1, 0.1, 0.1);

	//finalColor += texture(iChannel0, vec2(pos.x+iTime, pos.y*0.01+0.0)).xyz;
	vec3 smallVec = vec3(0.0025, 0, 0);
	vec3 normal = vec3(dist - DistanceToObject(pos - smallVec.xyy),
					   dist - DistanceToObject(pos - smallVec.yxy),
					   dist - DistanceToObject(pos - smallVec.yyx));
	normal = normalize(normal);
	float ambient = DistanceToObject(pos + normal * 1.0)*0.5;
	ambient += DistanceToObject(pos + normal * 0.1)*5.0;
	ambient = max(0.1, pow(ambient, 0.5));	// tone down ambient with a pow and min clamp it.
	vec4 texX = texture(iChannel0, pos.yz*0.75);
	vec4 texY = texture(iChannel0, pos.xz*0.75);
	vec4 texZ = texture(iChannel0, pos.xy*0.75);
	vec4 tex = mix(texX, texZ, abs(normal.z));
	tex = mix(tex, texY, abs(normal.y));//.zxyw;
	//tex = tex * tex;
	tex.xyz = mix(tex.xyz, vec3(1.0,1.0,1.0), 0.97);
	float wave = sin(pos.y+iTime)*0.5+0.5;
	wave = 1.0 - saturate(pow(wave, 0.25));
	if (finalMaterial == 1.0)
	{

		tex.xyz = mix(vec3(1.0, 0.2, 0.2), tex.xyz, wave);
		tex.xyz += vec3(1.0, 0.5, 0.5) * wave*16.0;
	//	tex.xyz += wave;
	}
	if (finalMaterial == 2.0)
	{
		tex.xyz = mix(vec3(0.2, 1.1, 0.2), tex.xyz, wave);
		tex.xyz += vec3(1.0, 0.5, 0.5) * wave*16.0;
	}
	vec3 ref = reflect(relVec, normal);
	if (t <= maxDepth)
	{
		vec3 envLight = mix(environmentGroundColor, environmentSphereColor * 1.0, (normal.y * 0.5 + 0.5));
			// calculate the reflection vector for highlights

		vec3 sunRef = GetSunColorReflection(ref, -sunDir)* sunCol * max(0.0, dot(normal, -sunDir));
		vec3 sunDirect = max(0.0, dot(-sunDir, normal)) * sunCol * 1.0;
		finalColor += (envLight) * tex.xyz;
		finalColor *= vec3(1.0,1.0,1.0) * ambient;
		finalColor += (sunDirect) * tex.xyz;// * ambient;
		finalColor += sunRef;
		//finalColor = mix(finalColor, vec3(0.015,0.015,0.015), pow(saturate(distance(pos, camPos)*0.075), 0.7) );
	}

	fragColor = vec4(sqrt(clamp(finalColor*1.0, 0.0, 1.0)),1.0);
}

float intersect(vec4 lsphere, vec3 ray){
// camera pos is origo
vec4 sphere= lsphere;
float a = dot(ray,ray);
float b = dot(ray, -2.0* sphere.xyz);
float c = dot(sphere.xyz,sphere.xyz)-dot(sphere.a,sphere.a);
float determinant = b*b-4.0*a*c;
if (determinant < 0.0) return -1.0;
float t2 = (-b-sqrt(determinant))/(2.0*a);
if(t2 > 0.001) return t2;
float t1 = (-b+sqrt(determinant))/(2.0*a);
if(t1 > 0.001) return t1;
return -1.0;
}
vec3 F(vec3 normal, vec3 light,vec3 F0){
float cos_a = abs(dot(normal,light));
	if (cos_a < 0.0) return vec3(0.0);
return F0+(vec3(1.0) - F0)*pow(1.0-cos_a,5.0);
}

vec3 calcColor(vec3 view, vec3 normal, vec3 light,float lightintensity){
vec3 nview = normalize(view);
vec3 nnormal = normalize(normal);
vec3 nlight = normalize(light);
vec3 ambient = vec3(0.12,0.12,0.12);
vec3 nhalf = normalize(nview+nlight);
vec3 reflected = reflect(nnormal*-1.0,nlight);

return ambient+(max(dot(nnormal,nlight),0.0)*0.3+0.4*pow(max(dot(reflected,nview),0.0),2.0))*lightintensity;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

  vec3 ray = vec3(fragCoord.xy/iResolution.xx,-3.0);
  vec4 light0 = vec4(sin(5.4*cos(iTime/2.0))*4.0,4.0+sin(5.4*sin(iTime/2.0))*4.0,5.0,1.0);


  vec4 sphere = vec4(0.5,0.0,-10.0,0.5);
  ray*=vec3(-1.0,-1.0,1.0);
  ray+=vec3(0.5,0.3,0.0);
  fragColor = vec4(vec3(0.0),0.6);
  float t=intersect(sphere,ray);
	if (t > 0.0){

	  // calc intersection point
	  vec3 point = (t+0.0)*ray;
	  // calc normal
	  vec3 normal = point - sphere.xyz;
	  // calc lightvector
	  vec3 lightvector = light0.xyz - point; 
	  fragColor  = vec4(calcColor(-1.0*ray,normal,lightvector,light0.a),1.0);

	}

}

struct traceResult {
	float d;
	vec3 intersection;
	vec3 normal;
	vec3 color;
};

traceResult trace( vec3 orig, vec3 delta ) {
	vec3 spherea = vec3(-200, 10, 2000);
	float spherea_r = 500.0;
	vec3 sphereb = vec3(400, 250, 1500);
	float sphereb_r = 300.0;
	vec3 spherec = vec3(200, -300, 1400);
	float spherec_r = 300.0;

	float plane_y = -300.0;

	float bar, foo, d=100000.0, d2;
	vec3 intersection, norm, color;
	color = vec3(0.5,0.5,0.5);

	bar = dot(delta,orig-spherea);
	foo = ( bar*bar - dot(orig-spherea,orig-spherea) + spherea_r*spherea_r );
	d2 = - bar - sqrt(foo);
	if( foo>0.0 && d2 > 0.01 && d2<d ) {
		intersection = orig + delta*d2;
		norm = normalize(spherea-intersection);
		d=d2;
		color=vec3(0.8,0.2,0.3);
	}

	bar = dot(delta,orig-sphereb);
	foo = ( bar*bar - dot(orig-sphereb,orig-sphereb) + sphereb_r*sphereb_r );
	d2 = - bar - sqrt(foo);
	if( foo>0.0 && d2 > 0.01 && d2<d ) {
		intersection = orig + delta*d2;
		norm = normalize(sphereb-intersection);
		d=d2;
		color=vec3(0.3,0.2,0.8);
	}

	bar = dot(delta,orig-spherec);
	foo = ( bar*bar - dot(orig-spherec,orig-spherec) + spherec_r*spherec_r );
	d2 = - bar - sqrt(foo);
	if( foo>0.0 && d2 > 0.01 && d2<d ) {
		intersection = orig + delta*d2;
		norm = normalize(spherec-intersection);
		d=d2;
		color=vec3(0.3,0.2,0.8);
	}

	d2 = (plane_y-orig.y)/delta.y;
	if( d2<d && d2 > 0.01) {
		intersection = orig + delta*d2;
		norm = vec3(0.0,-1.0,0.0);
		d=d2;
		color=vec3(0.1,0.3,0.2);
	}

	return traceResult( d, intersection, norm, color );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float st = sin(iTime);
	float ct = cos(iTime);
	vec3 delta = normalize( vec3(fragCoord.xy-0.5*iResolution.xy, iResolution.x*0.5) );
	delta = vec3(delta.x*ct-delta.z*st,delta.y,delta.z*ct+delta.x*st);
    vec3 orig = vec3( 1200.0*st, 0.0, 1600.0-1400.0*ct );
	vec3 lighta = vec3(1000, 1000, 1000);
	vec3 lightb = vec3(-1000, 2000, -1000);

	traceResult r = trace(orig, delta);

	vec3 delta2 = normalize(lighta-r.intersection);
	vec3 orig2 = r.intersection + 0.01*delta2;
	traceResult r2 = trace(orig2,delta2);

	vec3 delta3 = normalize(lightb-r.intersection);
	vec3 orig3 = r.intersection + 0.01*delta3;
	traceResult r3 = trace(orig3,delta3);

	if( ! (r.d > 0.001) ) {
		fragColor = vec4(1.0,0,0,0);
	}
	else {
		vec3 el = normalize(r.intersection-lighta);
		float e = dot( r.normal, el );
		float es = pow( dot( delta ,normalize(2.0*dot(el,r.normal)*r.normal-el) ), 100.0);
		//if(r2.d < distance(r2.intersection,lighta)-0.1 || e < 0.0){ e=0.0; es=0.0;} ;

		vec3 fl = normalize(r.intersection-lightb);
		float f = dot( r.normal, normalize(r.intersection-lightb) );
		float fs = pow( dot( delta ,normalize(2.0*dot(fl,r.normal)*r.normal-fl) ), 100.0);
		if(r3.d < distance(r3.intersection,lightb)-0.1 || f < 0.0) {f=0.0; fs=0.0;};
		//fragColor = vec4( sqrt(e*e+f*f)*r.color + (es+fs)*vec3(1.0,1.0,1.0), 1.0 );
        fragColor = vec4( sqrt(e*e+f*f)*r.color + (es+fs)*vec3(1.0,1.0,1.0), 1.0 );
	}
}

