vec3 c =  vec3(1.05);//vec3(.7,.9,1.41);
const int MaxIter = 12;
vec3 sundir = vec3(-1.0,0.0,0.0);


vec4 map( vec3 p)
{
	float dr = 1.0;
	float g=1.0;
	vec3 ot = vec3(1000.0); 
	float r2;

	for( int i=0; i<MaxIter;i++ )
	{          
        r2 = dot(p,p);
        if(r2>100.)continue;

        ot = min( ot, abs(p) );

		//Kali formula 
        p=abs(p)/r2*g-c;         
		dr= dr/r2*g;  		
	}

	float d;
	d = (abs(p.x)+abs(p.y))*length(p)/dr;	
	//d = (length(p.xz)*abs(p.y)+length(p.xy)*abs(p.z)+length(p.yz)*abs(p.x))/dr;
	//d = 1.5*(length(p.xz))*length(p.xy)/dr;
	//r2 = dot(p,p);d = .5*sqrt(r2)*log(r2)/dr;
	//d = 1.*length(p)/dr;
	return vec4(ot,d);

}



vec4 raymarch( in vec3 ro, in vec3 rd )
{
	vec4 sum = vec4(0, 0, 0, 0);

    float fog = .65+.6*cos(.3*iTime);


	float t = 0.0;
	for(int i=0; i<64; i++)
	{
		if( sum.a > 0.99 ) continue;

		vec3 pos = ro + t*rd;		
		vec4 col = map( pos );
		float d = col.a;
		col.a = 0.035*fog/d;
		col.rgb *= col.a;

		if(dot(pos,pos)<11.)sum = sum + col*(1.0 - sum.a);	

		//t += 0.1;
		t += min(0.1,d*.25);

	}

	sum.xyz /= (0.001+sum.w);

	return clamp( sum, 0.0, 1.0 );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0*q;
    p.x *= iResolution.x/ iResolution.y;
    vec2 mo = -1.0 + 2.0*(iMouse.xy) / iResolution.xy+sin(.1*iTime);

    // camera
    vec3 ro = 4.0*normalize(vec3(cos(2.75-3.0*mo.x), 0.7+(mo.y+1.0), sin(2.75-3.0*mo.x)));
	vec3 ta = vec3(0.0, 1.0, 0.0);
    vec3 ww = normalize( ta - ro);
    vec3 uu = normalize(cross( vec3(0.0,1.0,0.0), ww ));
    vec3 vv = normalize(cross(ww,uu));
    vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );


    vec4 res = raymarch( ro, rd );

	float sun = clamp( dot(sundir,rd), 0.0, 1.0 );
	vec3 col = vec3(0.6,0.71,0.75) - rd.y*0.2*vec3(1.0,0.5,1.0) + 0.15*0.5;
	col += 0.2*vec3(1.0,.6,0.1)*pow( sun, 8.0 );
	col *= 0.95;
	col = mix( col, res.xyz, res.w );
	col += 0.1*vec3(1.0,0.4,0.2)*pow( sun, 3.0 );

    fragColor = vec4( col, 1.0 );
}

#define PI 3.14159265359
#define MAX_ITER 128
#define MAX_ITER_INTERNAL 0
#define MAX_ITER_SHADOW 32
#define MAX_DIST_SHADOW 10.0
#define MIN_DIST_SHADOW 0.1
#define MAX_DIST 1000.0
#define MIN_DIST 0.0001
#define FOV 0.75
#define SPECULAR 5.0
#define SAMPLE_RADIUS 0.001
#define PENUMBRA 5.0
#define MULTISAMPLE 4
struct screen{
	vec2 p;
	vec2 r;
	vec2 m;
	float t;
};
struct ray{
	vec4 c;
	vec3 p;
	vec3 d;
	vec3 n;
	float t;
	int i;
};
struct light{
	vec4 c;
	vec3 p;
};
light l=light(
	vec4(0.5),
	vec3(0.0,-5.0,5.0)
);
vec4 a=vec4(0.5);
float s;
void srand(vec2 p){
	s=sin(dot(p,vec2(423.62431,321.54323)));
}
float rand(){
	s=fract(s*32322.65432+0.12333);
	return abs(fract(s));
}
mat2 rot2d(float a){
	float c=cos(a);
	float s=sin(a);
	return mat2(
		c,-s,
		s, c);
}
mat3 rot_x(float a){
	float c=cos(a);
	float s=sin(a);
	return mat3(
		1.0,0.0,0.0,
		0.0,  c, -s,
		0.0,  s,  c);
}
mat3 rot_y(float a){
	float c=cos(a);
	float s=sin(a);
	return mat3(
		c,  0.0,  s,
		0.0,1.0,0.0,
		-s, 0.0,  c);
}
mat3 rot_z(float a){
	float c=cos(a);
	float s=sin(a);
	return mat3(
		  c, -s,0.0,
		  s,  c,0.0,
	    0.0,0.0,1.0);
}
mat3 rot(vec3 z,float a){
	float c=cos(a);
	float s=sin(a);
	float b=1.0-c;
	return mat3(
		b*z.x*z.x+c,b*z.x*z.y-z.z*s,b*z.z*z.x+z.y*s,
		b*z.x*z.y+z.z*s,b*z.y*z.y+c,b*z.y*z.z-z.x*s,
		b*z.z*z.x-z.y*s,b*z.y*z.z+z.x*s,b*z.z*z.z+c);
}
float expstep(float x,float k,float n){
	return exp(-k*pow(x,n));
}
float smin(float a,float b,float k){
	float h=clamp(0.5+0.5*(b-a)/k,0.0,1.0);
    return mix(b,a,h)-k*h*(1.0-h);
}
float dome(inout ray r){
	float d=dot(r.p,vec3(0.0,0.0,1.0))+0.5;
	d=smin(d,50.0-length(r.p),10.0);
	if(d<0.0){
		if(fract(r.p.x)<0.5){
			r.c=vec4(0.0);
		}else{
			r.c=vec4(1.0);
		}		
		r.i=MAX_ITER_INTERNAL+1;
	}
	return d;
}
float box(inout ray r){
	float d=max(max(abs(r.p.x),abs(r.p.y))-0.5,abs(r.p.z)-0.5);
	if(d<0.0){
		if(fract(length(r.p)*10.0+0.25)<0.5)r.c=vec4(1.0);
		else r.c=vec4(0.5);
		r.i=MAX_ITER_INTERNAL+1;
	}
	return d;
}
float sphere(inout ray r){
	float d=length(r.p)-0.5;	
	if(d<0.0){
		r.c=vec4(1.0);
		r.i++;
	}
	return d;
}
float morph(inout ray r,vec3 o){
	vec3 p=r.p+o;
	float t=r.t/10.0;
	float d=0.0;
	t=fract(t);

	#define NUM_MORPH 4.0	
	float sphere=length(p)-0.5;
	float box=max(abs(p.x),max(abs(p.y),abs(p.z)))-0.5;
	float octa=dot(abs(p),normalize(vec3(1.0)))-0.29;
	float torus=length(vec2(length(p.xz)-0.25,p.y))-0.125;

	d+=max((NUM_MORPH*(abs(t-0.5))-0.5*(NUM_MORPH-2.0)),0.0)*octa;
	d+=max((1.0-NUM_MORPH*abs(t-1.0/NUM_MORPH)),0.0)*sphere;
	d+=max((1.0-NUM_MORPH*abs(t-2.0/NUM_MORPH)),0.0)*box;
	d+=max((1.0-NUM_MORPH*abs(t-3.0/NUM_MORPH)),0.0)*torus;

	if(d<0.0){
		if(fract(r.p.y*10.0)<0.5){
			r.c=vec4(0.0);
		}else{
			r.c=vec4(1.0);
		}		
		r.i=MAX_ITER_INTERNAL+1;
	}
	return d;
}
float dist(inout ray r){
	float d=MAX_DIST;
	d=min(d,dome(r));
	d=min(d,morph(r,vec3(0.0)));
	return d;
}
void normal(inout ray r){
	float d=dist(r);
	vec3 n=vec3(SAMPLE_RADIUS,0.0,0.0);
	ray r0=r;
	ray r1=r;
	ray r2=r;
	r0.p+=n.xyy;
	r1.p+=n.yxy;
	r2.p+=n.yyx;
	r.n=normalize(vec3(dist(r0)-d,dist(r1)-d,dist(r2)-d));
}
vec4 ambient(ray r){
	return r.c*a;
}
vec4 diffuse(ray r){
	vec3 v=l.p-r.p;
	return clamp(r.c*l.c*dot(r.n,normalize(v)),0.0,1.0);
}
vec4 specular(ray r){
	float d=length(l.p-r.p);
	vec3 v=normalize(l.p-r.p);
	return l.c*max(pow(dot(v,reflect(r.d,r.n)),SPECULAR),0.0);
}
vec4 shadow(ray r){
	float s=1.0;
	float t=MIN_DIST_SHADOW;
	for(int i=0;i<MAX_ITER_SHADOW;i++){
		ray tmp=r;
		tmp.p+=r.d*t;
		float h=dist(tmp);
		if(h<MIN_DIST)return vec4(0.0);
		s=min(s,PENUMBRA*h/t);
		t+=h;
		if(t>MAX_DIST_SHADOW)break;
	}
	return vec4(1.0)*s;
}
vec4 trace(inout ray r){
	r.c=vec4(1.0);
	for(int i=0;i<MAX_ITER;i++){
		float d=dist(r);
		if(r.i>MAX_ITER_INTERNAL)break;
		r.p+=r.d*max(d,MIN_DIST);
	}
	if(r.i==0){
		if(fract(r.p.x)<0.5){
			r.c=vec4(0.0);
		}else{
			r.c=vec4(1.0);
		}
	}
	normal(r);
	ray tmp=r;
	tmp.d=normalize(l.p-r.p);
	tmp.p-=2.0*MIN_DIST*r.d;	
	return ambient(r)+min(max(diffuse(r),specular(r)),shadow(tmp));
}
void initray(out ray r,screen s,vec2 o){
	vec3 l=vec3(0.0,0.0,0.0);
	vec3 tmp=vec3(2.0,0.0,0.0);
	tmp*=rot_y((PI*s.m.y)/4.0-PI/8.0);
	tmp*=rot_z(2.0*PI*s.m.x);
	vec3 e=l+tmp;
	vec3 u=vec3(0.0,0.0,1.0);
	vec3 d=normalize(l-e);
	vec3 h=normalize(cross(d,u));
	vec3 v=normalize(cross(h,d));
	d*=rot(v,FOV*s.p.x+o.x/s.r.x);
	d*=rot(h,FOV*s.p.y+o.x/s.r.y);
	r=ray(vec4(0.0),e,d,vec3(0.0),s.t,0);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	float t=iTime;
	float r=iResolution.x/iResolution.y;
	vec2 m=vec2(
		(iMouse.x-iResolution.x/2.0)/iResolution.x*r,
		(iMouse.y-iResolution.y/2.0)/iResolution.y);
	vec2 p=vec2(
		(fragCoord.x-iResolution.x/2.0)/iResolution.x*r,
		(fragCoord.y-iResolution.y/2.0)/iResolution.y);
	screen s=screen(p,iResolution.xy,m,t);
	srand(p);
	ray v;
	vec4 c=vec4(0.0);
	if(p.x<1.0/iResolution.x&&p.x>-1.0/iResolution.x){
		c=vec4(1.0,0.0,0.0,0.0);
	}else if(p.x<0.0){
		vec2 x=vec2(0.5,0.0);
		x*=rot2d(PI/8.0);
		for(int i=0;i<MULTISAMPLE;i++){
			initray(v,s,x);
			c+=trace(v)/float(MULTISAMPLE);		
			x*=rot2d(PI/2.0);
		}
	}else{
		initray(v,s,vec2(0.0));
		c=trace(v);
	}
	fragColor=c;
}

// http://www.fractalforums.com/new-theories-and-research/very-simple-formula-for-fractal-patterns/

const int iterations=20;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 z=fragCoord.xy / iResolution.xy-.5;
	z.y*=iResolution.y/iResolution.x;

	z.x += sin(z.y*2.0+iTime * .2)/10.0;
	z*=.6+pow(sin(iTime*.05),10.)*10.;
	z+=vec2(sin(iTime*.08),cos(iTime*.01));
	z=abs(vec2(z.x*cos(iTime*.12)-z.y*sin(iTime*.12)
			  ,z.y*cos(iTime*.12)+z.x*sin(iTime*.12)));

	vec2 c=vec2(.2, 0.188);

	float expsmooth=0.;
	float average=0.;
	float l=length(z);
	float prevl;

	for (int i=0; i<iterations; i++) 
	{
		z = abs(z * (2.2 + cos(iTime*0.2)))/(z.x*z.y)-c;	

		prevl=l;
		l=length(z);
		expsmooth+=exp(-.2/abs(l-prevl));
		average+=abs(l-prevl);
	}

	float brightness = expsmooth*.002;

	average/=float(iterations) * 22.87;

	vec3 myColor=vec3(max(abs(sin(iTime)), 0.45),max(abs(cos(iTime * 0.2)), 0.45),max(abs(sin(iTime* 2.)), 0.45));
	vec3 finalColor;

	finalColor.r = (float(average)/myColor.r);
	finalColor.g = (float(average)/myColor.g);
	finalColor.b = (float(average)/myColor.b);

	fragColor = vec4(finalColor*brightness,1.0);
}

