vec2 iSphere( in vec3 ro, in vec3 rd, in vec4 sph )
{
	vec3 oc = ro - sph.xyz;
	float b = dot( oc, rd );
	float c = dot( oc, oc ) - sph.w*sph.w;
	float h = b*b - c;
	if( h<0.0 ) return vec2(-1.0);
	h = sqrt(h);
	return vec2(-b-h, -b+h );
}

const int MaxIter = 12;
vec3 sundir = vec3(0.0,0.5,-1.0);
float g=.8;
float h=1.;



vec4 map( vec3 p)
{
	float dr = 1.0;
	vec3 ot = vec3(1000.0); 
	float r2;
	for( int i=0; i<MaxIter;i++ )
	{

        r2 = dot(p,p);
        if(r2>100.)continue;

        ot = min( ot, abs(p) );


		float k = max(h/r2,1.)*g;
		p  *= k;
		dr *= k;
		p=abs(p-0.5)-1.;		

	}

	float d;
	//d = (abs(p.x)+abs(p.y))*length(p)/dr;	
	//d = (length(p.xz)*abs(p.y)+length(p.xy)*abs(p.z)+length(p.yz)*abs(p.x))/dr;
	//d = 1.5*(length(p.xz))*length(p.xy)/dr;
	//d = 1.*length(p)*log(length(p))/dr;
	//d =1.*length(p)/dr;
    d=abs(p.x)/dr;
	return vec4(ot,d);

}

vec4 raymarch( in vec3 ro, in vec3 rd , in vec2 tminmax )
{
	vec4 sum = vec4(0, 0, 0, 0);

	float t = tminmax.x;
	for(int i=0; i<64; i++)
	{
		if( sum.a > 0.99 || t>tminmax.y ) continue;

		vec3 pos = ro + t*rd;		
		vec4 col = map( pos );
		float d = col.a;
		col.a = min(0.002/d,1.);
		col.rgb *= col.a;

		sum = sum + col*(1.0 - sum.a);
        t += min(0.1,d*.3);	

	}

	sum.xyz /= (0.001+sum.w);

	return clamp( sum, 0.0, 1.0 );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0*q;
    p.x *= iResolution.x/ iResolution.y;
    vec2 mo = iMouse.xy / iResolution.xy;
    float an = 2.0 + 0.2*iTime - mo.x;

	vec3 ro = 2.0*vec3(cos(an), 0.17, sin(an));
	vec3 ta = vec3(0.0, 0.0, 0.0);
    vec3 ww = normalize( ta - ro);
    vec3 uu = normalize( cross( vec3(0.0,1.0,0.0), ww ) );
    vec3 vv = normalize( cross(ww,uu) );
    vec3 rd = normalize( p.x*uu + p.y*vv + 2.0*ww );

	vec3 col = vec3(0.08,0.09,0.18);
    vec2 seg = iSphere( ro, rd, vec4(0.0,0.0,0.0,1.0) );
	if( seg.x>0.0 )
	{
        vec4 res = raymarch( ro, rd, seg );
	    col = mix( col, res.xyz, res.w );
	}

	col = mix( col, vec3(dot(col,vec3(0.333))), -0.1 );

	col = pow( col, vec3(0.45) ) * 1.2;

	col *= sqrt( 16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y) );

    fragColor = vec4( col, 1.0 );
}

// Andrew Caudwell 2014
// @acaudwell

#define MAX_RAY_STEPS 100
#define PI 3.14159265359

#define DEGREES_TO_RADIANS 0.017453292

#define KIFS_ITERATIONS 12

struct KIFS {
    vec3  offset;
    float scale;
    vec3  axis;
    float angle;
    mat4  transform;
};

KIFS KIFS_constructor(vec3 offset, vec3 axis, float angle, float scale) {
        KIFS kifs;
        kifs.offset = offset;
        kifs.axis   = axis;
        kifs.angle  = angle;
        kifs.scale  = scale;

        return kifs;
}

mat4 calc_transform(inout KIFS kifs) {
    float angle = kifs.angle * DEGREES_TO_RADIANS;

    float c = cos(angle);
    float s = sin(angle);

    vec3 t = (1.0-c) * kifs.axis;

    return mat4(
        vec4(c + t.x * kifs.axis.x, t.y * kifs.axis.x - s * kifs.axis.z, t.z * kifs.axis.x + s * kifs.axis.y, 0.0) * kifs.scale,
        vec4(t.x * kifs.axis.y + s * kifs.axis.z, (c + t.y * kifs.axis.y), t.z * kifs.axis.y - s * kifs.axis.x, 0.0) * kifs.scale,
        vec4(t.x * kifs.axis.z - s * kifs.axis.y, t.y * kifs.axis.z + s * kifs.axis.x, c + t.z * kifs.axis.z, 0.0) * kifs.scale,
        vec4(kifs.offset, 1.0)
    );
}

#define t iTime*0.6

int stage_no;
float stage_t;

KIFS kifs_N;
KIFS kifs_lerp;

void InitIFS() {

    if(stage_no >= KIFS_ITERATIONS) {
        stage_no = KIFS_ITERATIONS-(stage_no-KIFS_ITERATIONS)-1;
        stage_t  = 1.0-stage_t;
    }

    // KIFS to visualize
    kifs_N = KIFS_constructor(vec3(-1.5), normalize(vec3(-1.0)), -36.0, 1.5);

    kifs_lerp.axis   = kifs_N.axis;
    kifs_lerp.angle  = kifs_N.angle;

    // interpolate scale and position offset
    kifs_lerp.offset =kifs_N.offset * stage_t;
    kifs_lerp.scale  = 1.0 + (kifs_N.scale-1.0) * stage_t;

    // left mouse button disables interpolation
    if(iMouse.z>0.0) {
        kifs_lerp =kifs_N;
    }

    kifs_N.transform    = calc_transform(kifs_N);
    kifs_lerp.transform = calc_transform(kifs_lerp);
}

// The definitive Fractal Forums thread about this class of fractals:
// http://www.fractalforums.com/ifs-iterated-function-systems/kaleidoscopic-%28escape-time-ifs%29/

float scene(vec3 p) {

    KIFS kifs = kifs_N;

    float scale = 1.0;

    for(int i=0;i<KIFS_ITERATIONS;i++) {

        if(i==stage_no)kifs =kifs_lerp;
        else if(i>stage_no) break;

        // mirror on 2 axis to get a tree shape
        p.xy = abs(p.xy);

        // apply transform
        p = (kifs.transform * vec4(p, 1.0)).xyz;

        scale *=kifs.scale;
    }

    // divide by scale preserve correct distance
    return (length(p)-2.0) / scale;
}

vec3 normal(vec3 p) {

    vec2 o = vec2(0.001,0.0);

    float d = scene(p);

    float d1 = d-scene(p+o.xyy);
    float d2 = d-scene(p+o.yxy);
    float d3 = d-scene(p+o.yyx);

    return normalize(vec3(d1,d2,d3));
}

float AO(vec3 p, vec3 normal) {

    float a = 1.0;

    float c = 0.0;
    float s = 0.25;

    for(int i=0; i<3; i++) {
        c += s;
        a *= 1.0-max(0.0, (c -scene(p + normal*c)) * s / c);
    }

    return clamp(a,0.0,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    stage_no  = int(fract(t/float(KIFS_ITERATIONS*2)) * float(KIFS_ITERATIONS*2));
    stage_t = smoothstep(0.0, 1.0, fract(t));

    InitIFS();

    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;

    vec3 dir = normalize(vec3(uv.x, uv.y * (iResolution.y/iResolution.x), 1.0));

    vec3 p = vec3(0.0,0.0,-4.1);

    float d = 0.0;

    for(int i=0; i<MAX_RAY_STEPS; i++) {
        d = scene(p);
        p += d * dir;
        if(d<0.001) break;
    }

    vec3 c = vec3(0.0);

    if(d<0.001) {
        vec3 l = vec3(-3.0, 3.0, -3.0);

        vec3 n = -normal(p-dir*0.001);

        c = vec3(0.4);

        c += 1.5 * (max(0.0, dot(n, normalize(l-p)))/length(l-p));

        c *= AO(p, 0.5*(n+normalize(n+l)));
    }

    fragColor = vec4(c,1.0);
}

// Mandelbulb shader by cedric voisin 2014

// tuning
#define nIterMax 6
#define rSat .05 
#define rLight 1.4 
// relative base intensities (can take any value >0)
#define iSpec 3.
#define iTrap 1.5
#define iShad 10.
#define iAO 1.

float nMandelB; // nMandelB=8 for the usual bulb
vec3 colMb=.8*vec3(1.,1.,.9); // MB  olor
vec3 colSat = .8*vec3(1.,1.,.95); // sat color

//
//vec2 realSize = vec2(2.,2.*iResolution.y/iResolution.x);
//vec2 po=(fragCoord.xy-iResolution.xy/2.)*realSize/iResolution.xy;
vec3 col;
float zMin; // smallest distance reached by the orbit of the computed point
float pi=3.14;
float piSur2=1.57;
bool isSatOnTrajectory, isMbOnTrajectory;
vec3 ptInterSphere;

// def eye (the MB is fixed, the eye moves around)
float thetaScr;
float phiScr;
float dScr=5.;	// origin to (center of) screen
float dEye=5.;	// screen to eye


// Screen normal and directions
vec3 vN;
vec3 vUp;
vec3 vRight;
// Screen position
vec3 zScr;
// Eye position
vec3 zEye;


// Distance Estimator
// returns 0 if the test point is inside the MB, and the distance 0.5*r*logr/rD otherwise
float DE(vec3 ptTest){
	float theta,phi,r; // point
	float newtheta,newphi,newr;
	float dr;
	vec3 z=ptTest; // iterated point
	dr=1.;
	zMin=1000.;
	for (int i=0;i<nIterMax;i++){
		r=length(z);
		if (r>=pow(1.+float(nIterMax),2.)) break;
		newr=pow(r,nMandelB);
		dr=1.+dr*nMandelB*pow(r,nMandelB-1.);
		theta=acos(z.z/r);
		phi=atan(z.y,z.x);
		newtheta=theta*nMandelB;
		newphi=phi*nMandelB;
		z=ptTest+newr*vec3(sin(newtheta)*cos(newphi),sin(newtheta)*sin(newphi),cos(newtheta));
		zMin=min(zMin,length(z));
	}
	return(0.5*newr*log(newr)/dr);
}

// intersection with sphere
bool sphereInter(vec3 center, float rad, vec3 dir){
	vec3 result;
	vec3 dirSphere = zEye-center;
	float B = dot(dir,dirSphere);
	float C = length(dirSphere)*length(dirSphere)-rad*rad;
	float det2=B*B-C;
	if(det2>=0.){
		float s = min(-B+sqrt(det2),-B-sqrt(det2));
		ptInterSphere = zEye+dir*s;
		return true;
	} else {
		return false;
	}
}

// returns the nearest MB point
vec3 getMbPoint(vec2 ptTest){
	vec3 zRes;
	vec3 z=zScr+ptTest.x*vRight+ptTest.y*vUp;// position of the tested pixel (on the screen) in space
	vec3 dzds=normalize(z-zEye);
	isMbOnTrajectory = sphereInter(vec3(0.,0.,0.),2.,dzds);
	if (!isMbOnTrajectory) return z+dzds;
	float maxDist=10.;

	float s=0.;
	float de;
	// ray tracing
	for (int i=0;i<100;i++){ // awkward for because webgl forbids non constant loops :-(
		zRes=z+dzds*s;
		de=clamp(DE(zRes),0.000001,maxDist);
		if(de==0.000001) break; // distance estimated is small enough: we are on the MB
		if(s>dScr+5.) break; // gone to far: we didn't cross the MB
		s+=.5*de;
	}
	isMbOnTrajectory=true;
	if (s>=dScr+5.) isMbOnTrajectory=false;
	return (zRes);
}


// the light has to follow the eye
vec3 setLight(){
	float dthetaLight=pi+-pi/6.*cos(iTime/7.);
	float dphiLight=iTime/7.;
	// light direction
	float phiLight=phiScr+dphiLight;
	float thetaLight=thetaScr+dthetaLight;
	// light position
	return rLight*vec3(sin(thetaLight)*cos(phiLight),sin(thetaLight)*sin(phiLight),cos(thetaLight));
}

// normal
vec3 normal(vec3 ptO){
	vec3 dPt=vec3(.01,0.,0.);
	vec3 n=normalize(vec3(DE(ptO+dPt.xyy),DE(ptO+dPt.yxy),DE(ptO+dPt.yyx))); 
	return n;
}

// orbit trap coloring
// proportionnal to the (normalized) smallest distance reached by the orbit of the computed point
float orbitTrap(){
	float origin=.6; // to tune contrast and depth view
	return (zMin-origin)/(1.-origin);
}

// specularity (coloration by normal)
float specularity(vec3 n,vec3 vLight){
	return dot(n,normalize(vLight));
}

// ambiant occlusion
// compute the distance from MB for nk test points in the normal direction
// the smaller the distance, the larger the occlusion
float ambiantOcclusion(vec3 ptO,vec3 n){
	const int nk=6;
	float dt=.05; // small t for constrast, large t for soft shadows
	vec3 pTest; // test point
	float dTest; // test distance (pTest <-> MB)
	float ao=1.; // AO intensity
	float ikf,nkf;
	for (int ik=0;ik<nk;ik++){
		ikf = float(ik);
		nkf = float(nk);
		pTest=ptO+n*ikf*dt;
		dTest=DE(pTest);
		ao-=(ikf*dt-dTest);
	}
	return ao;
}

// shadows
// try some points on the light line. The more points inside the MB, the darkest the shadow.
float shadows(vec3 ptO,vec3 vLight){
	float L=length(vLight-ptO);
	vec3 dzds=normalize(vLight-ptO);
	float nbInMB=0.;
	const int nbPtTestInt=100; // number of test points on the line
	float nbPtTest=float(nbPtTestInt);	
	float s=0.;
	for (int i=0;i<nbPtTestInt;i++){
		if(DE(ptO+dzds*s)<0.01) nbInMB++;
		s+=L/nbPtTest;
	}
	return 1.-nbInMB/nbPtTest;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {	

    // constants
    nMandelB=4.+2.*cos(.01*iTime+1.57);
    thetaScr=piSur2;
    phiScr=-iTime/47.;
    vN=-vec3(sin(thetaScr)*cos(phiScr),sin(thetaScr)*sin(phiScr),cos(thetaScr));
	vUp=normalize(dScr*vec3(sin(thetaScr-piSur2)*cos(phiScr),sin(thetaScr-piSur2)*sin(phiScr),cos(thetaScr-piSur2)));
	vRight = cross(vN,vUp);
	zScr=-dScr*vN;
	zEye=-(dScr+dEye)*vN;
    //

	vec2 poNoScale=-1.+2.*fragCoord.xy/iResolution.xy;
	vec2 poScale=poNoScale*vec2(1.,iResolution.y/iResolution.x);
	vec3 vLight=setLight();
	vec3 ptMb=getMbPoint(poScale);
	isSatOnTrajectory = sphereInter(vLight, rSat, normalize(ptMb-zEye));
	vec3 ptSat= ptInterSphere;
	bool satFirst;
	vec3 n,obj;

	if (!isMbOnTrajectory) satFirst=true;
	if (!isSatOnTrajectory) satFirst=false;
	if (isMbOnTrajectory && isSatOnTrajectory){
		if(length(ptSat-zEye)<length(ptMb-zEye)) {satFirst=true;} else {satFirst=false;}
	}

	if(isMbOnTrajectory || isSatOnTrajectory) {
		if (satFirst){
			float intSat=clamp((1.+dot(normalize(ptSat-vLight),-normalize(vLight)))/2.,0.,1.);
			col = intSat*colSat;
		} else {
			n = normal(ptMb);
			// coefficients for intensities (0<c<1)
			float cTrap = orbitTrap();
			float cSpec = specularity(n,vLight);
			float cAO = ambiantOcclusion(ptMb,n);
			float cShad = shadows(ptMb,vLight);

			// Average intensity (weighted with computed coefs)
			float Intensity=clamp((cSpec*iSpec+cAO*iAO+cTrap*iTrap+cShad*iShad)/(iSpec+iTrap+iShad+iAO),0.,1.);
			col = Intensity*Intensity*colMb.xyz;
		}
	}

	if(!isMbOnTrajectory && !isSatOnTrajectory){// background
		float bgInt=clamp(1.-length(fragCoord.xy-iResolution.xy/2.)/length(iResolution.xy),0.,1.);
		col = .95*bgInt*vec3(1.,1.,1.);
	}
	fragColor= vec4(col,1.);
}

// KALI FRACTAL
vec3 kali( vec3 v , vec3 s ){
	float m = 0.0;
	for( int i = 0; i < 10 ; i ++){
	v.x = abs(v.x);
	v.y = abs(v.y); 
	v.z = abs(v.z);
	m = v.x * v.x + v.y * v.y + v.z * v.z;
	v.x = v.x / m + s.x;
	v.y = v.y / m + s.y;
	v.z = v.z / m + s.z;
    }
	return v;
}



