vec3 mod289(vec3 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec4 mod289(vec4 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec4 permute(vec4 x) {
     return mod289(((x*34.0)+1.0)*x);
}

vec4 taylorInvSqrt(vec4 r)
{
  return 1.79284291400159 - 0.85373472095314 * r;
}

float snoise(vec3 v)
  { 
  const vec2  C = vec2(1.0/6.0, 1.0/3.0) ;
  const vec4  D = vec4(0.0, 0.5, 1.0, 2.0);

// First corner
  vec3 i  = floor(v + dot(v, C.yyy) );
  vec3 x0 =   v - i + dot(i, C.xxx) ;

// Other corners
  vec3 g = step(x0.yzx, x0.xyz);
  vec3 l = 1.0 - g;
  vec3 i1 = min( g.xyz, l.zxy );
  vec3 i2 = max( g.xyz, l.zxy );

  //   x0 = x0 - 0.0 + 0.0 * C.xxx;
  //   x1 = x0 - i1  + 1.0 * C.xxx;
  //   x2 = x0 - i2  + 2.0 * C.xxx;
  //   x3 = x0 - 1.0 + 3.0 * C.xxx;
  vec3 x1 = x0 - i1 + C.xxx;
  vec3 x2 = x0 - i2 + C.yyy; // 2.0*C.x = 1/3 = C.y
  vec3 x3 = x0 - D.yyy;      // -1.0+3.0*C.x = -0.5 = -D.y

// Permutations
  i = mod289(i); 
  vec4 p = permute( permute( permute( 
             i.z + vec4(0.0, i1.z, i2.z, 1.0 ))
           + i.y + vec4(0.0, i1.y, i2.y, 1.0 )) 
           + i.x + vec4(0.0, i1.x, i2.x, 1.0 ));

// Gradients: 7x7 points over a square, mapped onto an octahedron.
// The ring size 17*17 = 289 is close to a multiple of 49 (49*6 = 294)
  float n_ = 0.142857142857; // 1.0/7.0
  vec3  ns = n_ * D.wyz - D.xzx;

  vec4 j = p - 49.0 * floor(p * ns.z * ns.z);  //  mod(p,7*7)

  vec4 x_ = floor(j * ns.z);
  vec4 y_ = floor(j - 7.0 * x_ );    // mod(j,N)

  vec4 x = x_ *ns.x + ns.yyyy;
  vec4 y = y_ *ns.x + ns.yyyy;
  vec4 h = 1.0 - abs(x) - abs(y);

  vec4 b0 = vec4( x.xy, y.xy );
  vec4 b1 = vec4( x.zw, y.zw );

  //vec4 s0 = vec4(lessThan(b0,0.0))*2.0 - 1.0;
  //vec4 s1 = vec4(lessThan(b1,0.0))*2.0 - 1.0;
  vec4 s0 = floor(b0)*2.0 + 1.0;
  vec4 s1 = floor(b1)*2.0 + 1.0;
  vec4 sh = -step(h, vec4(0.0));

  vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;
  vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;

  vec3 p0 = vec3(a0.xy,h.x);
  vec3 p1 = vec3(a0.zw,h.y);
  vec3 p2 = vec3(a1.xy,h.z);
  vec3 p3 = vec3(a1.zw,h.w);

//Normalise gradients
  vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
  p0 *= norm.x;
  p1 *= norm.y;
  p2 *= norm.z;
  p3 *= norm.w;

// Mix final noise value
  vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
  m = m * m;
  return 42.0 * dot( m*m, vec4( dot(p0,x0), dot(p1,x1), 
                                dot(p2,x2), dot(p3,x3) ) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	float ar = iResolution.x/iResolution.y;

	vec4 audio = texture( iChannel0 , uv );
	uv = audio.xy * .01 + uv;

	float noise = snoise( ar * vec3( uv.x * 10. , uv.y * 10. , 10.+10.*sin(iTime * .087) ));
	float noise2 = snoise( ar * vec3( uv.x * 2. , uv.y * 2. , 2.+2.*sin(iTime * .065) ));
	float noise1 = snoise( ar * vec3( uv.x * 5. , uv.y * 5. , 5.+5.*sin(iTime * .03) ));
	float noise3 = snoise( ar * vec3( uv.x * 20. , uv.y * 20. , 20.+20.*sin(iTime * .042) ));
	/*
	float noise = snoise( ar * vec3( uv.x * 10. , uv.y * 10. , 10.+10.*sin(iTime * .1) ));
	float noise2 = snoise( ar * vec3( uv.x * 2. , uv.y * 2. , 2.+2.*sin(iTime * .1) ));
	float noise1 = snoise( ar * vec3( uv.x * 5. , uv.y * 5. , 5.+5.*sin(iTime * .1) ));
	float noise3 = snoise( ar * vec3( uv.x * 20. , uv.y * 20. , 20.+20.*sin(iTime * .1) ));
	*/
	vec3 kali1 = kali( vec3( -uv.x , -uv.y , - .5+.5*sin(iTime )) , vec3( -abs( uv.y )  , -abs( noise1 ) , -abs(noise2 ) ) );
	vec3 kali2 = kali( vec3( -uv.x , -uv.y , - .5+.5*sin(iTime )) , vec3( -abs( uv.x )  , -abs( noise2 ) , -abs(noise ) ) );

	fragColor = vec4( normalize( kali1 + kali2  ),1.0);
}

/*
 * This study includes
 *
 * 1) findUV: a function that finds two orthogonal vectors, given a 
 * normal vector. The normal vector has to be given in spherical
 * coordinates, (theta, phi). findUV calculates an orthonormal
 * local coordinate system (n, u, v). This can be used in texture
 * mapping a plane, or orienting the camera.
 *
 * 2) iPlane2: Ray-plane intersection method. Its parameters are (besides
 * the ray parameters) the normal and the distance from the origin
 * of the plane.
 *
 * 3) checkerBoard3: An anti-aliased checker board pattern generator,
 * it does not need that (u,v) to be in [0,1]. they can go to infinity,
 * the pattern will repeat inifinitely.
 * It does work for closeby checker cells, but distant ones still
 * produce Moire patterns.
 *
 * 4) iBox: A box-ray intersection function. It is different than
 * the "axis-aligned box" method. It first defines 3 arbitrary normals.
 * (if these planes are orthogonal to each other, than we'll get a
 * rectangular box) For each normal, it defines two parallel planes on which
 * the sides of the box will lie.
 * The Kay-Kasia algorithm is applied to 3 slabs and the intersection
 * position and normal at the intersection are returned.
 *
 * 5) intersect: deals with whether we hit nothing, the ground plane or
 * the cube.
 *
 * 6) A camera implementation. the camera direction is given in spherical
 * coordinates. and u and v vectors on the camera plane are found
 * via findUV. Then, rays are sent with the formula:
 * normalize(sX * cU + sY * cV + FOV*cDir);
 * sX, sY: screen coordinates, cU,cV: camera UV. cDir: given direction.
 * The FOV parameter came for free. :-)
 * 
 */

#define PI 3.14159265359
#define TWO_PI 6.28318530718

vec3 light = normalize( vec3(0.57703) );
vec4 plane;
vec4 sph1;


// given spherical coordinates (theta, phi) of a normal vector (OR longitude and latitude)
// find the normal vector, and two vectors that are perpendicular to it (and each other)
// that lie on the plane defined by the normal vector
void findUV(in float th, in float ph, out vec3 dir, out vec3 u, out vec3 v) {
	dir = vec3(sin(th)*cos(ph), cos(th), sin(th)*sin(ph));
	// Second direction, V of the plane, is PI/2 upwards towards the north pole
	float thV, phV;
	if(th < 0.5*PI) {
		thV = 0.5*PI - th;
		phV = mod(ph + PI, TWO_PI);
	} else {
		thV = th - 0.5*PI;
		phV = ph;
	}
	v = vec3(sin(thV)*cos(phV), cos(thV), sin(thV)*sin(phV));
	// Third direction, U of plane, is perpendicular to both
	u = cross(dir, v);
}

// the bad example from IQ's article, Haha!
// http://www.iquilezles.org/www/articles/noacos/noacos.htm
mat3 rotationAxisAngle( vec3 v, float angle )
{
    float s = sin( angle );
    float c = cos( angle );
    float ic = 1.0 - c;

    return mat3( v.x*v.x*ic + c,     v.y*v.x*ic - s*v.z, v.z*v.x*ic + s*v.y,
                 v.x*v.y*ic + s*v.z, v.y*v.y*ic + c,     v.z*v.y*ic - s*v.x,
                 v.x*v.z*ic - s*v.y, v.y*v.z*ic + s*v.x, v.z*v.z*ic + c );
}

// assumes (u,v) in [0,1]. Divides the ares into nTile x nTile squares.
vec3 checkerBoard(vec2 uv, float nTile) {
	vec3 color1 = vec3(1.0, 1.0, 1.0);
	vec3 color2 = vec3(0.0, 0.0, 0.0);
	float side = 1.0/nTile;
	float c1 = mod(uv.x, 2.0 * side);
	c1 = step(side, c1);
	float c2 = mod(uv.y, 2.0 * side);
	c2 = step(side, c2);	
	vec3 color = mix(color1, color2, mod(c1+c2,2.0));
	return color;
}


// infinite area, tiles of size "side"
float stripe(float x, float side, float smth) {
	x = mod(x, 2.0*side);
	return smoothstep(0.0 - smth, 0.0 + smth, -x)
         + smoothstep(1.0*side - smth, 1.0*side + smth, x)
         - smoothstep(2.0*side - smth, 2.0*side + smth, x);
}
float checkerBoard3(vec2 uv, float side, float smth) {
	//smooth *= length(1.0*uv);
	float xStripes = stripe(uv.x, side, smth);
	float yStripes = stripe(uv.y, side, smth);
	float checker = abs(xStripes+yStripes-1.0);
	return checker;
}


float iPlane2(vec3 o, vec3 d, vec4 plane) { // ray Origin, ray Direction
	vec3 n = plane.xyz;
	float dist = -plane.w;
	float denom = dot(n, d);

	return - (dot(n, o)+dist)/denom;
}
vec3 nPlane2(vec3 pos) {
	return plane.xyz;
}

void swap(inout float x, inout float y) {
	if(x>y) {
		float temp = x;
		x = y;
		y = temp;
	}		
}

float iBox(vec3 o, vec3 d, vec3 center, vec3 sides, out vec3 normal) {
	vec3 axis = normalize(vec3(1.0, 1.0, 0.0));
	mat3 rot = rotationAxisAngle(axis, PI/2.);
	vec3 xn = rot*vec3(1.0, 0.0, 0.0);
	vec3 yn = rot*vec3(0.0, 1.0, 0.0);
	vec3 zn = rot*vec3(0.0, 0.0, 1.0);
	axis = normalize(vec3(0.0, 1.0, 0.0));
	rot = rotationAxisAngle(axis, iTime);
	xn = rot*xn;
	yn = rot*yn;
	zn = rot*zn;	
	float t1, t2;
	float tNear = -10000.0;
	float tFar = 10000.0;

	o = o - center; // translation

	// x -> yz-plane
	t1 = iPlane2(o, d, vec4(xn, -0.5*sides.x));
	t2 = iPlane2(o, d, vec4(xn, 0.5*sides.x));
	if(t1>t2) swap(t1, t2);
	if(t1 > tNear) { normal = xn; tNear = t1; }
	if(t2 < tFar) tFar = t2;
	if(tNear > tFar || tFar < 0.0) return -1.0;

	// y -> xz-plane
	t1 = iPlane2(o, d, vec4(yn, -0.5*sides.y));
	t2 = iPlane2(o, d, vec4(yn, 0.5*sides.y));	
	if(t1>t2) swap(t1, t2);
	if(t1 > tNear) { normal = yn; tNear = t1; }
	if(t2 < tFar) tFar = t2;
	if(tNear > tFar || tFar < 0.0) return -1.0;

	// z -> xy-plane
	t1 = iPlane2(o, d, vec4(zn, -0.5*sides.z));
	t2 = iPlane2(o, d, vec4(zn, 0.5*sides.z));	
	if(t1>t2) swap(t1, t2);
	if(t1 > tNear) { normal = zn; tNear = t1; }
	if(t2 < tFar) tFar = t2;
	if(tNear > tFar || tFar < 0.0) return -1.0;

	return tNear;
}

