	float cPhi = 0.2*PI*sin(iTime)-0.5*PI; // polar phi of direaction
	float cAlpha = 0.1*PI*cos(iTime); // tilt
	float FOV = 1.5; // smaller number wider view and vice versa.
	// direct the camera at given latitude and longitude
	vec3 cDir, cU, cV;
	findUV(cTheta, cPhi, cDir, cU, cV);	
	// Tilt the camera
	mat3 tilt = rotationAxisAngle(cDir, cAlpha);
	cU = tilt*cU; // just rotate camera U and V. Yay, tt works!
	cV = tilt*cV;	
	vec2 scan = (-1.0+2.0*uv)*vec2(1.78, 1.0); // magical numbers
	vec3 rd = normalize(scan.x * cU + scan.y * cV + FOV*cDir);
	//vec3 rd = normalize(vec3( (-1.0+2.0*uv)*vec2(1.78, 1.0), -1.0));	

	// ground
	float pTheta = 0.0*PI;
	float pPhi = 0.3*PI;
	vec3 pNorm, pU, pV;
	findUV(pTheta, pPhi, pNorm, pU, pV);
	plane = vec4(pNorm, 0.0);

	// send rays to detect intersections with objects
	float t;
	vec3 normal;
	// intersect will return the object type "id"
	// and set the ray parameter "t" and normal at the intersection point
	int id = intersect(ro, rd, t, normal);

	vec3 bgCol = vec3(244., 198., 61.)/255.;
	vec3 col = bgCol;
	// hit id
	vec3 pos = ro + t*rd;
	if(id == 3) { // hit the floor
		vec3 nor = nPlane2( pos );		
		float dif = clamp( dot(nor, light), 0.0, 1.0);
		float amb = 0.2;
		// find uv on the plane at the intersection
		vec3 projection = pos - dot(pos,nor)*nor;
		float u = dot(projection, pU);
		float v = dot(projection, pV);
		float side = 2.0;
		//vec3 texture = checkerBoard2( vec2(u, v), side );
		vec3 color1 = vec3(4., 105., 103.)/255.;
		vec3 color2 = vec3(234., 229., 206.)/255.;
		vec3 aveColor = mix(color1, color2, 0.5);
		float mixVal = checkerBoard3( vec2(u, v), side, 0.02*side );
		vec3 texture = mix(color1, color2, mixVal);
		float distVal = clamp(exp(-0.0005*t*t),0.0,0.99);
		vec3 fadedToAverage = mix(aveColor, texture, distVal*distVal);
		//vec3 texture = vec3( checkerBoard3( vec2(u, v), side, 0.5*side ) );
		//col = mix(texture*dif,bgCol, 0.001*t); // fade to background color
		//col = texture*dif;
		col = fadedToAverage*dif;
	} else if(id == 4) { // hit the box
		float dif = clamp( abs(dot(normal, light)), 0.0, 1.0);
		vec3 boxColor = vec3(215., 2., 6.)/255.;
		col = boxColor*dif;
	}
	col = sqrt(col*1.5);
	fragColor = vec4(col, 1.0);
}

