	float a = -(0.6+0.2*sin(iTime*3.1+sin((iTime*0.8+h*2.0)*3.0))*sin(iTime+h));
	float b = -(0.8+0.3*sin(iTime*1.7+sin((iTime+h*4.0))));
	float c = 1.25+sin((iTime+sin((iTime+h)*3.0))*1.3)*0.15;
	float l = a*x*x + b*x + c;
