	vec3 cp = vec3(0.0, 1.0,0.0);		// Y vector
	vec3 cu = cross(cw,cp);				// X vector
	vec3 dir = normalize(uv.x*cu + uv.y*cp + 1.3*cw);
	vec3 col = Scene(cameraPos, dir);

	fragColor=vec4(col,1.0);
}

