mat4 calc_transform(vec3 offset, vec3 axis, float angle, float scale) {

    angle *= radians(1.0);

    float c = cos(angle);
    float s = sin(angle);

    vec3 t = (1.0-c) * axis;

    return mat4(
        vec4(c + t.x * axis.x, t.y * axis.x - s * axis.z, t.z * axis.x + s * axis.y, 0.0) * scale,
        vec4(t.x * axis.y + s * axis.z, (c + t.y * axis.y),          t.z * axis.y - s * axis.x, 0.0) * scale,
        vec4(t.x * axis.z - s * axis.y, t.y * axis.z + s * axis.x, c + t.z * axis.z, 0.0) * scale,
        vec4(offset, 1.0)
    );
}

mat4 M;

float KIFS(vec3 p, float s) {

	p /= s;

	for(int i=0;i<KIFS_ITERATIONS;i++) {

		p = abs(p);

		// apply transform
		p = (M * vec4(p, 1.0)).xyz;                             
	}

	// divide by scale preserve correct distance
	return ((length(p)-1.0) * (pow(1.5, -float(KIFS_ITERATIONS))))*s;
}

vec3 dir;

bool intersect(vec3 p, float r) {

    float b = 2.0 * dot(dir, p);
    float c = dot(p, p) - r*r;

    float sq = sqrt(b*b - 4.0*c);

    float t1 = (-b + sq) * 0.5;
    float t2 = (-b - sq) * 0.5;

    float near = min(t1, t2);
    float far  = max(t1, t2);

    return near < far && far > 0.0;
}


void combineKIFS(vec3 p, float s, vec3 c, inout vec4 o) {

#ifdef INTERSECTION_TEST
    if(intersect(p, s*1.75)) {
#endif
		float d = KIFS(p,s);
		if(d<o.x) o = vec4(d,c);
#ifdef INTERSECTION_TEST
	}
#endif
}

#define SF 0.2

vec3 sp = normalize(vec3(1.0,1.0,-1.0));

vec4 scene(vec3 p) {

	vec3 p2 = p - (sp + sp*SF);
	vec3 p3 = p - (sp + sp*SF*2.0 + sp*SF*SF);
	vec3 p4 = p - (sp + sp*SF*2.0 + sp*SF*SF*2.0 + sp*SF*SF*SF);
	vec3 p5 = p - (sp + sp*SF*2.0 + sp*SF*SF*2.0 + sp*SF*SF*SF*2.0 + sp*SF*SF*SF*SF);
	vec3 p6 = p - (sp + sp*SF*2.0 + sp*SF*SF*2.0 + sp*SF*SF*SF*2.0 + sp*SF*SF*SF*SF*2.0 + sp*SF*SF*SF*SF*SF);

	vec4 o = vec4(10000.0,vec3(0.0));

	combineKIFS(p,1.0,             col1, o);
	combineKIFS(p2,SF,             col2, o);
	combineKIFS(p3,SF*SF,          col3, o);
	combineKIFS(p4,SF*SF*SF,       col4, o);
	combineKIFS(p5,SF*SF*SF*SF,    col5, o);
	//combineKIFS(p6,SF*SF*SF*SF*S, col6, o);

	return o;       
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    M = calc_transform(vec3(-0.4,-0.4,-0.55),normalize(vec3(1.0, 1.0, 1.0)), 40.0, 1.5);    

    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;

	dir = normalize(vec3(uv.x, uv.y * (iResolution.y/iResolution.x), 0.665));

	float t = log(1.0 + 3.0*fract(iTime/3.0)) / log(4.0);

	vec3 t1 = sp + sp*SF*2.0 + sp*SF*SF + vec3(-0.05,-0.05,-SF);
	vec3 t2 = sp + sp*SF*2.0 + sp*SF*SF*2.0 + sp*SF*SF*SF + vec3(-0.05*SF,-0.05*SF,-SF*SF);

	vec3 cam = t1 + (t2-t1) * t;

	float d = 1.0;
	float ray_length = 0.0;

	int steps = 0;

	vec3 bg = vec3(0.0, 0.55, 1.0)*(pow(length(vec2(uv.x,uv.y*2.0)),0.5)-1.05);

	vec3 c = bg;

	vec4 s = vec4(0.0);

	float lod = 0.56 /max(iResolution.x,iResolution.y);

	for(int i=0; i<MAX_RAY_STEPS; i++) {
		if(d<lod*ray_length) continue;
		s = scene(cam);
		d = s.x;
		cam += d * dir;
		ray_length += d;
		steps++;
	}

	if(ray_length<1.0) {
		c = s.yzw;

		float cost = float(steps)/float(MAX_RAY_STEPS);

		// cost based shading

		c *= pow(max(0.0, 1.0 - cost),5.0);

		c += pow(max(0.0, 1.0 - cost),27.0);

		if(uv.y>0.1) c = mix(c,bg,cost*step(0.5,cost));
	}

    fragColor = vec4(c,1.0);
}

#define FARCLIP    35.0

#define MARCHSTEPS 90

#define PI         3.14
#define PI2        PI*0.5    

#define MAT1       1.0

#define FOV 1.0


/***********************************************/
float rbox(vec3 p, vec3 s, float r) {	
    return length(max(abs(p)-s+vec3(r),0.0))-r;
}

vec2 rot(vec2 k, float t) {
    float ct=cos(t); 
    float st=sin(t);
    return vec2(ct*k.x-st*k.y,st*k.x+ct*k.y);
}

void oprep2(inout vec2 p, float l, float s, float k) {
	float r=1./l;
	float ofs=s+s/(r*2.0);
	float a= mod( atan(p.x, p.y) + PI2*r*k, PI*r) -PI2*r;
	p.xy=vec2(sin(a),cos(a))*length(p.xy) -ofs;
	p.x+=ofs;
}
/***********************************************
float hash(float n) { 
	return fract(sin(n)*43758.5453123); 
}
float noise3(vec3 x) {
    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);
    float n = p.x + p.y*57.0 + p.z*113.0;
    float res = mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                        mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y),
                    mix(mix( hash(n+113.0), hash(n+114.0),f.x),
                        mix( hash(n+170.0), hash(n+171.0),f.x),f.y),f.z);
    return res;
}
/***********************************************/

vec2 DE(vec3 p) {

vec3 q=p;
    //ball
    oprep2(p.xz,8.0,0.1, -iTime);
    oprep2(p.zy,16.0,0.25, -iTime); 
    float t=rbox(p,vec3(0.25,0.025,0.1),0.05);   
   //tube

    p=q; 
    p.y-=1.0;  
    p.xy=rot(p.xy,p.z*0.15); 
    p.z+=iTime;
    p.xy=mod(p.xy,6.0)-0.5*6.0;    
    p.xy=rot(p.xy,-floor(p.z/0.75)*0.35);   
    p.z=mod(p.z,0.75)-0.5*0.75;
    oprep2(p.xy,3.0,0.15, iTime);  

    float c=rbox(p,vec3(0.1,0.025,0.25),0.05);
   //tube
  p=q; 
    p.y+=2.0;  
    p.yz=rot(p.yz,p.x*0.15); 
    p.x+=iTime;
    p.yz=mod(p.yz,6.0)-0.5*6.0;    
    p.yz=rot(p.yz,-floor(p.x/0.75)*0.35);   
    p.x=mod(p.x,0.75)-0.5*0.75;
    oprep2(p.yz,3.0,0.15, iTime);  
    float r=rbox(p,vec3(0.25,0.1,0.025),0.05);

  t= min(min(t,c),r);

	return vec2( t*0.85 , MAT1);
}
/***********************************************/
vec3 normal(vec3 p) {
	vec3 e=vec3(0.01,-0.01,0.0);
	return normalize( vec3(	e.xyy*DE(p+e.xyy).x +	e.yyx*DE(p+e.yyx).x +	e.yxy*DE(p+e.yxy).x +	e.xxx*DE(p+e.xxx).x));
}
/***********************************************
void rot( inout vec3 p, vec3 r) {
	float sa=sin(r.y); float sb=sin(r.x); float sc=sin(r.z);
	float ca=cos(r.y); float cb=cos(r.x); float cc=cos(r.z);
	p*=mat3( cb*cc, cc*sa*sb-ca*sc, ca*cc*sb+sa*sc,	cb*sc, ca*cc+sa*sb*sc, -cc*sa+ca*sb*sc,	-sb, cb*sa, ca*cb );
}
/***********************************************/
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
    p.x *= iResolution.x/iResolution.y;	
	vec3 ta = vec3(0.0, 0.0, 0.0);
	vec3 ro =vec3(0.0, 0.0, -10.0);

//	vec2 mp=iMouse.xy/iResolution.xy;

//	float ma=iTime*0.2+ sin(iTime*0.5);
//	float mb=iTime*0.1- cos(iTime*0.1);
//	rot(ro,vec3(mp.x,mp.y,0.0));
    ro.xz=rot(ro.xz,iTime*0.23);
    ro.xy=rot(ro.xy,iTime*0.12);

	vec3 cf = normalize( ta - ro );
    vec3 cr = normalize( cross(cf,vec3(0.0,1.0,0.0) ) );
    vec3 cu = normalize( cross(cr,cf));
	vec3 rd = normalize( FOV*p.x*cr + FOV*p.y*cu + 2.5*cf );

	vec3 col=vec3(0.0);
	/* trace */

	vec2 r=vec2(0.0);	
	float d=0.0;
	vec3 ww;
	for(int i=0; i<MARCHSTEPS; i++) {
		ww=ro+rd*d;
		r=DE(ww);
        if( r.x<0.0 || r.x>FARCLIP ) break;
        d+=r.x;
	}
    r.x=d;
	/* draw */


	float lp=sin(iTime)+1.5;
    vec3 nor=normal(ww);

	if( r.x<FARCLIP ) {

        col=vec3(0.43,0.84,0.97); 

    	float amb=0.5;	
    	float dif=0.5*clamp(dot(ww,-nor),-1.0,1.0);
    	float bac=0.2*clamp(dot(ww,nor),0.0,1.0);

	    col *= amb + dif +bac;

	    float spe= pow(clamp( dot( nor, reflect(ww,nor) ), 0.0, 1.0 ) ,16.0 );
	    col += 0.5*pow(spe,1.0);

        col*=lp;

        col*=exp(-0.2*r.x); col*=1.5;
	} 

    /* sun */
    vec3 lpos=vec3(0.0, 0.0, 0.0);

	float m = dot(rd, -ro);
	      d = length(ro-lpos+m*rd);

	float a = -m;
	float b = r.x-m;
	float aa = atan(a/d);
	float ba = atan(b/d);
	float to = (ba-aa)/d;

    col+=to*0.15*lp;
    /* sun */

	fragColor = vec4( col, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	float time = iTime;
	vec4 t = texture(iChannel0,uv);
	float c = sin(t.r+1.0*uv.y)*uv.x/atan(t.r*1.0*uv.y+time*0.5)*uv.x*time*0.01;
	c+=abs(tan(sin(uv.x+abs(cos(time*0.1)*sin(time*0.003))*2.3)+cos(uv.y+cos(time)*sin(time*0.4)*0.5))*uv.x*2.5*t.x*0.01+time*0.5);
	c-=abs(tan(sin(uv.y+abs(sin(time*0.1)*sin(time*0.004))*3.1)+cos(uv.x+sin(time)*cos(time*0.4)*0.5))*uv.x*2.5*t.x*0.01+time*0.5);
	fragColor = vec4(c,c,c,1.0);
}

