        -dot(m[1].xyz,m[3].xyz),
        -dot(m[2].xyz,m[3].xyz),
        1.0 );
}*/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy) / iResolution.y;

     // camera movement	
	float an = 0.4*iTime;
	vec3 ro = vec3( 2.5*cos(an), 1.0, 2.5*sin(an) );
    vec3 ta = vec3( 0.0, 0.8, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
	// create view ray
	vec3 rd = normalize( p.x*uu + p.y*vv + 2.0*ww );

    // rotate and translate box	
	mat4 rot = rotationAxisAngle( normalize(vec3(1.0,1.0,0.0)), iTime );
	mat4 tra = translate( 0.0, 1.0, 0.0 );
	mat4 txi = tra * rot; 
	mat4 txx = inverse( txi );

    // raytrace
	float tmin = 10000.0;
	vec3  nor = vec3(0.0);
	vec3  pos = vec3(0.0);

	// raytrace-plane
	float oid = 0.0;
	float h = (0.0-ro.y)/rd.y;
	if( h>0.0 ) 
	{ 
		tmin = h; 
		nor = vec3(0.0,1.0,0.0); 
		oid = 1.0;
	}

	// raytrace box
	vec3 box = vec3(0.4,0.6,0.8) ;
	vec4 res = iBox( ro, rd, txx, txi, box);
	if( res.x>0.0 && res.x<tmin )
	{
		tmin = res.x; 
		nor = res.yzw;
		oid = 2.0;
	}

    // shading/lighting	
	vec3 col = vec3(0.6,0.75,0.85) - 0.97*rd.y;

	if( tmin<100.0 )
	{
		pos = ro + tmin*rd;

        // material
		float occ = 1.0;
		vec3  mate = vec3(1.0);
		if( oid<1.5 ) // plane
		{
		    mate = 0.35*texture( iChannel0, 0.25*pos.xz ).xyz;
			occ = 0.2 + 0.8*smoothstep( 0.0, 1.5, length(pos.xz) );
		}			
		else // box
		{
            // recover box space data (we want to do shading in object space)			
		    vec3 opos = (txx*vec4(pos,1.0)).xyz;
			vec3 onor = (txx*vec4(nor,0.0)).xyz;
		    mate = abs(onor.x)*texture( iChannel0, 0.5*opos.yz ).xyz + 
                   abs(onor.y)*texture( iChannel0, 0.5*opos.zx ).xyz + 
                   abs(onor.z)*texture( iChannel0, 0.5*opos.xy ).xyz;
			mate *= 0.35;
            occ = 0.6 + 0.4*nor.y;
		}		

        // lighting
        vec3  lig = normalize(vec3(0.8,0.4,-0.6));
        float dif = clamp( dot(nor,lig), 0.0, 1.0 );
        vec3  hal = normalize(lig-rd);
        float sha = step( iBox( pos+0.001*nor, lig, txx, txi, box ).x, 0.0 );
        float amb = 0.6 + 0.4*nor.y;
        float bou = clamp(0.3-0.7*nor.y,0.0,1.0);
        float spe = clamp(dot(nor,hal),0.0,1.0);
        col  = 4.0*vec3(1.00,0.80,0.60)*dif*sha;
        col += 2.0*vec3(0.20,0.30,0.40)*amb;
        col += 2.0*vec3(0.30,0.20,0.10)*bou;
        col *= mate;            
        col += 0.3*pow(spe,8.0)*dif*sha*(0.04+0.96*pow(clamp(dot(lig,hal),0.0,1.0),5.0));
        col = mix( col, vec3(0.6,0.7,0.8), 1.0-exp(-0.001*tmin*tmin) );           
	}

    // vignetting        
    col *= 1.0 - 0.1*dot(p,p);

    // gamma
    col = pow( col, vec3(0.45) );

	// grading
    col = clamp(col,0.0,1.0);
    col = col*col*(3.0-2.0*col);

	fragColor = vec4( col, 1.0 );
}

#define PI					3.14159265359
#define PIH 				(PI/2.0)
#define PIQ 				(PI/4.0)
#define PI2 				(PI*2.0)
#define MARCH_SCALE			0.95
#define MARCH_STEPS			48
#define PRECISION			0.001

#define LIGHT_POS			vec3(0.0, 0.0, -5.0)
#define COLOR_1				vec3(-2.5, 2.5, 4.0)
#define COLOR_2				vec3(4.0, 1.5, -1.0)

// Used inside the scene function, requires you to declare float depthCurr = 1000.0; float depthPrev = 1000.0; and float matID = -1.0;
#define OBJ(inOBJ, inMatID) depthPrev=depthCurr; depthCurr=min(depthCurr, inOBJ); if(depthCurr < depthPrev) matID = inMatID;

vec3 GetRayDir(vec2 inTC, vec2 inAspectRatio)	{return normalize(vec3((-0.5 + inTC)*1.2 * inAspectRatio, 1.0));}
mat3 RotX(float a)								{float s = sin(a); float c = cos(a); return mat3(1,0,0,0,c,s,0,-s,c);}
mat3 RotY(float a)								{float s = sin(a); float c = cos(a); return mat3(c,0,-s,0,1,0,s,0,c);}
mat3 RotZ(float a)								{float s = sin(a); float c = cos(a); return mat3(c,s,0,-s,c,0,0,0,1);}

// Global variables
vec2 CrossObj(vec3 p)
{
	float depthCurr	= 1000.0;
	float depthPrev	= 1000.0;
	float matID		= -1.0;

	const float cylinderHeight	= 1.0;
	const float cylinderRadius	= 0.005;


	vec3 crossp					= cross(vec3(1, 0, 0), vec3(sin(iTime),0,cos(iTime)));
	float crossHeight			= crossp.y;

	OBJ(max(length(p.zy)-cylinderRadius, abs(p.x-cylinderHeight*0.5)-cylinderHeight*0.5), 0.0); // X - Axis

	OBJ(max(length(p.xz)-cylinderRadius, abs(p.y-crossHeight*0.5)-abs(crossHeight)*0.5), 1.0);	// Y - Axis

	// We rotate only the Z axis vector
	p = RotY(iTime) * p;
	OBJ(max(length(p.xy)-cylinderRadius, abs(p.z-cylinderHeight*0.5)-cylinderHeight*0.5), 2.0); // Z - Axis

	OBJ(length(p)-0.05, 3.0); // Z - Axis

	return vec2(depthCurr, matID);
}


vec3 GetColor(float id)
{
	// Background color
	vec3 color = vec3(0,0,0);

	// Material Colors
	if(id == 0.0)color=vec3(1.0, 0.0, 0.0); // Red
	if(id == 1.0)color=vec3(0.0, 1.0, 0.0); // Green
	if(id == 2.0)color=vec3(0.0, 0.0, 1.0); // Blue
	if(id == 3.0)color=vec3(0.4, 0.4, 0.4); // Grey

	return color;
}


vec2 Intersect(vec3 ro, vec3 rd)
{		
	vec2 r		= vec2(1000.0, -1.0);
	float z		= 1.1;
	float matID	= -1.0;

	for(int i=0; i<MARCH_STEPS; i++)
	{
		r = CrossObj(ro + rd*z);
		if(r.x < PRECISION)
			continue;
		z		+=r.x*MARCH_SCALE;
		matID	= r.y;
	}

	if(z > 3.1)
		z = 0.0;

	return vec2(z, matID);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv		= fragCoord.xy / iResolution.xy;

	// Construct simple ray
	mat3 xrot	= RotX(0.5);
	vec2 aspect	= vec2(iResolution.x/iResolution.y, 1.0);
	vec3 ro		= xrot * vec3(0, 0.0, -2);
	vec3 rd		= xrot * GetRayDir(uv, aspect);

	// Draw background color
	vec3 c1		= mix(COLOR_2, COLOR_1, pow(uv.x, 2.0));
	vec3 c2		= mix(COLOR_2, COLOR_1, pow(uv.y, 2.0));
	vec3 color	= mix(c1, c2, 0.5) * 0.1;

	vec2 res	= Intersect(ro, rd);
	vec3 p;
	vec3 n;
	if(res.x > 0.0)
		color = GetColor(res.y);

	fragColor = vec4(color, 1.0);
}

