vec2 globalSetup(vec2 fragCoord){
	vec2 pct = fragCoord.xy / iResolution.xy;
	vec2 xy = -screenWidth/2.+screenWidth*pct;
	xy.y *= iResolution.y/iResolution.x;
	return xy;
}

void setupCam(vec3 p, float thetaCam, float phiCam, float f){
	pCam = p;
	fCam = f;
	nCam = vec3(cos(thetaCam)*cos(phiCam),cos(thetaCam)*sin(phiCam),sin(thetaCam));
	uCam = vec3(cos(thetaCam+PI/2.)*cos(phiCam),cos(thetaCam+PI/2.)*sin(phiCam),sin(thetaCam+PI/2.));
	rCam = cross(nCam,uCam);
	pEye = pCam - fCam*nCam;
}

//
// perlin
//

float rand(vec2 c){
	return fract(sin(dot(c.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float noise(vec2 p, float freq ){
	float unit = screenWidth/freq;
	vec2 ij = floor(p/unit);
	vec2 xy = mod(p,unit)/unit;
	//xy = 3.*xy*xy-2.*xy*xy*xy;
	xy = .5*(1.-cos(PI*xy));
	float a = rand((ij+vec2(0.,0.)));
	float b = rand((ij+vec2(1.,0.)));
	float c = rand((ij+vec2(0.,1.)));
	float d = rand((ij+vec2(1.,1.)));
	float x1 = mix(a, b, xy.x);
	float x2 = mix(c, d, xy.x);
	return mix(x1, x2, xy.y);
}

float pNoise(vec2 p, int res){
	float persistance = .5;
	float n = 0.;
	float normK = 0.;
	float f = 4.;
	float amp = 1.;
	int iCount = 0;
	for (int i = 0; i<50; i++){
		n+=amp*noise(p, f);
		f*=2.;
		normK+=amp;
		amp*=persistance;
		if (iCount == res) break;
		iCount++;
	}
	float nf = n/normK;
	return nf*nf*nf*nf;
}

//
// objects
//

// field(x,y)
float field(vec2 p, int res){
	float z = maxHeight*pNoise(p, res);
	return z;
}

float iField(vec3 pStart, vec3 dir, int res){
	vec3 p = pStart;
	float s = 0.;
	float h = 1.;
	for (int i=0; i<250; i++){
		h = p.z - field(p.xy, res);
		if (h<.1 || length(dir*s) > fov) break;
		s+=0.1*h;
		p=pStart+dir*s;
	}
	if (h >= .1) s = -1.;
	return s;
}

vec3 nField(vec3 p, int res){
	vec2 dp = vec2(.0001,0.);
	vec3 dpx = vec3(2.*dp.x, 0., field(p.xy+dp.xy,res)-field(p.xy-dp.xy,res));
	vec3 dpy = vec3(0., 2.*dp.x, field(p.xy+dp.yx,res)-field(p.xy-dp.yx,res));
	return normalize(cross(dpx,dpy));
}

// Swamp
float iSwamp(vec3 pStart, vec3 dir){
	float s=(swampHeight-pStart.z)/dir.z;
	if (length(dir*s) > fov) s=-1.;
	return s;	
}

// clouds
float iClouds (vec3 pStart, vec3 dir){
	float zClouds=15.;
	float s=(zClouds-pStart.z)/(dir.z+zClouds/fov);
	if (length(dir*s) > fov) s=-1.;
	return s;	
}

// Sky
float iSky (vec3 dir){
	float s;
	vec3 d = pEye;
	float B = dot(dir,d);
	float C = length(d)*length(d)-rSky*rSky;
	float det2=B*B-C;
	if(det2>=0.){
		s = max(-B+sqrt(det2),-B-sqrt(det2));
	} else {
		s = -1.;
	}
	return s;
}

//
// routines
//

float shadows(vec3 pi, vec3 pSun) {
	float L=distance(pi, pSun)/10.;
	vec3 dpds=normalize(pSun-pi);

	const int ni=20;
	float n=float(ni);	

	float s=0.;
	float iShad = 1.;

	for (int i=0;i<ni;i++){
		vec3 pt = pi+dpds*s;
		float h = pt.z - field(pt.xy, iHighRes);
		s+=L/n;
		if(h < 0.5) iShad*=.9;
	}

	return iShad;
}

vec3 clouds(vec3 p, vec2 wind, vec3 colSky, vec3 colClouds, vec2 fragCoord){
	float grad=clamp(1.5-fragCoord.y / iResolution.y,0.,1.);
	vec2 stretch = vec2 (3.,1.);
	return grad*mix(colSky, colClouds,field(.1*(p.xy+wind)*stretch,5));
}

float intersection(vec3 p, vec3 d, vec3 pLight, int res){
	float sit = 1000.*fov;

	idI = idBackground;
	float si = sit;

	sit = min(iField(p, d, res), si);
		if (sit >= 0. && sit < si){
		idI = idField;
		si = sit;
	}

	sit = min(iSwamp(p, d), si);
		if (sit >= 0. && sit < si){
		idI = idSwamp;
		si = sit;
	}

	sit = min(iClouds(p, d), si);
		if (sit >= 0. && sit < si){
		idI = idClouds;
		si = sit;
	}

	return si;
}

//
// at last :-)
//

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // constants
    time = iTime;
    //

	vec2 xy = globalSetup(fragCoord);
	float si;

	// time
	float camTime = 0.;
	float dayTime = dayTimeOrigin;
	#ifdef CAM_MOTION
		camTime = .03*time;
	#endif
	#ifdef NIGHT_AND_DAY
		dayTime = .2*time+dayTimeOrigin;
	#endif

	// cam
	float zoCam = swampHeight + screenWidth*iResolution.y/iResolution.x;
	float camOrbit = 100.;
	float xCam = camOrbit*sin(camTime);
	float yCam = -camOrbit*cos(camTime);
	setupCam(vec3 (xCam,yCam,zoCam),camTilt,camTime,camFocal);

	// sun
	float sunOrbit=2.*camOrbit;
	rSky = sunOrbit;
	vec3 pSun = vec3(1.5*sunOrbit*sin(dayTime),sunOrbit*cos(dayTime),sunOrbit*sin(dayTime));

	// wind	(speed)
	vec2 wind = vec2 (1.,-5.);

	// virtual screen
	vec3 pStart = pCam+xy.x*rCam+xy.y*uCam;
	vec3 dir = normalize(pStart-pEye);

	// ray march
	si = intersection(pStart, dir, pSun, iLowRes);
	vec3 pi = pStart + dir*si;
	vec3 nor = nField(pi, iHighRes);
	vec3 norLowRes = nField(pi, iLowRes);

	// colors
	vec3 col = vec3(0.,0.,0.);
	vec3 colSky = vec3(.2,.5,1.);
	float redishClouds = pow(1.2-abs(pSun.y/sunOrbit),.5);
	vec3 colClouds = vec3(1.,1.*redishClouds,.9*redishClouds);
	vec3 colSun = vec3 (1.,1.,.7);
	vec3 colGrass = vec3(.1,.4,.1);
	vec3 colMountain = vec3(.7,.6,.5);
	vec3 colFog = vec3(.5,.6,.7);
	vec3 colField = colMountain;
	if (nor.z > .8) colField = colGrass;

	// night and day corrections
	colSky*=smoothstep(-1.2,1.,sin(dayTime));
	colFog*=smoothstep(-1.2,1.,sin(dayTime));

	// illuminations
	if (idI == idBackground){ // in principle, never used here
		float cBg=clamp(1.5-fragCoord.y / iResolution.y,0.,1.);
		col += cBg*colSky;
		col = mix (colSky, vec3(1.,1.,1.),field(xy,7));
	}

	if (idI == idField){
		float iShad = shadows(pi, pSun);
		float iSpec=.5+.5*dot(nField(pi, iHighRes),normalize(pSun-pi));

		// AO
		vec3 p;
		float sAmb=0.;
		float iAmb = 1.;
		for (int i=0;i<5;i++) {
			float h = p.z - field (p.xy, iLowRes);
			if (h < sAmb) iAmb*=.8;
			sAmb+=float(i)*.2;
			p = pi + norLowRes*sAmb;
		}

		col += .2*iSpec*colSun;
		col += .2*nor.z*colSky;
		col += iSpec*colField;
		col *=iShad;
		col *=iAmb;
		col += .4*iSpec*colField*smoothstep(-.3,1.,sin(dayTime+PI)); // night light

		col = smoothstep(0.,1.2,col);
	}

	if (idI == idSwamp){		
		float iShad = shadows(pi, pSun);

		// clouds for fake reflection
		col += clouds(pi, wind*time, colSky, colClouds, fragCoord);

		// moisture
		col += 3.*mix (vec3(0.,.1,0.), vec3(.4,.7,.4),20.*pNoise(pi.xy,7));

		// foam
		col += 1.*mix (vec3(0.,0.,0.), vec3(.9,1.,.9),pNoise(5.*pi.xy,7));

		col *= iShad;
		col = smoothstep(0.,3.,col);
	}

	if (idI == idClouds){
		// clouds
		vec3 colCloudsInSky = clouds(pi, wind*time, colSky, colClouds, fragCoord);

		// sky (sphere)
		float siSky = iSky(dir);
		vec3 piSky = pStart + dir*siSky;
		float theta = acos(piSky.z/rSky);
		float phi = abs(atan(piSky.y, piSky.x)); // symmetric sky because perlin(2pi)!=perlin(0)
		vec3 colSkySphere = mix(colSky, vec3(.05,.1,.3),field(20.*vec2(theta, phi),7));

		// stars
		float pN = pNoise(2000.*vec2(theta, phi),5);
		colSkySphere += mix(colSky, vec3(1.,1.,1.),pN);

		col = mix(colCloudsInSky, colSkySphere, smoothstep(-.5,2.,sin(dayTime+PI)));
	}

	// fog
	float cFog = 1.-smoothstep(.3*fov, fov, length(dir*si))+smoothstep(fov+1., fov+2.,length(dir*si));
    col = mix(colFog, col, cFog );

	col = clamp(col,0.,1.);
	fragColor = vec4(col,1.);
}

#define DELTA				0.001
#define RAY_COUNT			5
#define RAY_LENGTH_MAX		100.0
#define RAY_STEP_MAX		50
#define LIGHT				vec3 (1.0, 1.0, -1.0)
#define REFRACT_FACTOR		0.6
#define REFRACT_INDEX		1.2 // 2.417 for real diamonds... but it would require RAY_COUNT to be increased (because of total internal reflections)
#define AMBIENT				0.2
#define SPECULAR_POWER		3.0
#define SPECULAR_INTENSITY	0.5
#define FADE_POWER			1.0
#define M_PI				3.1415926535897932384626433832795
#define GLOW_FACTOR			0.5
#define LUMINOSITY_FACTOR	2.0

//#define ATAN2 // Comment this to use the original atan function

mat3 mRotate (in vec3 angle) {
	float c = cos (angle.x);
	float s = sin (angle.x);
	mat3 rx = mat3 (1.0, 0.0, 0.0, 0.0, c, s, 0.0, -s, c);

	c = cos (angle.y);
	s = sin (angle.y);
	mat3 ry = mat3 (c, 0.0, -s, 0.0, 1.0, 0.0, s, 0.0, c);

	c = cos (angle.z);
	s = sin (angle.z);
	mat3 rz = mat3 (c, s, 0.0, -s, c, 0.0, 0.0, 0.0, 1.0);

	return rz * ry * rx;
}

vec3 vRotateY (in vec3 p, in float angle) {
	float c = cos (angle);
	float s = sin (angle);
	return vec3 (c * p.x - s * p.z, p.y, c * p.z + s * p.x);
}

#ifndef ATAN2
#define atan2 atan
#else
float atan2 (in float y, in float x) {

	// From http://www.deepdyve.com/lp/institute-of-electrical-and-electronics-engineers/full-quadrant-approximations-for-the-arctangent-function-tips-and-V6yJDoI0iF
	// atan (x) = x / (1 + 0.28086 x^2)

	float t1 = abs (y);
	float t2 = abs (x);
	float t3 = min (t1, t2) / max (t1, t2);
	t3 = t3 / (1.0 + 0.28086 * t3 * t3);
	t3 = t1 > t2 ? M_PI / 2.0 - t3 : t3;
	t3 = x < 0.0 ? M_PI - t3 : t3;
	t3 = y < 0.0 ? -t3 : t3;
	return t3;
}

float atan2_nobranch (in float y, in float x) {

	// From http://www.deepdyve.com/lp/institute-of-electrical-and-electronics-engineers/full-quadrant-approximations-for-the-arctangent-function-tips-and-V6yJDoI0iF
	// atan (x) = x / (1 + 0.28086 x^2)

	float t1 = abs (y);
	float t2 = abs (x);
	float t3 = min (t1, t2) / max (t1, t2);
	t3 = t3 / (1.0 + 0.28086 * t3 * t3);
	float t4 = M_PI / 2.0 - t3;
	t3 = step (0.0, t2 - t1) * (t3 - t4) + t4;
	t4 = M_PI - t3;
	t3 = step (0.0, x) * (t3 - t4) + t4;
	t3 = step (0.0, y) * (t3 + t3) - t3;
	return t3;
}
#endif

vec3 normalTopA = normalize (vec3 (0.0, 1.0, 1.4));
vec3 normalTopB = normalize (vec3 (0.0, 1.0, 1.0));
vec3 normalTopC = normalize (vec3 (0.0, 1.0, 0.8));
vec3 normalBottomA = normalize (vec3 (0.0, -1.0, 1.6));
vec3 normalBottomB = normalize (vec3 (0.0, -1.0, 2.0));
vec3 k;
float getDistance (in vec3 p) {
	float repeat = 20.0;
	vec3 q = p + repeat * 0.5;
	k = floor (q / repeat);
	q -= repeat * (k + 0.5);
	p = mRotate (k + iTime) * q;

	float topCut = p.y - 1.1;
	float angleStep = M_PI / max (1.0, abs (4.0 + k.x + 2.0 * k.y + 4.0 * k.z));
	float angle = angleStep * (0.5 + floor (atan2 (p.x, p.z) / angleStep));
	q = vRotateY (p, angle);
	float topA = dot (q, normalTopA) - 2.0;
	float bottomA = dot (q, normalBottomA) - 2.0;
	float topC = dot (q, normalTopC) - 1.8;
	q = vRotateY (p, -angleStep * 0.5);
	angle = angleStep * floor (atan2 (q.x, q.z) / angleStep);
	q = vRotateY (p, angle);
	float bottomB = dot (q, normalBottomB) - 1.95;
	float topB = dot (q, normalTopB) - 1.92;

	return max (topCut, max (topA, max (topB, max (topC, max (bottomA, bottomB)))));
}

vec3 getFragmentColor (in vec3 origin, in vec3 direction) {
	vec3 lightDirection = normalize (LIGHT);
	vec2 delta = vec2 (DELTA, 0.0);

	vec3 fragColor = vec3 (0.0, 0.0, 0.0);
	float intensity = 1.0;

	float distanceFactor = 1.0;
	float refractionRatio = 1.0 / REFRACT_INDEX;
	float rayStepCount = 0.0;
	for (int rayIndex = 0; rayIndex < RAY_COUNT; ++rayIndex) {

		// Ray marching
		float dist = RAY_LENGTH_MAX;
		float rayLength = 0.0;
		for (int rayStep = 0; rayStep < RAY_STEP_MAX; ++rayStep) {
			dist = distanceFactor * getDistance (origin);
			float distMin = max (dist, DELTA);
			rayLength += distMin;
			if (dist < 0.0 || rayLength > RAY_LENGTH_MAX) {
				break;
			}
			origin += direction * distMin;
			++rayStepCount;
		}

		// Check whether we hit something
		vec3 backColor = vec3 (0.0, 0.0, 0.1 + 0.2 * max (0.0, dot (-direction, lightDirection)));
		if (dist >= 0.0) {
			fragColor = fragColor * (1.0 - intensity) + backColor * intensity;
			break;
		}

		// Get the normal
		vec3 normal = normalize (distanceFactor * vec3 (
			getDistance (origin + delta.xyy) - getDistance (origin - delta.xyy),
			getDistance (origin + delta.yxy) - getDistance (origin - delta.yxy),
			getDistance (origin + delta.yyx) - getDistance (origin - delta.yyx)));

		// Basic lighting
		vec3 reflection = reflect (direction, normal);
		if (distanceFactor > 0.0) {
			float relfectionDiffuse = max (0.0, dot (normal, lightDirection));
			float relfectionSpecular = pow (max (0.0, dot (reflection, lightDirection)), SPECULAR_POWER) * SPECULAR_INTENSITY;
			float fade = pow (1.0 - rayLength / RAY_LENGTH_MAX, FADE_POWER);

			vec3 localColor = max (sin (k * k), 0.2);
			localColor = (AMBIENT + relfectionDiffuse) * localColor + relfectionSpecular;
			localColor = mix (backColor, localColor, fade);

			fragColor = fragColor * (1.0 - intensity) + localColor * intensity;
			intensity *= REFRACT_FACTOR;
		}

		// Next ray...
		vec3 refraction = refract (direction, normal, refractionRatio);
		if (dot (refraction, refraction) < DELTA) {
			direction = reflection;
			origin += direction * DELTA * 2.0;
		} else {
			direction = refraction;
			distanceFactor = -distanceFactor;
			refractionRatio = 1.0 / refractionRatio;
		}
	}

	// Return the fragment color
	return fragColor * LUMINOSITY_FACTOR + GLOW_FACTOR * rayStepCount / float (RAY_STEP_MAX * RAY_COUNT);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Define the ray corresponding to this fragment
	vec2 frag = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
	vec3 direction = normalize (vec3 (frag, 2.0));

	// Set the camera
	vec3 origin = vec3 ((15.0 * cos (iTime * 0.1)), 10.0 * sin (iTime * 0.2), 15.0 * sin (iTime * 0.1));
	vec3 forward = -origin;
	vec3 up = vec3 (sin (iTime * 0.3), 2.0, 0.0);
	mat3 rotation;
	rotation [2] = normalize (forward);
	rotation [0] = normalize (cross (up, forward));
	rotation [1] = cross (rotation [2], rotation [0]);
	direction = rotation * direction;

	// Set the fragment color
	fragColor = vec4 (getFragmentColor (origin, direction), 1.0);
}

#define PI 3.14159265359
#define TWO_PI 6.28318530718

// smooth min from, guess who? Yes, from iq. :-)
float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}
float smax( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( a, b, h ) + k*h*(1.0-h);
}

float creatureShape( vec3 p ) {
	float rr = dot(p.xz, p.xz);
	float r = sqrt(rr);
	float dist = 10000.0;
	const int nTentacle = 3;
	for(int i=0; i<nTentacle; i++){
		float ii = float(i);
		float th = ii * TWO_PI / float(nTentacle);
		// bend the veins, comment out to make the shader faster
		th += 0.1*(0.3*sin(r*2.1+ii)*.5+sin(r*4.3+ii+2.0)*.75+sin(r*6.7+9.3+ii)*.125);
		// q.x, qz: to set the direction of each tentacle
		// q.y: to embrace the sphere
		vec3 q = vec3( cos(th)*p.x - sin(th)*p.z, p.y+0.9*sin(0.5*rr+3.*PI/2.), sin(th)*p.x + cos(th)*p.z );
		q.y += 0.6;
		// vein radius
		float radius = (0.5)/(rr*r+1.0);
		// pumps
		float bulge = mod(2.5*iTime+float(2*i), 8.);
		radius += 0.12*(smoothstep(bulge, bulge+0.3,r) - smoothstep(bulge+0.5, bulge+0.8,r))/r;
		// cylinder distance function
		float newDist = length(vec3(0.0,q.y,q.z))-radius;// - 0.1*smoothstep(2.0, 3.0, rr);
		newDist = max(newDist, abs(q.x) - 2.0);
		// union all veins
		dist = smin(dist, newDist, 0.15 );
		// add the head/hearth with a sphere
		float distToBall = length(p-vec3(0.0,0.55,0.0))-0.2-0.3*sin(clamp(sin(iTime*7.0),0.0,1.0)+1.0);
		dist = smin(dist, distToBall, 0.15);
		// smooth out the feets
		dist = smax(dist, -p.y-1., 0.25);
	}
	return dist;
}
vec3 creatureMaterial(vec3 pos, vec3 nor) {
	vec3 col = vec3(1.0, 0.1, 0.02);
	col -= 0.25*texture( iChannel0, pos.xz ).x;

	float ao = 0.5 + 0.5*nor.y;
	col *= ao;

	return col;	
}

float floorShape( vec3 pos ) {
	return length(pos-vec3(0.0,-1.5,0.0))-2.;// + .5 + 0.01*sin(5.*pos.x)*sin(3.*pos.z);
}
vec3 floorMaterial(vec3 pos, vec3 nor) {
	vec3 col = vec3(0.6, 0.5, 0.2);
	vec2 q = vec2( length(pos.xy), atan(pos.y, pos.x) );

	//col = mix( col, texture(iChannel1, 0.1*pos.xz).x, 0.7);
	vec3 lands = mix( col, vec3(.1,2.2,.5)*texture(iChannel1, vec2(0.4/length(pos.xz), 0.4*atan(pos.z, pos.x))).xyz, 0.85);
	vec3  seas = smoothstep(0.25, 0.26, texture(iChannel2, 0.03*pos.xz+vec2(0.6,0.002)).xxx);
	col = vec3(0.1,0.1,1.0)*seas + (1.0-seas)*lands;
	float ao = 0.1 + 0.9*smoothstep(0.4, 2.0, length( pos.xz ));
	col *= ao;
	return col;	
}


// returns the smallest distance value and its ID
vec2 map(vec3 p) {
	vec2 d = vec2(10000.0, -1.);
	vec2 d1 = vec2( creatureShape(p), 1. );
	if( d1.x < d.x ) d = d1;
	vec2 d2 = vec2( floorShape(p), 2. );
	if( d2.x < d.x ) d = d2;
	return d;
}

vec3 calcNormal( vec3 p ) {
	// calculating the gradient.
	// evaluate the the distance function just below and above the surface
	// and take their difference.
	vec3 e = vec3(0.001, 0.0, 0.0);
	vec3 n;
	n.x = map(p+e.xyy).x - map(p-e.xyy).x;
	n.y = map(p+e.yxy).x - map(p-e.yxy).x;
	n.z = map(p+e.yyx).x - map(p-e.yyx).x;	
	return normalize( n );
}

float softShadow( vec3 ro, vec3 rd ) {
	float res = 1.0;
	// for some reason I had to start t at 1.0 instead of 0.1
	// to get the same shadow effect. Otherwise the shadow were too small.
	bool hit = false;
	float t=0.2;
	for( int i=0; i<50; i++ ) {
		if(!hit) {
			float h = map(ro+t*rd).x;
			if(h<0.01) {
				hit = true; //(?)
				res = 0.0;
			}
			res = min( res, 8.0*h/t );
			t += h;
			if(t>5.0) hit = true;
		}
	}
	return res;
}

float softShadowBreak( vec3 ro, vec3 rd ) {
	float res = 1.0;
	// for some reason I had to start t at 1.0 instead of 0.1
	// to get the same shadow effect. Otherwise the shadow were too small.
	float t=0.2; 
	for( int i=0; i<50; i++ ) {
		float h = map(ro+t*rd).x;
		if(h<0.01) return 0.0;
		res = min( res, 8.0*h/t );
		t += h;
		if(t<5.0) break;
	}
	return res;
}

vec2 intersect(vec3 ro, vec3 rd) {
	// instead of "for(float=0.0; t<6.0)" which gave error
	// also "while" does not work too.
	bool hit = false;
	vec2 res = vec2(0.0);
	float t=0.0;
	for( int i=0; i<50; i++ ) {
		if(!hit) {
			vec2 h = map( ro+t*rd ); // h.x: smallest distance, h.y: ID
			if(h.x<0.01) { // if close enough to an object
				res = vec2(t, h.y);
				hit = true;
			}
			if(t>5.0) {
				hit = true;
				res = vec2(0.0);
			}
		// the step size for ray marching is the distance to closes object.
		// A safe bet! This is called "raymarching distance fields"
			t += h.x; 
		}
	}
	return res;
}

vec2 intersectBreak(vec3 ro, vec3 rd) {
	// instead of "for(float=0.0; t<6.0)" which gave error
	// also "while" does not work too.
	float t=0.0;
	for( int i=0; i<50; i++ ) {
		vec2 h = map( ro+t*rd ); // h.x: smallest distance, h.y: ID
		if(h.x<0.01) return vec2(t, h.y); // if close enough to an object
		// the step size for ray marching is the distance to closes object.
		// A safe bet! This is called "raymarching distance fields"
		t += h.x; 
		if(t>5.0) break;
	}
	return vec2(0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.xy;
	vec2 p = (-1.0+2.0*q)*vec2(1.77,1.0);

	vec3 ro = 3.*vec3( cos(iTime*0.3), 0.6+0.4*sin(0.99*iTime), sin(iTime*0.3) );
	//vec3 ro = 2.3*vec3( cos(iTime*0.3), 0.01, sin(iTime*0.3) );

	vec3 target = vec3(0.0, -0.5, 0.0);
	vec3 ww = normalize( target - ro );
	vec3 uu = normalize( cross(vec3(0.0, 1.0, 0.0), ww) );
	vec3 vv = normalize( cross(ww, uu) );
	vec3 rd = normalize( p.x*uu + p.y*vv +1.5*ww );

	vec2 t = intersect( ro, rd );
	vec3 col = vec3(0.5, 0.7, 0.99);
	if( t.y > 0.5 ) {
		vec3 pos = ro + t.x*rd;
		vec3 nor = calcNormal( pos );
		vec3 lig = normalize( vec3(0.0, 0.9, 0.6) );
		vec3 blig = vec3( -lig.x, lig.y, -lig.z);
		vec3 ref = reflect(rd, nor);

		float amb = 0.5 + 0.5*nor.y;
		float dif = max(0.0, dot(nor, lig) );
		float bac = max(0.0, 0.2+0.8*dot(nor,blig) );
		float sha = softShadow(pos, lig);
		float spe = pow(clamp( dot(lig, ref), 0.0, 1.0), 8.0);
		float rim = pow( 1.0 + dot(nor, rd), 2.5);

		col += amb*vec3(0.1, 0.15, 0.2);
		col += dif*vec3(1.0, 0.97, 0.85)*sha;
		col += bac*vec3(1.0, 0.97, 0.85);

		if(t.y > 1.5 && t.y < 2.5) {
			col *= floorMaterial(pos, nor);
		}
		else {
			col *= creatureMaterial(pos, nor);
			col += 0.6*spe*sha*amb;
		}
		col += 0.6*rim*amb;
		//col += 0.6*spe*sha*amb;
		//col += 1.0*pow(spe, 1.0);

		col = 0.82*sqrt(col);	
	}

	col *= 0.2 + 0.8*pow(16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y), 0.2); //different kind of vignette	

	fragColor = vec4(col,1.0);
}

