#define SMOOTH true

float s_mask(vec2 p, float s) {
	float fw = fwidth(s);
	return smoothstep(-fw, fw, s);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (-1.0 + 2.0*fragCoord.xy / iResolution.xy)*vec2(iResolution.x/iResolution.y, 1.0);
	float beat = abs(sin(iTime)) + 0.7;
	float x = beat*p.x*3.0;
	float y = beat*p.y*4.0;
	float b = 1.2;
	float shape = x*x + pow((y - pow(x*x, 1.0/3.0)), 2.0) - 1.5;
	//float shape = pow(x*x+y*y-1.0, 3.0) - (x*x*y*y*y); //smooth not working well

	vec4 bCol = vec4(1.0, 0.65, 0.74, 1.0)*vec4(vec3(1.0 - 0.3*length(p)), 1.0);
	vec4 sCol = vec4(0.7, 0.062, 0.23, 1.0);

	if(iMouse.z > 0.0) {
		y -= 1.0;
		shape = pow(x*x+y*y+b*y, 2.0) - b*b*(x*x+y*y);
	}

	vec4 col = (shape < 0.0) ? sCol : bCol;

	if(SMOOTH) {
		float m = s_mask(p, shape);
		col = mix(sCol, bCol, m);
	}

	fragColor = col;
}

// TAU = 2*PI
#define TAU 6.283185307179586

vec2 rotate(float theta, vec2 p) {
	vec2 polar = vec2(atan(p.y, p.x), length(p));
	polar.x += theta;
	return vec2(polar.y * cos(polar.x), polar.y * sin(polar.x));
}

vec2 shear(float theta, vec2 p) {
	return vec2(p.x - p.y / tan(theta), p.y / sin(theta));
}

vec2 unshear(float theta, vec2 p) {
	float y = p.y * sin(theta);
	float x = p.x + y / tan(theta);
	return vec2(x, y);	
}

vec2 target(float theta, float delta, vec2 p) {
	return unshear(theta, floor(shear(theta, p) + delta) - delta + 0.5);
}

bool perforations(float theta, float rot, float scale, float r, vec2 p0) {
	vec2 p1 = scale * rotate(rot, p0);
	return distance(p1, target(theta, 0.5, p1)) < r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // relative coordinates: -1  p0.x  1
	vec2 p0 = 2.0*(fragCoord.xy - 0.5 * iResolution.xy) / iResolution.xx;

    // come up with some magic numbers...

    // try 4, 2, 1.9999
	float theta = TAU / 6.0;

	float rot1 = 0.01 * TAU * iTime;
	float rot2 = rot1 + TAU / 12.0 + TAU / 18.0 * sin(0.6 * iTime);

	float scale1 = 16.0;
	float scale2 = 16.0;

    // try 0.3
	float r1 = 0.4;
	float r2 = 0.4;

    // compute mask
	bool i1 = perforations(theta, rot1, scale1, r1, p0);
	bool i2 = perforations(theta, rot2, scale2, r2, p0);
	bool mask = i1 && i2;

    // choose color depending on mask
	vec3 color = mask
        ? vec3(1.0, 0.0, 1.0 - length(p0))
        : vec3(0.0, 0.0, 0.0);

	fragColor = vec4(color, 1.0);
}

