#define GAIN 1.6 // >1 is unsafe, but up to 2 still looks ok (noise don t sature dynamics)
#define NOISE 1 // 1: linear  2: blobby (abs)  3:  hairy (1-abs)

// --- scene    ( screen = [-1.8, 1.8] x [-1, 1] )

vec2 sphere1Pos = vec2(0.,0.);
float sphere1Rad = .7;         // sphere radius

float planePos = .1;

vec2 sphere2Pos = vec2(1.,0.);
float sphere2Rad = .2;         

// cloud appearance (superseeded by mouse tuning)

float H = .2;                 // skin layer thickness ( % of normalized sphere)
float sharp = 0.3;            // cloud sharness (0= ultra sharp ).



#define ANIM 1         // 1/0

#define PI 3.14159

// --- noise functions from https://www.shadertoy.com/view/XslGRr
// Created by inigo quilez - iq/2013
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

mat3 m = mat3( 0.00,  0.80,  0.60,
              -0.80,  0.36, -0.48,
              -0.60, -0.48,  0.64 );

float hash( float n )  // base rand in [0,1]; 
{
    return fract(sin(n-765.36334)*43758.5453);
    //return -1.+2.*fract(sin(n-765.36334)*43758.5453);
}

float noise( in vec3 x ) // base noise in [0,1]; 
{
    vec3 p = floor(x);
    vec3 f = fract(x);

    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*57.0 + 113.0*p.z;

    float res = mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                        mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y),
                    mix(mix( hash(n+113.0), hash(n+114.0),f.x),
                        mix( hash(n+170.0), hash(n+171.0),f.x),f.y),f.z);
#if NOISE==1
	return res;
#elif NOISE==2
	return abs(2.*res-1.);
#elif NOISE==3
	return 1.-abs(2.*res-1.);
#endif
}

float fbm( vec3 p ) // turbulent (=fractal) noise in [0,1]; 
{
    float f;
    f  = 0.5000*noise( p ); p = m*p*2.02;
    f += 0.2500*noise( p ); p = m*p*2.03;
    f += 0.1250*noise( p ); p = m*p*2.01;
    f += 0.0625*noise( p );
    return f;
}
// --- End of: Created by inigo quilez --------------------



// smooth distance to sphere = [-1,1] around radius +- thickness H

float sphere(vec2 uv, vec2 spherePos, float sphereRad)
{
	vec2 p = (uv-spherePos)/sphereRad; // pos in sphere normalized coordinates
	float d = (1.-length(p))/H;  
	return clamp(d,-1.,1.);
}

// smooth distance to plane = [-1,1] around plane +- thickness H

float plane(vec2 uv, float planePos, float planeRad) // planeRad to share normalization with spheres
{
	vec2 p = uv-vec2(planePos,0.); // pos in sphere normalized coordinates
	float d = -p.x/(H*planeRad);  
	return clamp(d,-1.,1.);
}

// smooth intersect operator

float inter(float d0, float d1) {
	d0 = (1.+d0)/2.;    	 //   [-1,1] -> [0,1], mul,  [0,1] -> [-1,1]
	d1 = (1.+d1)/2.;
	return 2.*d0*d1 -1.;
}

// smooth union operator

float add(float d0, float d1) {
	d0 = (1.+d0)/2.;     	//   [-1,1] -> [0,1], add,  [0,1] -> [-1,1]
	d1 = (1.+d1)/2.;
	return 2.*(d0+d1-d0*d1) -1.;
}

// jitter the distance around 0  and smoothclamp

float perturb(vec2 p, float d, float H) {
#if ANIM
   float t = iTime;
#else
  float t = 0.; 
#endif
    //float fillfactor=0.; d = (d+1.)*fillfactor-1.;
	if (d<=-1.) return -1.;  			// exterior
	if (d>= 1.) return 1.;   			// interior (1 when H% inside radius )

	float n = 2.*fbm(vec3(p/H,t)) -1.;  // perturbation in [-1,1]
	return  2.*(d + GAIN*n);   			// still in [-1,1] :-)
}

// convert [-1,1] distances into densities

float dist2dens(float d) {  	// transition around zero. Tunable sharpness
	return smoothstep(-sharp,sharp,d);
}


// user-define shape

float shape(vec2 uv,float n) {

	float v1 = sphere(uv, sphere1Pos, sphere1Rad),
		  v2 = plane (uv, planePos,   sphere1Rad), // share normalization radius
		  v3 = sphere(uv, sphere2Pos, sphere2Rad);
	float v;

#define globalNoise false

	if (globalNoise || (n==0.)) {
		v = add( inter(v1,v2), v3 );               // we combine smooth distances *then* perturbate
		if (n>0.) v = perturb(uv, v,H*sphere1Rad);
	}
	else {
		v = perturb(uv, inter(v1,v2), H*sphere1Rad); // we perturbate (with different coefs) *then* combine
		v = add( v, perturb(uv, v3 , H*sphere2Rad));
	}

	return v;
}

// main loop

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.* (fragCoord.xy / iResolution.y - vec2(.8,.5) );
	if (iMouse.z>0.) {       				   // mouse tuning
		vec2 m = iMouse.xy / iResolution.xy;
		H = m.x+1e-5 ; sharp = m.y+1e-5; 
	}

	float v = dist2dens( shape(uv,1.) ); 
	vec3 col = vec3(v);

	if (uv.y<0.) {                   	 // bottom half scren: display bounds

		sharp = 1e-5; // no noise for bounds
		float d = shape(uv,0.), dv = fwidth(d);

		v = dist2dens(d-0.99+dv)-dist2dens(d-0.99); // inner bound : draw on top
		col = mix(col, vec3(v,0.,0.),v);	

		v = dist2dens(d+.5*dv)-dist2dens(d-.5*dv);  // mid-bound: draw on top
		col = mix(col, vec3(0.,v,0.),v);

		v = dist2dens(d+.99)-dist2dens(d+0.99-dv);  // exterior bound : draw below
		float alpha = max(col.r,col.g);
		col = mix(vec3(0.,0.,v),col, alpha);	
		alpha = max(col.b,alpha);

		v = dist2dens(sphere(uv, sphere1Pos, sphere1Rad)+.99); // sphere without plane clipping
		col = mix(v*vec3(0.,0.,.3),col, alpha);	
	}

    fragColor = vec4(col,0.); 
}

#define PI 3.14159265359
#define MAX_ITER 128
#define MAX_ITER_INTERNAL 0
#define MAX_ITER_SHADOW 32
#define MAX_DIST_SHADOW 10.0
#define MIN_DIST_SHADOW 0.1
#define MAX_DIST 1000.0
#define MIN_DIST 0.01
#define FOV 0.75
#define SPECULAR 5.0
#define SAMPLE_RADIUS 0.001
#define PENUMBRA 2.0
struct ray{
	vec4 c;
	vec3 p;
	vec3 d;
	vec3 n;
	float t;
	int i;
};
struct light{
	vec4 c;
	vec3 p;
};
light l=light(
	vec4(0.5),
	vec3(0.0,-5.0,5.0)
);
vec4 a=vec4(0.5);
float s;
void srand(vec2 p){
	s=sin(dot(p,vec2(423.62431,321.54323)));
}
float rand(){
	s=fract(s*32322.65432+0.12333);
	return abs(fract(s));
}
mat3 rotx(float a){
	float c=cos(a);
	float s=sin(a);
	return mat3(1.0,0.0,0.0,0.0,c,-s,0.0,s,c);
}
mat3 roty(float a){
	float c=cos(a);
	float s=sin(a);
	return mat3(c,0.0,s,0.0,1.0,0.0,-s,0.0,c);
}
mat3 rotz(float a){
	float c=cos(a);
	float s=sin(a);
	return mat3(c,-s,0.0,s,c,0.0,0.0,0.0,1.0);
}
mat3 rot(vec3 z,float a){
	float c=cos(a);
	float s=sin(a);
	float b=1.0-c;
	return mat3(
		b*z.x*z.x+c,b*z.x*z.y-z.z*s,b*z.z*z.x+z.y*s,
		b*z.x*z.y+z.z*s,b*z.y*z.y+c,b*z.y*z.z-z.x*s,
		b*z.z*z.x-z.y*s,b*z.y*z.z+z.x*s,b*z.z*z.z+c);
}
mat2 rot2d(float a){
	float c=cos(a);
	float s=sin(a);
	return mat2(
		c,-s,
		s, c);
}
float expstep(float x,float k,float n){
	return exp(-k*pow(x,n));
}
float smin(float a,float b,float k){
	float h=clamp(0.5+0.5*(b-a)/k,0.0,1.0);
    return mix(b,a,h)-k*h*(1.0-h);
}
float voronoi2d(vec2 p,float t){
	float v=0.0;
	vec2 f=floor(p)+vec2(0.5);
	for(float i=-3.0;i<3.0;i++)
	for(float j=-3.0;j<3.0;j++){
		srand(f+vec2(i,j));
		vec2 o;
		o.x=rand();
		o.y=rand();
		o*=rot2d(t*(rand()-0.5));
		float r=distance(p,f+vec2(i,j)+o);
		v+=exp(-16.0*r);
	}
	//return -(1.0/16.0)*log(v);
	return -smin((1.0/16.0)*log(v),-0.1,0.1);
}
float dome(inout ray r){
	float v=voronoi2d(r.p.xy,r.t);
	float d=dot(r.p,vec3(0.0,0.0,1.0))+0.5-0.5*v;
	if(d<0.0){
		r.c=vec4(1.0);
		r.i=MAX_ITER_INTERNAL+1;
	}
	return d;
}
float sphere(inout ray r){
	float d=length(r.p)-0.5;	
	if(d<0.0){
		r.c=vec4(1.0);
		r.i=MAX_ITER_INTERNAL+1;
	}
	return d;
}
float dist(inout ray r){
	float d=MAX_DIST;
	d=min(d,dome(r));
	return d;
}
void normal(inout ray r){
	float d=dist(r);
	vec3 n=vec3(SAMPLE_RADIUS,0.0,0.0);
	ray r0=r;
	ray r1=r;
	ray r2=r;
	r0.p+=n.xyy;
	r1.p+=n.yxy;
	r2.p+=n.yyx;
	r.n=normalize(vec3(dist(r0)-d,dist(r1)-d,dist(r2)-d));
}
vec4 ambient(ray r){
	return r.c*a;
}
vec4 diffuse(ray r){
	vec3 v=l.p-r.p;
	return clamp(r.c*l.c*dot(r.n,normalize(v)),0.0,1.0);
}
vec4 specular(ray r){
	float d=length(l.p-r.p);
	vec3 v=normalize(l.p-r.p);
	return l.c*max(pow(dot(v,reflect(r.d,r.n)),SPECULAR),0.0);
}
vec4 shadow(ray r){
	float s=1.0;
	float t=MIN_DIST_SHADOW;
	for(int i=0;i<MAX_ITER_SHADOW;i++){
		ray tmp=r;
		tmp.p+=r.d*t;
		float h=dist(tmp);
		if(h<MIN_DIST)return vec4(0.0);
		s=min(s,PENUMBRA*h/t);
		t+=h;
		if(t>MAX_DIST_SHADOW)break;
	}
	return vec4(1.0)*s;
}
vec4 trace(inout ray r){
	r.c=vec4(1.0);
	for(int i=0;i<MAX_ITER;i++){
		float d=dist(r);
		if(r.i>MAX_ITER_INTERNAL)break;
		r.p+=r.d*max(d,MIN_DIST);
	}
	normal(r);
	ray tmp=r;
	tmp.d=normalize(l.p-r.p);
	tmp.p-=2.0*MIN_DIST*r.d;
	if(r.i<=MAX_ITER_INTERNAL){
		return vec4(1.0/exp(abs(r.p.z)));
	}else{
		return ambient(r)+min(max(diffuse(r),specular(r)),shadow(tmp));
	}
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	float t=iTime;
	float r=iResolution.x/iResolution.y;
	vec2 m=vec2(
		(iMouse.x-iResolution.x/2.0)/iResolution.x*r,
		(iMouse.y-iResolution.y/2.0)/iResolution.y);
	vec2 s=vec2(
		(fragCoord.x-iResolution.x/2.0)/iResolution.x*r,
		(fragCoord.y-iResolution.y/2.0)/iResolution.y);	
	vec3 l=vec3(0.0,0.0,0.0);
	vec3 tmp=vec3(2.0,0.0,0.0);
	tmp*=roty((PI*m.y)/4.0-PI/8.0);
	tmp*=rotz(2.0*PI*m.x);
	vec3 e=l+tmp;
	vec3 u=vec3(0.0,0.0,1.0);
	vec3 d=normalize(l-e);
	vec3 h=normalize(cross(d,u));
	vec3 v=normalize(cross(h,d));
	float f=0.75;
	d*=rot(v,FOV*s.x);
	d*=rot(h,FOV*s.y);
	ray a=ray(vec4(0.0),e,d,vec3(0.0),t,0);
	fragColor=trace(a);
}

