vec4 hsv2rgb(float h,float s,float v) {
	const float eps=1e-3;
	vec4 result=vec4(0.0, 0.0, 0.0, 1.0);
	if(s<=0.0)result.r=result.g=result.b=v;
	else {
		float hi=floor(h/60.0);
		float f=(h/60.0)-hi;
		float m=v*(1.0-s);
		float n=v*(1.0-s*f);
		float k=v*(1.0-s*(1.0-f));
		if(hi<=0.0+eps) {
			result.r=v;
			result.g=k;
			result.b=m;
		} else if(hi<=1.0+eps) {
			result.r=n;
			result.g=v;
			result.b=m;
		} else if(hi<=2.0+eps) {
			result.r=m;
			result.g=v;
			result.b=k;
		} else if(hi<=3.0+eps) {
			result.r=m;
			result.g=n;
			result.b=v;
		} else if(hi<=4.0+eps) {
			result.r=k;
			result.g=m;
			result.b=v;
		} else if(hi<=5.0+eps) {
			result.r=v;
			result.g=m;
			result.b=n;
		}
	}
	return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	const float pi=3.1415926535897932384626433832795028841;
	vec2 nowCoord=(fragCoord.xy/iResolution.xy)-vec2(0.5,0.5);
	if(iResolution.x>iResolution.y) {
		nowCoord.x*=iResolution.x/iResolution.y;
	} else {
		nowCoord.y*=iResolution.y/iResolution.x;
	}
	float tmp=length(nowCoord)/0.5;
	float angle=atan(nowCoord.y,nowCoord.x)*180.0/pi;
	angle+=mod(iTime*270.0,360.0);
	if(angle<0.0)angle+=360.0;
	angle=mod(angle,360.0);
	if(tmp>1.0) {
		fragColor=vec4(0.0, 0.0, 0.0, 1.0);
	} else {
		fragColor=hsv2rgb(
			angle,
			1.0,
			tmp<=1.0?sin(tmp*pi/2.0):0.0);
		fragColor.rgb*=tmp<0.99?1.0:sin((1.0-tmp)*100.0*pi/2.0);
	}
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	vec2 p = vec2(0.0, -1.0) + 2.0*uv;
	p.x *= iResolution.x/iResolution.y;
	p *= 10.0;
	vec2 wave1 = vec2(p.x, p.y + 3.0*sin(p.x*0.4 + iTime));
	vec2 wave2 = vec2(p.x, p.y + 3.0*sin(p.x*0.36+ 2.9 + iTime));
	float t = 1.0 - abs(wave1.y);
	t = max(t, 1.0 - abs(wave2.y));

	if (wave1.y * wave2.y < 0.0) t = mod(p.x + 3.141592654*iTime, 1.0);
	float c = t;
	c = smoothstep(0.7, 0.84, c);
	fragColor = vec4(c,c,c,1.0);
}

float hash( vec2 p )
{
	float h = dot(p,vec2(127.1,311.7));

    return -1.0 + 2.0*fract(sin(h)*43758.5453123);
}

float noise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( hash( i + vec2(0.0,0.0) ), 
                     hash( i + vec2(1.0,0.0) ), u.x),
                mix( hash( i + vec2(0.0,1.0) ), 
                     hash( i + vec2(1.0,1.0) ), u.x), u.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy) * vec2(5.0, 30.0) + vec2(iTime / 10.0, 0.0);

	uv.y = uv.y + sin((uv.x + (iTime / 3.0)) * 6.0);

	mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );

	float f = 0.0;
	f  = 0.5000*noise( uv ); uv = m*uv*iDate.z;
	f += 0.2500*noise( uv ); uv = m*uv*iDate.x;
	f += 0.1250*noise( uv ); uv = m*uv*iDate.y;
	f += 0.0625*noise( uv ); uv = m*uv*iDate.a;


	f = 0.5 + 0.5*f;

	f = step(f, 0.24);

	fragColor = vec4(f, f, f, 1.0);
}

#define PI 3.14159265359
#define FILTER 1
float s;
void srand(vec2 p){
	s=sin(dot(p,vec2(423.62431,321.54323)));
}
float rand(){
	s=fract(s*32322.65432+0.12333);
	return abs(fract(s));
}
vec4 hash2d(vec2 p){
	srand(p);
	vec4 c;
	c.r=rand();
	c.g=rand();
	c.b=rand();
	return c;
}
mat2 rot2d(float a){
	float c=cos(a);
	float s=sin(a);
	return mat2(
		c,-s,
		s, c);
}
mat3 rotx(float a){
	float c=cos(a);
	float s=sin(a);
	return mat3(
		1.0,0.0,0.0,
		0.0,  c, -s,
		0.0,  s,  c);
}
mat3 roty(float a){
	float c=cos(a);
	float s=sin(a);
	return mat3(
		c,  0.0,  s,
		0.0,1.0,0.0,
		-s, 0.0,  c);
}
mat3 rotz(float a){
	float c=cos(a);
	float s=sin(a);
	return mat3(
		  c, -s,0.0,
		  s,  c,0.0,
	    0.0,0.0,1.0);
}
vec2 simplex(vec2 p){
	vec2 r;
	r.x=1.1547*p.x;
	r.y=p.y+0.5*r.x;
	return r;
}
vec2 unsimplex(vec2 p){
	vec2 r;
	r.y=p.y-0.5*p.x;
	r.x=p.x/1.1547;
	return r;
}
vec4 noise2d(vec2 p){
	p=simplex(p);
	vec2 p0=floor(p);
	vec2 p1=p0+vec2(1.0,0.0);
	vec2 p2=p0+vec2(1.0,1.0);
	vec2 p3=p0+vec2(0.0,1.0);
	vec2 i=fract(p);
	vec4 r0,r1,r2;
	float d0,d1,d2;	
	if(i.x>i.y){
		r0=hash2d(p0);
		r1=hash2d(p1);
		r2=hash2d(p2);
		d0=max(1.0-distance(unsimplex(p),unsimplex(p0)),0.0);
		d1=max(1.0-distance(unsimplex(p),unsimplex(p1)),0.0);
		d2=max(1.0-distance(unsimplex(p),unsimplex(p2)),0.0);
		/*
		d0=max(1.0-distance(p,p0),0.0);
		d1=max(1.0-distance(p,p1),0.0);
		d2=max(1.0-distance(p,p2),0.0);
		*/
	}else{
		r0=hash2d(p0);
		r1=hash2d(p2);
		r2=hash2d(p3);
		d0=max(1.0-distance(unsimplex(p),unsimplex(p0)),0.0);
		d1=max(1.0-distance(unsimplex(p),unsimplex(p2)),0.0);
		d2=max(1.0-distance(unsimplex(p),unsimplex(p3)),0.0);
	}
	/*
	float d=d0+d1+d2;
	d0/=d;
	d1/=d;
	d2/=d;
	*/
	return d0*r0+d1*r1+d2*r2;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	float r=iResolution.x/iResolution.y;
	vec2 p=vec2((fragCoord.x-iResolution.x/2.0)/iResolution.x*r,(fragCoord.y-iResolution.y/2.0)/iResolution.y);
	vec4 n=vec4(0.0);
	float t=iTime/PI;
#if FILTER
	vec2 x=vec2(0.5/iResolution.y,0.0);
	x*=rot2d(PI/8.0);
	n+=0.25*noise2d((p+x)*10.0);
	x*=rot2d(PI/4.0);
	n+=0.25*noise2d((p+x)*10.0);
	x*=rot2d(PI/4.0);
	n+=0.25*noise2d((p+x)*10.0);
	x*=rot2d(PI/4.0);
	n+=0.25*noise2d((p+x)*10.0);
#else
	n=noise2d(p*10.0);
#endif
	n.rgb*=rotx(t);
	n.rgb*=roty(t);
	n.rgb*=rotz(t);
	fragColor=vec4(abs(n));
}

