#define AA 2

// { 2d cell id, distance to border, distnace to center )
vec4 hexagon( vec2 p ) 
{
	vec2 q = vec2( p.x*2.0*0.5773503, p.y + p.x*0.5773503 );

	vec2 pi = floor(q);
	vec2 pf = fract(q);

	float v = mod(pi.x + pi.y, 3.0);

	float ca = step(1.0,v);
	float cb = step(2.0,v);
	vec2  ma = step(pf.xy,pf.yx);

    // distance to borders
	float e = dot( ma, 1.0-pf.yx + ca*(pf.x+pf.y-1.0) + cb*(pf.yx-2.0*pf.xy) );

	// distance to center	
	p = vec2( q.x + floor(0.5+p.y/1.5), 4.0*p.y/3.0 )*0.5 + 0.5;
	float f = length( (fract(p) - 0.5)*vec2(1.0,0.85) );		

	return vec4( pi + ca - cb*ma, e, f );
}

float hash1( vec2  p ) { float n = dot(p,vec2(127.1,311.7) ); return fract(sin(n)*43758.5453); }

float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);
	vec2 uv = (p.xy+vec2(37.0,17.0)*p.z) + f.xy;
	vec2 rg = textureLod( iChannel0, (uv+0.5)/256.0, 0.0 ).yx;
	return mix( rg.x, rg.y, f.z );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
    vec3 tot = vec3(0.0);

    #if AA>1
    for( int mm=0; mm<AA; mm++ )
    for( int nn=0; nn<AA; nn++ )
    {
        vec2 off = vec2(mm,nn)/float(AA);
        vec2 uv = (fragCoord+off)/iResolution.xy;
        vec2 pos = (-iResolution.xy + 2.0*(fragCoord+off))/iResolution.y;
    #else    
    {
        vec2 uv = fragCoord/iResolution.xy;
        vec2 pos = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
    #endif

        // distort
        pos *= 1.0 + 0.1*length(pos);

        // gray
        vec4 h = hexagon(8.0*pos + 0.5*iTime);
        float n = noise( vec3(0.3*h.xy+iTime*0.1,iTime) );
        vec3 col = 0.15 + 0.15*hash1(h.xy+1.2)*vec3(1.0);
        col *= smoothstep( 0.10, 0.11, h.z );
        col *= smoothstep( 0.10, 0.11, h.w );
        col *= 1.0 + 0.15*sin(40.0*h.z);
        col *= 0.75 + 0.5*h.z*n;


        // red
        h = hexagon(6.0*pos + 0.6*iTime);
        n = noise( vec3(0.3*h.xy+iTime*0.1,iTime) );
        vec3 colb = 0.9 + 0.8*sin( hash1(h.xy)*1.5 + 2.0 + vec3(0.0,1.0,1.0) );
        colb *= smoothstep( 0.10, 0.11, h.z );
        colb *= 1.0 + 0.15*sin(40.0*h.z);
        colb *= 0.75 + 0.5*h.z*n;

        h = hexagon(6.0*(pos+0.1*vec2(-1.3,1.0)) + 0.6*iTime);
        col *= 1.0-0.8*smoothstep(0.45,0.451,noise( vec3(0.3*h.xy+iTime*0.1,iTime) ));

        col = mix( col, colb, smoothstep(0.45,0.451,n) );

        col *= pow( 16.0*uv.x*(1.0-uv.x)*uv.y*(1.0-uv.y), 0.1 );

        tot += col;
	}	
 	#if AA>1
    tot /= float(AA*AA);
    #endif

	fragColor = vec4( tot, 1.0 );
}

// Rendering parameters
#define FLOW
#define RAY_LENGTH_MAX		50.0
#define RAY_STEP_MAX		50
#define LIGHT				vec3 (0.5, 0.0, -2.0)
#define AMBIENT				0.5
#define SPECULAR_POWER		4.0
#define SPECULAR_INTENSITY	0.2
#define FADE_POWER			3.0
#define GAMMA				(1.0 / 2.2)
#define HSV2RGB_FAST
#define TEXTURE_SCALE		0.4

// Constants
#define PI		3.14159265359
#define DELTA	0.01

vec3 vRotateZ (in vec3 p, in float angle) {
	float c = cos (angle);
	float s = sin (angle);
	return vec3 (c * p.x + s * p.y, c * p.y - s * p.x, p.z);
}

float sphere (in vec3 p, in float r) {
	return length (p) - r;
}

float box (in vec3 p, in vec3 b, in float r) {
	#ifdef BOX_NEGATIVE
	vec3 d = abs (p) - b + r;
	return min (max (d.x, max (d.y, d.z)), 0.0) + length (max (d, 0.0)) - r;
	#else
	return length (max (abs (p) - b + r, 0.0)) - r;
	#endif
}

float torusZ (in vec3 p, in float r1, in float r2) {
	vec2 q = vec2 (length (p.xy) - r1, p.z);
	return length (q) - r2;
}

float cylinderZ (in vec3 p, in float r) {
 	return length (p.xy) - r;
}

vec3 twistZ (in vec3 p, in float k, in float angle) {
	return vRotateZ (p, angle + k * p.z);
}

float fixDistance (in float d, in float correction, in float k) {
	correction = max (correction, 0.0);
	k = clamp (k, 0.0, 1.0);
	return min (d, max ((d - DELTA) * k + DELTA, d - correction));
}

float getDistance (in vec3 p, out vec4 q) {

	// Global deformation
	p += vec3 (2.0 * sin (p.z * 0.2 + iTime * 2.0), sin (p.z * 0.1 + iTime), 0.0);

	// Cylinder
	q.xyz = p;
	q.w = -1.0;
	float d = fixDistance (-cylinderZ (q.xyz, 4.0) + 0.5 * sin (atan (q.y, q.x) * 6.0) * sin (q.z), 0.4, 0.8);

	// Twisted boxes
	vec3 q_;
	q_.xy = mod (p.xy, 5.0) - 0.5 * 5.0;
	q_.z = mod (p.z, 12.0) - 0.5 * 12.0;	
	q_ = twistZ (q_, 1.0, iTime);
	float d_ = fixDistance (box (q_, vec3 (0.6, 0.6, 1.5), 0.3), 0.4, 0.8);
	if (d_ < d) {
		q.xyz = q_;
		d = d_;
	}

	// Rotating spheres
	q_ = p;
	q_.z += 12.0;
	q_ = vRotateZ (q_, sin (iTime * 4.0));
	q_.xy = mod (q_.xy, 4.5) - 0.5 * 4.5;
	q_.z = mod (q_.z, 24.0) - 0.5 * 24.0;
	d_ = sphere (q_, 0.5);
	if (d_ < d) {
		q.xyz = q_;
		d = d_;
	}

	// Torus
	q_ = p;
	q_.z = mod (q_.z + 12.0, 24.0) - 0.5 * 24.0;
	d_ = torusZ (q_, 3.5, 0.4);
	if (d_ < d) {
		q.xyz = q_;
		d = d_;
	}

	// Flow of boxes and spheres
	#ifdef FLOW
	q_ = p;
	q_.z += iTime * 20.0;
	const float spacing = 0.5;
	const float stepCount = 3.0;
	const float period = spacing * stepCount;
	for (float step = 0.0; step < stepCount; ++step) {
		float k1 = floor (q_.z / period + 0.5);
 		float k2 = k1 * stepCount + step;
		vec3 qq = q_ - vec3 (0.4 * sin (k2), 0.4 * sin (k2 * 13.0), period * k1);
		if (mod (k2, 2.0) > 0.5) {
			d_ = box (vRotateZ (qq, k2), vec3 (0.08), 0.01);
		} else {
			d_ = sphere (qq, 0.08);
		}
		if (d_ < d) {
			q.xyz = qq;
			q.w = 1.0;
			d = d_;
		}
		q_.z += spacing;
	}
	#endif

	// Final distance
	return d;
}

vec3 getNormal (in vec3 p) {
	vec4 q;
	const vec2 h = vec2 (DELTA, 0.0);
	return normalize (vec3 (
		getDistance (p + h.xyy, q) - getDistance (p - h.xyy, q),
		getDistance (p + h.yxy, q) - getDistance (p - h.yxy, q),
		getDistance (p + h.yyx, q) - getDistance (p - h.yyx, q)
	));
}

vec3 hsv2rgb (in vec3 hsv) {
	#ifdef HSV2RGB_SAFE
	hsv.yz = clamp (hsv.yz, 0.0, 1.0);
	#endif
	#ifdef HSV2RGB_FAST
	return hsv.z * (1.0 + 0.5 * hsv.y * (cos (2.0 * PI * (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0))) - 1.0));
	#else
	return hsv.z * (1.0 + hsv.y * clamp (abs (fract (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0)) * 6.0 - 3.0) - 2.0, -1.0, 0.0));
	#endif
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Define the ray corresponding to this fragment
	vec2 frag = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
	vec3 direction = normalize (vec3 (frag, 2.0));

	// Set the camera
	vec3 origin = vec3 (0.0, 0.0, iTime * 6.0);
	vec3 forward = vec3 (0.2 * cos (iTime), 0.2 * sin (iTime), cos (iTime * 0.3));
	vec3 up = vRotateZ (vec3 (0.0, 1.0, 0.0), PI * sin (iTime) * sin (iTime * 0.2));
	mat3 rotation;
	rotation [2] = normalize (forward);
	rotation [0] = normalize (cross (up, forward));
	rotation [1] = cross (rotation [2], rotation [0]);
	direction = rotation * direction;

	// Ray marching
	vec3 p = origin;
	vec4 q;
	float rayLength = 0.0;
	int rayStepCounter = 0;
	for (int rayStep = 0; rayStep < RAY_STEP_MAX; ++rayStep) {
		float dist = getDistance (p, q);
		rayLength += dist;
		if (dist < DELTA || rayLength > RAY_LENGTH_MAX) {
			break;
		}
		p += dist * direction;
		++rayStepCounter;
	}

	// Compute the fragment color
	vec3 color;
	float bass = texture (iChannel0, vec2 (0.0)).x;
	if (rayLength > RAY_LENGTH_MAX) {
		color = vec3 (0.0);
	} else {

		// Object color
		vec3 normal = getNormal (p);
		float hue = (p.z + iTime) * 0.1;
		if (q.w < 0.0) {
			#ifdef TEXTURE_SCALE
			vec3 textureUV = q.xyz * TEXTURE_SCALE;
			vec3 textureFactor = abs (normal);
			mat3 textureColor = mat3 (
				texture (iChannel1, textureUV.yz).rgb,
				texture (iChannel1, textureUV.zx).rgb,
				texture (iChannel1, textureUV.xy).rgb
				);
			color = hsv2rgb (vec3 (hue, 1.0, 1.0));
			color *= textureColor * textureFactor / (textureFactor.x + textureFactor.y + textureFactor.z);
			#else
			float treble = texture (iChannel0, vec2 (0.8, 0.0)).x;
			float saturation = 0.8 + (0.2 + 0.8 * treble) * 0.4 * sin (q.x * 10.0) * sin (q.y * 10.0) * sin (q.z * 10.0);
			float value = 0.2 + 0.8 * bass;
			color = hsv2rgb (vec3 (hue, saturation, value));
			#endif
		} else {
			color = hsv2rgb (vec3 (hue, 1.0, 1.0));
		}

		// Lighting
		vec3 lightDirection = normalize (LIGHT);
		vec3 reflectDirection = reflect (direction, normal);
		float diffuse = max (0.0, dot (normal, lightDirection));
		float specular = pow (max (0.0, dot (reflectDirection, lightDirection)), SPECULAR_POWER) * SPECULAR_INTENSITY;
		float fade = pow (1.0 - rayLength / RAY_LENGTH_MAX, FADE_POWER);
		color = ((AMBIENT + diffuse) * color + specular) * fade;

		// Special effect
		color *= max (1.0, 10.0 * sin (p.z * 0.1 - iTime * 4.0) - 7.0);

		// Gamma correction
		color = pow (color, vec3 (GAMMA));
	}

	// Another special effect
	color.r = mix (color.r, float (rayStepCounter) / float (RAY_STEP_MAX / 2), bass * bass);

	// Set the fragment color
	fragColor = vec4 (color, 1.0);
}

#define time (iTime * 0.5 - 2.0)

#define dithering			2.01

#define samples 32

#define brightness	1.0
#define gamma		1.0

//#define crossEyeStereo
//#define nonlinearPerspective

struct material {
	vec3 color;
	vec3 light;
	float spec;
};

struct hit {
	vec3 p;
	vec3 n;
	float t;
	material m;
};


vec2 sphere (vec3 v, float f, vec3 p, vec3 d) {
	vec3 rp = p - v;

	float b = dot (d, rp);
	float c = dot (rp, rp) - f*f;

	float g = b*b - c;

	if (g < 0.0)
		return vec2 (-1);

	float root = sqrt (g);
	return vec2 (-root, root) - b;

}

hit scene (const in vec3 p, const in vec3 d) {
	hit h;h.t = 1e20;
	float c = sin (time * 2.0) * 0.5 + 1.5;

	float t;
	vec2 t2;
	vec3 t3;

	vec3 invd = 1.0 / d;

	material m1 = material (vec3 ( 1.0, 1.0, 1.0), vec3 ( 0.0, 0.0, 0.0),-1.0);
	material m2 = material (vec3 ( 0.8, 0.5, 0.2), vec3 ( 0.0, 0.0, 0.0),-1.0);
	material m3 = material (vec3 ( 0.2, 0.5, 0.8), vec3 ( 0.0, 0.0, 0.0),-1.0);
	material m4 = material (vec3 ( 0.5, 0.7, 0.8), vec3 ( 0.0, 0.0, 0.0),-1.0);
	material m5 = material (vec3 ( 1.0, 1.0, 1.0), vec3 ( 1.0, 1.0, 1.0),-1.0);
	material m6 = material (vec3 ( 1.0, 0.0, 0.0), vec3 ( 0.0, 0.0, 0.0), 2.0);

	t3 = vec3 (0,0.5,0);
	t2 = sphere (t3, 0.5, p, d);

	t = t2.x;
	if (all (greaterThan (vec2 (t, h.t), vec2 (0, t)))) {
		vec3 r = p + d * t;
		if (r.y < 0.25)
			h = hit (r, normalize (r - t3), t, m2);
	}
	t = t2.y;
	if (all (greaterThan (vec2 (t, h.t), vec2 (0, t)))) {
		vec3 r = p + d * t;
		if (r.y < 0.25) {
			h = hit (r, normalize (r - t3), t, m1);
			h.m.color *= mod (dot (fract (r * 4.0), vec3 (1)), 2.0) * 0.5 + 0.5;
		}
	}
	t2 = sphere (t3, 0.4, p, d);
	t = t2.x;
	if (all (greaterThan (vec2 (t, h.t), vec2 (0, t)))) {
		vec3 r = p + d * t;
		h = hit (r, normalize (r - t3), t, m1);
		h.m.spec = 
			dot (cos (h.p.yz * 20.0), vec2 (h.p.x*h.p.x)) +
			dot (cos (h.p.xz * 20.0), vec2 (h.p.y*h.p.y)) +
			dot (cos (h.p.xy * 20.0), vec2 (h.p.z*h.p.z));
		h.m.spec *= 10.0;
	}

	t2 = sphere (t3, 1.0, p, d);

	t = t2.x;
	if (all (greaterThan (vec2 (t, h.t), vec2 (0, t)))) {
		vec3 r = p + d * t;
		float m = dot (r.yz, r.yz) * 0.9999999;
		if (any (lessThan (vec2 (abs (r.z), m), vec2 (0.25, 0.5)))) {
			h = hit (r, normalize (r - t3), t, m3);
		}
	}
	t = t2.y;
	if (all (greaterThan (vec2 (t, h.t), vec2 (0, t)))) {
		vec3 r = p + d * t;
		float m = dot (r.yz, r.yz) * 0.9999999;
		if (any (lessThan (vec2 (abs (r.z), m), vec2 (0.25, 0.5)))) {
			h = hit (r, normalize (r - t3), t, m5);
			h.m.light = fract (floor (m * 6.0) * 0.5) * vec3 (0.0, 1.0, 1.0);
			h.m.color = vec3 (0.8, 0.2, 0.5);
		}
	}

	t = -p.y * invd.y;
	if (all (greaterThan (vec2 (t, h.t), vec2 (0, t)))) {
		vec3 r = p + d * t;
		h = hit (r, vec3 (0,1,0), t, m4);
		h.m.color *= mod (dot (floor (r.xz), vec2 (1)), 2.0) * 0.5 + 0.5;
	}
	t = (3.0 - p.y) * invd.y;
	if (all (greaterThan (vec2 (t, h.t), vec2 (0, t)))) {
		vec3 r = p + d * t;
		h = hit (r, vec3 (0,1,0), t, m5);
		h.m.light *= sin (time * 0.2) * 0.5 + 0.5;
	}
	t = (4.0 - p.x) * invd.x;
	if (all (greaterThan (vec2 (t, h.t), vec2 (0, t)))) {
		vec3 r = p + d * t;
		h = hit (r, vec3 (1,0,0), t, m1);
	}
	t = (-4.0 - p.x) * invd.x;
	if (all (greaterThan (vec2 (t, h.t), vec2 (0, t)))) {
		vec3 r = p + d * t;
		h = hit (r, vec3 (1,0,0), t, m1);
	}
	t = (4.0 - p.z) * invd.z;
	if (all (greaterThan (vec2 (t, h.t), vec2 (0, t)))) {
		vec3 r = p + d * t;
		h = hit (r, vec3 (0,0,1), t, m6);
	}
	t = (-4.0 - p.z) * invd.z;
	if (all (greaterThan (vec2 (t, h.t), vec2 (0, t)))) {
		vec3 r = p + d * t;
		h = hit (r, vec3 (0,0,1), t, m1);
	}

	h.n = faceforward (h.n, h.n, d);

	return h;
}

float seed1;
float rndValue () {
	seed1 = fract (seed1 * 3.01);
	return seed1;
}
vec2 rndUnit2D () {
	float angle = rndValue () * 6.28318530718;
	return vec2 (sin (angle), cos (angle));
}
vec2 rnd2D () {
	float radius = sqrt (rndValue ());
	return rndUnit2D () * radius;
}
vec3 rndUnit3D () {
	float z = rndValue () * 2.0 - 1.0;
	vec2 xy = rndUnit2D () * sqrt (1.0 - z*z);
	return vec3 (xy, z);
}

vec3 dir (const in vec3 n, const in vec3 d, const in float p) {
	vec3 r = reflect (d, n);
	vec3 b = rndUnit3D ();

	vec3 na = normalize (cross (n, b));
	vec3 nb = cross (n, na);

	vec3 ra = normalize (cross (r, b));
	vec3 rb = cross (r, ra);

	vec2 ng = rnd2D ();
	float nl = length (ng);

	float t = pow (nl, p);
	vec2 rg = ng * t;
	float rl = nl * t;

	vec3 o;
	if (p < 0.0)
		o = ng.x * na + ng.y * nb + n * sqrt (1.0 - nl*nl);
	else
		o = rg.x * ra + rg.y * rb + r * sqrt (1.0 - rl*rl);

	return o + n * clamp (-dot (o, n), 0.0, 1.0) * 2.0;
}

vec3 rayColor (const in vec3 p, const in vec3 d) {
	hit h;
	vec3 p1 = p;
	vec3 d1 = d;

	vec3 light = vec3 (0);
	vec3 color = vec3 (1);
	for (int i = 0; i < 3; i++) {
		h = scene (p1, d1);
		light += h.m.light * color;
		color *= h.m.color;

		d1 = dir (h.n, d1, h.m.spec);
		p1 = h.p + d1 * 0.001;
	}

	return light;
}

vec3 rayDirection (const in vec3 r, const in vec3 u, const in vec3 f, const in float fov, in vec2 cr) {
	vec2 rs = cr * 3.14159 * fov;
	vec2 sn = sin (rs);
	vec2 cs = cos (rs);

	#ifdef nonlinearPerspective
	return normalize (r * sn.x + u * sn.y + f * cs.x * cs.y);
	#else
	return normalize (r * cr.x * 4.0 * fov + u * cr.y * 4.0 * fov + f);
	#endif
}

void camera (inout vec3 right, inout vec3 up, inout vec3 forward, inout vec3 p, inout vec2 cr) {
	forward = normalize (forward);
	right = normalize (cross (up, forward));
	up = normalize (cross (forward, right));

	#ifdef crossEyeStereo
	float s = sign (cr.x);
	cr.x = (cr.x - s * 0.25);
	p -= right * s / iResolution.x * 200.0;
	#endif

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	seed1 = 1.0;

	vec2 tmp = mod (fragCoord.xy + time, dithering);
	seed1 += tmp.x + tmp.y / dithering;

	//seed1 += mod (fragCoord.x + fragCoord.y * 2.0, dithering);

	vec2 cr = (fragCoord.xy - iResolution.xy * 0.5) / iResolution.x;
	vec3 p = vec3 (sin (time) * 2.0, sin (time) + 1.25, cos (time) * 2.0);

	vec3 right, up = vec3 (0,1,0), forward = vec3 (0,0.75,0)-p;
	camera (right, up, forward, p, cr);

	vec3 color = vec3 (0);
	for (int i = 0; i < samples; i++) {
		vec3 d = rayDirection (
			right, up, forward, 1.0,
			cr + vec2 (rndValue (), rndValue ()) / iResolution.x);

		color += rayColor (p, d);
	}
	color = pow (color / float (samples), vec3 (gamma));
	fragColor = vec4 (color * brightness, 0.0);
}

