// Based on Clouds by inigo quilez : https://www.shadertoy.com/view/XslGRr

vec3  c =  vec3(1.05);
vec3  c1 =  vec3(.7);
const int MaxIter = 12;
vec3 sundir = vec3(-1.0,0.0,0.0);
float fog = .65+.6*cos(.3*iTime);
float g=.8;
float h=1.;
float zoom=.25;



vec4 map( vec3 p)
{
	float dr = 1.0;
	vec3 ot = vec3(1000.0); 
	float r2;
	for( int i=0; i<MaxIter;i++ )
	{

        r2 = dot(p,p);
        if(r2>100.)continue;

        ot = min( ot, abs(p) );

		//Box fold       
       //p = clamp(p,-c1,  c1) * 2.0 - p;

		//Sphere fold
		float k = max(h/r2,.1)*g;p *= k;dr *= k;

		//Exotic squaring
		p=abs(p*p.zxy)-c;dr*=2.*length(p);	
	}

	float d;
	//d = (abs(p.x)+abs(p.y))*length(p)/dr;	
	//d = (length(p.xz)*abs(p.y)+length(p.xy)*abs(p.z)+length(p.yz)*abs(p.x))/dr;
	//d = 1.5*(length(p.xz))*length(p.xy)/dr;
	d = sqrt(r2)*log(r2)/dr;
	//d = 2.*length(p)/dr;
	return vec4(ot,d);

}



vec4 raymarch( in vec3 ro, in vec3 rd )
{
	vec4 sum = vec4(0, 0, 0, 0);

	float t = 0.0;
	for(int i=0; i<64; i++)
	{
		if( sum.a > 0.99 ) continue;

		vec3 pos = ro + t*rd;		
		vec4 col = map( pos );
		float d = col.a;
		col.a = 0.035*fog/d;
		col.rgb *= col.a;

		if(dot(pos,pos)<11.)sum = sum + col*(1.0 - sum.a);	

		t += min(0.1,d*.3);

	}

	sum.xyz /= (0.001+sum.w);

	return clamp( sum, 0.0, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0*q;
    p.x *= iResolution.x/ iResolution.y;
    vec2 mo = -1.0 + 2.0*(iMouse.xy) / iResolution.xy+sin(.1*iTime);

    // camera
    vec3 ro =zoom* 4.0*normalize(vec3(cos(2.75-3.0*mo.x), 0.7+(mo.y+1.0), sin(2.75-3.0*mo.x)));
	vec3 ta = vec3(0.0, 0.0, 0.0);
    vec3 ww = normalize( ta - ro);
    vec3 uu = normalize(cross( vec3(0.0,1.0,0.0), ww ));
    vec3 vv = normalize(cross(ww,uu));
    vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );


    vec4 res = raymarch( ro, rd );

	float sun = clamp( dot(sundir,rd), 0.0, 1.0 );
	vec3 col = vec3(0.6,0.71,0.75) - rd.y*0.2*vec3(1.0,0.5,1.0) + 0.15*0.5;
	col += 0.2*vec3(1.0,.6,0.1)*pow( sun, 8.0 );
	col *= 0.95;
	col = mix( col, res.xyz, res.w );
	col += 0.1*vec3(1.0,0.4,0.2)*pow( sun, 3.0 );

    fragColor = vec4( col, 1.0 );
}

#define SAMPLE 200      // per 1x1 pixel bin
#define STEP 32.        // bin width
#define LAZZY 1         // lazzy exact noise evaluation
#define SMOOTH 0.       // smooth histogramm on 3 values.  0. / 1. / sub-relaxation
#define NOISE_SRC 1  	// 0: texture 1: math


#define NOISE_TYPE 1    // 1: linear  2: blobby (abs)  3:  hairy (1-abs)


#define ANIM 1          
#define PI 3.14159

float t;
vec2 FragCoord;

// --- noise functions from https://www.shadertoy.com/view/XslGRr
// Created by inigo quilez - iq/2013
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

mat3 m = mat3( 0.00,  0.80,  0.60,
              -0.80,  0.36, -0.48,
              -0.60, -0.48,  0.64 );

float hash( float n )  // base rand in [0,1]; 
{
#if NOISE_SRC==0
	return texture(iChannel0,vec2(n,n/256.)).r;
#elif NOISE_SRC==1
	return fract(sin(n-765.36334)*43758.5453);
#endif


}

float noise( in vec3 x ) // base noise in [0,1]; 
{
    vec3 p = floor(x);
    vec3 f = fract(x);

    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*57.0 + 113.0*p.z;

    float res = mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                        mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y),
                    mix(mix( hash(n+113.0), hash(n+114.0),f.x),
                        mix( hash(n+170.0), hash(n+171.0),f.x),f.y),f.z);
#if NOISE_TYPE==1
	return res;
#elif NOISE_TYPE==2
	return abs(2.*res-1.);
#elif NOISE_TYPE==3
	return 1.-abs(2.*res-1.);
#endif
}

float fbm( vec3 p ) // turbulent (=fractal) noise in [0,1]; 
{
    float f;
    f  = 0.5000*noise( p ); p = m*p*2.02;
    f += 0.2500*noise( p ); p = m*p*2.03;
    f += 0.1250*noise( p ); p = m*p*2.01;
    f += 0.0625*noise( p );
    return f;
}
// --- End of: Created by inigo quilez --------------------


// for noise to be thresholded, we not always need to compute high freq

float fbm_lazzy( vec3 p , float v0, float v1) // v0,v1: min/max thresholds
{
    float v01 = .5*(v0+v1), dv = .5*(v1-v0);
	float s=1.,f,r=1.,t;

	              s*=.5; f  = s*noise( p ); r-=s; if (abs(f-v01)>r+dv) return f+.5*r; 
	p = m*p*2.02; s*=.5; f += s*noise( p ); r-=s; if (abs(f-v01)>r+dv) return f+.5*r; 
	p = m*p*2.03; s*=.5; f += s*noise( p ); r-=s; if (abs(f-v01)>r+dv) return f+.5*r;
	p = m*p*2.01; s*=.5; f += s*noise( p );

    return f;
}


// calc histogramm of noise

float histogramm(vec2 uv) {
	float dx = dFdx(uv.x)*STEP;   // slice size
	float s = 0., q=0., n;
#if ANIM
  float t = iTime;
#else
  float t = 0.; 
#endif	
	for (int j=0; j<= SAMPLE; j++)
	{
		float y = float(j)/float(SAMPLE);
#if !LAZZY
		n = fbm(8.*vec3(0.,y,t));
#else
		n = fbm_lazzy(8.*vec3(0.,y,t), uv.x, uv.x+dx);
#endif

		if (abs(n-uv.x) < .5*dx) s++;
		q++;
	}
	return .1*s/(q*dx);
}


// smmothing using hardware derivatives. if SMOOTH <1, sub-relaxation

float smoothf(float v)
{
	float vx = -dFdx(v)*(2.*mod(FragCoord.x-.5,2.)-1.),
		  vy = -dFdy(v)*(2.*mod(FragCoord.y-.5,2.)-1.);

	return v + SMOOTH*(vx+vy)/3.;
}

// main loop

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    FragCoord=fragCoord;
	vec3 col=vec3(0.);
	if (uv.y < exp(-.5*pow((uv.x-.5)/.15,2.))*.5) col = vec3(0.,0.,.5);
	if (mod(uv.y,.1)<=1./iResolution.y) col = vec3(0.,1.,0.);
	if (mod(uv.y,.5)<=4./iResolution.y) col = vec3(0.,1.,0.);
	if (mod(uv.x,.1)<=1./iResolution.x) col = vec3(1.,0.,0.);
	if (mod(uv.x,.5)<=4./iResolution.x) col = vec3(1.,0.,0.);


	float t = histogramm(uv);

	if (SMOOTH != 0.) t = smoothf(t);  // average with neightboor pixels
	if (t > uv.y)  col=vec3(1.);		     // draw bar

	fragColor = vec4(col,1.); 
}

float gRaymarchEpsilon = 0.001;
//float gMusicSamplert
vec2 GetUV();
float plane(vec3 p, vec3 n)
{
	return dot(p,n);
}
float sphere(vec3 p, float radius)
{
	float modval = 2. * radius + radius;
	p.xz = mod(p.xz, modval) - modval / 2.;

	return length(p) - radius;
}

// distance estimator
float DE(vec3 p)
{
	float s0 = sphere(p, 0.25);
	float p0 = plane(p, normalize(vec3(0,1,0.1)));
	return min(s0, p0);
	return s0;
}

void raymarch( vec3 ray_start, vec3 ray_dir, out float depth, out float dist)
{
   vec3 point = ray_start;

	dist = 1.0;
	depth = 0.0;
	for (int i = 0; i < 100; ++i)
	{
		if (dist < gRaymarchEpsilon) continue;
		vec3 point = ray_start + ray_dir * depth;
		dist = DE(point);
		depth += dist;
	}
}

vec2 GetUV()
{
	float aspect = iResolution.x / iResolution.y;
	vec2 uv = gl_FragCoord.xy;
	uv /= iResolution.xy;
	uv *= 2.0;
	uv -= 1.0;
	uv.x *= aspect;

	return uv;
}

vec3 GetNormal(vec3 point)
{
	float epsilon= 0.1;
	vec3 dx = vec3(epsilon,0,0);
	vec3 dy = vec3(0,epsilon,0);
	vec3 dz = vec3(0,0,epsilon);
	return normalize(vec3(
		DE(point + dx)- DE(point - dx),
		DE(point + dy)- DE(point - dy),
		DE(point + dz)- DE(point - dz)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = GetUV();

	float camZ = 0.115 * sin(iTime) + 0.25 + 1. + 0.1;
	float camY = 0.115 * sin(iTime) + 0.45;
	vec3 camPos = vec3(0,camY, camZ);
	vec3 camDir = normalize(-camPos);

    vec3 rayStart = camPos;
    vec3 rayDir = normalize(vec3(uv,0) - rayStart);

    vec3 color = vec3(0);
	float depth;
	float dist;
    raymarch(rayStart, rayDir, depth, dist);
    if (dist < gRaymarchEpsilon)
	{
		vec3 n = GetNormal(rayStart + rayDir * depth);
		vec3 diffuseCol = vec3((uv + 1.) / 2., 0);
		vec3 specularCol = vec3(0.7,0.7,0.7);
		float shinyness = 128.;

		vec3 l = normalize(vec3(.5,1,1));
		vec3 dPara = n * dot(n, camDir);
		vec3 dPerp = camDir - dPara;
		vec3 r = normalize(-dPara + dPerp);
		color += diffuseCol * max(dot(n,l), 0.0);
		color += specularCol * pow(max(dot(n, r), 0.0), shinyness);

	}
	else
	{
		//color.xy += uv;
	}
	fragColor = vec4(color,1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 mouse = iMouse.xy / iResolution.xy;

	float bg = (cos(uv.x*3.14159*2.0) + sin((uv.y)*3.14159)) * 0.15;

	vec2 p = uv*2.0 - 1.0;
	p *= 15.0;
	vec2 sfunc = vec2(p.x, p.y + 5.0*sin(uv.x*10.0-iTime*2.0 + cos(iTime*7.0) )+2.0*cos(uv.x*25.0+iTime*4.0));
	sfunc.y *= uv.x*2.0+0.05;
	sfunc.y *= 2.0 - uv.x*2.0+0.05;
	sfunc.y /= 0.1; // Thickness fix

	vec3 c = vec3(abs(sfunc.y));
	c = pow(c, vec3(-0.5));
	c *= vec3(0.3,0.85,1.0);
	//c += vec3(bg, bg*0.8, bg*0.4);

	fragColor = vec4(c,1.0);
}

