vec3 colormap(float value) {
	float maxv = ClampLevel;
	vec3 c1,c2;
	float t;
	if (value < maxv / 3.) {
		c1 = vec3(1.);   	   c2 = vec3(1., 1., .5);
		t =  1./3.;
	} else if (value < maxv * 2. / 3.) {
		c1 = vec3(1., 1., .5); c2 = vec3(1., 0,  0.);
		t =  2./3. ;
	} else {
		c1 = vec3(1., 0., 0.); c2 = vec3(0.);
		t =  1.;
	}
	t = (t*maxv-value)/(maxv/3.);
	return t*c1 + (1.-t)*c2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) { // --------------------------------------

    float t = iTime;

    vec2 uv = fragCoord.xy/ iResolution.y;

	float d = 1.; // initial density

 #if Anim
	float cycle = cos(mod(-t,100.)/100.*2.*3.14);
	float n_tiles_level_1 = exp(cycle*cycle*ZoomDistance)*pow(2.,FirstDivision);
#else
	float n_tiles_level_1 = exp(iMouse.x/iResolution.x*ZoomDistance)*pow(2.,FirstDivision);
#endif

	// zoom and centering
	uv = (uv - vec2(.9,.5))*n_tiles_level_1 + vec2(.9,.5);

	float theta = 4.+.008*t; // some rotations, not necessary
    mat2 m = fRatio*mat2( cos(theta),sin(theta), 
					 -sin(theta),cos(theta) );

	// computation of the multiplicative noise
	float q = 1.;
	for (float i = 0.; i < NbScales; i++) {
		if (d<1e-2) continue;

		// multiply the amplitude to maintain the total density
		float c = (i+1.< NbScales) ? 2. : 1.;

		float nn = noise(uv + 10.7*i*i);

		for (float j = 0.; j < GazConcentration; j++) {
			nn = sin(nn*3.14159265359/2.);
		}

#if Mode == 0
		float n = c* 0.5*(1.+nn);
#else
		float n = nn;
#endif	

		// compute only the visible scales
		float crit = n_tiles_level_1 *q - iResolution.x/LimitDetails;
		if (crit < SmoothZone && i >= FirstScale) {
			if (crit>0.) {  // avoid aliasing
				float t = crit/SmoothZone;				
				n = n*(1.-t);
#if Mode == 0
				n += t;
#endif
			}

#if Mode == 0
			d *= n;
#else
			d += n*(0.5);
#endif			


		}

		uv = m*uv; q*= fRatio; // go to the next octave
	}

	d = clamp(d,0.0,d);
	fragColor.xyz = (keyToggle(67)) ? vec3(exp(-d)) :colormap(exp(-d));
}

#define PI 3.14159265359

vec3 col1 = vec3(0.216, 0.471, 0.698); // blue
vec3 col2 = vec3(1.00, 0.329, 0.298); // yellow
vec3 col3 = vec3(0.867, 0.910, 0.247); // red

float disk(vec2 r, vec2 center, float radius) {
	return 1.0 - smoothstep( radius-0.008, radius+0.008, length(r-center));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float t = iTime*2.;
	vec2 r = (2.0*fragCoord.xy - iResolution.xy) / iResolution.y;
	r *= 1.0 + 0.05*sin(r.x*5.+iTime) + 0.05*sin(r.y*3.+iTime);
	r *= 1.0 + 0.2*length(r);
	float side = 0.5;
	vec2 r2 = mod(r, side);
	vec2 r3 = r2-side/2.;
	float i = floor(r.x/side)+2.;
	float j = floor(r.y/side)+4.;
	float ii = r.x/side+2.;
	float jj = r.y/side+4.;	

	vec3 pix = vec3(1.0);

	float rad, disks;

	rad = 0.15 + 0.05*sin(t+ii*jj);
	disks = disk(r3, vec2(0.,0.), rad);
	pix = mix(pix, col2, disks);

	float speed = 2.0;
	float tt = iTime*speed+0.1*i+0.08*j;
	float stopEveryAngle = PI/2.0;
	float stopRatio = 0.7;
	float t1 = (floor(tt) + smoothstep(0.0, 1.0-stopRatio, fract(tt)) )*stopEveryAngle;

	float x = -0.07*cos(t1+i);
	float y = 0.055*(sin(t1+j)+cos(t1+i));
	rad = 0.1 + 0.05*sin(t+i+j);
	disks = disk(r3, vec2(x,y), rad);
	pix = mix(pix, col1, disks);

	rad = 0.2 + 0.05*sin(t*(1.0+0.01*i));
	disks = disk(r3, vec2(0.,0.), rad);
	pix += 0.2*col3*disks * sin(t+i*j+i);

	pix -= smoothstep(0.3, 5.5, length(r));	
	fragColor = vec4(pix,1.0);
}

