vec3 L = normalize(vec3(-.3, 1., .2));
vec3 n1 = normalize(vec3(2., -1., 0));
vec3 n3 = normalize(vec3(4.,  1., 0));

const float cz = -.8;
const vec3 tgt = vec3(0, cz, 0);
const vec3 cpos = vec3(0, cz, 11.5);

#define precis .01
#define farval 1e5
#define gap .03
#define hip .3
#define dmax 20.0
#define rayiter 70

#define MAT_SHINYBLACK .0
#define MAT_DULLGRAY   1.
#define MAT_MIDGRAY    2.
#define MAT_WHITE      3.
#define MAT_EYE        4.

#define OVERLAY

vec2 opU(in vec2 a, in vec2 b) {
	return a.x < b.x ? a : b;
}

float sdBox(in vec3 p, in vec3 b ) {
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),.0) +
         length(max(d,.0));
}

float sdCylinder(in vec3 p, in vec2 h) {
  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),.0) + length(max(d,.0));
}


vec2 baseEllipsoid(in vec3 pos) {
    vec3 abc = vec3(1., 2.5, 1.);
    abc = pos/(abc*abc);
	return vec2((dot(pos * abc, vec3(.5)) - .5)*inversesqrt(dot(abc,abc)), MAT_WHITE);
}

float sdMidCyl(in vec3 pos) {
    return .5*baseEllipsoid(2.*pos*vec3(1.,1.,0)).x;
}


mat3 rotX(in float t) {
    float cx = cos(t), sx = sin(t);
    return mat3(1., 0, 0, 
                0, cx, sx,
                0, -sx, cx);
}

mat3 rotY(in float t) {
    float cy = cos(t), sy = sin(t);
	return mat3(cy, 0, -sy,
                0, 1., 0,
                sy, 0, cy);

}

vec2 ant(inout vec3 pos, in vec3 p1) {
	float u = clamp(dot(pos, p1)/dot(p1,p1), .0, 1.)-1.0;
    pos -= p1;
    return vec2(length(pos-p1*u)-.025, MAT_SHINYBLACK);
}


float sdCone( in vec3 p ){
    p.z -= 0.58;
	vec2 q = vec2( length(p.xy), -p.z );
	return max( max( dot(q,vec2(inversesqrt(2.))), -p.z), p.z-1. );
}

vec2 stick(in vec3 pos, in float l) {

    float d;
	d = dot(abs(pos.xz), normalize(vec2(3., -1.)));
    pos.z -= l;
    d = max(d,sdBox(pos, vec3(.15, .05, l)));

	return vec2(d, MAT_SHINYBLACK);

}

vec2 torus(in vec3 pos, in vec3 x, in vec3 y) {

	vec3 n = normalize(cross(x, y));

	vec3 pp = length(x)*normalize(pos - n*dot(n, pos));

	float d = length(pos-pp)-.05;
	d = max(d, dot(pos, normalize(cross(x, n))));
	d = max(d, dot(pos, normalize(cross(n, y))));

	return vec2(d, MAT_SHINYBLACK);


}


vec2 map(in vec3 pos) {

    // antennae
    vec2 rval = baseEllipsoid(pos);

    vec3 lpos = pos - vec3(-.3, .2, -.2);

    rval = opU(rval, ant(lpos, vec3(0, 2.7, 0)));

    lpos = pos - vec3(-.5, 2., -.2);
    rval = opU(rval, ant(lpos, vec3(0, 1., 0)));
    rval = opU(rval, ant(lpos, vec3(.2, .2, 0)));
    rval = opU(rval, ant(lpos, vec3(0, .4, 0)));

	pos.x = abs(pos.x);


	rval.x = max(rval.x, min(dot(pos, n1), sdMidCyl(pos)));
	rval.x = max(rval.x, -sdCone(pos));
    rval.x = max(rval.x, -abs(pos.x)+.5*gap);
	rval.x = max(rval.x, -sdBox(pos - vec3(0, -.15, 0), vec3(.7, .7, .5)));
	//rval.x = max(rval.x, -sdCylinder((pos-vec3(0, 0, .5)).xzy, vec2(.23, .5)));

    rval = opU(rval, vec2(length(pos-vec3(0, 0,.56))-.3, MAT_EYE));
	rval = opU(rval, vec2(sdBox(pos-vec3(0, -.15, 0), vec3(.3, .75, .8)), MAT_DULLGRAY));
    rval = opU(rval, vec2(sdBox(pos+vec3(0, 13.5, .5), vec3(4., 10.1, 4.)), 2.9));
	//rval = opU(rval, vec2(pos.y+3.4, 2.5));

	// front leg
    rval = opU(rval, torus(pos-vec3(.45, -1.2, .3), 
                           vec3(-.45, .1, -1.), 
                           vec3( .45, -.25, .9)));

	lpos = rotX(1.8)*rotY(-.2)*(pos+vec3(-.5, 2.5, 0));

	vec2 e = baseEllipsoid(lpos);

	e.x = max(e.x, -(e.x+hip));	
	e.x = max(e.x, -sdMidCyl(lpos));
	e.x = max(e.x, dot(lpos, n3));
	e.x = max(e.x, -lpos.x);
	e.x = max(e.x, -lpos.z);

    rval = opU(rval, e);
    rval = opU(rval, stick(lpos-vec3(.22, -2.25, -1.45), 0.775));

    // back leg
    rval = opU(rval, torus(pos-vec3(0, -.7, -1.15),
                           vec3(0, .05, -.7),
                           vec3(0, -.35, .55)));

	lpos = rotX(-1.8)*(pos+vec3(0, 1.5, .4));

	e = baseEllipsoid(lpos);

	e.x = max(e.x, -(e.x+hip));	
	e.x = max(e.x, -sdMidCyl(lpos));
	e.x = max(e.x, dot(lpos, n3));
	e.x = max(e.x, lpos.z);

    rval = opU(rval, e);

	rval = opU(rval, stick(vec3(0, -2.32, 2.5)-lpos, 1.3));


    // side
    pos.x -= .7;

    e = baseEllipsoid(pos);

	e.x = max(e.x, -sdMidCyl(pos)+gap);
	e.x = max(e.x, dot(pos, -n3)+gap);
	e.x = max(e.x, dot(pos, -n1)+gap);

	e.x = max(e.x, -sdBox(pos-vec3(1.6, 0, 0), vec3(.75, .1, .5)));

    rval = opU(rval, e);

 	e = vec2(sdBox(pos-vec3(.35, -.15, 0), vec3(.3, .65, .4)), MAT_DULLGRAY);
	e = opU(e, vec2(sdBox(vec3(pos.xy, abs(pos.z))-vec3(0, -.2, .3), vec3(.5, .05, .05)), MAT_SHINYBLACK));
    pos.y = abs(pos.y);
	e = opU(e, vec2(sdCylinder((pos-vec3(.28, .2, .5)).xzy, vec2(.13, .2)), MAT_MIDGRAY));
	e.x = max(e.x, -sdCylinder((pos-vec3(.28, .2, .35)).xzy, vec2(.1, .4)));

    rval = opU(rval, e);

	return rval;

}


vec2 castRay( in vec3 ro, in vec3 rd )
{
    vec2 res = vec2(precis*2., -1.);
    float t = .0;
    for( int i=0; i<rayiter; i++ )
    {
        if( abs(res.x)<precis||t>dmax ) continue;//break;
        t += res.x;
	    res = map( ro+rd*t );
	    res.y = res.y;
    }
	if (t > dmax || abs(res.x) > precis) { res.y = -1.; }
    res.x = t;
    return res;
}

vec3 calcNormal( in vec3 pos )
{
	vec3 eps = vec3( .001, 0, 0 );
	vec3 nor = vec3(
	    map(pos+eps.xyy).x - map(pos-eps.xyy).x,
	    map(pos+eps.yxy).x - map(pos-eps.yxy).x,
	    map(pos+eps.yyx).x - map(pos-eps.yyx).x );
	return normalize(nor);
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float totao = .0;
    float sca = 1.;
    for( int aoi=0; aoi<5; aoi++ )
    {
        float hr = .01 + .04*float(aoi);
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        totao += -(dd-hr)*sca;
        sca *= .8;
    }
    return clamp( 1. - 3.*totao, .0, 1. );
}

#ifdef OVERLAY

float raycyl(vec3 o, vec3 d) { 

    float r = .025;
	float a = dot(d,d) - d.z*d.z;
	float b = 2.*(dot(o,d)-o.z*d.z);
	float c = dot(o,o) - o.z*o.z - r*r;

    a *= 2.;

	float qd = b*b - 2.*a*c;
 	float sd = sqrt(qd);
	float x1 = (-b + sd)/a;
    float x2 = (-b - sd)/a;

    float t = qd < .0 ? farval : min(x1 < .0 ? farval : x1,
			 			             x2 < .0 ? farval : x2);

	float u = o.z + t*d.z;

    return (t < farval && u >= .0) ? t : farval;

}

#endif


vec3 shade( in vec3 ro, in vec3 rd ) {

	vec2 tm = castRay(ro, rd);
	vec3 rcolor;

	if (tm.y >= .0) {
		vec3 pos = ro + tm.x * rd;
		vec3 nor = calcNormal(pos);
		float ao = calcAO( pos, nor );

		// 0 = shiny black
		// 1 = dark gray
		// 2 = mid gray
		// 3 = shiny white
		// 4 = eye

		float r = clamp(mix(.2, 1., tm.y/3.), .0, 1.);
		r*=r;
		float gb = tm.y < 4. ? r : .0;

		if (tm.y == 4.) {
			float k = length(pos.xy);
			r *= (1. - 1.*cos(k*30.0));
			float a = atan(pos.y, pos.x);
			r *= (1. - .5*cos(a*16.));
		}

		float gamma = abs(tm.y - 1.5) < .75 ? .0 : .5;

		vec3 color = vec3(r, vec2(gb));

		float c1 = dot(nor, L);

		vec3 diffamb = (.5 + .5 * max(c1, -c1*.2))*color;

		vec3 R = 2.*nor*dot(nor,L)-L;
		float spec = gamma*pow(clamp(-dot(R, rd), .0, 1.), 24.);
		rcolor = (diffamb + spec)*ao;

	} else {
		float g = max(.3*dot(L,rd)+.6, .0);
		rcolor = vec3(g*g);
	}

#ifdef OVERLAY

	if (raycyl(ro, rd) < tm.x) {
		rcolor = mix(rcolor, vec3(1., .3, .3), .5 + .15*sin(94.*iTime));
	}

#endif

	return rcolor;

}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 uv = (fragCoord.xy - .5*iResolution.xy) * 0.8 / (iResolution.y);

	vec3 rz = normalize(tgt - cpos),
        rx = normalize(cross(rz,vec3(0,1.,0))),
        ry = cross(rx,rz);

	float thetax = -.05, thetay = -.10;

	if (max(iMouse.x, iMouse.y) > 20.0) { 
		thetax = (iMouse.y - .5*iResolution.y) * 3.14/iResolution.y; 
		thetay = (iMouse.x - .5*iResolution.x) * -6.28/iResolution.x; 
	}

    mat3 R = mat3(rx,ry,rz)*rotX(thetax)*rotY(thetay);

	vec3 rd = R*normalize(vec3(uv, 1.)),
        ro = tgt + R*vec3(0,0,-length(cpos-tgt));

	fragColor.xyz = pow(shade(ro, rd),vec3(.8));

}

// Whitney Music Box - Jim Bumgardner
// whitneymusicbox.org

const float rad = 0.9;
const float dots = 64.0;
const float duration = 180.0;
const vec3 colorsep = vec3(0,2.09,4.18);
const float PI = 3.1415926535897932384626433832795;
const float PI2 = 2.0*3.1415926535897932384626433832795;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
  float tm = mod(iTime,duration)/duration;
  p.y *= iResolution.y/iResolution.x;

  vec3 gradient = vec3(0.0);

  for (float i=1.0; i<=dots; i++)
  {
    float i2pi = i*PI2;
    float ang = mod(tm*i2pi, PI2);
    float amp = rad*(1.0-(i-1.0)/dots);
    float cang = i2pi/dots;
    float fade = 0.7 - pow(smoothstep(0.0,1.0,ang),2.0)*0.5;
    vec2 star_pos = vec2(cos(ang) * amp, -sin(ang) * amp);
    gradient += (cos(cang+colorsep) + 1.0/2.0) * ((fade / 384.0) / pow(length(star_pos - p), 1.5)) * fade;
  }
  fragColor = vec4( gradient, 1.0);
}

//Human fovea detector by nimitz (twitter: @stormoid)

/*
I was playing with procedural texture generation when I came across this.
You might need to tweak the scale value depending on your monitor's ppi.

Different shapes might provide better results, haven't tried many.
*/

//migh need ot tweak this value depending on monitor ppi (tuned for ~100 ppi)
#define scale 90.

#define thickness 0.0
#define lengt 0.13
#define layers 15.
#define time iTime*3.

vec2 hash12(float p)
{
	return fract(vec2(sin(p * 591.32), cos(p * 391.32)));
}

float hash21(in vec2 n) 
{ 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

vec2 hash22(in vec2 p)
{
    p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return fract(sin(p)*43758.5453);
}

mat2 makem2(in float theta)
{
	float c = cos(theta);
	float s = sin(theta);
	return mat2(c,-s,s,c);
}

float field1(in vec2 p)
{
	vec2 n = floor(p)-0.5;
    vec2 f = fract(p)-0.5;
    vec2 o = hash22(n)*.35;
	vec2 r = - f - o;
	r *= makem2(time+hash21(n)*3.14);

	float d =  1.0-smoothstep(thickness,thickness+0.09,abs(r.x));
	d *= 1.-smoothstep(lengt,lengt+0.02,abs(r.y));

	float d2 =  1.0-smoothstep(thickness,thickness+0.09,abs(r.y));
	d2 *= 1.-smoothstep(lengt,lengt+0.02,abs(r.x));

    return max(d,d2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy-0.5;
	p.x *= iResolution.x/iResolution.y;

	float mul = (iResolution.x+iResolution.y)/scale;

	vec3 col = vec3(0);
	for (float i=0.;i <layers;i++)
	{
		vec2 ds = hash12(i*2.5)*.20;
		col = max(col,field1((p+ds)*mul)*(sin(ds.x*5100. + vec3(1.,2.,3.5))*.4+.6));
	}

	fragColor = vec4(col,1.0);
}

#define time iTime*.5
#define resolution iResolution.xy

vec3 ldir;
float ot;

float julia(vec2 p) {
	p=p*2.+vec2(0.,-0.12);
	ot=1000.;
	float z = 0.0;
	int iter;
	for (int i=0; i<18; i++) {
		float l=dot(p,p);
		ot=min(ot,abs(l-.6));
		iter = i;
		if(l>4.0)
			break;
		float x = p.x*p.x-p.y*p.y;
		float y = 2.*p.x*p.y;
		p = vec2(x,y)+vec2(0.285,0.01);  
	}
	return dot(p,p)*.2;
}

float light(vec2 p) {
	vec2 d=vec2(0.,.003);
	float d1=julia(p-d.xy)-julia(p+d.xy);
	float d2=julia(p-d.yx)-julia(p+d.yx);	
  	vec3 n1=vec3(0.,d.y,d1);
  	vec3 n2=vec3(d.y,0.,d2);
  	vec3 n=normalize(cross(n1,n2));
  	float diff=max(0.,dot(ldir,n))*.6;
	vec3 r=reflect(vec3(0.,0.,1.),ldir);
	float spec=pow(max(0.,dot(r,n)),25.)*.4;
  	return (diff+spec+.15)*max(0.4,1.-julia(p));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy/resolution.xy-.5;
	vec2 aspect=vec2(resolution.x/resolution.y,1.);
  	p*=aspect;
	vec3 lightpos=vec3(sin(time*3.)*.8,cos(time)*.9,-1.);
	lightpos.xy*=aspect*.5;
	ldir=normalize(vec3(p,-julia(p))+lightpos);
  	float l=light(p);
  	ot=max(1.-ot*.7,0.);
	vec3 col=l*vec3(ot*ot*1.45,ot*.6,ot*ot*.75);
	col+=pow(max(0.,.2-length(p+lightpos.xy))/.2,5.);
	col*=pow(max(0.,1.-length(p+lightpos.xy)*.3),2.5);
	fragColor = vec4(col+.03, 1.0 );
}

#define PRECISION 0.001

//----------------------------------------------------

vec3 opTwist(vec3 p) {
	float c = cos(5.0*p.y);
    float s = sin(5.0*p.y);
    mat2  m = mat2(c,-s,s,c);
    return vec3(m*p.xz,p.y);
}

vec2 opU(vec2 d1, vec2 d2) {
	return (d1.x < d2.x) ? d1 : d2;
}

vec3 opT(vec3 p, vec3 t) {
	return p - t;
}

vec3 opD(vec3 p, float c) {
	return mod(p, c) - 0.5 * c;
}

vec2 opS(vec2 d1, vec2 d2) {
	return vec2(max(-d2.x, d1.x), 2.0);
}

//-----------------------------------------------------

vec2 sdSphere(vec3 p, float r) {
	return vec2(length(p) - r, 1.0);
}

vec2 sdPlane(vec3 p) {
	return vec2(p.y, 3.0);
}

vec2 sdBox(vec3 p, vec3 d) {
	//p = opTwist(p);
	return vec2(length(max(abs(p)-d,0.0)), 2.0);
}

//-----------------------------------------------------------------

vec2 scene(vec3 p) {
	vec3 v = vec3(0.0, -0.4-abs(sin(iTime)), 0.0);
	p.x = mod(p.x, 2.0) - 2.0*0.5;
	p.z = mod(p.z, 2.0) - 2.0*0.5;
	//beat implementation
	float beat;
	float ct = iChannelTime[0];
	if ((ct > 8.0 && ct < 33.5)
	|| (ct > 38.0 && ct < 88.5)
	|| (ct > 93.0 && ct < 194.5))
		beat = pow(sin(ct*3.1416*3.78+1.9)*0.5+0.5,15.0)*0.1;
	//end beat implementation
	return opU(sdSphere(p+v, 0.18+beat*sin(2.0*(0.3*iTime))) ,opU(sdPlane(p), opS(sdBox(p, vec3(0.4, 1.0, 0.4)), sdSphere(opT(p, vec3(0.0, 1.0, 0.0)), 0.3*abs(sin(iTime))))));
}

vec2 raymarch(vec3 ro, vec3 rd, float maxd) {
	float t = 0.0;
	float h = PRECISION * 2.0;
	float m = 0.0;

	for(int i = 0; i < 128; i++) {
		if(abs(h) < PRECISION || t > maxd) {
			break;
		}
		t += h;
		vec2 res = scene(ro + rd * t);
		h = res.x;
		m = res.y;
	}

	if(t > maxd) {
		m = 0.0;
	}

	return vec2(t, m);
}

vec3 calcNormal(vec3 pos) {
	vec3 eps = vec3(0.001, 0.0, 0.0);
	vec3 nor = vec3(
		scene(pos + eps.xyy).x - scene(pos - eps.xyy).x,
		scene(pos + eps.yxy).x - scene(pos - eps.yxy).x,
		scene(pos + eps.yyx).x - scene(pos - eps.yyx).x
	);
	return normalize(nor);
}

float calcAO(vec3 pos, vec3 nor) {
	float totao = 0.0;
	float sca = 1.0;
	for(int i = 0; i < 5; i++) {
		float hr = 0.01 + 0.05 * float(i);
		vec3 aopos = nor * hr + pos;
		float dd = scene(aopos).x;
		totao += -(dd - hr) * sca;
		sca *= 0.75;
	}
	return clamp(1.0 - 4.0 * totao, 0.0, 1.0);
}

float softshadow( in vec3 ro, in vec3 rd, in float mint, in float maxt, in float k) {
	float res = 1.0;
    float t = mint;
    for(int i = 0; i < 30; i++) {
		if(t < maxt) {
        	float h = scene( ro + rd*t ).x;
        	res = min(res, k*h/t);
        	t += 0.02;
		}
    }
    return clamp( res, 0.0, 1.0 );
}

vec3 render(vec3 ro, vec3 rd) {
	vec3 col = vec3(0.6, rd.y*(0.5+0.5*abs(sin(iTime))), rd.x*(0.5+0.5*abs(cos(iTime))));

	vec2 res = raymarch(ro, rd, 40.0);
	float id = res.y;
	float t = res.x;

	if(id != 0.0) {
		//point on the shape surface
		vec3 pos = ro + rd * res.x;
		//normal vector to the surface
		vec3 nor = calcNormal(pos);
		//initial color
		if(id == 1.0) {
			col = vec3(1.0, 0.0, 0.3);
		} else if(id == 2.0) {
			col = vec3(0.4, 1.0, 0.0);
		} else if(id == 3.0) {
			col = vec3(0.0, 0.4, 1.0);
		}
		float ao = calcAO( pos, nor );

		vec3 lig = normalize(vec3(-0.6, 0.7, -0.5));
		lig.x *= cos(iTime);
		lig.y *= sin(iTime);
		float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
        float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
        float bac = clamp( dot( nor, normalize(vec3(-lig.x,0.0,-lig.z))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);

		float sh = 1.0;
		if( dif>0.02 ) { sh = softshadow( pos, lig, 0.02, 10.0, 7.0 ); dif *= sh; }

		vec3 brdf = vec3(0.0);
		brdf += 0.20*amb*vec3(0.10,0.11,0.13)*ao;
        brdf += 0.20*bac*vec3(0.15,0.15,0.15)*ao;
        brdf += 1.20*dif*vec3(1.00,0.90,0.70);

		float pp = clamp( dot( reflect(rd,nor), lig ), 0.0, 1.0 );
		float spe = sh*pow(pp,16.0);
		float fre = ao*pow( clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );

		float fo = 1.0-exp(-0.0007*t*t);
		vec3 fco = vec3(0.55,0.65,0.75) + 0.6*vec3(1.0,0.8,0.5);

		col = mix(col*brdf + vec3(1.0)*col*spe + 0.2*fre*(0.5+0.5*col), fco, fo);
	}
	return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (-1.0 + 2.0 * fragCoord.xy / iResolution.xy) * vec2(iResolution.x/iResolution.y, 1.0);

	vec2 mo = iMouse.xy/iResolution.xy; 
	float time = 15.0 + iTime;

	//camera
	vec3 ro = vec3( -0.5+3.2*cos(0.5*time + 6.0*mo.x), 3.0 + 2.0*mo.y, 10.0 + 3.2*sin(0.5*time + 6.0*mo.x) );
	vec3 ta = vec3( -0.5, -0.4, 0.5 );	

	//camera setup
	vec3 cw = normalize(ta - ro);
	vec3 cp = vec3(0.0, 1.0, 0.0);
	vec3 cu = normalize(cross(cw, cp));
	vec3 cv = normalize(cross(cu, cw));
	vec3 rd = normalize(p.x * cu + p.y * cv + 2.5 * cw);

	//vec3 hp = vec3(0.5/ iResolution.xy, 0.0);
	vec3 col = render(ro, rd);

	col = sqrt(col);

	fragColor = vec4(col,1.0);
}

// Sine Puke II, by WAHa.06x36.
// Same as my original Sine Puke, but with more rainbow, and less "newp".

// Based on code by Spektre posted at http://stackoverflow.com/questions/3407942/rgb-values-of-visible-spectrum
vec3 spectral_colour(float l) // RGB <0,1> <- lambda l <400,700> [nm]
{
	float r=0.0,g=0.0,b=0.0;
         if ((l>=400.0)&&(l<410.0)) { float t=(l-400.0)/(410.0-400.0); r=    +(0.33*t)-(0.20*t*t); }
    else if ((l>=410.0)&&(l<475.0)) { float t=(l-410.0)/(475.0-410.0); r=0.14         -(0.13*t*t); }
    else if ((l>=545.0)&&(l<595.0)) { float t=(l-545.0)/(595.0-545.0); r=    +(1.98*t)-(     t*t); }
    else if ((l>=595.0)&&(l<650.0)) { float t=(l-595.0)/(650.0-595.0); r=0.98+(0.06*t)-(0.40*t*t); }
    else if ((l>=650.0)&&(l<700.0)) { float t=(l-650.0)/(700.0-650.0); r=0.65-(0.84*t)+(0.20*t*t); }
         if ((l>=415.0)&&(l<475.0)) { float t=(l-415.0)/(475.0-415.0); g=             +(0.80*t*t); }
    else if ((l>=475.0)&&(l<590.0)) { float t=(l-475.0)/(590.0-475.0); g=0.8 +(0.76*t)-(0.80*t*t); }
    else if ((l>=585.0)&&(l<639.0)) { float t=(l-585.0)/(639.0-585.0); g=0.82-(0.80*t)           ; }
         if ((l>=400.0)&&(l<475.0)) { float t=(l-400.0)/(475.0-400.0); b=    +(2.20*t)-(1.50*t*t); }
    else if ((l>=475.0)&&(l<560.0)) { float t=(l-475.0)/(560.0-475.0); b=0.7 -(     t)+(0.30*t*t); }

	return vec3(r,g,b);
}

vec3 spectral_palette(float x) { return spectral_colour(x*300.0+400.0); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
