}

// variant from okeli4408: https://www.shadertoy.com/view/XdXXzM#

bool keyToggle(int ascii) {
	return (texture(iChannel2,vec2((.5+float(ascii))/256.,0.75)).x > 0.);
}

float showFlag(vec2 p, vec2 uv, float v) {
	float d = length(2.*(uv-p)*iResolution.xy/iResolution.y);
	return 	1.-step(.06*v,d) + smoothstep(0.005,0.,abs(d-.06));
}

float showFlag(vec2 p, vec2 uv, bool flag) {
	return showFlag(p, uv, (flag) ? 1.: 0.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	// --- tunings 

	vec2 mouse = iMouse.xy / iResolution.xy;

	bool BG_BW, BG_COL, FG_COL,FLIP,GAMMA, VID;

	if (iMouse.z<=0.) { // no mouse: autodemo
		float t = iTime/3.;
		float t0 = mod(t,3.); int i = int(t0);

		// (!BG_BW) = (i==0)
		BG_BW  = (i==1);
		BG_COL = (i==2);
		FLIP = (mod(t/3.,2.)>1.);
		GAMMA = (mod(t/6.,2.)>1.);
		VID = (iResolution.y<200.) || (iChannelResolution[0].y<=0.) || (mod(t/12.,2.)>1.);
		FG_COL=false; // already there for BG + high gamma

		mouse = .5*( 1.+ .5*vec2(cos(3.*t)+cos(t), sin(3.3*t)+cos(.7*t) ) );
	}	
	else 
	{
		BG_BW  = keyToggle(66);
		BG_COL = keyToggle(67);
		FG_COL = keyToggle(70);
		FLIP   = keyToggle(32);
		GAMMA  = keyToggle(71);
		VID    = keyToggle(86);
	}

	float panel = showFlag(vec2(.25,.05),uv, bool(BG_BW))
				+ showFlag(vec2(.35,.05),uv, bool(BG_COL))
				+ showFlag(vec2(.45,.05),uv, bool(FG_COL))
				+ showFlag(vec2(.55,.05),uv, bool(GAMMA))
				+ showFlag(vec2(.65,.05),uv, bool(FLIP))
				+ showFlag(mouse,uv, true);

    // --- display 

	vec3 col = (VID) ? texture(iChannel1, vec2(1.-uv.x,uv.y)).rgb 
					 : texture(iChannel0, vec2(1.-uv.x,uv.y)).rgb;

	// edge = norm of luminance derivative.
	float lum = col.x + col.y + col.z;
	vec2 deriv = vec2(dFdx(lum), dFdy(lum));
	float edge = sqrt(dot(deriv,deriv));
	// improve:
	edge = smoothstep(0.,mouse.x,edge);
	if (GAMMA) edge = pow(edge, exp(2.*2.*(mouse.y-.7))); // gamma contrasting

	if (FLIP) edge = 1.-edge;

	vec3 bg = vec3 ( (BG_BW) ? 1.: 0.);  // black vs white background
	if (BG_COL) bg = 1.-col; 			 // background = reverse video
	if (!FG_COL) col = 1.-bg;			 // forground = rev of background

	// key transform: ink + paper
	col = mix(col,bg,edge); 

	if (!GAMMA) col = pow(col, vec3(exp(3.*mouse.y))); // gamma contrasting

	col.b = (col.b+.2*(col.r+col.g) < panel) ? panel:  col.b-panel;
	fragColor = vec4(col,1.0);
}

#define DETECT_BLINK 1.5
#define DETECT_BORDER 4.0
#define DETECT_CROSS 8.0
#define DETECT_SEQUENCE 10.0


// hash function
float hash(int n) {
    return fract(sin(float(n))*43758.5453);
}

// round box distance function
float roundBox(vec2 p, vec2 b, float r) {
	return length(max(abs(p)-b,0.0))-r;
}

// square signal
float signal(float value) {
	value -= floor(value);
	if (value < 0.5) return 0.0;
	return 1.0;
}

// pixel coordinate to screen content
vec3 getPixel(vec2 p) {

	// stretch to get the real ratio
	p.y *= iResolution.y / iResolution.x;

	// small border around the whole screen
	float border = roundBox(p, vec2(0.92, 0.42), 0.0);
	if (border > 0.02) return vec3(0.0);
	if (border > 0.01) return vec3(1.0);
	if (border > 0.00) return vec3(0.0);

	// change the scale
	p *= 0.425;

	// translation with time
	vec2 trans = vec2(iTime)*vec2(0.0331,0.0051);

	// now do some stuff detection
	int detectID = int(iTime / DETECT_SEQUENCE);
	float detectTime = mod(iTime, DETECT_SEQUENCE);
	vec2 center = vec2(hash(detectID), hash(detectID*1500))*2.0-1.0;
	center.x *= .3; center.y *= .1;
	center -= vec2(mod(trans.x, 0.8)-.4, mod(trans.y,.4)-.2);

	if (detectTime < DETECT_BLINK) {
		if (signal(detectTime*5.0) < 0.5) {
			float detectBorder = roundBox(p-center, vec2(0.001, 0.001), 0.0);
			if (detectBorder < 0.005) {
				return vec3(1.0);
			}
		}
	} else if (detectTime < DETECT_BORDER) {
		detectTime = min(detectTime, DETECT_BORDER);
		float size = (detectTime-DETECT_BLINK)/(DETECT_BORDER-DETECT_BLINK)*0.02;
		float detectBorder = abs(roundBox(p-center, vec2(size, size), 0.01));
		if (detectBorder < 0.002) {
			return vec3(1.0);
		}
	} else if (detectTime < DETECT_CROSS) {
		float detectBorder = roundBox(p-center, vec2(0.02, 0.02), 0.01);
		if (detectBorder > 0.002) {
			detectBorder = roundBox(p-center, vec2(100.0, 0.002), 0.0);
			if (detectBorder < 0.0001) return vec3(1.0);
			detectBorder = roundBox(p-center, vec2(0.002, 100.0), 0.0);
			if (detectBorder < 0.0001) return vec3(1.0);
		}
		detectBorder = abs(roundBox(p-center, vec2(0.02, 0.02), 0.01));
		if (detectBorder < 0.002) {
			return vec3(1.0);
		}
	}

	// translate the background
	p += trans;
	// what the fuck am i doing
    vec3 uv = vec3(texture(iChannel1, p));
	uv.x *= cos(uv.y+iTime)*.35124;
	uv.y /= cos(uv.x*sqrt(uv.y))*.23312/abs(uv.y);
    vec3 co = vec3(texture(iChannel1, uv.xy));
    return co*.8;
}

// pixel coordinate to screen value, with scanline and glitches etc
vec3 getScreen(vec2 p) {

	// fade to black on outside
	float boundValue = roundBox(p, vec2(0.72, 0.6), 0.1)*5.0;
	boundValue = 1.0 - clamp(boundValue, 0.0, 1.0);

	// screen scanline
	float scanline = sin(p.y*3.1415*110.0)*.5+.5;
	scanline = sqrt(scanline);

	// glitch the point around
	float glitch = sin(18.245*iTime)*cos(11.323*iTime)*sin(4.313*iTime);
	glitch *= glitch;
	p.x += sin(p.y*19.1)*glitch*.01;
	p.x += sin(p.y*459.1)*glitch*glitch*.02;

	// get the screen content
	vec3 color = getPixel(p);

	// and blend everything together
	float factor = scanline*boundValue;
	return color*factor*.6+vec3(0, factor*color.g*.8, 0);
}

// pixel coordinate to physical screen value
vec3 getScreenBorder(vec2 p) {
	// outer border
	float boundOut = roundBox(p, vec2(1.15, 1.15), 0.0)*8.0;
	boundOut = clamp(boundOut, 0.5, 1.0);

	// inner border
	vec3 cin = vec3(texture(iChannel0, p*.88));
	cin = vec3(cin.r*.02, cin.g*.18, cin.b*.03);

	// add light to simulate screen normal
	float light = sin((atan(p.x, p.y)+0.2)*2.0);
	cin += light*.10+0.13;
	float boundIn = roundBox(p, vec2(0.99, 0.9), 0.05)*150.0;
	boundIn = clamp(boundIn, 0.0, 1.0);

	// add a slight green haze
	float hazeV = roundBox(p, vec2(0.83, 0.73), 0.03)*3.5;
	hazeV = 1.0 - clamp(hazeV, 0.8, 1.0);
	hazeV += (sin(iTime*2.3) + sin(iTime*20.912) * .4)*.06;
	vec3 haze = vec3(0.0, hazeV*.14, 0.0);

	// return values
	return mix(cin*boundIn, vec3(0), boundOut) + haze;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	// move the point away from the center
	vec2 p = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
	p += p*dot(p, p)*0.22;

	// add a small led
	vec2 delta = vec2(0.97, 0.04) - (fragCoord.xy / iResolution.xy);
	delta.y *= iResolution.y / iResolution.x;
	float ledv = clamp(1.5 - length(delta)*150.0, 0.0, 1.0);
	ledv *= sin(2.0*iTime)*.5+.5;
	vec3 led = vec3(ledv*.4, ledv, ledv*.4);

	fragColor = vec4(getScreenBorder(p)+getScreen(p)+led, 1);
}

//controls size of each ball
// (actually represents size with bloom included)
const float threshold=50.0;

//higher values mean thinner metaballs
//1.0 or higher means one ball is not enough to form a solid
const float metaballFactor=0.75;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float dist;
	vec2 cPoint;
	float sum=0.0;
	//not normalized: circles are not stretched
	vec2 uv = fragCoord.xy;


	//let's make some points...
	// hurr durr, counting to a hundred in a shader program
	//  what could possibly go wrong
	for (float i=1.0;i<100.0;i++) {
		//animate each point to wobble around
		cPoint=vec2(0.5*iResolution);
		cPoint.x+=sin(cos(i)*iTime*0.273)*iResolution.x*0.5;
		cPoint.y+=cos((sin(i*1.51))*iTime*0.32)*iResolution.y*0.5;

		//add current ball weight if within range
		dist=distance(cPoint,uv);
		if (dist<threshold) {
			sum+=1.0-sqrt(dist/threshold);
		}
	}
	//cutoff based on accumulated total weight
	if (sum>metaballFactor) {
		sum*=0.5;
		sum=min(sum,.5);
		sum*=1.8;
		fragColor = vec4(sum,sum,sum*1.2,1.0);
	} else {
		//not strong enough to be solid
		//(act as bloom instead)
		sum*=.3;
		fragColor = vec4 (sum,sum,sum*1.4,1.0);
	}
}

