vec2 cart_polar(vec2 p) {
	return vec2(atan(((p).y) / ((p).x)), length(p));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	bool hit = run(((fragCoord).xy) / ((iResolution).xy) * 2.0 - 1.0, (iTime) + 100.0 / 1000.0);
	if(hit) {
