bool run(vec2 p, float t) {
	t = (t) - 100.0;
	bool hit = false;
	vec2 start = cart_polar(vec2(0.0, 0.5));
	for(int temp_1 = 0; temp_1 < 100; ++temp_1) {
		t = (t) + 1.0;
		vec2 cp = move(start, t);
		float dist = length((polar_cart(cp)) - (p));
		if((dist) < 0.005) {
			hit = true;
		}
	}
	return hit;
}
vec2 polar_cart(vec2 p) {
	return (vec2(cos((p).x), sin((p).x))) * ((p).y);
