float lava( vec2 p )
{
	p += vec2(2.0,4.0);
    float f;
    f  = 0.5000*noise( p ); p = p*2.02;
    f += 0.2500*noise( p ); p = p*2.03;
    f += 0.1250*noise( p ); p = p*2.01;
    f += 0.0625*noise( p );
    return f;
}

// I think this is just a rotation matrix - haven't analyzed it really.
const mat3 m = mat3( 0.00,  0.80,  0.60,
                    -0.80,  0.36, -0.48,
                    -0.60, -0.48,  0.64 );

float displacement( vec3 p )
{
//	p += vec3(1.0,0.0,0.8);

    float f;
    f  = 0.5000*noise( p ); p = m*p*2.02;
    f += 0.2500*noise( p ); p = m*p*2.03;
    f += 0.1250*noise( p ); p = m*p*2.01;
    f += 0.0625*noise( p ); 

	// This just adds more high frequency noise to the surface. 
	float n = noise( p*5.5 );
    f += 0.03*n*n;

    return f;
}

const float satradius = 5.0;
const float satRotSpeed = 3.14*0.1;

vec3 yRot( vec3 p, float rads ) {
    return vec3(
        p.x*cos(rads) - p.z*sin(rads),
        p.y,
        p.x*sin(rads) + p.z*cos(rads) );
}

const float SATBUMP = 1.;

float planetSDF( in vec3 p )
{
    p = yRot(p, iTime*satRotSpeed);
	const float bandFreq = 8.0;
	vec3 spotDir = normalize(vec3(0.0,1.0,0.0));
	float noisePart = SATBUMP * 0.005 * noise(5.0*p) * sin(3.0*bandFreq*dot(normalize(p),spotDir));
	return length(p) - satradius + noisePart;
}

float unionn( float a, float b ) { return min(a,b); }
float subtract( float a, float b ) { return max(-a, b); }
float intersect( float a, float b ) { return max(a, b); }

float sceneSDF( in vec3 pos )
{
    return planetSDF(pos);
}

float raymarchTerrain( in vec3 ro, in vec3 rd )
{
	float maxd = 30.0;
	float precis = 0.001;						
    float distToSurf = 1.0;
    float t = 0.1;
    for( int i=0; i<160; i++ )
    {
        if( abs(distToSurf)<precis||t>maxd ) continue;//break;
	    distToSurf = sceneSDF( ro+rd*t );
        t += distToSurf;
    }

    if( t>maxd ) t=-1.0;
    return t;
}

vec3 calcNormal( in vec3 pos )
{
    vec3 eps = vec3(0.02,0.0,0.0);
	return normalize( vec3(
           sceneSDF(pos+eps.xyy) - sceneSDF(pos-eps.xyy),
           sceneSDF(pos+eps.yxy) - sceneSDF(pos-eps.yxy),
           sceneSDF(pos+eps.yyx) - sceneSDF(pos-eps.yyx) ) );

}

float softshadow( in vec3 ro, in vec3 rd, float mint, float k )
{
    float res = 1.0;
    float t = mint;
    for( int i=0; i<48; i++ )
    {
        float h = sceneSDF(ro + rd*t);
		h = max( h, 0.0 );
        res = min( res, k*h/t );
        t += clamp( h, 0.02, 0.5 );
    }
    return clamp(res,0.0,1.0);
}

vec3 path( float time )
{
    //time *= 0.;
	return vec3( 16.0*cos(0.2+0.5*.1*time*1.5), 1.5, 16.0*sin(0.1+0.5*0.11*time*1.5) );

}

vec3 blendrings( vec3 col, float t, vec3 ro, vec3 rd )
{
    // TODO: lighting/shadows?

    const float ringrad = satradius*2.1;

    float tring = -ro.y/rd.y;
   	vec3 pring = ro + tring*rd;
    pring = yRot(pring, iTime*PI/20.);
    float r = length(pring.xz);

    if( (tring < t || t <= 0.0) && r <= ringrad )
    {
        float noise = snoise1d(r*0.4 + 573.62);

        float normnoise = noise * 0.5 + 0.5;
        float edgesoft = 0.1;
        float denstart = 0.4;
        float density = smoothstep(denstart, denstart+edgesoft, normnoise);

        // more high freq radial noise on top
        density += snoise1d(r*0.8)*0.3;

        density = clamp(density, 0., 1.);

        // soften outer edge
        density *= 1.0 - smoothstep(ringrad*0.98, ringrad, r);

        // soften inner edge, against planet
        density *= smoothstep(satradius, satradius*1.05, r);

        vec3 ringcol = mix( vec3(1.0, 0.3, 0.0), vec3(1.0, 0.2, 0.0), snoise1d(r)*0.5+0.5 );
        ringcol = mix(vec3(0.0,0.0,0.0), ringcol, pow(r/ringrad,2.0));
        ringcol *= mix(0.95, 1.0, snoise2d(pring.xz*5.));

        // darken towards the middle
        ringcol *= smoothstep(ringrad-satradius*1.2, ringrad, r);
        ringcol *= 0.9;


        //col += density*ringcol;
        col = mix( col, ringcol, density);
    }
 	return col;
}

vec2 curl2d(in vec2 p)
{
    const float E = 0.9;
    float x = snoise2d(p);
    float xey = snoise2d(p+vec2(0., E));
    float xex = snoise2d(p+vec2(E, 0.));

    return vec2( xex-x, -(xey-x) ) / E;
}

vec3 saturn_gradient(in vec3 pos, in vec3 rd)
{
    // shades of orane
    vec3 albedo = mix( vec3(0.8,0.6,0.0), vec3(0.7,0.7,1.0), snoise2d( vec2(0.0, pos.y*1.2) ) );

    // some blue as we go lower
    albedo += vec3(0,0.5,0.8) * smoothstep(0., -2., pos.y);

    return albedo;
}

vec3 shade_saturn( in vec3 pos, in vec3 rd )
{
    float t = iTime;
    vec3 p = pos;

    // rotate the flow map a bit
    p = yRot(p, iTime * PI/20.);

    // to spherical
    float r = length(p);
    float phi = atan(p.x, p.z);
    float theta = acos(p.y/r);

    // to UV
    vec2 uv = vec2( phi/PI - 0.5, -(theta/PI - 0.5) );

    // backwards curl advection
    for( int i = 0; i < 20; i++ ) {
        vec2 duv = curl2d(uv*20.)*.0004*.5;
        uv += duv;
    }

    // back to spherical
    phi = (uv.x+0.5) * PI;
    theta = (-uv.y+0.5) * PI;

    // add some ripple in opposite direction so it can flow through the warp
    theta += sin(TPI*phi/(0.1*PI) + t*(1.3*PI)) * PI*0.005;

    // back to cartesian
    p = vec3(
        r * sin(theta) * cos(phi),
        r * cos(theta),
        r * sin(theta) * sin(phi));

    vec3 alb = saturn_gradient( p, rd );

    // make the center glow a bit
	vec3 nor = pos/length(pos);    
    alb += vec3(1,0.8,0.5) * smoothstep(1.5, 0.0, abs(pos.y)) * pow(dot(nor,-rd),1.0);

    return alb;

}

vec2 hash( vec2 p )
{
	return texture( iChannel0, (p+0.5)/200.0, -100.0 ).xy;
}

vec3 voronoi( in vec2 x, out vec2 cpId )
{
    vec2 n = floor(x);
    vec2 f = fract(x);

    //----------------------------------
    // first pass: regular voronoi
    //----------------------------------
	vec2 mg, mr;

    float md = 8.0;
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec2 g = vec2(float(i),float(j));
		vec2 o = hash( n + g );
		#ifdef ANIMATE
        o = animbias + animscale*sin( iTime*0.5 + 6.2831*o );
        #endif	
        vec2 r = g + o - f;
        float d = dot(r,r);

        if( d<md )
        {
            md = d;
            mr = r;
            mg = g;
        }
    }

    //----------------------------------
    // second pass: distance to borders
    //----------------------------------
    md = 8.0;
    for( int j=-2; j<=2; j++ )
    for( int i=-2; i<=2; i++ )
    {
        vec2 g = mg + vec2(float(i),float(j));
		vec2 o = hash( n + g );
		#ifdef ANIMATE
        o = animbias + animscale*sin( iTime*0.5 + 6.2831*o );
        #endif	
        vec2 r = g + o - f;


        if( dot(mr-r,mr-r)>0.000001 )
		{
        // distance to line		
        float d = dot( 0.5*(mr+r), normalize(r-mr) );

        md = min( md, d );
		}
    }

	cpId = n+mg;

    return vec3( md, mr );
}

vec4 stars(vec3 rd)
{
    float t = iTime;

    float r = length(rd);
    float phi = atan(rd.x, rd.z);
    float theta = acos(rd.y/r);
    vec2 polar = vec2(phi, theta);

    vec2 cpid;
    const float zoom = 0.18;
    vec3 c = voronoi( polar/zoom, cpid );
	float centerDist = length( c.yz );
	float borderDist = c.x;

    const float minsize = 0.005;
    float size = mix(minsize, 0.05, snoise2d(cpid*20.0)*0.5+0.5);
    float sharp = mix(0.3, 0.9, snoise2d(cpid*10.0)*0.5+0.5);

    float flickerFreq = mix(1., 2., snoise2d(cpid*15.0)*0.5+0.5);
    const float flickerAmt = 0.2;
    size += minsize * snoise1d(t*flickerFreq);

    return vec4(1., 1., 0.8, smoothstep(0., 1., pow(centerDist/size, sharp)) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	#ifdef STEREO
	float eyeID = mod(fragCoord.x + mod(fragCoord.y,2.0),2.0);
    #endif

    vec2 q = fragCoord.xy / iResolution.xy;
	vec2 p = -1.0 + 2.0*q;
	p.x *= iResolution.x / iResolution.y;

    // camera	
	float time = 2.7+iTime;

    // TEMP freeze camera
    time *= 0.1;

	vec3 ro = path( time+0.0 );
	vec3 camTarget = path( time+1.6 );
	//camTarget.y *= 0.3 + 0.25*cos(0.11*time);
	camTarget.y *= 0.35 + 0.25*sin(0.09*time);
	float roll = 0.3*sin(1.0+0.001*time);

	// TEMP
	roll = -200.0;
	camTarget = vec3(0,0,0);

	// stop going into the planet
	ro = normalize(ro)*max( 10.0, length(ro));

	// camera tx
	vec3 cw = normalize(camTarget-ro);
	vec3 cp = vec3(sin(roll), cos(roll),0.0);
	vec3 cu = normalize(cross(cw,cp));
	vec3 cv = normalize(cross(cu,cw));

	float r2 = p.x*p.x*0.32 + p.y*p.y;
    p *= (7.0-sqrt(37.5-11.5*r2))/(r2+1.0);

	vec3 rd = normalize( p.x*cu + p.y*cv + 2.1*cw );

	#ifdef STEREO
	vec3 fo = ro + rd*7.0; // put focus plane behind Mike
	ro -= 0.2*cu*eyeID;    // eye separation
	rd = normalize(fo-ro);
    #endif

    // sky	 
	vec3 col = vec3(0.1,0.01,0.01) - rd.y*0.1;
    col *= 0.9;

	vec3 bcol = col;

    // terrain	
	float t = raymarchTerrain(ro, rd)+0.0;

    if( t>0.0 )
	{
        // we've hit saturn

		vec3 pos = ro + t*rd;
		vec3 nor = calcNormal( pos );
		vec3 ref = reflect( rd, nor );	// not used?

		// Perturb the normal by sampling 3D noise
		// This is largely responsible for the rough, crumbly look of the volcanic rock. Very cool!
		vec3 bn = -1.0 + 2.0*texcube( iChannel0, 3.0*pos/4.0, nor ).xyz;
		nor = normalize( nor + 0.2*bn );

		float hh = 1.0 - smoothstep( -2.0, 1.0, pos.y );

        // lighting
		float dif = sqrt(clamp( dot( nor, lig ), 0.0, 1.0 ));
		float sha = 0.0;
		if( dif>0.01)
			sha=softshadow(pos,lig,0.01,32.0);
		float bac = clamp( dot( nor, normalize(lig*vec3(-1.0,0.0,-1.0)) ), 0.0, 1.0 );
		float sky = 0.5 + 0.5*nor.y;
        //float lav = smoothstep( 0.5, 0.55, lava(0.1*pos.xz) )*hh*clamp(0.5-0.5*nor.y,0.0,1.0);
		float occ = pow( (1.0-displacement(pos*vec3(0.8,1.0,0.8)))*1.6-0.5, 2.0 );

		float amb = 2.5;

		col = vec3(0.8);

		float cd = length(pos);

        // shade Saturn
        vec3 albedo = shade_saturn(pos, rd);
        vec3 brdf =	dif * albedo;
        brdf += 0.1 * amb * albedo;

        // anti-rim lighting
        brdf -= pow(1.0-clamp(dot(nor,-rd),0.,1.),1.9);

        // light/surface interaction		
        col = brdf * col;

		// atmospheric
		col = mix( col, (1.0-0.7*hh)*bcol, 1.0-exp(-0.00006*t*t*t) );
	}
    else {
        // hit space - do stars!
        vec4 colstars = stars(rd);
        col = mix(colstars.rgb, col, colstars.a);
    }

    // rings - ray trace directly on to the y=0 plane
    // TODO: bind lights here
    col = blendrings(col, t, ro, rd);

    // film grain over everything
    col *= mix(0.95, 1.0, snoise2d(p*50.0));

    if(false && fragCoord.x < 200.) {
        vec2 c = curl2d(fragCoord.xy*.025)*0.5+0.5;
        fragColor = vec4( c, 0., 1.);
    }
    else {
		fragColor = vec4( col, 1.0 );
    }
}

#define CUTOFF 0.23

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy/iResolution.xy;
	uv.x *= 1000.0;
	uv.y *= 550.0;

	// blob setup	

	vec2 blob1pos = vec2(250.0, 180.0);
	float blob1size = 350.0;
	vec2 blob2pos = vec2(450.0, 200.0);
	float blob2size = 300.0;
	vec2 blob3pos = vec2(600.0, 260.0);
	float blob3size = 400.0;

	// movement

	blob1pos.x += sin(iTime*3.0) * 70.0;
	blob1pos.y += sin(iTime*1.2) * 90.0;
	blob2pos.x += sin(iTime*2.0) * 150.0;
	blob2pos.y += sin(iTime*3.0) * 20.0;
	blob3pos.y += sin(iTime*1.4) * 100.0;
	blob3pos.x += sin(iTime*1.7) * 100.0;

	// calc

	float blob1dist = clamp(distance(blob1pos, uv),0.0, blob1size);
	float blob1influence = clamp( 1.0 - blob1dist/blob1size, 0.0, 1.0 );

	float blob2dist = clamp(distance(blob2pos, uv),0.0, blob2size);
	float blob2influence = clamp( 1.0 - blob2dist/blob2size, 0.0, 1.0 );

	float blob3dist = clamp(distance(blob3pos, uv),0.0, blob3size);
	float blob3influence = clamp( 1.0 - blob3dist/blob3size, 0.0, 1.0 );

	// finished

	float intensity = (pow(blob1influence,6.0) + pow(blob2influence,6.0) + pow(blob3influence,6.0));
	intensity = (clamp(intensity,CUTOFF, CUTOFF + 0.0125)-CUTOFF) * 80.0;

	fragColor = vec4(intensity) - vec4(blob1influence,blob2influence,blob3influence,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 sampled = texture(iChannel0,uv);
	vec4 fallback = 0.2 + texture(iChannel0,vec2(0.5, 1.0-uv.y)) * 0.3;

	// greenscreen remover
	float dif = (sampled.r + sampled.b + 0.04) - sampled.g;
	dif = clamp(dif+0.05, 0.0, 0.33) * 3.0;
	sampled = mix(fallback, sampled, dif);

	//equalize
	float sum = sampled.r + sampled.g + sampled.b;
	sampled *= sum;

	//colorize
	vec4 color = vec4(uv*(0.5+0.5*sin(iTime*2.0)),0.5+0.5*sin(iTime*3.0),1.0) * 0.3;

	fragColor = sampled + color;
}

#define NUM_ITER 15
#define PI 3.14159265359

float f(vec2 pixel, int i, float mod)
{
	float time = iTime + 0.3 + mod;
	float v = cos(iTime + pixel.y + cos(2.0 * iTime + PI * pixel.x));
	return v;
}

float g(vec2 pixel, int i, float mod)
{
	float time2 = iTime +  2.0 * 0.3 + mod;
	float v = cos(time2 + pixel.x + cos( 3.0 * (time2 + 0.3) + PI * pixel.y));
	return v;
}

float integrateX(vec2 pixel)
{
	float r = 0.0;
	float dt = 0.3;

	const int iter = NUM_ITER;

	for(int i = 0; i < iter; ++i)
	{
		r = r + dt * f(pixel, i, 0.3);
	}

	return r;
}

float integrateY(vec2 pixel)
{
	float r = 0.0;
	float dt = 0.3;

	const int iter = NUM_ITER;

	for(int i = 0; i < iter; ++i)
	{
		r = r + dt * g(pixel, i, 0.3);
	}

	return r;
}

vec2 density(vec2 pixel)
{
	float x = integrateX(pixel);
	float y = integrateY(pixel);
	return vec2(x, y);
}


vec2 random2f( vec2 seed )
{
	float rnd1 = mod(fract(sin(dot(seed, vec2(14.9898,78.233))) * 43758.5453), 1.0);
	float rnd2 = mod(fract(sin(dot(seed+vec2(rnd1), vec2(14.9898,78.233))) * 43758.5453), 1.0);
	return vec2(rnd1, rnd2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 rand = random2f(vec2(25.0, 10.0));

	vec2 uv = (fragCoord.xy + rand) / iResolution.xy;

	vec2 d = density(uv);

	vec4 color = vec4( (1.0 + sin(d.x * d.y)) * 0.5,
					   (1.0 + cos(d.x + d.y)) * 0.5,
					   (1.0 + sin(d.x + d.y)) * 0.5, 1.0);

	fragColor = color;
}

/*--------------------------------------------------------------
 * Shader Tiles: Tesselation of the coordinate space into 
 * regular polygons. 
 *
 * License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
 *
 * https://www.shadertoy.com/user/TheVaporTrail
 * Contact: david (at) TheVaporTrail (dot) com
 * Projects: http://www.TheVaporTrail.com
 *
 * Supported tesselations: Square, Triangle, Hexagon, Octagon
 *
 * These routines work with the idea of a "tile", which describes
 * a regular polygon. A tile size is given as the radius of a 
 * circle that encloses the polygon. The tile definition has the
 * radius, center, and number of sides. It also has a rotation,
 * direction, inner radius, "horizontal angle". (The horizontal
 * angle is the angle between the first vertex and horizontal.)
 *
 * Different tesselation "styles" are supported. A style describes
 * the orientations and directions of tiles:
 *   STYLE_MATCHING: All tiles have the same direction and same 
 *     starting angle (except alternating triangles are rotated 180)
 *   STYLE_SEAMLESS: Triangles and squares are arranged so that all
 *     vertices 0 are together, vertices 1 are together, etc. The 
 *     direction will therefore alternate in adjacent tiles.
 *
 * There are two sets of routines: locate routines and calc routines.
 * Locate routines find a tile given a coordinate and a tile size.
 * Calc routines calculate some piece of information given a tile
 * and a coordinate or other argument.
 *
 * Calc routines:
 *   Vertex 
 *   Angle (of point in tile relative to horizontal)
 *   Edge vector
 *   Relative Position
 *   Relative Position UV (0.0<=x<=1.0, 0.0<=y<=1.0)
 *   Contains Point
 *   Distance to Edge
 *   Distance to Closest Edge
 *   Distance to Vertex
 *   Distance to Closest Vertex
 *
 *
 *
 *--------------------------------------------------------------*/

#define DegreeToRad (3.14159/180.0)
#define Tau (3.14159 * 2.0)
#define cos30 (0.8660254) /* sqrt(3)/2 */

#define TILE_TRI 0
#define TILE_SQU 1
#define TILE_HEX 2
#define TILE_OCT 3

#define STYLE_MATCHING 0
#define STYLE_SEAMLESS 1

#define TILE_MAX_SIDES 8

/*--------------------------------------------------------------
 *
 *--------------------------------------------------------------*/
struct Tile_t
{
	vec2  center;     // Center of the tile
	int   sides;      // Number of sides
	float len;        // Length of the side
	float radius;     // Radius to the vertex
	float inner;      // Radius to closest point on the edge
	float angle;      // Angle to first vertex
	float horzangle;  // Angle between canonical first vertex and horizontal 
	int   direction;  // Rotation direction (+1 or -1)
};

/*--------------------------------------------------------------
 * Locate (Equilateral) Triangle Tile
 *
 * Canonical tile: vertex zero at 90 degrees, alternating tiles rotated 180 degrees
 *
 * Styles:
 *   MATCHING
 *   SEAMLESS
 *--------------------------------------------------------------*/
Tile_t tile_LocateTriangle(vec2 aCoord, int tileStyle, float radius)
{
	Tile_t t;

	float sideLen = radius * 2.0 * cos30;

	// Compute the size of a box to contain an equilateral triangle with a side of length=sideLen
	vec2 boxSize = vec2(sideLen, sideLen * cos30);

	// Determine if this is even or odd row. First convert the vertical location to a row number
	// Determine if it is an odd or even row
	// For odd rows, invert the triangle
	// -- This inverts the results when y<0 -- int row = int(aCoord.y/boxSize.y);
	// -- This inverts the results when y<0 -- bool evenRow = ((row - 2*(row/2)) == 0);
	float row = floor(aCoord.y/boxSize.y);
	bool evenRow = ((row - 2.0*floor(row/2.0)) < 0.01);

	// Compute the center of the triangle relative to the bottom-left corner of the box
	// Note that triangles are inverted for odd rows, so the center is shifted
	vec2 ctrAdjA = vec2(boxSize.x * 0.5, boxSize.y * (evenRow?1.0:2.0)/3.0);
	vec2 coordA  = aCoord.xy;
	// Find the box containing the coord, then compute the triangle center
	vec2 boxA    = floor(coordA/boxSize);
	vec2 ctrA    = boxA * boxSize + ctrAdjA;
	// Triangles are inverted on odd rows
	float angleA = evenRow ? 90.0 : 270.0;
	int   dirA   = 1;

    if (tileStyle == STYLE_SEAMLESS)
    {
		int idx = int(boxA.x - 3.0 * floor(boxA.x/3.0));
		dirA = evenRow ? 1 : -1;
		angleA = 330.0 - float(idx) * 120.0;
		if (!evenRow)
			angleA = -angleA;
    }

    // Same as above, but we shift sideways by half a box
    // and invert all of the triangles
	vec2 shiftB  = vec2(boxSize.x * 0.5, 0.0);
	vec2 ctrAdjB = vec2(boxSize.x * 0.5, boxSize.y * (evenRow?2.0:1.0)/3.0);
	vec2 coordB  = aCoord.xy + shiftB;
	vec2 boxB    = floor((coordB)/boxSize);
	vec2 ctrB    = boxB * boxSize - shiftB + ctrAdjB;
	float angleB = evenRow ? 270.0 : 90.0;
	int   dirB   = 1;

    if (tileStyle == STYLE_SEAMLESS)
    {
		int idx = int(boxB.x - 3.0 * floor((boxB.x)/3.0));
		dirB = evenRow ? -1 : 1;
		angleB = 150.0 + float(idx) * 120.0;
		if (!evenRow)
			angleB = -angleB;
    }

	bool chooseA = (distance(aCoord, ctrA) < distance(aCoord, ctrB));
	vec2 ctr     = (chooseA) ? ctrA : ctrB;
	float angle  = (chooseA) ? angleA : angleB;
    int   dir    = (chooseA) ? dirA : dirB;

	t.center    = ctr;
	t.len       = sideLen;
	t.sides     = 3;
	t.radius    = radius;
	t.inner     = sideLen/4.0;
	t.angle     = angle;
	t.horzangle = -90.0;
	t.direction = dir;


	return t;
}

/*--------------------------------------------------------------
 *
 *--------------------------------------------------------------*/
Tile_t tile_LocateHexagon(vec2 aCoord, int tileStyle, float radius)
{
	Tile_t t;

	float sideLen = radius;

	// Compute the size of a box to contain hexagon with a side of length=sideLen
	vec2 boxSize = vec2(sideLen * 3.0, sideLen*cos30*2.0);
	float hexWidth = sideLen * 2.0;

	// Convert the vertical location to a row number
	float row = floor(aCoord.y/boxSize.y);

	// Determine if it is an odd or even row
	// For odd rows, invert the triangle
	bool evenRow = ((row - 2.0*floor(row/2.0)) < 0.01);

	vec2 ctrAdj = vec2(sideLen, sideLen * cos30);

	vec2 shiftA  = vec2(0.0);
	float angleA = 0.0;
	int   dirA   = evenRow ? 1 : -1;
	vec2 coordA  = aCoord.xy + shiftA;
	vec2 boxA    = floor(coordA/boxSize);
	vec2 ctrA    = boxA * boxSize - shiftA + ctrAdj;
	bool hasA    = (coordA.x - boxA.x * boxSize.x < hexWidth);

	vec2 shiftB  = vec2(sideLen + sideLen * 0.5, sideLen * cos30);
	float angleB = 0.0;
	int   dirB   = evenRow ? 1 : -1;
	vec2 coordB  = aCoord.xy + shiftB;
	vec2 boxB    = floor((coordB)/boxSize);
	vec2 ctrB    = boxB * boxSize - shiftB + ctrAdj;
	bool hasB    = (coordB.x - boxB.x * boxSize.x < hexWidth);

	bool chooseA = (!hasB || (hasA && distance(aCoord, ctrA) < distance(aCoord, ctrB)));
	vec2 ctr     = (chooseA) ? ctrA : ctrB;
	float angle  = (chooseA) ? angleA : angleB;
    int   dir    = (chooseA) ? dirA : dirB;

	t.center    = ctr;
	t.len       = sideLen;
	t.sides     = 6;
	t.radius    = radius;
	t.inner     = sideLen/cos30;
	t.angle     = angle;
	t.horzangle = -30.0;
	t.direction = 1;

	return t;
}

/*--------------------------------------------------------------
 *
 *--------------------------------------------------------------*/
Tile_t tile_LocateSquare(vec2 aCoord, int tileStyle, float radius)
{
	Tile_t t;

	float sideLen = radius*sqrt(2.0);

	// Compute the size of a box 
	vec2 boxSize = vec2(sideLen, sideLen);

	// Convert the vertical location to a row number
	float row = floor(aCoord.y/boxSize.y);

	// Determine if it is an odd or even row
	bool evenRow = ((row - 2.0*floor(row/2.0)) < 0.01);


	float angle   = 45.0;
	vec2  coord   = aCoord.xy;
	vec2  box     = floor(coord/boxSize);
	vec2  ctr     = box * boxSize + boxSize/2.0;

	t.center    = ctr;
	t.len       = sideLen;
	t.sides     = 4;
	t.radius    = radius;
	t.inner     = sideLen/2.0;
	t.angle     = angle;
	t.horzangle = -45.0;
	t.direction = 1;

	return t;
}

/*--------------------------------------------------------------
 *
 *--------------------------------------------------------------*/
Tile_t tile_LocateOctagon(vec2 aCoord, int tileStyle, float radius)
{
	Tile_t t;

	float sideLen = radius * 2.0 * sin(Tau/16.0);
	float inner   = radius * cos(Tau/16.0);

	// Compute the size of a box 
	vec2 octBoxSize = vec2(inner * 2.0, inner * 2.0);

	float angle   = 360.0/16.0;
	vec2  coord   = aCoord.xy;
	vec2  box     = floor(coord/octBoxSize);
	vec2  ctrOct  = box * octBoxSize + octBoxSize/2.0;

	// Compute the size of the grid to locate the squares
	// The squares are rotated 45 degrees, so the "width" is measured across the diagonal..
	float squRadius   = sideLen / sqrt(2.0);
	vec2 squBoxSize   = vec2(squRadius * 2.0 + sideLen);
	vec2 shiftB  = vec2(sideLen + squRadius, sideLen + squRadius);
	vec2 coordB  = aCoord.xy - shiftB;
	vec2 boxB    = floor((coordB)/squBoxSize);
	vec2 ctrSqu  = boxB * squBoxSize + shiftB + vec2(squRadius);
	// We can use the fact that the sides of the square are diagonal lines with 
	// slope=+1 or slope=-1 to determine if a point is in the square.
	vec2 relPt   = coordB - boxB * squBoxSize - vec2(squRadius);
	bool inSqu   = ((abs(relPt.x) + abs(relPt.y)) < squRadius);
	//bool hasSqu  = ((coordB.x - boxB.x * squBoxSize.x < squRadius * 2.0) && (coordB.y - boxB.y * squBoxSize.y < squRadius * 2.0));

	if (inSqu)
	{
		t.center    = ctrSqu;
		t.len       = sideLen;
		t.sides     = 4;
		t.radius    = squRadius;
		t.inner     = squRadius/sqrt(2.0);
		t.angle     = 0.0;
		t.horzangle = 0.0;
		t.direction = 1;
	}
	else
	{
		t.center    = ctrOct;
		t.len       = sideLen;
		t.sides     = 8;
		t.radius    = radius;
		t.inner     = inner;
		t.angle     = angle;
		t.horzangle = -360.0/16.0;
		t.direction = 1;
	}

	return t;
}

/*--------------------------------------------------------------
 * Locate dispatch routine
 *--------------------------------------------------------------*/
Tile_t tile_Locate(vec2 aCoord, int tileType, int tileStyle, float radius)
{
	Tile_t t;

	if (tileType == TILE_TRI)
		t = tile_LocateTriangle(aCoord, tileStyle, radius);
	else if (tileType == TILE_SQU)
		t = tile_LocateSquare(aCoord, tileStyle, radius);
	else if (tileType == TILE_HEX)
		t = tile_LocateHexagon(aCoord, tileStyle, radius);
	else if (tileType == TILE_OCT)
		t = tile_LocateOctagon(aCoord, tileStyle, radius);

	return t;
}


/*--------------------------------------------------------------
 *
 *--------------------------------------------------------------*/
vec2 tile_CalcVertex(Tile_t tile, int vertexIdx)
{
	vec2 pt;
	float angle;

	angle = DegreeToRad * (tile.angle + float(vertexIdx) * float(tile.direction) * 360.0 / float(tile.sides));

	pt.x = tile.center.x + tile.radius * cos(angle);
	pt.y = tile.center.y + tile.radius * sin(angle);

	return pt;
}

/*--------------------------------------------------------------
 * Calculate the angle between two vectors, one from the center
 * to the coord and the other between the center and vertex zero
 *--------------------------------------------------------------*/
float tile_CalcAngle(Tile_t tile, vec2 coord)
{
	float angle = 0.0;

	angle = atan(coord.y - tile.center.y, coord.x - tile.center.x) / DegreeToRad;

	angle += tile.angle;

	angle *= float(tile.direction);

	return angle;
}

/*--------------------------------------------------------------
 *
 *--------------------------------------------------------------*/
vec2 tile_CalcEdgeVector(Tile_t tile, int vertexIdx)
{
	vec2 vect;

	return vect;
}


/*--------------------------------------------------------------
 * Calculate the position of a coordinate relative to vertex 0,
 * taking into consideration the direction of the tile.
 * The result is -radius<=x<=radius, -radius<=y<=radius
 *--------------------------------------------------------------*/
vec2 tile_CalcRelPosition(Tile_t tile, vec2 coord, float twist)
{
	vec2 relPos;

	float angle = -(tile.angle + tile.horzangle + twist * float(tile.direction)) *  DegreeToRad;

	float cA = cos(angle);
	float sA = sin(angle);

	mat2 rm = mat2(cA, sA, -sA, cA);

	relPos = coord - tile.center;

	relPos = rm * relPos;

	if (tile.direction == -1)
		relPos.x = -relPos.x;
	return relPos;
}


/*--------------------------------------------------------------
 * Calculate the relative position, but return values appropriate
 * for a texture lookup.
 *--------------------------------------------------------------*/
vec2 tile_CalcRelPositionUV(Tile_t tile, vec2 coord, float twist)
{
	vec2 relPos = tile_CalcRelPosition(tile, coord, twist);

	vec2 uv = (relPos + vec2(tile.radius, tile.radius))/(tile.radius * 2.0);

	return uv;
}


