void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	Tile_t tile;
	vec2 coord = fragCoord.xy;
	float d;
	float radius = 60.0;

	vec4 clr;


	float triRadius = radius + 10.0 * sin(40.0 * iTime * DegreeToRad);
	float angle = 6.0*iTime * DegreeToRad;
	float cA = cos(angle);
	float sA = sin(angle);
	mat2 rm = mat2(cA, sA, -sA, cA);
	vec2 coord2 = rm * (coord - iResolution.xy/2.0) + iResolution.xy/2.0;

	tile = tile_Locate(coord2, TILE_TRI, 0, triRadius);	
	d = tile_CalcDistanceToClosestEdge(tile, coord2);	
	float cTri = (d < 5.0) ? (5.0-d)/5.0 : 0.0;

	float octRadius = radius - 15.0 * sin(30.0 * iTime * DegreeToRad);
	angle = -4.0*iTime * DegreeToRad;
	cA = cos(angle);
	sA = sin(angle);
	rm = mat2(cA, sA, -sA, cA);
	vec2 coord3 = rm * (coord - iResolution.xy/2.0) + iResolution.xy/2.0;
	tile = tile_Locate(coord3, TILE_OCT, 0, octRadius);	
	d = tile_CalcDistanceToClosestEdge(tile, coord3);	
	float cOct = (d < 5.0) ? (5.0-d)/5.0 : 0.0;

	vec4 clrTri;
	vec4 clrOct;

	if (triRadius < octRadius)
	{
		clrTri =  vec4(cTri, 0., 0.0, cTri);
		clrOct =  vec4(0., 1.0, 0.0, cOct);
		fragColor = mix(clrTri, clrOct, clrOct.a);
	}
	else
	{
		clrTri =  vec4(1.0, 0., 0.0, cTri);
		clrOct =  vec4(0., cOct, 0.0, cOct);
		fragColor = mix(clrOct, clrTri, clrTri.a);
	}

}

//simple shape struct that supports creation of either planes or spheres
//hence the variable names - pos for spheres, normals for planes etc
struct Shape{
	vec3  posOrNormal;
	vec4  col;
	float radiusOrDistance;
	bool  isPlane;
};

int numShapes = 6;

//rotation matrices
mat3 RotationMatrixY = mat3( cos( iTime ),  0.0,   sin( iTime ), 
			    				0.0,              0.0,   0.0,
			             	-sin( iTime ),  0.0,   cos(iTime) );

mat3 RotationMatrixX = mat3( 1.0,        0.0,            0.0,
			    			0.0,  cos( iTime ),    -sin(iTime), 
			             	0.0,  sin(iTime),      cos(iTime) );

//camera origin	
vec3 camOrigin = vec3(0.0,2.0,-3.0);
vec3 lookAt    = vec3(0.0,0.0,0.0);

//one light
vec3 lightPos[2];


const float fov = 30.0 * 3.1415926/180.0;

const float dist = 0.5/tan(fov * 0.5);

const float NO_INTERSECTION = 1000.0;

//get the texture co-ordinates for the sphere at the
//ray interesection point - because these aren't rasterised spheres 
//we are unable to make the spheres "rotate" around their own axis , we have to 
//rotate the vectors used for the sampling
vec2 GetUVForSphere(vec3 centerToIntersection)
{
	vec3 center = RotationMatrixX * centerToIntersection;
	vec3 north = RotationMatrixX * vec3(0.0,1.0,0.0); 
	vec3 equator = RotationMatrixX * vec3(1.0,0.0,0.0);
	float numerator = acos(dot(-vec3(0.0,1.0,0.0),center));
	float v = numerator/3.141259;
	float u;

	float possU = (acos(dot( equator,center))/sin(numerator))/(2.0*3.141259);

	if(dot(cross(vec3(0.0,1.0,0.0), equator),center) > 0.0)
	{
		u = possU;
	}
	else
	{
		u = 1.0 - possU;
	}

	return vec2(u,v);
}

float IntersectPlane(vec3 dir,vec3 origin,Shape inShape)
{
  float val1 = -(dot(origin,inShape.posOrNormal)+ inShape.radiusOrDistance);
  float val2 = dot(dir,inShape.posOrNormal);
  float tVal = val1/val2;

  return tVal > 0.0 ? tVal : NO_INTERSECTION;	
	//return NO_INTERSECTION;
}

float IntersectSphere(vec3 dir,vec3 origin,Shape inShape)
{
	//calc all the dot products necessary here
	float radiusSquared = inShape.radiusOrDistance * inShape.radiusOrDistance;
	float rayOrigDot    = dot(origin,origin);
	float rayOriDirDot  = dot(dir,origin);
	float rayDirDot     = dot(dir,dir);
	float spherePosDot = dot(inShape.posOrNormal,inShape.posOrNormal);

	//here are the variables for the quadratic equation
	//a is the dot prod between the ray direction and itself
	float b = 2.0 * (rayOriDirDot - dot(dir,inShape.posOrNormal));
	float c = rayOrigDot + spherePosDot - 2.0 * dot(origin,inShape.posOrNormal) - radiusSquared;


	//the value for a is raydirdot
	float discriminant = (b*b) - 4.0 * rayDirDot * c;

	if(discriminant < 0.0) return NO_INTERSECTION;

	discriminant = sqrt(discriminant);

	if(-b+discriminant < 0.0) return NO_INTERSECTION;

	float denom = 1.0/(2.0 * rayDirDot);
	float t1 = (-b + discriminant) * denom;
	float t2 = (-b - discriminant) * denom;

	return t1 < t2 ? t1 : t2; 
}


vec3 CreateRay()
{
	vec3 dir      = normalize(lookAt - camOrigin); 
	vec3 upVec    = normalize(cross(vec3(1.0,0.0,0.0),dir));	
	vec3 rightVec = normalize(cross(dir,upVec));
	vec3 factor1  = (0.5 - (gl_FragCoord.y/iResolution.y)) * upVec;
	vec3 factor2  = ((gl_FragCoord.x/iResolution.x)-0.5) * rightVec;
	dir = dir * dist + factor1 + factor2;  
	return normalize(dir);
}

//once we know the instersect/contact point for the ray lets find the colour
vec4 GenColor(vec3 contactPoint,Shape inShape[6],int index,vec3 dir,vec3 normal)
{
	vec4 col = vec4(0.0,0.0,0.0,1.0);

	for(int light = 0; light < 2; light++)
	{
		for(int s = 0; s < 6; s++)
		{
			//because of the way glsl is compiled we can't use
			//inshape[index] in the code, so we have to iterate through each shape and 
			//use a test
			if(s == index)
			{
				vec3 lightDir = normalize(lightPos[light]-contactPoint);
				vec3 viewVec  =  normalize(contactPoint - camOrigin);
				vec3 reflectVec = reflect(-lightDir,normal);
				vec4 diff = max(dot(normal,lightDir),0.0) * inShape[s].col;
				vec4 spec = pow(max(dot(viewVec,reflectVec),0.0),16.0) * vec4(1.0,1.0,1.0,1.0);

				if(!inShape[s].isPlane)
				{
					vec2 coords = GetUVForSphere(normalize(inShape[s].posOrNormal-contactPoint));
					diff *= texture(iChannel0,coords);
				}

				col += clamp(diff + spec,0.0,1.0);
			}
		}
	}

	return clamp(col,0.0,1.0);
}

//create the shapes for this frame, give new positions for the spheres
//using rotation matrices and the current value of the iTime
//to create an "orbit"
void CreateShapes(out Shape shapes[6])
{
	shapes[0].posOrNormal       = RotationMatrixY * vec3(0.0,0.0,0.0);
	shapes[0].radiusOrDistance  = 0.1;
	shapes[0].col               = vec4(1.0,0.0,0.0,1.0);
	shapes[0].isPlane           = false;

	shapes[1].posOrNormal       = RotationMatrixY * vec3(0.4,0.0,-0.2);
	shapes[1].radiusOrDistance  = 0.15;
	shapes[1].col               = vec4(0.0,1.0,0.0,1.0);
	shapes[1].isPlane           = false;

	shapes[2].posOrNormal       = RotationMatrixY * vec3(-0.2,0.0,0.2);
	shapes[2].radiusOrDistance  = 0.05;
	shapes[2].col               = vec4(0.5,0.0,1.0,1.0);
	shapes[2].isPlane           = false;

	shapes[3].posOrNormal       = RotationMatrixY * vec3(0.1,0.0,-0.5);
	shapes[3].radiusOrDistance  = 0.05;
	shapes[3].col               = vec4(0.6,0.2,0.0,1.0);
	shapes[3].isPlane           = false;

	shapes[4].posOrNormal       = RotationMatrixY * vec3(0.6,0.0,0.5);
	shapes[4].radiusOrDistance  = 0.1;
	shapes[4].col               = vec4(1.0,0.5,0.5,1.0);
	shapes[4].isPlane           = false;

	shapes[5].posOrNormal		= vec3(0.0,1.0,0.0);
	shapes[5].radiusOrDistance  = 0.5;
	shapes[5].col               = vec4(0.5,0.2,0.5,1.0);
	shapes[5].isPlane			= true;

	lightPos[0] = vec3(0.0,3.0,0.0);
	lightPos[1] = vec3(8.0,0.0,-5.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	Shape shapes[6];
	CreateShapes(shapes);

	vec3 ray = CreateRay();
	vec4 col = vec4(0.0,0.0,0.0,1.0);
	vec3 origin = camOrigin;
	vec3 contactPoint = vec3(0.0,0.0,0.0);
	vec3 normal = vec3(0.0,0.0,0.0);
	float t = NO_INTERSECTION;
	int index = -1;


	for(int depth = 0; depth < 2; depth++)
	{
		float refCoeff = 1.0;

		float newT;
		for(int s = 0; s < 6; s++)
		{


		  if(shapes[s].isPlane)
		  {
			  newT = IntersectPlane(ray,origin,shapes[s]);
		  }
		  else
		  {
			  newT = IntersectSphere(ray,origin,shapes[s]);
		  }

		  if(newT < t && newT > 0.0)
		  {
			  t = newT;
			  index = s;
			  contactPoint = origin + t * ray;

			  if(shapes[s].isPlane)
			  {  
			    normal = shapes[s].posOrNormal;
			  }
			  else
			  {

				normal   = normalize(contactPoint - shapes[s].posOrNormal);
			  }

		  }
		}

		if(index != -1)
		{
			col += refCoeff * GenColor(contactPoint,shapes,index,ray,normal);
			ray = normalize(reflect(ray,normal));
			origin = contactPoint + ray * 0.1;
			refCoeff * 0.5;
			t = NO_INTERSECTION;
		}
		else
		{
			break;
		}


	}

	fragColor = clamp(col,0.0,1.0);
}

// hash based 3d value noise
float hash( float n )
{
    return fract(sin(n)*43758.5453);
}
float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);
    float n = p.x + p.y*57.0 + 113.0*p.z;
    return mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                   mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y),
               mix(mix( hash(n+113.0), hash(n+114.0),f.x),
                   mix( hash(n+170.0), hash(n+171.0),f.x),f.y),f.z);
}


const float KAPPA = 0.1;  // extinction coefficient

vec3 sun_direction()
{
	float freq = 1.0;
	float phase = 2.0 + freq*iTime;
	return normalize(vec3(cos(phase), 0.1, sin(phase)));
}

// returns clouds extinction coeff. as a function of world position
float clouds_extinction( in vec3 p )
{	
	float spatial_period = 40.0;
	p /= spatial_period;

	float timefreq = 2.0;
	vec3 q = p - vec3(1.0,0.1,0.0)*timefreq*iTime;

	float cloud_height = 2.5;
	float m = sin(0.05*timefreq*iTime*2.0*3.14);
	float layer_y = -0.9*(1.0+0.25*m*m)*cloud_height;

	float N;
	float g = 1.0;
	float beta = 0.4;
    N  = g * noise( q ); q *= 2.0; g *= beta;
    N += g * noise( q ); q *= 3.0; g *= beta;
    N += g * noise( q ); q *= 4.0; g *= beta;
    N += g * noise( q );

	float s = 0.16; // edge smoothness (0=hard, 1=smooth)

	float cloudtop_y = layer_y + cloud_height*pow(abs(N), 0.7);
	return KAPPA * (smoothstep(p.y-s*cloud_height, p.y+s*cloud_height, cloudtop_y));
}


// return sun+sky radiance
vec3 sky_color( in vec3 rd )
{
	vec3 skyColor = vec3(0.6,0.71,0.78) - rd.y*0.25*vec3(1.0,0.5,1.0);
	float sp = dot(sun_direction(), rd) - cos(radians(5.0));
	vec3 sun = 2.0e6 * vec3(1.0,0.9,0.7) * pow(sp * step(0.0, sp), 2.3);
	skyColor += sun;
	return skyColor;
}


// return radiance reaching the point pos from the sun
vec3 reduced_sun( in vec3 pos )
{
	const int num_steps = 2;
	const float max_dist = 8.0;
	float dl = max_dist/float(num_steps);
	float Transmittance = 1.0;
	for(int i=0; i<num_steps; i++)
	{
		if(Transmittance < 0.001) break;
		pos += dl*sun_direction();
		float kappa = clouds_extinction( pos );
		Transmittance *= exp(-kappa*dl);
	}
	return Transmittance * sky_color(sun_direction());
}


// Henyey-Greenstein phase function
float phasefunction(in vec3 a, in vec3 b)
{
	float mu = dot(a, b);
	float g = 0.2;
	float gSqr = g*g;
	float oofp = 1.0/(4.0*3.141592);
	return oofp * (1.0 - gSqr) / pow(1.0 - 2.0*g*mu + gSqr, 1.5);	
}


// raymarch to complete volume rendering integral
vec3 primary( in vec3 ro, in vec3 rd )
{
	const float max_dist = 500.0;
	const int num_steps = 128;
	float dl = max_dist/float(num_steps);

	vec3 pos = ro;
	float Transmittance = 1.0;

	// Calculate volume rendering integral along primary ray
	vec3 InScattered = vec3(0.0);
	for(int i=0; i<num_steps; i++)
	{
		if(Transmittance < 0.01) break;
		pos += dl*rd;
		float kappa = clouds_extinction( pos );
		Transmittance *= exp(-kappa*dl);

		// single scattering given by in-scatter of sunlight
		const vec3 albedo = vec3(0.85, 0.82, 0.90);
		vec3 single_scatt = albedo * kappa * dl * reduced_sun(pos) * phasefunction(sun_direction(), rd);

		// Fake multiple scattering by a constant emission field (power is an ad-hoc aesthetic tune-
		// though the power 1.0 possibly has some physical basis, since the diffuse light can be expected
		// to be roughly proportional to the density of scatterers
		vec3 fake_multiple_scatt = 0.9*albedo * kappa * dl * pow(kappa/KAPPA, 0.3) * 2.5 * vec3(0.33, 0.35, 0.34);

		// Accumulate integrand
		InScattered += Transmittance * (single_scatt + fake_multiple_scatt);
	}

	vec3 AttenuatedBackground = Transmittance*sky_color(rd);
	return InScattered + AttenuatedBackground;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0*q;
    p.x *= iResolution.x/ iResolution.y;
    vec2 mo = -1.0 + 2.0*iMouse.xy / iResolution.xy;

    // camera
    vec3 ro = 4.0*normalize(vec3(cos(2.75-3.0*mo.x), 1.5+(2.0*mo.y+1.0), sin(2.75-3.0*mo.x)));
	vec3 ta = vec3(0.0, 1.0, 0.0);
    vec3 ww = normalize( ta - ro);
    vec3 uu = normalize(cross( vec3(0.0,1.0,0.0), ww ));
    vec3 vv = normalize(cross(ww,uu));
    vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );

	// raymarch to obtain transmittance along ray through clouds
    vec3 L = primary( ro, rd );

    fragColor = vec4( L, 1.0 );
}

