float smin( float a, float b, float k )
{
    a = pow( a, k ); b = pow( b, k );
    return pow( (a*b)/(a+b), 1.0/k );
}
float smin2( float a, float b, float k )
{
	return log( max( 0.001, exp( k*a ) ) + exp( k*b ) ) / k;
}
float cos3( vec3 a )
{
	return cos( a.x ) * cos( a.y ) * cos( a.z );
}

vec3 repeat( vec3 p, float s )
{
	return mod( p+0.5*s, s ) - 0.5*s;
}

vec3 repeatXZ( vec3 p, float s )
{
	float	h = 0.5*s;
	return vec3( mod( p.x+h, s ) - h, p.y, mod( p.z+h, s ) - h );
}

float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);

	vec2 uv = (p.xy+vec2(37.0,17.0)*p.z) + f.xy;
	vec2 rg = texture( iChannel1, (uv+0.5)/256.0, -100.0 ).yx;
	return mix( rg.x, rg.y, f.z );
}

float fbm( vec3 p )
{
	vec3	f = vec3( 0.5, 2.0, 0.4 );
	vec3	mulF = vec3( 6.0, 8.0, 6.0 );
	const float	mulA = 0.45;
	float	a = 1.0;
	float	v  = a * noise( f*p ); f *= mulF; a *= mulA;
			v += a * noise( f*p ); f *= mulF; a *= mulA;
			v += a * noise( f*p ); f *= mulF; a *= mulA;
			v += a * noise( f*p ); f *= mulF; a *= mulA;
	return v * (1.0 / (1.0+0.5+0.25+0.125));
}

float randCell( vec2 cell, vec2 bisou )
{
	return fract( 10.0 + sin( 32151.16 * (cell.x+bisou.x) + 0.12191 )
				 + sin( -2558.5189 * (cell.y + bisou.y) + 0.59168 ) );
}

float map( vec3 p )
{
	const float	RepeatRate = 8.0;
	vec2	cellXZ = floor( (p.xz - 0.5 * RepeatRate) / RepeatRate );
	float	radius = 1.5 + 0.5 * sin( cellXZ.x + 0.564 * iTime )*sin( 1.4651 * cellXZ.y + 1.2169216* iTime );

	float	cellRandom = randCell( cellXZ, vec2( 0, 0 ) );
	vec3	height = vec3( 0.0,
						  -4.0
						  * abs( sin( 37861.32 * cellXZ.x + 2.863 * iTime )
						  *sin( 031651.0 * cellXZ.y + 1.894 * iTime ) )
						  , 0.0 );
	float	vscale = mix( 0.125, 0.8, cellRandom );
	vec3	scale = vec3( 1, vscale, 1 );
	float 	dsphere = length( repeatXZ( scale *( p + height), RepeatRate ) ) - radius;
	float 	dplane = p.y + 2.0;

	return smin2( dsphere, dplane, -1.8 );//, cos(p.x)+cos(p.y)+cos(p.z));
}

vec3 normal( vec3 p )
{
	const vec2 e = vec2( 0.001, 0.0 );
	float c = map( p );
	return normalize( vec3(
		map( p + e.xyy ) - map( p - e.xyy ),
		map( p + e.yxy ) - map( p - e.yxy ),
		map( p + e.yyx ) - map( p - e.yyx )
		) );
}

float AO( vec3 p, vec3 n )
{
	const float step = 0.01;
	p += 0.1 * n;
	float AO = 1.0;
	for ( int i=0; i < 16; i++ )
	{
		float	d = max( 0.0, map( p ) );
		p += step * n;

		AO *= 1.0 - exp( -20.0 * d * (2.0+float(i)) );
	}
	return AO;
}

float	Shadow( vec3 p, vec3 l )
{
	const float step = 0.5;
	p += 0.5 * l;
	float S = 1.0;
	for ( int i=0; i < 16; i++ )
	{
		float	d = max( 0.0, map( p ) );
		p += step * l;

		S *= 1.0 - exp( -20.0 * d * (3.0+float(i)) );
	}
	return S;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2	uv = fragCoord.xy / iResolution.xy;
	vec3	p = vec3( 5.0 * sin( 1.0 * iTime )
					 , 6.0 + 4.0 * sin( 0.5 * iTime )
					 , 4.0 * iTime );
	vec3	target = p + vec3(
		sin( 1.2 * iTime ),
		sin( 1.0 + 0.7891 * iTime ),
		5.0 + 2.0 * sin( 1.2 * iTime )
		);

	vec3	at = normalize( target - p );
	vec3	right = normalize( cross( at, vec3( 0, 1, 0 ) ) );
	vec3	up = cross( right, at );

	float	Tan = 0.6;
	vec3	v = normalize( vec3( iResolution.x / iResolution.y * Tan * (2.0 * uv.x - 1.0), Tan * (2.0 * uv.y - 1.0), 1.0 ) );
			v = v.x * right + v.y * up + v.z * at;

	float	t = 0.0;
	for ( int i=0; i < 64; i++ )
	{
		float	d = map( p );
		t += d;
		p += d * v;
	}

	if ( t <= 0.0 )
		t = 1e4;


	vec3	color = vec3( 0.0 );

	vec3	n = normal( p );

	vec3	Light = normalize( vec3( 1 ) );

	float	strip = fract( 1.0 * p.y + p.z - 1.0 * p.x );
			strip = step( strip, 0.4 ); 

	vec3	surfColor = mix( vec3( 1 ), vec3( 1, 0.1, 0.1 ), strip );

	vec3	diff = Shadow( p, Light ) * clamp( 0.0, 1.0, 0.5 + 0.5 * dot( n, Light )) * vec3( 1 );
			diff += AO( p, n ) * mix( vec3( 0.5 ), 0.2 * vec3( 0.2, 0.8, 1.0 ), (0.5 + 0.5 * n.y) );

	color = surfColor * diff;

	float fogExt = 0.025 * mix( 1.0, 1.5, 0.5 * (1.0 + noise( 0.625*vec3(1.5, 1.0, 1.5) * p + 1.0 * iTime * vec3( 0, 0, 2 ) )) );
	float fog = exp( -fogExt * t );
	color = mix( vec3( 1.0, 0.8, 0.4 ), color, fog );

	fragColor = vec4( color, 1.0 );
}

//xyz is position, w is radius
const vec4 theSphere = vec4(0.0,0.0,0.0,2.0);

//variables for raytrace output
vec3 hitPos;
vec3 hitNormal;
float hitT;
float light;
vec3 frustum;


//ray vs. sphere collision test
//(taken directly from Inigo Quilez's website)
bool intSphere (in vec4 sphere,in vec3 rayOrigin,in vec3 rayDir) {
	vec3 diff = rayOrigin-sphere.xyz;
	float b = dot(rayDir,diff);
	float c = dot(diff,diff) - sphere.w*sphere.w;
	float t = b*b - c;
	if (t>0.0) {
		t = -b - sqrt(t);
		if (t>0.0) {
			hitPos = rayOrigin + rayDir*t;
			hitNormal = normalize(hitPos - sphere.xyz);
			hitT=t;
			return(true);
		}
	}

	return(false);
}

bool intGround (in float height,in vec3 rayOrigin,in vec3 rayDir) {
	float diff = -height-rayOrigin.y;
	if (rayDir.y==0.0) {
		return(false);
	}
	float t = diff / dot(rayDir,vec3(0.0,-1.0,0.0));
	if (t>0.0) {
		hitPos=rayOrigin+rayDir*t;
		hitNormal=vec3(0.0,1.0,0.0);
		hitT=t;
		return(true);
	}
	return(false);
}

//Turbo simple lighting calculation
//nDotL and distance falloff
float pointLight(vec3 samplePos,vec4 light,vec3 normal) {
	vec3 l = light.xyz - samplePos;
	if (dot(l,l)>light.w*light.w) {
		return(0.0);
	}
	float nDotL = dot(normal,normalize(l));
	return(nDotL*(1.0-length(l)/light.w));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//where's the camera?
	vec3 camPos = vec3(sin(iTime*1.0),cos(iTime*.387),-8.0);

	//camera projection settings
	//points along the +X/+Y corner of the view frustum
	frustum = vec3(1.0,iResolution.y/iResolution.x,1.0);


	//where's the pixel?
	vec2 uv = fragCoord.xy / iResolution.xy;

	//what's the view ray of the current pixel?
	vec3 t2=vec3(uv,1.0);
	t2=t2 * 2.0 - 1.0;
	vec3 viewDir = normalize(frustum * t2);

	//where's the light?
	//xyz is position, w is size
	vec4 theLight = vec4(5.0+cos(iTime*1.23),
						 6.0,
						 sin(iTime*1.23),
						 25.0+cos(iTime*.037));

	//is the view ray hitting the sphere or ground?
	bool hit=false;
	vec3 finalPos;
	vec3 finalNormal;
	float finalT;
	//collide against sphere and ground plane
	if (intSphere(theSphere,camPos,viewDir)) {
		hit=true;

		finalT=hitT;
		finalPos=hitPos;
		finalNormal=hitNormal;
	}
	if (intGround(-2.5,camPos,viewDir)) {

		if (hit==false||hitT<finalT) {
			hit=true;
			finalT=hitT;
			finalPos=hitPos;
			finalNormal=hitNormal;
		}
	}
	if (hit) {
		//hitPos and hitNormal are set inside collision functions
		light = pointLight(finalPos,theLight,finalNormal);

		//check lit pixels for shadows
		if (light>.05) {
			if (intSphere(theSphere,finalPos,normalize(theLight.xyz-finalPos))) {
				light*=0.3;
			}
		}

		//apply ambient light
		light=max(0.05,light);

		vec3 finalColor=vec3(light,light,light);

		//post processing: slight fog
		finalColor+=normalize(vec3(0.75,0.75,0.95)-finalColor)*min(finalT,20.0)/60.0;

		//all done: fire away!
		fragColor = vec4(finalColor,1.0);
	} else {
		//pixel not hitting anything

		fragColor = vec4(.5,.5,.7,1.0)*(1.0-.25*t2.y);
	}
}

float time = 0.;
float pi = 3.14159;
float cycle = 0.;

float usin(float x) {return 0.5+0.5*sin(x);}
float osin(float x) {return 0.75+0.25*sin(x);}
float ucos(float x) {return 0.5+0.5*cos(x);}
float ocos(float x) {return 0.75+0.25*cos(x);}

const vec4 nullcol = vec4(0.);
const vec3 nulldir = vec3(0.);

struct shapevals {vec2 pos; float ang; float scale; float line;};
struct rayblob {vec3 pos; float ang; float scale; vec3 col;};

vec3 sphere(vec3 p, vec3 r, rayblob s) {
	vec3 inc = p+r;
	if (length(inc-s.pos) < s.scale) {
		float b = 2.*dot(r, p-s.pos);
		float c = dot(p-s.pos, p-s.pos) - s.scale*s.scale;
		float d = b*b-4.*c;
		if (d < 0.) return nulldir;
		float t1 = (-b + sqrt(d))/2.;
		float t2 = (-b - sqrt(d))/2.;
		float t = min(t1,t2);
		return inc-t*r;
	} 
	return nulldir;
}

float surf(vec2 p) {
	float z = usin(p.x*7.+2.*time) + ucos(p.y*4.+osin(time*4.)*2.);	

	return z*.3;
}

const vec3 eye = vec3(0.,0.,4.);
const vec3 at = vec3(0.,0.,0.);
const vec3 up = vec3(0.,1.,0.);
const float frustrum = 2.0;
vec4 raysurf(vec2 p, rayblob s) {
	vec3 ray = normalize(at-eye);
	vec3 right = cross(up, ray);

	vec3 pt = normalize(ray*frustrum + p.x*right + p.y*up);
	ray = eye;

	vec3 collide;
	vec3 col = vec3(0.);

	float dt = .2;
	float z = eye.z;
	for (int i = 0; i < 32; i++) {
		collide = sphere(ray, pt*dt, s);
		if (collide != nulldir) {
			vec3 n = normalize(collide - s.pos);
			pt = -n;
			z = dot(ray, n);
			col += s.col;
			break;
		}

		z = surf(ray.xy);
		if (z > ray.z) break;
		ray += pt*dt;
	}

	float stripe = ceil(sin(z*(30.*osin(time*4.))));
	vec3 scol = vec3(stripe*.8, stripe*.5, 0.);

	col += scol*(z+.3);
	return vec4(col,1.);	
}


float pattern(vec3 ray) {
	float res = 0.;
	float r = length(ray.xy);

	return usin(r*cycle*2.-time*10.)+ucos(12.*atan(ray.y/ray.x)+2.*time+r*cycle*10.);
}

vec4 circle(vec2 p, shapevals s, float j) { 
	vec2 pos = s.pos;

	vec2 os = p-pos;
	float a = atan(os.y,os.x)+s.ang;
	float d = length(os)-usin(a*j)*s.scale;

	if (d<s.scale) {
		return vec4(usin(pos.x), usin(pos.y),pattern(os.xyx),1.0);
	} else if (d-s.line < s.scale) {
		return vec4(nullcol.xyz,1.);
	}return nullcol;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv = 2.*uv-1.;
	uv.x *= iResolution.x/iResolution.y;

    time = iTime;
    cycle = 2.*pi;

	vec4 col;

	shapevals f1 = shapevals(vec2(sin(time),cos(time*4.)), time*4.+sin(time)*2., 0.2,0.05);
	shapevals f2 = shapevals(vec2(cos(time*4.+pi/2.),sin(time)), time*2.-sin(time*2.)*3., 0.25,.05);
	shapevals rain;

	rayblob s1 = rayblob(vec3(sin(time), cos(time*2.), .5), 0., 1., vec3(0.,.4,0.));

	const int num = 10;
	for (int i = 0; i < num; i++) {
		float j = float(i);
		float fall = usin(sin(j));
		fall = fall*-time*2.;
		rain = shapevals(vec2(mod(j, pi)-pi/2.,mod(j+fall, pi)-pi/2.), time*(0.5+0.5*usin(j))+sin(time+j)*2., 0.1+0.02*usin(cycle*time*4.+j), .05);	
		col = circle(uv, rain, 5.);
		if (col != nullcol) {
			break;
		}
	}

	if (col == nullcol) {
		col = circle(uv, f1, 2.);
	}	
	if (col == nullcol) {
		col = circle(uv, f2, 3.);
	}
	if (col == nullcol) {
		col = raysurf(uv, s1);
	}

	fragColor = col;
}

// Rolling ball. By David Hoskins, April 2014.
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// https://www.shadertoy.com/view/lsfXz4

// Uses https://www.shadertoy.com/view/Xsf3zX as base.


float PI  = 4.0*atan(1.0);
vec3 sunLight  = normalize( vec3(  0.35, 0.2,  0.3 ) );
vec3 cameraPos;
vec3 camTar;
vec3 sunColour = vec3(1.0, .6, .4);
const mat2 rotate2D = mat2(1.932, 1.623, -1.623, 1.952);
float gTime = 0.0;
vec2 ballRoll;
float bounce;
//#define MOD2 vec2(3.07965, 7.4235)
#define MOD2 vec2(.16632,.17369)
#define f(p) length(fract(p/70.) - .5)

//--------------------------------------------------------------------------
// Noise functions...
float Hash( float p )
{
	vec2 p2 = fract(vec2(p) / MOD2);
    p2 += dot(p2.yx, p2.xy+19.19);
	return fract(p2.x * p2.y);
}

//--------------------------------------------------------------------------
float Hash(vec2 p)
{
	p  = fract(p / MOD2);
    p += dot(p.xy, p.yx+19.19);
    return fract(p.x * p.y);
}

//--------------------------------------------------------------------------
float Noise( in vec2 x )
{
    vec2 p = floor(x);
    vec2 f = fract(x);
    f = f*f*(3.0-2.0*f);

    float res = mix(mix( Hash(p), Hash(p+ vec2(1.0, 0.0)),f.x),
                    mix( Hash(p+ vec2(.0, 1.0)), Hash(p+ vec2(1.0, 1.0)),f.x),f.y);
    return res;
}

//--------------------------------------------------------------------------
vec2 Rotate2(vec2 p, float a)
{
	float si = sin(a);
	float co = cos(a);
	return mat2(co, si, -si, co) * p;
}


//  2 out, 2 in...
vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

//--------------------------------------------------------------------------
vec2 Voronoi( in vec2 x )
{
	vec2 p = floor( x );
	vec2 f = fract( x );
	float res=100.0;
    vec2 id;
	for( int j=-1; j<=1; j++ )
	for( int i=-1; i<=1; i++ )
	{
		vec2 b = vec2( float(i), float(j) );
		vec2 r = b - f  + hash22( p + b );
		float d = dot(r,r);
		if( d < res )
		{
			res = d;
			id.x  = Hash(p+b);
		}			
    }
	return vec2(max(.4-sqrt(res), 0.0),id.x);
}


//--------------------------------------------------------------------------
vec3 BallTexture( in vec3 n)
{
	vec3 col = vec3(.5, 0.0, 0.0);
	col= mix(col, vec3(.0, .6, 0.9), smoothstep(-0.05, -.04, n.y) * smoothstep(0.05, .04, n.y));
	float at = cos(atan(n.x, n.z) * 5.0)*(n.y*n.y);
	col =mix(col, vec3(.7, .7, 0.0), smoothstep(0.3, .32, at));
	return col * .8;
}

//--------------------------------------------------------------------------
vec2 Terrain( in vec2 p)
{
	float type = 0.0;
	vec2 pos = p*0.003;
	float w = 50.0;
	float f = .0;
	for (int i = 0; i < 3; i++)
	{
		f += Noise(pos) * w;
		w = w * 0.62;
		pos *= 2.6;
	}

	return vec2(f, type);
}

//--------------------------------------------------------------------------
vec2 Map(in vec3 p)
{
	vec2 h = Terrain(p.xz);
    return vec2(p.y - h.x, h.y);
}

//--------------------------------------------------------------------------
float FractalNoise(in vec2 xy)
{
	float w = .7;
	float f = 0.0;

	for (int i = 0; i < 3; i++)
	{
		f += Noise(xy) * w;
		w = w*0.6;
		xy = 2.0 * xy;
	}
	return f;
}

//--------------------------------------------------------------------------
// Grab all sky information for a given ray from camera
vec3 GetSky(in vec3 rd, bool doClouds)
{
	float sunAmount = max( dot( rd, sunLight), 0.0 );
	float v = pow(1.0-max(rd.y,0.0),6.);
	vec3  sky = mix(vec3(.1, .2, .3), vec3(.32, .32, .32), v);
	sky = sky + sunColour * sunAmount * sunAmount * .25;
	sky = sky + sunColour * min(pow(sunAmount, 800.0)*1.5, .4);

	if (doClouds)
	{
		vec2 cl = rd.xz * (1.0/rd.y);
		v = FractalNoise(cl) * .3;
		sky = mix(sky, sunColour, v*v);
	}

	return clamp(sky, 0.0, 1.0);
}

//--------------------------------------------------------------------------
// Merge grass into the sky background for correct fog colouring...
vec3 ApplyFog( in vec3  rgb, in float dis, in vec3 dir)
{
	float fogAmount = clamp(dis*dis* 0.0000011, 0.0, 1.0);
	return mix( rgb, GetSky(dir, false), fogAmount );
}

//--------------------------------------------------------------------------
vec3 DE(vec3 p)
{
	float base = Terrain(p.xz).x - 1.9;
	float height = Noise(p.xz*2.0)*.75 + Noise(p.xz)*.35 + Noise(p.xz*.5)*.2;
	//p.y += height;

	float y = p.y - base-height;
	y = y*y;

	// Move grass out of way of target (ball)...
	vec2 move = (p.xz-camTar.xz);
	float l = length(move);
	move = (move * y) * smoothstep(15.0, -6.0, l)/ (bounce+1.0);
	p.xz -= move;

	vec2 ret = Voronoi((p.xz*2.5+sin(y*4.0+p.zx*3.23)*0.12+vec2(sin(iTime*2.3+0.5*p.z),sin(iTime*3.6+.5*p.x))*y*.5));
	float f = ret.x * .6 + y * .58;
	return vec3( y - f*1.4, clamp(f * 1.5, 0.0, 1.0), ret.y);
}

//--------------------------------------------------------------------------
// eiffie's code for calculating the aperture size for a given distance...
float CircleOfConfusion(float t)
{
	return max(t * .04, (2.0 / iResolution.y) * (1.0+t));
}

//--------------------------------------------------------------------------
float Linstep(float a, float b, float t)
{
	return clamp((t-a)/(b-a),0.,1.);
}

//--------------------------------------------------------------------------
float Sphere( in vec3 ro, in vec3 rd, in vec4 sph )
{
	vec3 oc = ro - sph.xyz;
	float b = dot( oc, rd );
	float c = dot( oc, oc ) - sph.w*sph.w;
	float h = b*b - c;
	if( h<0.0 ) return -1.0;
	return -b - sqrt( h );
}

//--------------------------------------------------------------------------
// Calculate sun light...
void DoLighting(inout vec3 mat, in vec3 normal, in float dis)
{
	float h = dot(sunLight,normal);
	mat = mat * sunColour*(max(h, 0.0)+max((normal.y+.3) * .2, 0.0)+.1);
}

//--------------------------------------------------------------------------
vec3 GrassOut(in vec3 rO, in vec3 rD, in vec3 mat, in vec3 normal, in float dist)
{
	float d = -2.0;

	// Only calculate cCoC once is enough here...
	float rCoC = CircleOfConfusion(dist*.3);
	float alpha = 0.0;

	vec4 col = vec4(mat, 0.0);

	for (int i = 0; i < 10; i++)
	{
		if (col.w > .99 || d > dist) break;
		vec3 p = rO + rD * d;

		vec3 ret = DE(p);
		ret.x += .5 * rCoC;

		if (ret.x < rCoC)
		{
			alpha = (1.0 - col.w) * Linstep(-rCoC, rCoC, -ret.x);//calculate the mix like cloud density

			// Mix material with white tips for grass...
			vec3 gra = mix(vec3(.0, .2, 0.0), vec3(.1, .4, min(pow(ret.z, 4.0)*35.0, .35)),
						   pow(ret.y, 9.0)*.7) * ret.y * .7;
			col += vec4(gra * alpha, alpha);
		}
		d += .02;
	}

	DoLighting(col.xyz, normal, dist);

	col.xyz = mix(mat, col.xyz, col.w);

	return col.xyz;
}


//--------------------------------------------------------------------------
vec3 GrassBlades(in vec3 rO, in vec3 rD, in vec3 mat, in float dist)
{
	float d = 0.0;
	float f;
	// Only calculate cCoC once is enough here...
	float rCoC = CircleOfConfusion(dist*.3);
	float alpha = 0.0;

	vec4 col = vec4(mat*0.15, 0.0);

	for (int i = 0; i < 15; i++)
	{
		if (col.w > .99) break;
		vec3 p = rO + rD * d;

		vec3 ret = DE(p);
		ret.x += .5 * rCoC;

		if (ret.x < rCoC)
		{
			alpha = (1.0 - col.y) * Linstep(-rCoC, rCoC, -ret.x) * 2.0;//calculate the mix like cloud density
			f = clamp(ret.y, 0.0, 1.0);
			// Mix material with white tips for grass...
			vec3 gra = mix(mat, vec3(.2, .3, min(pow(ret.z, 14.0)*3.0, .3)), pow(ret.y,100.0)*.6 ) * ret.y;
			col += vec4(gra * alpha, alpha);
		}
		d += max(ret.x * .7, .02);
	}
	if(col.w < .2)col.xyz = vec3(0.1, .15, 0.05);
	return col.xyz;
}

//--------------------------------------------------------------------------
vec3 TerrainColour(vec3 pos, vec3 dir,  vec3 normal, float dis, float type)
{
	vec3 mat;
	if (type == 0.0)
	{
		// Random colour...
		mat = mix(vec3(.0,.2,.0), vec3(.1,.3,.0), Noise(pos.xz*.025));
		// Random shadows...
		float t = FractalNoise(pos.xz * .1)+.5;
		// Do grass blade tracing...
		mat = GrassBlades(pos, dir, mat, dis) * t;
		DoLighting(mat, normal, dis);
		float f = Sphere( pos, sunLight, vec4(camTar, 10.0));
		if (f > 0.0)
		{
			mat *= clamp(f*.05, 0.4, 1.0);
		}
	}else
	{
		// Ball...
		vec3 nor = normalize(pos-camTar);
		vec3 spin = nor;

		spin.xz = Rotate2(spin.xz, ballRoll.y);
		spin.zy = Rotate2(spin.zy, ballRoll.x);
		spin.xy = Rotate2(spin.xy, .4);

		mat = BallTexture(spin);
		DoLighting(mat, nor, dis);
		vec3 ref = reflect(dir, nor);
		mat += sunColour * pow(max(dot(ref, sunLight), 0.0), 6.0) * .3;

		if (pos.y < Terrain(pos.xz).x+1.5)
		{
			mat = GrassOut(pos, dir, mat, normal, dis);
		}
	}
	mat = ApplyFog(mat, dis, dir);
	return mat;
}

//--------------------------------------------------------------------------
// Home in on the surface by dividing by two and split...
float BinarySubdivision(in vec3 rO, in vec3 rD, float t, float oldT)
{
	float halfwayT = 0.0;
	for (int n = 0; n < 5; n++)
	{
		halfwayT = (oldT + t ) * .5;
		if (Map(rO + halfwayT*rD).x < .05)
		{
			t = halfwayT;
		}else
		{
			oldT = halfwayT;
		}
	}
	return t;
}


//--------------------------------------------------------------------------
vec3 CameraPath( float t )
{
	//t = time + t;
    vec2 p = vec2(200.0 * sin(3.54*t), 200.0 * cos(3.54*t) );
	return vec3(p.x+5.0,  0.0, -94.0+p.y);
} 

//--------------------------------------------------------------------------
bool Scene(in vec3 rO, in vec3 rD, inout float resT, inout float type )
{
    float t = 5.;
	float oldT = 0.0;
	float delta = 0.;
	vec2 h = vec2(1.0, 1.0);
	bool hit = false;
	for( int j=0; j < 70; j++ )
	{
	    vec3 p = rO + t*rD;
        if (p.y > 90.0) break;

		h = Map(p); // ...Get this position's height mapping.

		// Are we inside, and close enough to fudge a hit?...
		if( h.x < 0.05)
		{
			hit = true;
            break;
		}

		delta = h.x + (t*0.03);
		oldT = t;
		t += delta;
	}
    type = h.y;
    resT = BinarySubdivision(rO, rD, t, oldT);
	float f = Sphere( rO, rD, vec4(camTar, 10.0));

	if (f > 0.0 && f < resT+4.5)
	{
		hit = true;
		type = 1.0;
		resT = f;


	}
	return hit;
}

//--------------------------------------------------------------------------
vec3 PostEffects(vec3 rgb, vec2 xy)
{
	// Gamma first...
	rgb = pow(rgb, vec3(0.45));

	// Then...
	#define CONTRAST 1.1
	#define SATURATION 1.3
	#define BRIGHTNESS 1.3
	rgb = mix(vec3(.5), mix(vec3(dot(vec3(.2125, .7154, .0721), rgb*BRIGHTNESS)), rgb*BRIGHTNESS, SATURATION), CONTRAST);
	// Vignette...
	rgb *= .4+0.6*pow(40.0*xy.x*xy.y*(1.0-xy.x)*(1.0-xy.y), 0.2 );	
	return rgb;
}

//--------------------------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float m = (iMouse.x/iResolution.x)*300.0;
	float gTime = (iTime*5.0+m+468.0)*.006;
    vec2 xy = fragCoord.xy / iResolution.xy;
	vec2 uv = (-1.0 + 2.0 * xy) * vec2(iResolution.x/iResolution.y,1.0);

	if (xy.y < .13 || xy.y >= .87)
	{
		// Top and bottom cine-crop - what a waste! :)
		fragColor=vec4(vec4(0.0));
		return;
	}

	cameraPos = CameraPath(gTime + 0.0);
    cameraPos.x-= 20.0;
	camTar	  = CameraPath(gTime + .06);

	vec2 diff = (camTar.xz - CameraPath(gTime + .13).xz);
	ballRoll.x = -gTime * 72.0;
	ballRoll.y = atan(diff.x, diff.y);

	cameraPos.y = Terrain(cameraPos.xz).x + 7.0;
	camTar.y    = Terrain(camTar.xz).x+7.0;
	bounce = abs(sin(gTime*130.0))* 40.0 * max(abs(sin(gTime*15.0))-.85, 0.0);
	camTar.y += bounce;

	float roll = .3*sin(gTime*3.0+.5);
	vec3 cw = normalize(vec3(camTar.x, cameraPos.y, camTar.z)-cameraPos);
	vec3 cp = vec3(sin(roll), cos(roll),0.0);
	vec3 cu = cross(cw,cp);
	vec3 cv = cross(cu,cw);
	vec3 dir = normalize(uv.x*cu + uv.y*cv + 1.3*cw);
	mat3 camMat = mat3(cu, cv, cw);

	vec3 col;
	float distance = 1e20;
	float type = 0.0;
	if( !Scene(cameraPos, dir, distance, type) )
	{
		// Missed scene, now just get the sky...
		col = GetSky(dir, true);
	}
	else
	{
		// Get world coordinate of landscape...
		vec3 pos = cameraPos + distance * dir;
		// Get normal from sampling the high definition height map
		vec2 p = vec2(0.1, 0.0);
		vec3 nor  	= vec3(0.0,		Terrain(pos.xz).x, 0.0);
		vec3 v2		= nor-vec3(p.x,	Terrain(pos.xz+p).x, 0.0);
		vec3 v3		= nor-vec3(0.0,	Terrain(pos.xz-p.yx).x, -p.x);
		nor = cross(v2, v3);
		nor = normalize(nor);

		// Get the colour using all available data...
		col = TerrainColour(pos, dir, nor, distance, type);
	}

	// bri is the brightness of sun at the centre of the camera direction.
	float bri = dot(cw, sunLight)*.75;
	if (bri > 0.0)
	{
		vec2 sunPos = vec2( dot( sunLight, cu ), dot( sunLight, cv ) );
		vec2 uvT = uv-sunPos;
		uvT = uvT*(length(uvT));
		bri = pow(bri, 6.0)*.8;

		// glare = the red shifted blob...
		float glare1 = max(dot(normalize(vec3(dir.x, dir.y+.3, dir.z)),sunLight),0.0)*1.4;
		// glare2 is the yellow ring...
		float glare2 = max(1.0-length(uvT+sunPos*.5)*4.0, 0.0);
		uvT = mix (uvT, uv, -2.3);
		// glare3 is a purple splodge...
		float glare3 = max(1.0-length(uvT+sunPos*5.0)*1.2, 0.0);

		col += bri * vec3(1.0, .0, .0)  * pow(glare1, 12.5)*.05;
		col += bri * vec3(1.0, 1.0, 0.2) * pow(glare2, 2.0)*2.5;
		col += bri * sunColour * pow(glare3, 2.0)*3.0;
	}
	col = PostEffects(col, xy);	

	fragColor=vec4(col,1.0);
}

