void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalize x and y values
    vec2 uv = fragCoord.xy / iResolution.xy;

    // get how long shader has been running
    float t = iTime * 3.;

    // move and scale viewport
    float x = uv.x * 50. - 25.5;
    float y = uv.y * 5. - 2.5;


    ////////////////////////////////////////////////////////////////////////////
    // function generated by GA
    ////////////////////////////////////////////////////////////////////////////
 	float v= smoothstep(xorb(clamp(abs(x), smoothstep(xora(t, y), t, mul(t, t)), gt(sub(t, y), clamp(t, t, t))), xor(gta(y, log(x)), clamp(t, sqrt(x), mod(y, y)))), iff(pow(mix(sqrt(t), gtb(x, x), unisin(t)), y), x, log2(min(clamp(x, t, t), uniacos(x)))), mix(y, unicos(x), iff(gtb(lt(x, y), clamp(x, t, y)), mul(lta(x, y), iff(t, x, t)), mix(gtb(y, y), orb(x, y), mix(t, x, y)))));
    // output our function
    fragColor = vec4(v, v, v, 1.0);
}

//#define NO_BAIL
const vec3 grid = vec3(4.0);
const vec3 griddiv = vec3(2.0);
const vec3 aabb  = vec3(0.75, 0.25, 0.25);
const vec3 aabb2 = vec3(0.25, 0.85, 0.25);
const vec3 aabb3 = vec3(0.25, 0.25, 0.85);

float pyramid( vec3 p, float h) {
	vec3 q=abs(p);
	return max(-p.y, (q.x+q.y+q.z-h)/3.0 );
}

float box(vec3 p) {
	return min(  pyramid( vec3(p.x,-p.y,p.z)-vec3(0.0,0.0,0.0),1.0),
		min( pyramid(p-vec3(0.0, 0.0, 0.0), 0.90),
		min(min(length(max(abs(p)-aabb, 0.0)),
			length(max(abs(p)-aabb2, 0.0))),
			length(max(abs(p)-aabb3, 0.0)))));
}

vec3 rot(vec3 p, float f) {
	float s = sin(f);
	float c = cos(f);
	p.xy *= mat2(c, -s, s, c);
	p.yz *= mat2(c, -s, s, c);
	return p;
}

vec3 trans(vec3 p, out float rotout) {
	vec3 rep = floor(p/grid);
	p = mod(p,grid)-griddiv;
	rotout = iTime*0.28 + (rep.x+rep.z+rep.y)*0.3;
	p = rot(p, rotout);
	return p;	
}
vec3 normal(vec3 p) {
	vec3 e = vec3(0.02,0.0,0.0);
	return normalize(vec3(
		box(p+e.xyy)-box(p-e.xyy),
		box(p+e.yxy)-box(p-e.yxy),
		box(p+e.yyx)-box(p-e.yyx)));
}

float scene(vec3 p) {
	float dummy;
	return box(trans(p,dummy));
}

vec3 normal(vec3 p, float d) {
	vec3 e = vec3(0.04,0.0,0.0);
	return normalize(vec3(
		scene(p+e.xyy)-d,
		scene(p+e.yxy)-d,
		scene(p+e.yyx)-d));
}

bool raystep(inout float d, inout vec3 p,vec3 ray){
	const float eps=0.016;
	d=scene(p);
	p+=max(eps,d*0.99)*ray;
	return (d < eps);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 xy = fragCoord.xy / iResolution.xy - vec2(0.5,0.5);
	xy.y *= -iResolution.y / iResolution.x;

	vec3 ro = 2.0*normalize(vec3(cos(iTime/4.0),cos(iTime/4.0),sin(iTime/4.0)));
    vec3 eyed = normalize(vec3(0.0) - ro);
    vec3 ud = normalize(cross(vec3(0.0,1.0,0.0), eyed));
    vec3 vd = normalize(cross(eyed,ud));

	const float fov = 3.14 * 0.7;
	float f = fov * length(xy);
	vec3 rd = normalize(normalize(xy.x*ud + xy.y*vd) + (1.0/tan(f))*eyed);

	vec3 p = ro;
	float d=0.017;
	float dummy;
	bool hit=false;

	for(int i = 0; i < 64; i++) 
	{
		#ifdef NO_BAIL
		hit = ( hit == false ) ? raystep(d,p,rd):true;
		#else
		hit = ( hit == false ) ? raystep(d,p,rd):true;
		if (hit) continue;
		#endif
	}

	vec3 bg = normalize(p).zzz + 0.21;

	if(d < 0.016) {
		vec3 n = normal(p,d); 
		vec3 col = vec3(dot(vec3(0.0,0.0,1.0), n));
		float objrot;
		vec3 objp = trans(p,objrot);
		vec3 objn = abs(rot(n,objrot));

		vec2 uv = 
			(objn.y > 0.707) ? vec2(objp.zx) : 
			(objn.x > 0.707) ? vec2(objp.zy) :
							   vec2(objp.xy) ;
		vec3 tex = texture(iChannel0, uv).rgb;
		vec3 hl = smoothstep(0.6, 1.0, col);
		col *= clamp(tex.xyz+0.13, 0.0, 1.0);

		col = col + hl*.4;
		float dall = length(p-ro)*1.20;
		float fog = clamp(dall/mix(90.0,40.0,((rd.z+1.0)*0.5)), 0.0, 1.0);

		fragColor = vec4(mix(col, bg, fog),1.0);
	}
	else {
		fragColor = vec4(bg, 1.0);
	}

}

