float intersectPlane(in vec3 origin, in vec3 direction, out vec3 normal) {

	normal = vec3(0.0, 1.0, 0.0);
	return -origin.y / direction.y;
}

float intersectSphere(in vec3 origin, in vec3 direction, in vec4 sphere, out vec3 position, out vec3 normal) {

	vec3 center = origin - sphere.xyz;

	float b = 2.0 * dot(center, direction);
	float c = dot(center, center) - sphere.w * sphere.w;
	float h = b * b- 4.0 * c;

	if(h < 0.0)
		return -1.0;

	float t = (-b - sqrt(h)) / 2.0;

	position = origin + direction * t;

	normal = normalize(position - sphere.xyz);

	return t;
}

vec3 light = vec3(4.0, 5.0, 0.0);
vec4 sphere1 = vec4(0.0, 1.0, 1.0, 1.0);
vec4 sphere2 = vec4(0.0, 1.0, 1.0, 0.5);

float intersect(in vec3 origin, in vec3 direction, out float t, out vec3 position, out vec3 normal) {

	t = 100000.0;
	float id = -1.0; 
	vec3 normals1;
	vec3 normals2;
	vec3 normalp;

	float ts1 = intersectSphere(origin, direction, sphere1, position, normals1);
	float ts2 = intersectSphere(origin, direction, sphere2, position, normals2);
	float tp = intersectPlane(origin, direction, normalp);

	if(ts1 > 0.0) {

		id = 1.0;
		t = ts1;
		normal = normals1;
	}

	if(ts2 > 0.0 && ts2 < t) {

		id = 1.5;
		t = ts2;
		normal = normals2;
	}

	if(tp > 0.0 && tp < t) {
		id = 2.0;
		t = tp;
		normal = normalp;
	}

	position = origin + (t - 0.0001) * direction;

	return id;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	sphere1.x = sin(iTime / 1.0);
	sphere1.z = cos(iTime / 1.0);

	sphere2.x = sphere1.x + sin(iTime / 0.5) * 1.5;
	sphere2.z = sphere1.z + cos(iTime / 0.5) * 1.5;

	float aspect = iResolution.y / iResolution.x;
	vec2 uv = fragCoord.xy / iResolution.xy * vec2(1, aspect);
	vec3 origin = vec3(0.0, 2.0, 4.0);
	vec3 direction = normalize(vec3(-1.0 + 2.0 * uv, -1.0));

	float distance;
	vec3 normal;
	vec3 position;
	float id = intersect(origin, direction, distance, position, normal);

	vec3 col = vec3(0.0);

	vec3 lightDir = normalize(light - position);

	float diffuse = clamp(dot(normal, lightDir), 0.0, 1.0) * 0.9;
	vec3 ambient = vec3(0.4, 0.4, 0.4);

	if(id > 0.9 && id < 1.1) {

		col = vec3(1.0, 0.8, 0.9);
		col *= 0.7 + 0.3 * normal.y; // hacked AO
	}

	else if(id > 1.4 && id < 1.6) {

		col = vec3(0.8, 1.0, 0.7);
		col *= 0.7 + 0.3 * normal.y; // hacked AO
	}

	else if(id > 1.9 && id < 2.1) {

		col = vec3(0.7, 0.7, 0.9);

		if(fract(position.x / 10.0) > 0.5 ^^ fract(position.z / 10.0) > 0.5)
			col *= 0.9;

	}

	vec3 shadowRay = normalize(light - position);

	float shadowT;
	vec3 shadowP;
	vec3 shadowN;

	float shadow = 1.0;

	if(intersect(position, shadowRay, shadowT,  shadowP, shadowN) > 0.0)
		shadow = 0.5;

	col = col * ambient + col * diffuse * shadow;

	fragColor = vec4(col, min(1.0, iTime));
}

