// Assuming n1=1 (air) we get:
//	F0 = ((n2 - n1) / (n2 + n1))Â²
//	=> n2 = (1 + sqrt(F0)) / (1 - sqrt(F0))
//
vec3	Fresnel_IORFromF0( vec3 _F0 )
{
	vec3	SqrtF0 = sqrt( _F0 );
	return (1.0 + SqrtF0) / (1.00001 - SqrtF0);
}

// Full accurate Fresnel computation (from Walter's paper Â§5.1 => http://www.cs.cornell.edu/~srm/publications/EGSR07-btdf.pdf)
// For dielectrics only but who cares!?
vec3	Fresnel( vec3 _IOR, float _CosTheta, float _FresnelStrength )
{
	float	c = mix( 1.0, _CosTheta, _FresnelStrength );
	vec3	g_squared = max( vec3(0.0), _IOR*_IOR - 1.0 + c*c );
// 	if ( g_squared < 0.0 )
// 		return 1.0;	// Total internal reflection

	vec3	g = sqrt( g_squared );

	vec3	a = (g - c) / (g + c);
			a *= a;
	vec3	b = (c * (g+c) - 1.0) / (c * (g-c) + 1.0);
			b = 1.0 + b*b;

	return 0.5 * a * b;
}

float smoothinterp( float x0, float x1, float t )
{
	return mix( x0, x1, smoothstep( 0.0, 1.0, t ) );
}

vec2 tunnelCenter( float z )
{
#ifdef ANIMATE_TUNNEL
	return vec2(	0.2 * (sin( 0.5919 * z + 4.0 * iTime ) + sin( 1.2591 * z )*cos( 0.915 * z )),
					0.2 * (sin( 1.8 * z + 4.0 * iTime ) + sin( 0.1378 * z )) );
#else
	return vec2( 0.0, 0.0 );
#endif
}

const float barGap = 1.0;

void barCenterAxis( float z, out vec3 barCenter, out vec3 barAxis, out float barOffset )
{	
	vec2	c = tunnelCenter( z ); 	// Tunnel center

	// Retrieve bar index & compute rotation/offset
	float	barIndex = floor( z / barGap );
	float	zcenter = (0.5+barIndex) * barGap;
	float	angle = 2.0 * PI * 0.156 * (0.2+barIndex);// + iTime;

	angle = 0.798 * barIndex;	// Fixed bars
#ifdef ROTATE_BARS
	angle += iTime * (0.5 + 1.0 * sin( 1234.567 * barIndex ));
#endif

	barAxis = vec3( cos( angle ), sin( angle ), 0.2 * sin( zcenter ) );

#ifdef TRANSLATE_BARS
	barOffset = 0.0 + 0.5 * sin( 17.21191 * barIndex + iTime * (0.5 + 1.0 * sin( 8976.5431 * barIndex )) );
#else
	barOffset = 0.0 + 0.5 * sin( 17.21191 * barIndex );
#endif

	barCenter = vec3( c + barOffset * vec2( -barAxis.y, barAxis.x ), zcenter );
}

vec3 safePosition( float z, float rand )
{
	vec3	barCenter0, barAxis0; float barOffset0;
	barCenterAxis( z - 0.5 * barGap, barCenter0, barAxis0, barOffset0 );
	vec2	tunnelCenter0 = tunnelCenter( barCenter0.z );

	vec3	barCenter1, barAxis1; float barOffset1;
	barCenterAxis( z + 0.5 * barGap, barCenter1, barAxis1, barOffset1 );
	vec2	tunnelCenter1 = tunnelCenter( barCenter1.z );

	float	z0 = barCenter0.z;
	float	z1 = barCenter1.z;
	float	t = (z - z0) / (z1 - z0); // Interpolant

	// Compute the 2 valid positions for each bar
	float	off0 = mix( 0.5*(1.0+barOffset0), 0.5*(-1.0+barOffset0),
						step( 0.0, sign( sin( 37.85961 * (z0 + rand) ) ) ) );	// Choose between left or right of the bar...
	vec2	safePos0 = tunnelCenter0 + off0 * vec2( -barAxis0.y, barAxis0.x );

	float	off1 = mix( 0.5*(1.0+barOffset1), 0.5*(-1.0+barOffset1),
						step( 0.0, sign( sin( 37.85961 * (z1 + rand) ) ) ) );
	vec2	safePos1 = tunnelCenter1 + off1 * vec2( -barAxis1.y, barAxis1.x );

	return vec3(	smoothinterp( safePos0.x, safePos1.x, t ),
					smoothinterp( safePos0.y, safePos1.y, t ),
					z );
}

float bisou( vec3 p )
{
	vec3	c, axis; float offset;
	barCenterAxis( p.z, c, axis, offset );

	vec3	toP = p-c;
	vec3	planeP = toP - dot( toP, axis ) * axis;

	return length( planeP ) - 0.1
//		- 0.05 * noise(37.0569 * p )
		;
}

float map( vec3 p )
{
	vec2	c = tunnelCenter( p.z );
	float	d_tunnel = 1.0 - length( p.xy - c );
	float	d_bar = bisou( p );

	return smin2( d_tunnel, d_bar, 0.7 )
#ifdef TURD_MODE
		- 0.025 * noise( 67.0569 * p )
#endif
	;

	return min( d_tunnel, d_bar );
	return smin( d_tunnel, d_bar, -4.0 );
}

vec3 normal( vec3 p, const float eps, out float cheapAO )
{
	vec2 e = vec2( eps, 0.0 );
	float c = map( p );
	vec3	n = vec3(
		map( p + e.xyy ) - map( p - e.xyy ),
		map( p + e.yxy ) - map( p - e.yxy ),
		map( p + e.yyx ) - map( p - e.yyx )
		);
	cheapAO = length( n );
	return n / cheapAO;
}

vec3 reflection( vec3 p, vec3 v, vec3 n )
{
	v = reflect( v, n );
//	p += (0.01 / dot( v, n )) * v;
	p += 0.01 * n;

	float	t = 0.0;
	for ( int i=0; i < 64; i++ )
	{
		float	d = map( p );
		if ( d < 0.005 ) break;

		t += d;
		p += d * v;
	}

	return p;
}

float AO( vec3 p, vec3 n )
{
	const float step = 0.01;
	p += 0.1 * n;
	float AO = 1.0;
	for ( int i=0; i < 16; i++ )
	{
		float	d = max( 0.0, map( p ) );
		p += step * n;

		AO *= 1.0 - exp( -20.0 * d * (2.0+float(i)) );
	}
	return AO;
}

float	Shadow( vec3 p, vec3 l, float distance2Light )
{
	const float	k = 10.0;

	float S = 1.0;
	float t = 0.05;
	for ( int i=0; i < 64; i++ )
	{
		float	h = map( p + t * l );
		if ( h < 0.0001 )
			return 0.0;

		S = min( S, k*h / t );
		t += h;
		if ( t > distance2Light )
			break;
	}
	return S;	
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2	uv = fragCoord.xy / iResolution.xy;

	float	z = 1.0 * iTime;

	vec3	p = safePosition( z, 0.0 );

	vec3	target = p + vec3(
		sin( 1.2 * iTime ),
		sin( 1.0 + 0.7891 * iTime ),
		5.0 + 2.0 * sin( 1.2 * iTime )
		);

	vec3	at = normalize( target - p );
	vec3	right = normalize( cross( at, vec3( 0, 1, 0 ) ) );
	vec3	up = cross( right, at );

	float	Tan = 0.6;
	vec3	v = normalize( vec3( iResolution.x / iResolution.y * Tan * (2.0 * uv.x - 1.0), Tan * (2.0 * uv.y - 1.0), 1.0 ) );
			v = v.x * right + v.y * up + v.z * at;

	// Compute light position
	float	lightTime = 0.25 * iTime;
	float	z_light = z + 4.0 + 3.0 * sin( 3.156 * lightTime ) * sin( 0.15891 * lightTime );
	vec3	l = safePosition( z_light, 187.65 );

	// March!
	vec3	prevPos2Light = l - p;
	float	prevDist2Light = length( prevPos2Light );
			prevPos2Light /= max( 1e-4, prevDist2Light );

	float	scatt = 0.0;
	vec3	p_orig = p;
	for ( int i=0; i < 64; i++ )
	{
		float	d = map( p );
		if ( d < 0.005 ) break;
		p += d * v;

		vec3	pos2Light = l - p;
		float	dist2Light = length( pos2Light );
				pos2Light /= max( 1e-4, dist2Light );
		scatt += d / pow( 0.5 * (prevDist2Light + dist2Light), 2.0 );
		prevPos2Light = pos2Light;
		prevDist2Light = dist2Light;
	}

	const float		LightIntensity = 0.5;
	const vec3	C0 = 1.0 * vec3( 0.2, 0.2, 0.2 );
	const vec3	C1 = vec3( 1.0, 1.0, 1.0 );

	scatt = pow( max( 0.0, 0.2 * (scatt - 0.25) ), 2.0 );

	// Compute normal and Fresnel
	float	AO;
	vec3	n = normal( p, 0.0001, AO );
	vec3	F0 = FRESNEL * vec3( 0.5, 0.8, 1.0 );
	vec3	Fr = clamp( vec3( 0.0 ), vec3( 1.0 ), Fresnel( Fresnel_IORFromF0( F0 ), dot( -v, n ), 1.0 ) );

	// Compute direct lighting
	vec3	Light = l - p;
	float	dLight = length( Light );
			Light *= 1.0 / max( 0.001, dLight );
	float	shadow = Shadow( p, Light, dLight );
	vec3	colorT = (LightIntensity / (dLight*dLight)) * mix( C0, shadow * C1, 0.5 + 0.5 * dot( n, Light ));

	// Compute reflection
	vec3	p_refl = reflection( p, v, n );
	vec3	n_refl = normal( p_refl, 0.0001, AO );

			Light = l - p_refl;
			dLight = max( 0.05, length( Light ) );
			Light *= 1.0 / dLight;
	vec3	colorR = (LightIntensity / (dLight*dLight)) * mix( AO*C0, C1, 0.5 + 0.5 * dot( n_refl, Light ));

	vec3	color = mix( colorT, colorR, Fr );

	color += scatt * vec3( 1.0, 0.5, 0.3 );

	// Apply fog
	float	t = length( p - p_orig );
	float	fog = exp( -0.05 * t );
	color = mix( vec3( 1.0, 0.9, 0.8 ), color, fog );

	color = pow( color, vec3(1.0/2.2) );

	fragColor = vec4( color, 1.0 );
}

#define PI 3.14159265359

bool COMB;
bool ANTI_A;
float STEP;

float zoom;

bool keyToggle(int ascii) {
	return (texture(iChannel2,vec2((.5+float(ascii))/256.,0.75)).x > 0.);
}

float fwidth2(float k) { vec2 dk = vec2(dFdx(k),dFdy(k)); return length(dk); }

float Icos(float k, float S) { // cos with or without antialiasing
	float dk = 1.;
	if (ANTI_A) { // antialiasing: return 1/dk . int( cos(k), k= -dk/2..dk/2 )
		dk = fwidth(k);
		if (dk>0.) dk = 2.*sin(dk/2.)/dk ;
		dk *= 1./S; // *zoom; // scaling should not change intensity
	}
	return cos(k)*dk;

}
float rosace(float s, float z, float L, float S, float T, bool clip) { 
	s = S*(s-T); z = S*(z-T); // translation and scaling
	if (clip && ( (z<=0.) || (z>L) || (s-z<0.) || (s-z>L) )) return 0.;
	// canonical:  s = x+y,  z = x
	// <x,y> - <0,k> = <k,L> - <0,k> 
	// => x.(L-k)=k.(y-k) => k2 -k.s + zL = 0 => D = s2-4zL
	float d = 1.-4.*z*L/(s*s); if (d<0.) return 0.;

	d = sqrt(d);
	float k1 = s*(1.+d)/2., k2 = s*(1.-d)/2.;

#if 1
	float s1 = Icos(PI*k1/STEP,S) , s2 = Icos(PI*k2/STEP,S);
#else
	float s1 = (clip && ( (k1<0.)||(k1>L) )) ? 0. : Icos(PI*k1/STEP,S),
		  s2 = (clip && ( (k2<0.)||(k2>L) )) ? 0. : Icos(PI*k2/STEP,S);	
#endif

	return .5+.5* ( (COMB) ? max(s1,s2) : (s1+s2) );
}

vec2 tile1(vec2 uv, float L, float S) {
	S=1.;
	float r,g, s = uv.x+uv.y, S2=S*2.,T=L/2.;
	r   = rosace(s, uv.x,  L,S,0., true)   + rosace(s, uv.y,  L,S,0., true);
	r  += rosace(s, uv.x,  L,S2,T, true) + rosace(s, uv.y,  L,S2,T, true);

	uv.x = L-uv.x; s = uv.x+uv.y;
	g  = rosace(s, uv.x,  L,S,0., true)   + rosace(s, uv.y,  L,S,0., true);
	g += rosace(s, uv.x,  L,S2,T, true) + rosace(s, uv.y,  L,S2,T, true);

	return vec2(r,g);
}

vec2 tile2(vec2 uv, float L, float S) {
	S=1.;
	float r,g, s = uv.x+uv.y, S2=S*2.,T=L/2.;
	r  = rosace(s, uv.x,  L,S2,T, true) + rosace(s, uv.y,  L,S2,T, true);
	r += rosace(s, mod(uv.x+L/2.,L),  L,S2,T, true) + rosace(s, mod(uv.y+L/2.,L),  L,S2,T, true);

	uv.x = L-uv.x; s = uv.x+uv.y;
	g  = rosace(s, uv.x,  L,S2,T, true) + rosace(s, uv.y,  L,S2,T, true);
	g += rosace(s, mod(uv.x+L/2.,L),  L,S2,T, true) + rosace(s, mod(uv.y+L/2.,L),  L,S2,T, true);

	return vec2(r,g);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float t = 10.*iTime;
    float L = iResolution.y;
	vec2 uv = fragCoord.xy-.5-iResolution.xy/2.;
	vec2 trans = vec2(0.);

	// --- tuning

	vec4 mouse = abs((iMouse-.5)/iResolution.y);
	if (iMouse.z<=0.) {
		zoom = 1.+10.*(1.-cos(.02*t))/2.;
		STEP = 1.+50.*(1.-cos(.1*t))/2.;
		ANTI_A = true;
		COMB = ( mod(.02*t/(2.*PI),1.) > .5 );  
		trans = .5+iResolution.xy/2.*(1.+vec2(cos(.02*t),sin(.03*t))/(1.+zoom));
	}
	else
	{
		ANTI_A = !keyToggle(65);
		COMB   = (! keyToggle(64+13)); // 'M'

		trans = iMouse.xy;
		if (!keyToggle(64+26)) { zoom=mouse.y*10.; trans.y=iMouse.w; } 
		else 					 zoom = mouse.w*10.;
		if (keyToggle(64+19)) { STEP = mouse.x*100.; trans.x=abs(iMouse.z); }
		else				    STEP = 4.; // mouse.z*100.;
	}

	uv -= 10.*(trans-.5-iResolution.xy/2.);
	uv *= zoom;

	// --- display 

	vec3 col = vec3(0.); 
	col.rg = tile1(mod(uv,L),L,zoom);

	fragColor = (!keyToggle(67)) ? vec4(col.r+col.g) : vec4(col.r,col.g,0.,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 blob1 = vec3(cos(iTime) * 0.5 + 0.5, sin(iTime) * 0.5 +0.5, 2.),
		blob2 = vec3(sin(iTime) * 0.3 + 0.5, cos(iTime) * 0.2 + 0.2, 2.5),
		blob3 = vec3(cos(iTime) * 0.4 + 0.5, cos(iTime) * 0.3 + 0.6, 3.0);
	vec2 uv = fragCoord.xy / iResolution.xy;

	// multiplies the distances to the blobs together
	float shade = distance(uv, blob1.xy) * blob1.z;

	float shade1 = distance(uv, blob2.xy) * blob2.z;
	shade *= shade1;

	float shade2 = distance(uv, blob3.xy) * blob3.z;
	shade *= shade2;

	shade = 1.0 - abs(shade);

	// make it shine
	fragColor = vec4(shade * (cos(iTime) * 0.25 + 0.75),
						shade * (sin(iTime) * 0.25 + 0.75),
						shade * (cos(iTime) * 0.5 + 0.5), 1.0);
}

#define ANGLE 360./8.
#define R_THROB 20.
#define R_DOT 6.
#define PI 3.14159265359
#define PLAY_H 8.
#define PAUSE_DIV 2.
#define BAR_H 30.
#define STRIP_L BAR_H/2. - 3.
#define STRIP_H BAR_H/2. + 3.
#define STRIP_R_M 35.
#define STRIP_L_M 60.
#define CIRCLE_RAD 7.
#define VOLUME -15.
#define UI_SHADE 0.7

void volume(vec2 pos, inout vec3 col)
{
	pos -= vec2(iResolution.x-5., BAR_H/2.-PLAY_H);
	vec3 col_vol = pos.x < VOLUME ? vec3(UI_SHADE) : vec3(0.4);
	float l = step(0.,pos.y)
		*(1.-step(0.,pos.x))
		*(1.-smoothstep(0.,1.,pos.y-pos.x/2.5-PLAY_H*2.));
	col = mix(col, col_vol, l);
}

void circle(vec2 pos, inout vec3 col)
{
	float l = length(pos-vec2(clamp(iMouse.x, STRIP_R_M, iResolution.x-STRIP_L_M), BAR_H/2.));
	col = mix(vec3(UI_SHADE), col, smoothstep(CIRCLE_RAD, CIRCLE_RAD+1., l));
}

void strip(vec2 pos, inout vec3 col)
{
	float l = smoothstep(STRIP_L-2., STRIP_L+2., pos.y)
		* (1.-smoothstep(STRIP_H-.2,STRIP_H+2., pos.y))
		* step(STRIP_R_M ,pos.x)
		* (1.-step(iResolution.x-STRIP_L_M, pos.x));
	vec3 col_strip = pos.x < iMouse.x ? vec3(1.,0.,0.) : vec3(0.);
	col = mix(col,col_strip,l);
}

void bar(vec2 pos, inout vec3 col)
{
	if (pos.y < BAR_H)
	{
		col = vec3(.3);
	}
}

void gradient(vec2 pos, inout vec3 col)
{
	if (pos.y < BAR_H)
	{
		col *= 1. - pos.y/150.;
	}
}

void play(vec2 pos, inout vec3 col)
{
	pos -= vec2(10.,BAR_H/2.);
	float l = smoothstep(0., 1., pos.x)*smoothstep(0.,1.5,PLAY_H-abs(pos.y)-pos.x/1.5);

	col = mix(col,vec3(UI_SHADE),l);
}

void pause(vec2 pos, inout vec3 col)
{
	pos -= vec2(BAR_H/2.,BAR_H/2.);
	float l = step(-PLAY_H, pos.x)
		*(1.-step(PLAY_H, pos.x))
		*step(-PLAY_H, pos.y)
		*(1.-step(PLAY_H, pos.y));
	l*=(1.-step(-PAUSE_DIV, pos.x)
		*(1.-step(PAUSE_DIV, pos.x)));
	col = mix(col,vec3(UI_SHADE),l);
}

vec3 throbber(vec2 pos)
{
	vec3 col = vec3(.0);
	vec2 pol = vec2(length(pos), -atan(pos.y, pos.x)*180./PI);
	float ang = (mod(pol.y+ANGLE/2., ANGLE)-ANGLE/2.)*PI/180.;
	float index = pol.y*PI/180. - ang;
	index -= iTime*10.;
	index = mod(index,2.*PI);
	pos = pol.x*vec2(cos(ang), sin(ang));
	col = vec3(index)/2./PI * (1.-smoothstep(R_DOT-1.,R_DOT,length(pos - vec2(R_THROB,0.))));
	return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 pos = fragCoord.xy;
	vec2 mid = iResolution.xy/2.;
	vec3 col = throbber(pos-mid-vec2(0.,BAR_H/2.));
	bar(pos, col);
	//play(pos, col);
	pause(pos, col);
	strip(pos, col);
	circle(pos, col);
	volume(pos, col);
	gradient(pos, col);
	fragColor = vec4(col, 1.);
}

// inspired from https://www.shadertoy.com/view/MdjGWy#


// for faster eval, you can decrease proportionnaly NB and GAUSS_F to keep coverage
float GAUSS_F = .1;    // size of gabor blobs
#define NB 100.        // number or gabor blobs

#define SCALE 30.      // SCALING FACTOR for superimposing signal and fourier spaces

#define PI 3.14159265358979

// --- key toggles -----------------------------------------------------

bool keyToggle(int ascii) {
	return (texture(iChannel2,vec2((.5+float(ascii))/256.,0.75)).x > 0.);
}


