// Set up a camera looking at the scene.
// origin - camera is positioned relative to, and looking at, this point
// distance - how far camera is from origin
// rotation - about x & y axes, by left-hand screw rule, relative to camera looking along +z
// zoom - the relative length of the lens
void CamPolar( out vec3 pos, out vec3 ray, in vec3 origin, in vec2 rotation, in float distance, in float zoom, vec2 fragCoord)
{
	// get rotation coefficients
	vec2 c = vec2(cos(rotation.x),cos(rotation.y));
	vec4 s;
	s.xy = vec2(sin(rotation.x),sin(rotation.y)); // worth testing if this is faster as sin or sqrt(1.0-cos);
	s.zw = -s.xy;

	// ray in view space
	ray.xy = fragCoord.xy - iResolution.xy*.5;
	ray.z = iResolution.y*zoom;
	ray = normalize(ray);
//	localRay = ray;

	// rotate ray
	ray.yz = ray.yz*c.xx + ray.zy*s.zx;
	ray.xz = ray.xz*c.yy + ray.zx*s.yw;

	// position camera
	pos = origin - distance*vec3(c.x*s.y,s.z,c.x*c.y);
}

// return the distance to a sphere
float Psphere( vec3 p, vec3 c, float r )
{
	return length(p-c)-r;
}

float Pcapsule( vec3 p, vec3 a, vec3 b, float rmin, float rmax )
{
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	vec3 norm = normalize( b - a );
	float d = -dot( norm, a );
	float dist = dot( norm, p ) + d ;

	float rf = dist / length( b-a );

	float r = mix( rmin, rmax, rf*rf*rf );

    return length( pa - ba*h ) - r;
}

// polynomial smooth min (k = 0.1);
float blendPrim( float a, float b  )
{
	float k=0.32 ;
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
//	return min( a, b );
}

// Convert a vector into 2 Auler angle
vec2 ToEuler( vec3 p )
{
	return vec2( atan( p.z, p.x  ),
			     atan( sqrt(p.x*p.x+p.z*p.z), p.y)) ;
//	return vec2( atan( p.x, p.z ),
//			     atan( p.y, sqrt(p.x*p.x+p.z*p.z) )) ;
}

vec3 FromEuler( vec2 e )
{
	float c = sin(e.y);
	return vec3( c * cos(e.x),
			 	 cos(e.y),
			 	 c * sin(e.x)		);
}

float nbSpikes = 16. ;
float PI = 3.141592653 ;
vec3 ClassifyDir( vec3 p, out float id )
{
	vec2 angle = ToEuler( p )  ;	

	float div = 2.*PI / nbSpikes	;

	angle /= div ;
	angle = floor(angle);
	id = angle.x+angle.y ;
	angle = angle*div;
	angle += div*0.5 ;

	return FromEuler(angle);
//	return normalize(p) ;
//	if( p.y > 0. )
//		return vec3(0.,1.,0.);
//	else
//		return vec3(0.,-1.,0.);
}

float Pcapsule1( vec3 p, vec3 dir, float dist, float thickmin, float thickmax )
{
	return 	Pcapsule( p, dir*1., dir*(dist-3.), thickmin, thickmax );
}

vec4 QuatFromAxisAngle( vec3 axis, float angle )
{
	float theta = 0.5 * angle	;
	float sine = sin(theta)			;
	return vec4( cos(theta),
				 axis.x * sine,
				 axis.y * sine,
				 axis.z * sine );
}

vec3 TransformVecQuat( vec3 v, vec4 q )
{
	vec3 t = 2. * cross(q.xyz, v);
	return v + q.w * t + cross(q.xyz, t);
}

vec4 PlaneFromNormPos( vec3 n, vec3 p )
{
	return vec4( n.x, n.y, n.z, 
				 - dot( n, p ) );
}

float Pplane( vec3 p, vec4 n )
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

// Compute the Spiky object
float Spiky( vec3 p )
{
	// transform input position*
	vec4 q = QuatFromAxisAngle( normalize( vec3(cos(iTime*0.7),1,0) ), iTime*1.5 - 0.35*length(p) );
	p = TransformVecQuat( p, q );


	// Spiky !	
	float id ;
//	float p0 = Pcapsule( p, vec3(0,1,0), vec3(0,5,0), 0.05 );
	vec3 d = ClassifyDir(p,id);
	vec3 infos = vec3( 10., 0.3, 0.02 );
/*	float modc = mod(id,4. ) ;
	if( modc < 1. )
		infos = vec3( 11., 0.2, 0.04 );
	else if( modc < 2. )
		infos = vec3( 9., 0.4, 0.035 );
	else if( modc < 3. )
		infos = vec3( 8., 0.1, 0.055 );*/
	float p0 = Pcapsule1( p, d, infos.x, infos.y, infos.z );
	float p1 = Psphere( p, vec3(0,0,0), 2.5 );

	return blendPrim( p0, p1 );	// blend primitives	
//	return p1;	// blend primitives	
}

float PlaneY = -7. ;

// return the distance to the scene
float Scene( vec3 p )
{
	float s  = Spiky( p ) ;

	// Floor and background
	vec4 floorp = PlaneFromNormPos( vec3(0.,1.,0.), vec3(0.,PlaneY,0.) );
	float f = Pplane( p, floorp );

	return min( s, f );
//	return f ;
//	return p1 ;	// blend primitives
}


vec3 calcNormal( in vec3 p, out float valid )
{

	vec3 e = vec3(0.001,0.0,0.0);
	vec3 norm = vec3(Scene(p+e.xyy) - Scene(p-e.xyy),
						   Scene(p+e.yxy) - Scene(p-e.yxy),
						   Scene(p+e.yyx) - Scene(p-e.yyx) );
	float l = length( norm );
//	if( l > 0.0015 && l < 1000. )
//	{
		valid = 1. ;
		norm /= l ;
//	}
//	else
//	{
//		valid = 0. ;
//	}
	return norm ;		   
}

float calcAOSpiky( in vec3 pos, in vec3 nor )
{
    float ao = 1.0;
    float totao = 0.0;
    float sca = 15.0;
    for( int aoi=0; aoi<5; aoi++ )
    {
        float hr = 0.002 + 0.04*float(aoi*aoi);
        vec3 aopos =  nor * hr + pos;
        float dd = Spiky( aopos );
        totao += -(dd-hr)*sca;
        sca *= 0.5;
    }
    return 1.0 - clamp( totao, 0.0, 1.0 );
}

// compute soft shadow from Spiky object
float softShadowSpiky( in vec3 ro, in vec3 rd, in float mint, in float maxt, in float k )
{
	float res = 1.0;
    float dt = 0.02;
    float t = mint;
    for( int i=0; i<32; i++ )
    {
		if( t<maxt )
		{
        float h = Spiky( ro + rd*t );
        res = min( res, k*h/t );
        t += max( 0.05, dt );
		}
    }
    return clamp( res, 0.0, 1.0 );
}

float TraceSpiky( vec3 vpos, vec3 vdir )
{	
	float tmax = 80.0;
	float dist = 1.0;
	float t = 0.0;
	for( int i=0; i < 30; i++ )
	{
		if( dist<0.01 || dist>tmax ) 
			break;

		vec3 p = vpos + t*vdir;
		dist = Spiky( p );	

		t += dist ;
	}

	return t ;
}

float TraceFloor( vec3 vpos, vec3 vdir, vec3 viewDir )
{
	float tmax = 80.0;
	float dist = 1.0;
	float t = 0.0;
	for( int i=0; i < 30; i++ )
	{
		if( dist<0.01 || dist>tmax ) 
			break;

		vec3 p = vpos + t*vdir;

		vec4 floorp = PlaneFromNormPos( vec3(0.,1.,0.), vec3(0.,PlaneY,0.) );
		dist = Pplane( p, floorp );	

//		dist = min( dist, dot( p, -viewDir )-60. );

		t += dist ;
	}	

	return t ;
}

// main light
vec3 lightDir = vec3( 1, -0.8, 0.5 );
vec3 lightCol = vec3( 0.9, 0.9, 0.9 );

// pseudo sss color
vec3 sssCol = 2.*vec3( 0.12, 0.1, 0.07 );


// fog
vec3 fogCol = vec3(1.,1.,1.);
float fogCutIn = 20. ;
float fogPower = 0.04 ;


float ComputeFog( vec3 pos, vec3 vpos )
{
	// fog
	float lfog = length(pos-vpos) - fogCutIn ; // start the fog a much further from the camera
	return exp( -fogPower*lfog );	
}

vec3 ShadeSpiky( vec3 pos, vec3 vpos, vec3 vdir, vec3 viewDir )
{
	float validNorm = 0.;
	vec3 nor = calcNormal( pos, validNorm );
//		vec3 nor = ClassifyDir( pos );

	lightDir = normalize( lightDir );
	float l0i = clamp( -dot( nor, lightDir ), 0., 1. );
	float l0Sh = 1. ;
	if( l0i >0.02 )
		l0Sh = softShadowSpiky( pos, -lightDir, 5., 40., 3. );
	vec3 l0 = l0i * l0Sh * lightCol; 


	vec3 lightDir1 = vec3( -1, 1, 0 );
	vec3 lightCol1 = 0.5*vec3( 0.22, 0.2, -0.17 );

	vec3 lightDir2 = normalize( viewDir + vec3(0.5,0.1,0.) );
	vec3 lightCol2 = 0.5*vec3( 0.12, 0.12, 0.2 );

	float sshCoef = 1.-clamp( -dot( nor, viewDir ), 0., 1. );
	sshCoef = pow( sshCoef, 1. );

	// va calculer l'ambient occlusion
	float ao = 1.-calcAOSpiky( pos, nor );

	vec3 l1 = clamp( -dot( nor, lightDir1 ) * lightCol1, 0., 1. );
	vec3 l2 = clamp( -dot( nor, lightDir2 ) * lightCol2, 0., 1. );


	vec3 col = mix( l0+l1, l2, ao ) ;	
	col += sshCoef * sssCol * validNorm ;

	// apply fog
	float fi =  ComputeFog( pos, vpos );
	col = mix( fogCol, col, fi );	

	return col;
}

vec3 ShadeFloor( vec3 pos, vec3 vpos, vec3 vdir )
{
	float aoSh = calcAOSpiky( pos, vec3(0.,1.,0.) );
	float l0Sh = softShadowSpiky( pos, -lightDir, 4., 10., 3.5 );
	l0Sh *= aoSh;

	vec3 col = mix( vec3(0.85), 0.5*sssCol, 0.4*(1.-l0Sh) ) ;

	// compute reflection :
	vec3 rdir = reflect( vdir, vec3(0,1,0) );
	float ts = TraceSpiky( pos, rdir );
	if( ts < 20. )
	{
		vec3 hpos = pos+rdir*ts ;
		float attY = exp( -0.2*max(hpos.y+8.,0.) );
		col = mix( col, ShadeSpiky( hpos, vpos, rdir, vdir ), attY );
	}


	// apply fog
	float fi =  ComputeFog( pos, vpos );
	col = mix( fogCol, col, fi );		

	return col ;
//	return vec3(length( vpos-pos)/120.1);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 camRot = vec2(0.4,2.6)+vec2(-2.5,8.5)*(iMouse.yx/iResolution.yx) ;
	camRot.x = max( camRot.x, 0. );
	vec3 vpos, vdir;
	vec3 targetPos = vec3(0.,0.,0.);
	CamPolar( vpos, vdir, targetPos, camRot, 25.0 + 5.*cos(iTime*0.1), 1.5, fragCoord);
	vec3 viewDir = normalize( targetPos - vpos );

	vec2 uv = fragCoord.xy / iResolution.xy;

	vec2 p = -1.0 + 2.0*uv;
	p.x *= iResolution.x/iResolution.y;


	float ts = TraceSpiky( vpos, vdir );
	float tf = TraceFloor( vpos, vdir, viewDir );

	vec3 col ; 
	if( ts < tf )
		col = ShadeSpiky( vpos+vdir*ts, vpos, vdir, viewDir );
	else
		col = ShadeFloor( vpos+vdir*tf, vpos, vdir );


	fragColor = vec4(col,1.0);
}

#define FARCLIP    55.0

#define MARCHSTEPS 60
#define SHSTEPS    30
#define SHPOWER    1.5

#define MAT1       1.0

#define CLD1 iTime*0.35
#define CLD2 iTime*0.15

/***********************************************/

float hash(float n) { 
	return fract(sin(n)*43758.5453123); 
}

float noise3(vec3 x) {
    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);
    float n = p.x + p.y*57.0 + p.z*113.0;
    float res = mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                        mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y),
                    mix(mix( hash(n+113.0), hash(n+114.0),f.x),
                        mix( hash(n+170.0), hash(n+171.0),f.x),f.y),f.z);
    return res;
}

/***********************************************/
vec2 DE(vec3 p) {
    vec2 t=vec2( (p.y+4.0-texture(iChannel0,p.xz*0.002).x*5.0- texture(iChannel0,p.xz*0.03).x*0.2 )*0.5, 1.0);

    float g=p.y+2.4;
    if (t.x>g) t.x=g;

    return t;
}

/***********************************************/

void DEcloudsPWR(vec3 p, float t, inout float cpwr) {
    float c= -p.y-1.0-noise3(p*1.9+CLD1)*0.4;
    if (c>t) cpwr=c-t;
}

vec4 DEclouds(vec3 p, vec3 lig) {
    float c=-p.y-1.0-noise3(p*1.3+CLD1)*0.6;
        vec4 clr =vec4( mix( vec3(0.1,0.1,0.1), vec3(0.8,0.9,1.0), c*0.5 ), clamp(c,0.0,1.0));
        float ww=-p.y-1.0-noise3(p*1.5+0.6*lig+CLD2)*0.8;
	    float dif =  clamp((clr.w - ww)/0.6, 0.0, 1.0 );
        vec3 brdf = 0.9+ vec3(0.6, 0.5, 0.4)*dif;  
	clr.xyz *= brdf;
    clr.w*=0.35;
    clr.xyz*=clr.w;
    return clr;
}

/***********************************************/
vec3 normal(vec3 p) {
	vec3 e=vec3(0.01,-0.01,0.0);
	return normalize( vec3(	e.xyy*DE(p+e.xyy).x +	e.yyx*DE(p+e.yyx).x +	e.yxy*DE(p+e.yxy).x +	e.xxx*DE(p+e.xxx).x));
}
/***********************************************/
float calcSh( vec3 ro, vec3 rd, float s, float e, float k ) {
	float res = 1.0;
    for( int i=0; i<SHSTEPS; i++ ) {
    	if( s>e ) break;
        float h = DE( ro + rd*s ).x;
        res = min( res, k*h/s );
    	s += 0.02*SHPOWER;
    }
    return clamp( res, 0.0, 1.0 );
}

/***********************************************
void rot( inout vec3 p, vec3 r) {
	float sa=sin(r.y); float sb=sin(r.x); float sc=sin(r.z);
	float ca=cos(r.y); float cb=cos(r.x); float cc=cos(r.z);
	p*=mat3( cb*cc, cc*sa*sb-ca*sc, ca*cc*sb+sa*sc,	cb*sc, ca*cc+sa*sb*sc, -cc*sa+ca*sb*sc,	-sb, cb*sa, ca*cb );
}

/***********************************************/
#define sun normalize(vec3(-0.4,0.15,1.0))

vec3 sky(vec3 rd){
    float sa=max(dot(rd,sun),0.0);
    float v=pow(0.9-max(rd.y+0.1,0.0),12.0);
    vec3 s=mix(vec3(0.8,0.6,0.3),vec3(0.1,0.1,0.1),v);
        s=s+vec3(0.7,0.2,0.0)*sa*sa*0.5;
        s=s+vec3(1.0,2.0,1.0)*min(pow(sa,650.0)*1.5,0.3);
    return clamp(s,0.0,1.0);
}
/***********************************************/
vec3 fog(vec3 color, vec3 fcolor, float depth, float density){
	const float e = 2.71828182845904523536028747135266249;
	float f = pow(e, -pow(depth*density, 2.0));
	return mix(fcolor, color, f);
}

/***********************************************/
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
    p.x *= iResolution.x/iResolution.y;	
	vec3 ta = vec3(0.0, 0.0, 0.0);
	vec3 ro =vec3(0.0, 1.3, -5.0);
    vec3 lig=vec3(0.0,1.0,1.0);

ta.z+=iTime*2.5;
ro.z+=iTime*2.5;

/*	vec2 mp=iMouse.xy/iResolution.xy;
	rot(ro,vec3(mp.x,mp.y,0.0));
	rot(lig,vec3(mp.x,mp.y,0.0));	*/

	vec3 cf = normalize( ta - ro );
    vec3 cr = normalize( cross(cf,vec3(0.0,1.0,0.0) ) );
    vec3 cu = normalize( cross(cr,cf));
	vec3 rd = normalize( p.x*cr + p.y*cu + 2.5*cf );

	vec4 clds=vec4(0.0);
	float cpwr=0.0;

	//march terrain ...
	vec2 r=vec2(0.0);	
	float d=0.0;
	vec3 ww;
	for(int i=0; i<MARCHSTEPS; i++) {
		ww=ro+rd*d;
		r=DE(ww);
    	DEcloudsPWR(ww,r.x,cpwr);
        if( abs(r.x)<0.0 || r.x>FARCLIP ) break;
        d+=r.x;
	}
    r.x=d;

    //march clouds ...
    float t=0.0;
    for(int i=0; i<MARCHSTEPS; i++) {
		vec3 p=ro+rd*t;
		clds+=DEclouds(p,lig)*(1.0 - clds.w);
		t+=0.6;
	}

	//colors ... 

    vec3 col=sky(rd);

	if( r.x<FARCLIP ) {
            vec3 nor=normal(ww);
        	float amb= 1.0;		
        	float dif= clamp(dot(nor, lig), 0.0,1.0);
        	float bac= clamp(dot(nor,-lig), 0.0,1.0);
        	float sh= calcSh(ww, lig, 0.01, 2.0, 4.0);

    		if (r.y==MAT1) ww=vec3(1.3)* texture(iChannel1, ww.xz*0.2).xyz;
    	    ww *= 0.5*amb + 0.4*dif*sh + 0.05*bac;

		   //faking sunlight like a big boy here
    	   vec3 colf=mix(col*0.8,ww, clamp(abs(nor.z*1.8), 0.0,1.0));     

           colf = mix( colf, clds.xyz, clamp(cpwr,0.0,1.0) );

           col=fog(colf, col, r.x, 0.03);
	} else {
	    //clouds top
	    ro.z-=iTime*100.0;
	    float sd=dot(rd,sun);
	    float t = clamp( pow(1.0-0.5*rd.y, 8.0), 0.0,1.0);
	    clds=vec4(0.0);
	        for (int i=0; i<MARCHSTEPS; i++) {
                float c = (float(i-MARCHSTEPS)*2.0+3.0-ro.y) / rd.y;
                vec3 cpos = ro + rd*c;
                     cpos*=vec3(0.007,0.025,0.0062);
                float a = smoothstep(0.0, 1.0, noise3( cpos ))*0.8; 
                vec3 lc = mix(vec3( 0.8, 0.8, 0.9 ), vec3( 0.1,0.1,0.1 ), a); 
                a=(1.0-clds.w)*a; 
                clds += vec4(lc*a, a); 
                if (clds.w>0.9) break;
            }

            clds.rgb /= clds.w+0.001;
            col=mix( col, clds.rgb , clds.w*(1.0-t) );            
	} 

	fragColor = vec4( col, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	const float zoom_rate = 1.5;
	const float max_zoom = 25.0; // also time spent in each zoom
	const int bailout = 400;

	int zoom_idx = int(mod(iTime, max_zoom * 3.0) / max_zoom);

	// select zoom target
	float center_r;
	float center_i;
	if (zoom_idx == 0) {
		center_r = 0.28595756563098457;
		center_i = 0.011516107407704111;
	}
	else if (zoom_idx == 1) {
		center_r = -0.70024170070240843;
		center_i = -0.26508919693469957;
	}
	else {
		center_r = -1.1617553883109397;
		center_i = -0.19740616627583873;
	}

	float zoom = pow(zoom_rate, mod(iTime, max_zoom));

	float w = float(iResolution.x);
	float h = float(iResolution.y);

	float pos_x = float(fragCoord.x) / w;
	float pos_y = float(fragCoord.y) / h;

	float cx = center_r + (pos_x - 0.5) / zoom;
	float cy = center_i + ((pos_y - 0.5) * (h / w)) / zoom;

	float x = 0.0;
	float y = 0.0;

	int j;
	for (int i = 0; i < bailout; i++)	{
		float xx = x * x - y * y + cx;
		y = 2.0 * x * y + cy;
		x = xx;
		if (x*x + y*y >= 4.0) {
  			j = i;
			break;
		}
		j = 0;
	}

  	vec3 col;
	float c;
	if (j == 0) {
		c = 0.0;
	}
	else {
		c = float(j) / float(bailout);
	}

	if (zoom_idx == 0) {
		col = vec3(3.0 * c, 2.0 * c, 0.0 * c);
	}
	else if (zoom_idx == 1) {
		col = vec3(0.5 * c , 1.5 * c, 1.1 * c);
	}
	else {
		col = vec3(0.0 * c , 2.5 * c, 3.0 * c);
	}
	fragColor = vec4(col, 1.0);
}

const float pi = 3.141593;
const float cycle = 2.0*pi;

float usin(float x) {return 0.5+0.5*sin(x);}
float ucos(float x) {return 0.5+0.5*cos(x);}
vec3 icol(float r, float g, float b) { return vec3(r/255., g/255., b/255.);}

vec3 spiral(vec2 uv) {
	float ang = atan(uv.x, uv.y);	
	float len = length(uv);
	float s = iTime;

	vec3 c1 = icol(170.,59.,91.);
	vec3 c2 = icol(249.,249.,79.);
	vec3 c3 = icol(249.,167.,24.);
	vec3 c4 = icol(167.,232.,62.);

	float rw = len - len*0.5*usin(s)*ucos(ang*8.0 + s);
	float r = usin(rw*cycle - s*2.0);


	float g = ucos(len*24.0 - usin(s)*8.0 - s*4.0 + ang);

	float b = usin(sin(s)/len*len*8.+ang*8.0 - (ucos(len-pi*s*2.))*8.0);

	float si = mod(s, 4.0);


	return r*c1 + g*c2 + b*c3;
}

vec3 bg(vec2 uv) {
	uv.y = 0.5*(uv.y + 1.0);
	vec3 c1	= icol(230.,181.,52.);
	vec3 c2	= icol(40.,72.,140.);
	vec3 c3 = icol(238.,238.,91.);

	float i = exp(-2.0*uv.y);

	return i*c2 + (1.0-i)*c1;

}

float grid(vec2 uv) {
	float lin =0.001;
	float blur = 2.0;
	float ph = blur/iResolution.y;

	float square = 1.0/10.0;

	float x = mod(uv.x, square);
	if (abs(uv.x) < lin*4.) return 0.;
	if ( x < lin) return 0.;
	if (x < lin + ph) return x/(ph+lin);

	float y = mod(uv.y, square);
	if (abs(uv.y) < lin*4.) return 0.;
	if ( y < lin) return 0.;
	if (y < lin + ph) return y/(ph+lin);

	return 1.0;
}

float line(vec2 uv, float f) {
	float wide = 0.02;

	vec3 col = vec3(1.,0.,0.);

	if (abs(f-uv.y) < wide) return 1.;
	return 0.;
}

float vector(vec2 uv, vec2 v, vec2 p) {
	uv = uv - p;
	float d = length(uv - v * dot(uv, v)/dot(v,v));	
	if (d > 0.02 ||
	   	uv.y * v.y < 0. ||
		uv.x * v.x < 0. ||
	   length(uv) > length(v)) return 0.;
	return 1.;
}

vec2 matrix2(vec2 uv, mat2 m, vec2 p) {
	return vec2(vector(uv, m[0], p), vector(uv, m[1], p));	
}
mat2 rot(float t) {
	mat2 r;
	r[0] = vec2(sin(t), cos(t));
	r[1] = vec2(cos(t), -sin(t));
	return r;
}

vec2 distort(vec2 uv) {
	float t = iTime;
	vec2 polar = vec2(length(uv), atan(uv.x, uv.y));
	float th = t;;

	mat2 scale;
	float s = 4.0;
	scale[0] = vec2(s, 0.);
	scale[1] = vec2(0., s);
	return scale * uv;
}

mat2 diag() {
	mat2 q, l, qt;
	q[0] = vec2(0.96, .28);
	q[1] = vec2(.28, .96);

	l[0] = vec2(5., 2.);
	l[1] = vec2(2., 3.);

	qt[0] = vec2(0.96, .28);
	qt[1] = vec2(.28, .96);

	return q * l * qt;
}

const int ev_n = 32;
vec3 ev(vec2 uv) {
	float t = iTime;
	vec3 cx = vec3(1.,0.,0.);
	vec3 ce = vec3(0., 1., 0.);
	vec3 cr = vec3(0.);
	float s = 1./float(ev_n);;
	vec2 v;
	mat2 e;

	// this vector is used fo eigenvalues
	e[0] = vec2(sin(t)*5., cos(0.3*t)*2.);
	e[1] = vec2(sin(t*0.5)*2.,-cos(t*1.2)*3.);

	for (int i = 0; i < ev_n; i++) {
		v = rot(float(i)*s*cycle+t)*vec2(1.,0.);
		cr += cx * vector(uv, v, vec2(0.));
		cr += ce * vector(uv, e*v, v);
	}
	return cr;
}



