// ray marching
const int max_iterations = 260;
const float eps = 0.001;
const float clip_far = 10000.0;

// math
const float PI = 3.14159265359;
const float DEG_TO_RAD = PI / 180.0;

const vec3 fog = vec3(0.4, 0.5, 1.0);



float dist_box( vec3 pos, vec3 size, float radius ) {

	float radFac = 1.1 + sin(iTime) * 0.9;
	radius = radFac;
	size-=radFac;

	pos = mod(pos,vec3(8.0)) - vec3(4.0);

	return length( max( abs( pos ) - size, 0.0 ) ) - radius;
}

// get distance in the world
float dist_field( vec3 pos ) {
	return  dist_box( pos, vec3( 2.0 ), 0.4 );
}

// phong shading
vec3 shading( vec3 v, vec3 n, vec3 eye, float distance, vec2 uv, float steps ) {
	// ...add lights here...

	float shininess = 122.0;

	vec3 final = vec3( 0.0 );

	vec3 ev = normalize( v - eye );
	vec3 ref_ev = reflect( ev, n );

	{
		vec3 light_pos   = vec3( 20.0, 20.0, 20.0 );
		vec3 light_color = vec3( 0.3, 0.7, 0.7 );

		vec3 vl = normalize( light_pos - v );

		float diffuse  = max( 0.0, dot( vl, n ) );
		float specular = max( 0.0, dot( vl, ref_ev ) );
		specular = pow( specular, shininess );

		final += light_color * ( diffuse + specular ); 
	}

	{
		vec3 light_pos   = vec3( -20.0, -2.0, -20.0 );
		vec3 light_color = vec3( 0.3, 0.4, 0.7 );

		vec3 vl = normalize( light_pos - v );

		float diffuse  = max( 0.0, dot( vl, n ) );
		float specular = max( 0.0, dot( vl, ref_ev ) );
		specular = pow( specular, shininess );

		final += light_color * ( diffuse + specular ); 
	}

	float fogFactor = min(1.0, 1.0 - 10.0 / distance);

	if(uv.x < 0.5)
		return final + (fog - final) * fogFactor;
	else if(uv.y < 0.5)
		return vec3(n);
		else
		return vec3(1.0 - steps);
}

// get gradient in the world
vec3 gradient( vec3 pos ) {
	const vec3 dx = vec3( eps, 0.0, 0.0 );
	const vec3 dy = vec3( 0.0, eps, 0.0 );
	const vec3 dz = vec3( 0.0, 0.0, eps );
	return normalize (
		vec3(
			dist_field( pos + dx ) - dist_field( pos - dx ),
			dist_field( pos + dy ) - dist_field( pos - dy ),
			dist_field( pos + dz ) - dist_field( pos - dz )			
		)
	);
}

// ray marching
float ray_marching( vec3 origin, vec3 dir, float start, float end, out float steps ) {
	float depth = start;
	steps = 0.0;
	for ( int i = 0; i < max_iterations; i++ ) {
		float dist = dist_field( origin + dir * depth );
		steps += 1.0 / float(max_iterations);
		if ( dist < eps ) {
			return depth;
		}
		depth += dist;
		if ( depth >= end) {
			return end;
		}

	}
	return end;
}

// get ray direction
vec3 ray_dir( float fov, vec2 size, vec2 pos ) {
	vec2 xy = pos - size * 0.5;

	float cot_half_fov = tan( ( 90.0 - fov * 0.5 ) * DEG_TO_RAD );	
	float z = size.y * 0.5 * cot_half_fov;

	return normalize( vec3( xy, -z ) );
}

// camera rotation : pitch, yaw
mat3 rotationXY( vec2 angle ) {
	vec2 c = cos( angle );
	vec2 s = sin( angle );

	return mat3(
		c.y      ,  0.0, -s.y,
		s.y * s.x,  c.x,  c.y * s.x,
		s.y * c.x, -s.x,  c.y * c.x
	);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 col = vec3(0.0);

	// default ray dir
	vec3 dir = ray_dir( 45.0, iResolution.xy, fragCoord.xy );

	// default ray origin
	vec3 eye = vec3( 0.0, 0.0, iTime );

	// rotate camera
	mat3 rot = rotationXY(-iMouse.yx / 100.0);
	dir = rot * dir;
	//eye = rot * eye;

	// ray marching
	float steps;
	float depth = ray_marching( eye, dir, 0.0, clip_far, steps );

	vec3 pos = eye + dir * depth;
	vec3 n = gradient( pos );

	col = shading( pos, n, eye, depth, fragCoord.xy / iResolution.xy, steps );


	fragColor = vec4( col, 1.0 );
}

