float rnd(vec2 uv) { return fract(sin(5423.1*uv.x-65.543*uv.y)*1e5); }
float rnd0(vec2 uv) { return (rnd(uv)>.5) ? 1. : 0. ; }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float t = iTime;
    vec2 uv = (fragCoord.xy -.5*iResolution.xy)/ ( floor(iResolution.y/64.)*64.);


	vec2 mouse = iMouse.xy / iResolution.xy;
	if (iMouse.z<=0.) {
		mouse.x = .5*(1.+cos(.1*t));
		mouse.y = .5*(1.+cos(t));
	}

	uv *= pow(2.,2.-6.*mouse.x);
#define REPEAT (7./8.)
	vec2 vrep = uv/REPEAT +.4;
	uv = mod(vrep,1.)*REPEAT -.4 ;  		 // repeat the macro pattern
	float irep = mod( floor(vrep.x)+floor(vrep.y) ,2.);

	vec2 v = floor(uv*16.);	
	float fv = mod(v.x+v.y,2.);					 		// large checker	
	vec2 sv = mod(floor(uv*16.*4.-.5),2.);		 		// for small squares
	float fsv = sv.x+sv.y + 1.-sv.x*sv.y;  		 		// eliminates odd rows and cols
	vec2 m = floor(uv*16.*2.);					 		// for mask
	float fm = m.x+m.y;							 		// half checker
	fm += ((uv.x-1./32.)*(uv.y-1./32.)<0.) ? 1. : 0.;   // translates by 1 row
	fm += irep;

	t = mod(floor(iTime),2.);
	fm += t;
	// fm += rnd0(m+t);

	if (length(v)>6.25) fm = 0.;

	fsv = mod(fsv,2.)*mod(fm,2.)*mouse.y;

#if 0
	fv = mod(fv+fsv,2.); 
#else
	fv =  (fv > .5) ? 1.-fsv : fsv;
#endif

	fragColor = vec4(fv);
}

vec4 circle(vec2 p, vec2 center, float radius)
{
	return mix(vec4(1,1,1,0), vec4(1,0,0,1), smoothstep(radius + 0.005, radius - 0.005, length(p - center)));
}

vec4 scene(vec2 uv, float t)
{
	return circle(uv, vec2(0, sin(t * 16.0) * (sin(t) * 0.5 + 0.5) * 0.5), 0.2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 resol = iResolution.xy / vec2(6,1);
	vec2 coord = mod(fragCoord.xy, resol);
	float view = floor(fragCoord.x / resol.x);

	vec2 uv = coord / resol;
	uv = uv * 2.0 - vec2(1);
	uv.x *= resol.x / resol.y;

	fragColor = vec4(1,1,1,1);

	float frametime = (60. / (floor(view / 2.) + 1.));
	float time = floor((iTime + 3.) * frametime) / frametime;
	vec4 mainCol = scene(uv, time);

	vec4 blurCol = vec4(0,0,0,0);
	for(int i = 0; i < 32; i++)
	{
		if ((i < 8 || view >= 2.0) && (i < 16 || view >= 4.0))
		{
			blurCol += scene(uv, time - float(i) * (1. / 15. / 32.));
		}
	}
	blurCol /= pow(2., floor(view / 2.) + 3.);

	if (mod(view, 2.) == 0.)
		fragColor = mainCol;
	else
		fragColor = blurCol;

	if (iMouse.z > 0. && mod(view, 2.) == mod(floor(iMouse.z / resol.x), 2.))
		fragColor = vec4(0,0,0,1);
}

// logistic map
// http://en.wikipedia.org/wiki/Logistic_map
// r varies with time
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float r=4.0*sin(0.5*iTime);
	vec2 p1 = 2.0*fragCoord.xy/iResolution.xy-0.5;
	vec2 p2 = p1;

	float hit = 3.0;
	for(int i=0;i<25;i++)
	{
		p1=r*p1*(1.0-p1);

		float d=distance(p1,p2);
		if (d<hit)
		    hit=d;
	}
	hit=5.0*clamp(hit,0.0,0.2);
	fragColor = vec4(hit,hit,hit,1.0);
}

// logistic map
// http://en.wikipedia.org/wiki/Logistic_map
// zoom varies with time
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p1 = (fragCoord.xy/iResolution.xy-vec2(0.5,0.5))*8.0*(0.0+sin(0.1*iTime));
	vec2 p2 = p1;
	float hit = 3.0;
	for(int i=0;i<50;i++)
	{
		p1.x=p1.y*p1.x*(1.0-p1.x);
		float d=distance(p1.x,p2.x);
		if (d<hit)
		    hit=d;
	}
	hit=20.0*clamp(hit,0.0,0.05);
	hit = 1.0-hit;
	hit=hit*hit;
	fragColor = vec4(hit,hit,hit,1.0);
}

