void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0*(fragCoord.xy/iResolution.xy) - 1.0;
    float col = 0.0;
    float vol = texture(iChannel0, vec2(0.5)).x;

    col += sin((17.0 - (9.0 * vol)) *
               (pow(uv.x * (3.0 + (10.0 * vol) + sin(iTime * 0.9)), 2.0) +
                pow(uv.y * (11.0 + (-10.0 * vol) + sin(iTime * 1.1)), 2.0)));

    fragColor = vec4(col,col,col,1.0);
}

#define LIGHTING_DIRECT /* uncomment to enable direct lighting calculation with shadows */
#define LIGHTING_BOUNCE_ONCE /* uncomment to enable the first reflection */
//#define LIGHTING_BOUNCE_TWICE /* uncomment to enable a second reflection */

struct Slide {
	vec2 pos;
    vec2 normal;
};

#define SLIDE_COUNT 6

mat2 rotation(float theta) {
	return mat2(cos(theta), -sin(theta), sin(theta), cos(theta));  
}

float boxDistance(vec2 pos, vec2 center, vec2 normal, vec2 scale) {
    mat2 rot = mat2(normal.x, -normal.y, normal.y, normal.x);
	vec2 delta = rot * (pos - center);
    delta = clamp(delta, -scale, scale);
    vec2 clampPos = center + delta * rot;
    return length(clampPos - pos);
}

vec2 map(vec2 pos, Slide slides[SLIDE_COUNT]) {
    float dist = 100.0;
    float hit = -1.0;
    for (int i = 0; i < SLIDE_COUNT; ++i) {
     	float thisDist = boxDistance(pos, slides[i].pos, slides[i].normal, vec2(0.2, 0.01));
        if (thisDist < dist) {
         	hit = float(i);
            dist = thisDist;
        }
    }
    return vec2(dist, hit);
}

vec3 trace(vec2 origin, vec2 dir, float far, Slide slides[SLIDE_COUNT]) {
 	float t = 0.0;
    float hit = -1.0;
    for (int i = 0; i < 16; ++i) {
     	vec2 pos = origin + dir * t;
        vec2 d = map(pos, slides);
        t += d.x * 1.0;
        hit = d.y;
        if (t >= far) {
         	return vec3(t, 1.0, -1.0);   
        }
        if (d.x < 0.0) {
         	return vec3(t, 0.0, hit);   
        }
    }
	return vec3(t, 0.0, hit);
}

float reflection(vec2 a, vec2 b, float power, float fresnel, Slide slides[SLIDE_COUNT]) {
    float r = 0.0;

#ifdef LIGHTING_DIRECT
	vec2 delta = b - a;
    float len = length(delta);
	vec3 visa = trace(a, delta/len, len, slides);
	r += visa.y / (1.0 + len * len * power);
#endif

#ifdef LIGHTING_BOUNCE_ONCE
    for (int i = 0; i < SLIDE_COUNT; ++i) {
        vec2 deltai = a - slides[i].pos;
        vec2 refi = slides[i].pos - reflect(deltai, slides[i].normal);

        vec2 rayi = refi - b;
        float leni = length(rayi);
        rayi /= leni;
        vec3 pathi = trace(b, rayi, leni, slides);
        if (pathi.z != float(i)) {
            continue;
        }
		vec2 hiti = b + rayi * pathi.x;

        vec2 delta = hiti - a;
        float len = length(delta);
        delta /= len;
        vec3 path = trace(a, delta, len*0.99, slides);
        if (path.y == 0.0) {
            continue;   
        }

        float pathLength = leni;
        float prod = abs(dot(-normalize(delta), slides[i].normal));
        r += (1.0 - prod * fresnel) / (1.0 + pathLength * pathLength * power);
    }
#endif // LIGHTING_BOUNCE_ONCE

#ifdef LIGHTING_BOUNCE_TWICE
	for (int i = 0; i < SLIDE_COUNT; ++i) {
        vec2 deltai = a - slides[i].pos;
        vec2 refi = slides[i].pos - reflect(deltai, slides[i].normal);
        for (int j = 0; j < SLIDE_COUNT; ++j) {
            if (j==i) {
                continue;
            }

        	vec2 deltaj = refi - slides[j].pos;
        	vec2 refj = slides[j].pos - reflect(deltaj, slides[j].normal);

            vec2 rayj = refj - b;
            float lenj = length(rayj);
            rayj /= lenj;
            vec3 pathj = trace(b, rayj, lenj, slides);
            if (pathj.z != float(j)) {
                continue;
            }
            vec2 hitj = b + rayj * pathj.x;

            vec2 rayi = refi - hitj;
            float leni = length(rayi);
            rayi /= leni;
            hitj += rayi * 0.01;
            vec3 pathi = trace(hitj, rayi, leni, slides);
            if (pathi.z != float(i)) {
                continue;
            }
            vec2 hiti = rayi + hitj * pathi.x;

            vec2 delta = hiti - a;
            float len = length(delta);
            delta /= len;
            vec3 path = trace(a, delta, len*0.99, slides);
            if (path.y == 0.0) {
                continue;
            }

        	float pathLength = lenj;
        	float prod = abs(dot(-normalize(delta), slides[i].normal));
            prod *= abs(dot(-normalize(rayi), slides[j].normal));
        	r += (1.0 - prod * fresnel) / (1.0 + pathLength * pathLength * power);
        }
    }
#endif // LIGHTING_BOUNCE_TWICE

    return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    Slide slides[SLIDE_COUNT];

    slides[0].pos = vec2(0.0, 0.0);
    slides[0].normal = vec2(0.0, 1.0) * rotation(-iTime*0.5);

    slides[1].pos = vec2(-0.2, -0.8);
    slides[1].normal = vec2(0.0, 1.0) * rotation(-3.14/2.5);

    slides[2].pos = vec2(0.3, -0.8);
    slides[2].normal = vec2(0.0, 1.0) * rotation(3.14/2.5);

    slides[3].pos = vec2(1.2, 0.5);
    slides[3].normal = vec2(0.0, 1.0) * rotation(3.14/8.0);

    slides[4].pos = vec2(-1.0, -0.2);
    slides[4].normal = vec2(1.0, 0.0);

    slides[5].pos = vec2(-1.0, 0.2);
    slides[5].normal = vec2(1.0, 0.0);

    vec2 lightPos = vec2(cos(iTime), sin(iTime)) * 0.6;

    if (iMouse.z >= 1.0) {
    	lightPos = iMouse.xy / iResolution.xy * 2.0 - 1.0;
    	lightPos.x *= iResolution.x / iResolution.y;
    }

    float lighting = reflection(lightPos, uv, 1.0, 0.0, slides);
    lighting = 0.3 + lighting * 0.7;

    vec2 delta = lightPos - uv;
    float dist = 1.0 / (1.0 + dot(delta, delta) * 100.0);
    float orb = clamp(pow(0.1 + dist,8.0),0.0,1.0);

    vec3 colour = vec3(1.0);
    colour.y *= 0.5 + 0.5 * cos(1.57 - iTime / 10.0);
    colour.z *= 0.5 + 0.5 * cos(1.57 + iTime / 20.0);

	vec3 tex = texture(iChannel0, uv * 2.0).xyz;

    vec3 ground = (tex * lighting + orb) * colour;

    float solid = abs(map(uv,slides).x) < 0.01 ? 1.0 : 0.0;

    vec3 final = mix(ground, vec3(lighting,lighting,lighting), solid);

    fragColor = vec4(final, 1.0);
}

#define PI 3.1415926535897932384626433832795

float map( in vec3 p) {
    vec3 firstSpherePosition =  vec3(0.0, 0.0, 2.0);
    vec3 q = p;
    q.xz = mod(p.xz + firstSpherePosition.xz + 2.0, 4.0) - 2.0;
    float dSphere = length( q ) - 1.0 + cos(iTime) * .15;

    float dPlane = p.y + 1.0;

    float blendingRatio = 1.;
    float ratio = clamp(.5 + .5 * (dSphere - dPlane) / blendingRatio, 0., 1.);

    float dist = mix(dSphere, dPlane, ratio) - blendingRatio * ratio * (1. - ratio);

    return dist;
}

vec3 calcNormal (in vec3 p) {
	vec2 e = vec2(0.0001, 0.0);
    return normalize(vec3( 	map(p + e.xyy) - map(p - e.xyy),
                            map(p + e.yxy) - map(p - e.yxy),
                          	map(p + e.yyx) - map(p - e.yyx)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0 * uv;
    p.x *= iResolution.x/iResolution.y;

    vec3 ro = vec3(0.0, 0.0, 0.0);
    vec3 rd = normalize( vec3( p, 1.0 ));
    float rotation = (1.0 - (iMouse.x / iResolution.x) * 2.0) * PI;
    vec3 direction = vec3(cos(rotation + PI * .5), 0.0, sin(rotation + PI * .5));
    rd = normalize(direction + vec3( cos(rotation) * p.x, p.y, sin(rotation) * p.x ));

    vec3 col = vec3(.8);

    float tmax = 100.0;
    float h = 1.0;
    float t = 0.0;

    for(int i = 0; i < 100; i++) {
        if (h < 0.00001 || h > tmax) break;
        h = map( ro + rd * t);
        t += h;
    }

    if (t < tmax) {
        col = calcNormal(ro + rd * t);
    }

	fragColor = vec4(col, 1.0);
}

const vec3 background  = vec3(0.0, 0.0, 0.2);
const vec3 light_1     = vec3(4.0, 8.0,  3.0);
const vec3 light_2     = vec3(-4.0, 8.0, -7.0);
const vec2 eps         = vec2(0.001, 0.0);
const int maxSteps     = 164;

//const float time = iTime;

vec3 shade(vec3 color, vec3 point, vec3 normal, vec3 rd)
{

	vec3 dtl       = normalize(light_1 - point);
	float diffuse  = dot(dtl, normal); //diffuse
	float specular = 0.75 * pow(max(dot(reflect(dtl, normal), rd), 0.0), 64.0); //specular
	vec3 c = (diffuse + specular) * color * 0.85;

	dtl      =  normalize(light_2 - point);
	diffuse  = dot(dtl, normal); //more diffuse
	specular = 0.9 * pow(max(dot(reflect(dtl, normal), rd), 0.0), 128.0); //more specular
	return clamp( c + (diffuse + specular) * 0.25 * color, 0.0, 1.0);
}

// estimates the distance from Point p to implicit given geometry
float distanceEstimator(vec3 p)
{
	float t = mod(iTime, 70.0);
	p = p - vec3(t, t * 0.5, t * 0.3);

	float rpm = 1.0;
	vec3 repeater = mod(p, vec3(rpm)) - 0.5 * vec3(rpm);

	float sphere = length(repeater) - 0.06 * rpm;

	vec2 cylinder = vec2(min(length(repeater.xz) - 0.59, .015), repeater.y);
	float doughnut = length(cylinder) - 0.015 * rpm;
	//return doughnut;
	return min(doughnut, sphere);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	float ratio  = iResolution.x / iResolution.y;
	vec2 fragment = fragCoord.xy / iResolution.xy;

	vec2 uv = -1.0 + 2.0 * fragment;
	uv.x *= ratio;

	//camera setup taken from iq's raymarching box: https://www.shadertoy.com/view/Xds3zN
	vec3 ta = vec3( 0.0, 0.0, -3.5 );
	vec3 ro = vec3( -3.0 + 3.2*cos(0.3*iTime + 6.0), 4.0, 1.0 + 3.2*sin(0.2*iTime + 6.0) );
	vec3 cw = normalize( ta-ro );
	vec3 cp = vec3( 0.0, 1.0, 0.0 );
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
	vec3 rd = normalize( uv.x*cu + uv.y*cv + 2.5*cw );

	vec3 col             = background;
	float t              = 0.0;
	vec3 p               = vec3(0.0);

	// march
	float steps = 0.0;
	for (int i = 0; i < maxSteps; i++) {
		p = ro + t * rd;
		float distanceEstimation = distanceEstimator(p);
		if (distanceEstimation > 0.0005) {
			t += distanceEstimation;
			steps += 1.0;
		} else {
			break;
		}
	}

	//vec3 c = (cos(p * 0.5 - 0.5) + 1.0) / 2.0;
    vec3 c = vec3(1.0);
	vec3 normal = normalize(vec3(distanceEstimator(p + eps.xyy) - distanceEstimator(p - eps.xyy),
								 distanceEstimator(p + eps.yxy) - distanceEstimator(p - eps.yxy),
								 distanceEstimator(p + eps.yyx) - distanceEstimator(p - eps.yyx)));

	col = shade(c, p, normal, rd);
	//col = mix(col, background, smoothstep(0.8, 1.0, float(steps) / float(maxSteps)));


	fragColor = vec4(col, 1.0); 

}

#ifdef GL_ES
precision mediump float;
#endif


vec2 getNewUV( vec2 uv, vec2 pos, float radius, float strength, out float dist)
{
	vec2 fromUVToPoint = pos - uv;
	dist = length( fromUVToPoint );

	float mag = (1.0 - (dist / radius)) * strength;
	mag *= step( dist, radius );

	return uv + (mag * fromUVToPoint);
}

vec4 proceduralTexture2D( vec2 uv, vec2 resolution )
{
	vec2 fragCoord = (uv  * resolution.x );
	vec3 brightColor = vec3( 1.0, 1.0, 1.0 );
	vec3 darkColor = vec3( 0.0, 0.0, 0.0 );
	vec2 p = floor( (fragCoord.xy / resolution.x) * 10.0 );


	float t = mod(p.x + p.y, 2.0) * 10.0;
	vec3 color = mix( darkColor, brightColor, t);
	return vec4( color, 1.0 );	
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
	vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
	uv.x *= iResolution.x / iResolution.y;

	float radius = 0.50;

	float ct = cos( iTime / 3.0 );
	float st0 = sin( iTime / 3.0 );
	float st1 = sin( iTime );

	vec2 origin = vec2( 0.0, 0.0 );
	float x = origin.x + ( ct * st0) * 2.70;
	float y = origin.y + ( st1 ) * 0.50;
	vec2 pos = vec2(x,y);


	float dist = 0.0;
	vec2 newUV = getNewUV( uv, pos, radius, 0.5, dist);

	float start = 0.42;
	float glowT = sin(iTime)*0.5 + 0.5;
	float outlineRadius = radius +  (1.0-glowT)*0.01 + (glowT * 0.08);
	vec4 highlight = vec4( 0.00, 0.00, 0.00, 1.0 );
	float t = (outlineRadius - start) / max( (dist - start), 0.01);
	highlight = mix( vec4( 0.00, 0.00, 0.00, 1.0 ), vec4( 0.00, 0.50, 0.80, 1.0 ), t);

	vec4 color = proceduralTexture2D( newUV, iResolution.xy ) + highlight;
	color.a = 1.0;

	fragColor = color;

}

