	q = min(p,mod(p+.5*c,c)-.5*c); // less than 0
    q = max(q,p-(lim-vec3(1.0))*c); // more than limit
    return scene1(q);
}

float opRepLimitCenter_sceneT(vec3 p,vec3 c,vec3 lim)
{
    vec3 lm1 = lim-vec3(1.0);
    p += c*.5*lm1;
    vec3 q;
	q = min(p,mod(p+.5*c,c)-.5*c); // less than 0
    q = max(q,p-(lim-vec3(1.0))*c); // more than limit
    return sceneT(q);
}

