vec3 eye = vec3(0,0,-10);
vec3 sphere1Pos = vec3(-.5,.2,.5);
vec3 sphere2Pos = vec3(-.5,.3,-.1);
vec3 torusPos = vec3(0,0,0.);
const float maxIterations = 64.;

struct ray{
 	vec3 position;
    vec3 direction;
};

float smin( float a, float b, float k){
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float distSphere( vec3 p, float s )
{
  return length(p)-s;
}

float distTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float distFunc(vec3 p){
    float d1 = distSphere(p - sphere1Pos, .5);
	float d2 = distSphere(p - sphere2Pos,0.3);
    float d3 = distTorus(p - torusPos, vec2(.75,.2));
    //return min(min(d1,d2),d3);
    return smin(smin(d1,d2, .2),d3, .2);
}

ray lookAt(in vec2 uv, in vec3 targetPoint){
	vec3 forward = normalize(targetPoint - eye);
    vec3 up = vec3(0.,1.0,0.0);
    vec3 right = cross(forward, up);
    up = cross(forward, right);
    //return ray(eye, normalize(up + right + forward));
    vec3 ro = eye + forward * .1 + right * uv.x + up * uv.y;
    vec3 rd = forward;
    return ray(ro,rd);
}

vec3 rayMarch(ray marcher){
    float epsilon = 0.00001;
    float t = 0.;
    for (float i = 0.; i < maxIterations; i++){
        vec3 point = marcher.position + marcher.direction * t;
        float d = distFunc(point);
        if (d < epsilon){
            return vec3(1.-i/maxIterations); 
        }
        t+=d;
    }
    return vec3(0,0,0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    eye = vec3(cos(iTime) * 5. ,sin(iTime) * 3., sin(iTime) * 5.);
    vec2 uv = fragCoord.xy / iResolution.xy * 2. - 1.;
	uv.x *= iResolution.x / iResolution.y;
    ray eyeRay = lookAt(uv, vec3(0)); 
	fragColor = vec4(rayMarch(eyeRay),1.0);
}

