void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 	vec2 p = fragCoord/iResolution.x;
    vec2 q = p;
    p.x = mod(p.x,1.0/3.0) - 1.0/6.0;

    p.y -= 0.5*iResolution.y/iResolution.x;
    p.y += 0.04;

    float e = 1.0/iResolution.x;

    float time = iTime;

    //time = mod( time, 8.0 );
    float an = 0.3*(1.0-smoothstep(-0.1,0.1,sin(0.125*6.283185*(time+1.0/2.0))));

    vec2 r = vec2( sin(an), cos(an) );
    vec2 pe = r.yx*vec2(-1.0,1.0);

    vec3 col = vec3(0.15);
    col = vec3(21,32,43)/255.0;

    float wi = 0.0015;
    float s = dot(p,r);
    if( s>0.0 )
    {
        float r = length(p);
        if( r<0.12 )
        {
            float nr = r/0.12;
            col += 0.25*nr*nr;
        }
        col = mix(col,vec3(0.7), 1.0-smoothstep(-e,e,abs(r-0.12)-wi));
    }

    col = mix( col, vec3(0.7), arrow(p, vec2(0.0), r*0.18, wi, 0.01, e) );
    col = mix( col, vec3(0.7), line(p, -0.12*pe, 0.12*pe, wi, e) );

    {
    float an = cos(0.5*6.283185*time);
    vec2 v = vec2( -cos(an), sin(an) )*0.12;
    vec2 f;
         if( q.x<0.333 ) f = flipIfNeg( v, r );
    else if( q.x<0.666 ) f = reflIfNeg( v, r );
    else                 f = clipIfNeg( v, r );

    col = mix( col, col+0.2, arrow(p, vec2(0.0), v, wi, 5.0*wi, e) );
    col = mix( col, vec3(1.0,0.7,0.2), arrow(p, vec2(0.0), f, wi, 5.0*wi, e) );
    }

    fragColor = vec4( col, 1.0 );
}

// Whitney Music Box - Jim Bumgardner
// whitneymusicbox.org

const float rad = 0.6;
const float dots = 32.0;
const float duration = 180.0;
const vec3 colorsep = vec3(0,2.09,4.18);
const float PI = 3.1415926535897932384626433832795;
const float PI2 = 2.0*3.1415926535897932384626433832795;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
  float tm = mod(iTime,duration)/duration;
  p.y *= iResolution.y/iResolution.x;

  vec3 gradient = vec3(0.0);

  for (float i=1.0; i<=dots; i++)
  {
    float i2pi = i*PI2;
    float ang = mod(tm*i2pi, PI2);
    float amp = rad*(1.0-(i-1.0)/dots);
    float cang = i2pi/dots;
    //float fade = 0.7 - pow(smoothstep(0.0,1.0,ang),2.0)*0.5;
    float fade = 0.5 + 0.1 * tan(ang);
    vec2 star_pos = vec2(cos(ang) * amp, -sin(ang) * amp);
    gradient += (cos(cang+colorsep) + 1.0/2.0) * ((fade / 384.0) / pow(length(star_pos - p), 1.5)) * fade;
  }
  fragColor = vec4( gradient, 1.0);
}

