const int iterations = 8;
const float colour_separation = 6.283185307/float(iterations);
float angle_separation = colour_separation * (sin(iTime * 0.002)* 16.0 + 16.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = vec2(fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    float d = dot(uv, uv);
    float a = atan(uv.x, uv.y);
    vec3 c = vec3(0);
    for (int i = 0; i < 16; ++i)
    {
    	float x = (pow(d, 0.02) * 2.5 - 2.3 + sin((a + float(i) * angle_separation) *5.0) * 0.1) * 10.0;
    	x = x < 1.0 ? x : 2.0 - x;
        vec3 r = vec3(sin(float(i) * colour_separation        ) * 0.5 + 0.5,
                      sin(float(i) * colour_separation + 2.094) * 0.5 + 0.5,
                      sin(float(i) * colour_separation + 4.189) * 0.5 + 0.5);
        c += r*x;
    }
    c = c / float(iterations) * 2.0;
    vec3 f = pow(c, vec3(0.8));
    f *= clamp(c * 2.0, vec3(0.25), vec3(1.0));
	fragColor = vec4(f, 1.0);
}

#define NUM_BALLS 5
#define STRAT 4.0
#define WIDTH 0.5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = -1.0 + 2.0 * uv;
    vec2 pos;
    float t = iTime;
    float sum = 0.0;
    for( int i = 0; i < NUM_BALLS; i++ )
    {
        float st = sin( t );
        float fi = float(i);

    	pos = 0.5*vec2( 0.5*st + 0.5*cos(t+fi)+cos( t + float(i)), cos ( (1.0+0.5*fi)*t +st + float(i)));
        float d = 0.02/((pos.x-uv.x)*(pos.x-uv.x) + (pos.y-uv.y)*(pos.y-uv.y) );
        sum+=d;
    }
    sum = floor(STRAT*sum)/STRAT;
    sum = step(WIDTH,sum)*sum;
    vec2 mPos = iMouse.xy/iResolution.xy; mPos.x = 1.0-mPos.x;
    vec3 col = vec3(1,1,0)-vec3(mPos,0);
	fragColor = vec4(sum*col + vec3(0.7-0.2*length(uv))*step(sum,0.01)*col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
