    vec2 p1 = vec2(cos(iTime*1.1)-0.5,-.2+sin(iTime*0.8));
    vec2 p2 = vec2(0.0,.4+0.4*sin(iTime+1.0));
    vec2 p3 = vec2(.4+sin(iTime),0.0);
    p1 = mix(mix(p1,p2,uv.y),mix(p2,p3,uv.x),uv.x), 0.0, 1.0;
    float x = clamp(smoothstep(0.0,2.0,distance(uv,p1)), 0.0, 1.0);
    x = sqrt(1.0-x*x);
	fragColor = vec4(mix(vec3(.1,.0,.15), vec3(1.0,.8,.4), x),1.0);
}

#define M_PI 3.1415926535897932384626433832795

float vx(float x,float y, float a)
{
    float l=1.0/sqrt(a*a+(1.0-a)*(1.0-a));
    float u=x*a-y*(1.0-a);
    float v=x*(1.0-a)+y*a;
    u*=l;
    v*=l;
//    return (sin(u)+sin(v)+2.0)/2.0;
    float scale=0.00043*iResolution.x;
    u=fract(u*scale)-0.5;
    v=fract(v*scale)-0.5;
	return 1.0-sqrt(u*u+v*v)*sqrt(2.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy-iMouse.xy) / max(iResolution.x,iResolution.y)*4.0-0.5;
