    float r=length(uv);
	float a=atan(uv.x,uv.y)/M_PI/2.0+sin(r+t)*0.1;

    fragColor = vec4(
        vx(uv.x, uv.y,iMouse.x/iResolution.x*2.0-0.5),0,0,1
        );
//    return;

    fragColor = vec4(
         (r-sin(r*M_PI*2.0+t*4.0)*0.1-sin(a*M_PI*22.0+t*4.0)*0.1+sin(a*M_PI*12.0+t)*0.4-1.5)+vx(uv.x*150.0,uv.y*150.0,0.12)
        ,(r-sin(r*M_PI*3.0+t*5.0)*0.1-sin(a*M_PI*26.0+t*5.0)*0.1-sin(a*M_PI*8.0-t )*0.4-1.5)+vx(uv.x*150.0,uv.y*150.0,0.34)
        ,(r-sin(r*M_PI*2.0+t*8.0)*0.1-sin(a*M_PI*24.0+t*6.0)*0.1-sin(a*M_PI*10.0  )*0.4-1.5)+vx(uv.x*150.0,uv.y*150.0,0.69)
        ,1.0);
}

/*
Shitty little gaussian effect
Load a track into channel0
A kernel uses the mouse as input, so try it!
*/
float gauss (vec2 kernel, float amp, float spreadx, float spready,vec2 fragCoord)
{
    vec2 delta = (fragCoord.xy - kernel)/iResolution.xy;
    vec2 d1 = delta*delta;
    d1.x /= 2.0*spreadx*spreadx;
    d1.y /= 2.0*spready*spready;
    return amp*exp (-(d1.x + d1.y));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iChannelTime[0];
    float s = texture (iChannel0, vec2 (0,0)).x;
    fragColor = vec4 (0,0,0.2,1);
    //First kernel
    vec2 k1 = vec2 (0.3*iResolution.x, 0.3*iResolution.y);
	k1.x += 0.5*(160.0*cos (0.7*iTime) + 160.0*cos (0.7*t));
	k1.y -= 0.5*(240.0*sin (0.2*iTime) + 240.0*sin (0.2*t));
    fragColor.y += gauss (k1, 0.5 + 0.2*s, 0.3, 0.3,fragCoord);
    //Second kernel
    vec2 k2 = vec2 (iResolution.x, 0.13*iResolution.y);
	k2.x -= 0.5*(100.0*cos (1.5*iTime) + 100.0*cos (1.5*t));
	k2.y += 0.5*(150.0*sin (0.5*iTime) + 150.0*sin (0.5*t));
	fragColor.y += gauss (k2, 0.4 + 0.15*s, 0.2, 0.2,fragCoord);
    //Third kernel
	vec2 k3 = vec2 (0.5*iResolution.x, 0.65*iResolution.y);
	k3.x += 130.0*cos (0.9*t);
	k3.y -= 95.0*sin (1.3*t);
    float v = gauss (k3, 0.1*s + 0.5 + 0.2*cos (t), 0.3, 0.3,fragCoord);
	fragColor.y += v;
    fragColor.x += 0.2*s + 0.5*sin (t);
    //Fourth kernel
	vec2 k4 = vec2 (0.69*iResolution.x, 0.55*iResolution.y);
	k4.x -= 200.0*cos (0.7*t);
	k4.y += 150.0*sin (t + s);
	fragColor.x += gauss (k4, abs (0.8*sin (t)), 0.3, 0.3,fragCoord);
    //Fifth kernel
	vec2 k5 = vec2 (0.73*iResolution.x, 0.28*iResolution.y);
	k5.x -= 185.0*cos (1.25*t);
	k5.y += 150.0*sin (0.95*t);
	fragColor.z += gauss (k5, abs (0.5*cos (0.5*t + s)), 0.3, 0.4,fragCoord);        
    //Add mouse for fun
    vec2 ofs = iMouse.xy;
    ofs.x += 43.0*cos (t + 0.5*iTime);
    ofs.y -= 56.0*sin (t + 0.5*iTime);
    fragColor.y += gauss (ofs, 0.3, 0.2, 0.2,fragCoord);
}

vec3 rotateX (float a, vec3 v) {
    return vec3(v.x, 
                cos(a)*v.y + sin(a) * v.z,
                cos(a)*v.z - sin(a) * v.y);
}

vec3 rotateY (float a, vec3 v) {
    return vec3(cos(a)*v.x + sin(a) * v.z, 
                v.y,
                cos(a)*v.z - sin(a) * v.x);
}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) +
         length(max(d,0.0));
}

float hash( float n )
{
    return fract(sin(n)*43758.5453);
}

vec3 hash3( float n )
{
    return fract(sin(vec3(n,n+1.0,n+2.0))*
        vec3(43758.5453123,22578.1459123,19642.3490423));
}

float map (in vec3 p) {
    vec3 q = rotateY(iTime, p);

    float plane = p.y + 1.0;

    float sphere = length(q) - 1.2;

    //q = rotateY(0.6, p);
    float cube = sdBox(q, vec3(1.0));
    float m = max(-sphere, cube);

    return min(plane, m);
}

vec3 calcNormal( in vec3 pos ) {
    vec3 eps = vec3(0.2,0.0,0.0);

    return normalize( vec3(
           map(pos+eps.xyy) - map(pos-eps.xyy),
           map(pos+eps.yxy) - map(pos-eps.yxy),
           map(pos+eps.yyx) - map(pos-eps.yyx)) );
}

float softshadow( in vec3 ro, in vec3 rd, float mint, float k ) {
    float res = 1.0;
    float t = mint;
    for( int i=0; i<48; i++ )
    {
        float h = map(ro + rd*t);
        h = max( h, 0.0 );
        res = min( res, k*h/t );
        t += clamp( h, 0.01, 0.5 );
    }
    return clamp(res,0.0,1.0);
}

float calcAO( in vec3 pos, in vec3 nor ) {
    float totao = 0.0;
    for( int aoi=0; aoi<16; aoi++ )
    {
        vec3 aopos = -1.0+2.0*hash3(float(aoi)*213.47);
        aopos *= sign( dot(aopos,nor) );
        aopos = pos + aopos*0.5;
        float dd = clamp( map( aopos )*4.0, 0.0, 1.0 );
        totao += dd;
    }
    totao /= 16.0;

    return clamp( totao*totao*1.5, 0.0, 1.0 );
}

float intersect (in vec3 ro, in vec3 rd) {

    float h = 1.0;
    float tmax = 45.0;
    float t = 0.0;

    for (int i=0; i<180; i++) {
        if (h < 0.0001 || t > tmax) break;

        h = map(ro + t*rd);
        t += h;
    }

    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0 * uv;
    p.x *= iResolution.x / iResolution.y;

    vec3 ro = vec3 (0.0, 1.0, 4.0);
    vec3 rd = normalize(vec3(p, -1.0));

    float tmax = 20.0;
    float t = intersect(ro, rd);

    vec3 lig = normalize(
        rotateY(iTime/2.0, vec3(-0.5,0.7,-1.0))
        );

    vec3 bgc = 0.8*vec3(0.8,0.9,1.0)*(0.5 + 0.3*rd.y);
    vec3 col = bgc;

    if (t<tmax) {
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal (pos);
        vec3 ligh = normalize(lig - pos);

        float occ = calcAO( pos, nor );
        float amb = 0.8 + 0.2*nor.y;
        float dif = max(dot(nor,lig),0.0);
        float bac = max(dot(nor,normalize(vec3(-lig.x,0.0,-lig.z))),0.0);
        float sha = 0.0; if( dif>0.001 ) sha=softshadow( pos, lig, 0.1, 32.0 );
        float fre = pow( clamp( 1.0 + dot(nor,rd), 0.0, 1.0 ), 2.0 );

        // lights
        vec3 brdf = vec3(0.0);
        brdf += 1.0*dif*vec3(1.00,0.90,0.65)*pow(vec3(sha),vec3(1.0,1.2,1.5));
        brdf += 1.0*amb*vec3(0.05,0.05,0.05)*occ;
        brdf += 1.0*bac*vec3(0.03,0.03,0.03)*occ;
        brdf += 1.0*fre*vec3(1.00,0.70,0.40)*occ*(0.2+0.8*sha);

        col = brdf;
        //col = mix( col, bgc, clamp(1.0-1.2*exp(-0.0002*t*t ),0.0,1.0) );
    } else {
        vec3 sun = vec3(1.0,0.8,0.5)*pow( clamp(dot(rd,lig),0.0,1.0), 32.0 );
        col += sun;
    }

    //col += 0.6*vec3(0.2,0.14,0.1)*pow( clamp(dot(rd,lig),0.0,1.0), 5.0 );
    col = pow( col, vec3(0.75) );
    col = 1.3*col-0.1;
    col *= vec3( 1.0, 1.04, 1.0);
    col *= pow( 16.0*uv.x*uv.y*(1.0-uv.x)*(1.0-uv.y), 0.1 );


    fragColor = vec4(col, 1.0);
}

