#define USE_BOUND_PLANE


const mat2 m2 = mat2(1.6,-1.2,1.2,1.6);

float noi( in vec2 p )
{
    return 0.5*(cos(6.2831*p.x) + cos(6.2831*p.y));
}

float terrainLow( vec2 p )
{
    p *= 0.0013;

    float s = 1.0;
	float t = 0.0;
	for( int i=0; i<2; i++ )
	{
        t += s*noi( p );
		s *= 0.5 + 0.1*t;
        p = 0.97*m2*p + (t-0.5)*0.2;
	}
	return t*55.0;
}

float terrainMed( vec2 p )
{
    p *= 0.0013;

    float s = 1.0;
	float t = 0.0;
	for( int i=0; i<6; i++ )
	{
        t += s*noi( p );
		s *= 0.5 + 0.1*t;
        p = 0.97*m2*p + (t-0.5)*0.2;
	}

    return t*55.0;
}

float terrainHigh( vec2 p )
{
    vec2 q = p;
    p *= 0.0013;

    float s = 1.0;
	float t = 0.0;
	for( int i=0; i<7; i++ )
	{
        t += s*noi( p );
		s *= 0.5 + 0.1*t;
        p = 0.97*m2*p + (t-0.5)*0.2;
	}

    t +=   0.05*textureLod( iChannel0, 0.001*q, 0.0 ).x;
    t +=   0.03*textureLod( iChannel0, 0.005*q, 0.0 ).x;
    t += t*0.03*textureLod( iChannel0, 0.020*q, 0.0 ).x;

	return t*55.0;
}

float tubes( vec3 pos, float time )
{
    float sep = 400.0;

    pos.z -= sep*0.025*noi( 0.005*pos.xz*vec2(0.5,1.5) );
    pos.x -= sep*0.050*noi( 0.005*pos.zy*vec2(0.5,1.5) );

    vec3 qos = mod( pos + sep*0.5, sep ) - sep*0.5; 
    qos.y = pos.y - 70.0;
    qos.x += sep*0.3*cos( 0.01*pos.z);
    qos.y += sep*0.1*cos( 0.01*pos.x );

    float sph = length( qos.xy ) - sep*0.012;

    sph -= (1.0-0.8*smoothstep(-10.0,0.0,qos.y))*sep*0.003*noi( 0.15*pos.xy*vec2(0.2,1.0) );

    return sph;
}


float tubesH( vec3 pos, float time )
{
    float t = tubes( pos, time );

    t += 1.0*texture( iChannel3, 0.01*pos.yz ).x;
    t += 2.0*texture( iChannel0, 0.005*pos.xy ).x;

    return t;
}

vec2 map( in vec3 pos, float time )
{
    float m = 0.0;
	float h = pos.y - terrainMed(pos.xz);

    float sph = tubes( pos, time );
    float k = 60.0;
    float w = clamp( 0.5 + 0.5*(h-sph)/k, 0.0, 1.0 );
    h = mix( h, sph, w ) - k*w*(1.0-w);
    m = mix( m, 1.0, w ) - 1.0*w*(1.0-w);
    m = clamp(m,0.0,1.0);

    return vec2( h, m );
}

float mapH( in vec3 pos, in float time )
{
    float y = terrainHigh(pos.xz);

    float h = pos.y - y;

    float sph = tubesH( pos, time );
    float k = 60.0;
    float w = clamp( 0.5 + 0.5*(h-sph)/k, 0.0, 1.0 );
    h = mix( h, sph, w ) - k*w*(1.0-w);

    return h;
}

vec2 interesct( in vec3 ro, in vec3 rd, in float tmin, in float tmax, in float time )
{
    float t = tmin;
    float  m = 0.0;
	for( int i=0; i<160; i++ )
	{
        vec3 pos = ro + t*rd;
        vec2 res = map( pos, time );
        m = res.y;
        if( res.x<(0.001*t) || t>tmax  ) break;
        t += res.x * 0.5;
	}

	return vec2( t, m );
}

float calcShadow(in vec3 ro, in vec3 rd )
{
	vec2  eps = vec2(150.0,0.0);
    float h1 = terrainMed( ro.xz );
    float h2 = terrainLow( ro.xz );

    float d1 = 10.0;
    float d2 = 80.0;
    float d3 = 200.0;
    float s1 = clamp( 1.0*(h1 + rd.y*d1 - terrainMed(ro.xz + d1*rd.xz)), 0.0, 1.0 );
    float s2 = clamp( 0.5*(h1 + rd.y*d2 - terrainMed(ro.xz + d2*rd.xz)), 0.0, 1.0 );
    float s3 = clamp( 0.2*(h2 + rd.y*d3 - terrainLow(ro.xz + d3*rd.xz)), 0.0, 1.0 );

    return min(min(s1,s2),s3);
}

vec3 calcNormalHigh( in vec3 pos, float t, in float time )
{
    vec2 e = vec2(1.0,-1.0)*0.001*t;

    return normalize( e.xyy*mapH( pos + e.xyy, time ) + 
					  e.yyx*mapH( pos + e.yyx, time ) + 
					  e.yxy*mapH( pos + e.yxy, time ) + 
					  e.xxx*mapH( pos + e.xxx, time ) );
}


vec3 calcNormalMed( in vec3 pos, float t )
{
	float e = 0.005*t;
    vec2  eps = vec2(e,0.0);
    float h = terrainMed( pos.xz );
    return normalize(vec3( terrainMed(pos.xz-eps.xy)-h, e, terrainMed(pos.xz-eps.yx)-h ));
}

vec3 camPath( float time )
{
    vec2 p = 1100.0*vec2( cos(0.0+0.23*time), cos(1.5+0.205*time) );
	return vec3( p.x, 0.0, p.y );
}

vec3 dome( in vec3 rd, in vec3 light1 )
{
	float sda = clamp(0.5 + 0.5*dot(rd,light1),0.0,1.0);
    float cho = max(rd.y,0.0);

    vec3 bgcol = mix( mix(vec3(0.00,0.40,0.60)*0.7, 
                          vec3(0.80,0.70,0.20),                        pow(1.0-cho,3.0 + 4.0-4.0*sda)), 
                          vec3(0.43+0.2*sda,0.4-0.1*sda,0.4-0.25*sda), pow(1.0-cho,10.0+ 8.0-8.0*sda) );
    bgcol *= 0.8 + 0.2*sda;
    return bgcol*0.75;
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3  cw = normalize(ta-ro);
	vec3  cp = vec3(sin(cr), cos(cr),0.0);
	vec3  cu = normalize( cross(cw,cp) );
	vec3  cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = -1.0 + 2.0*fragCoord.xy/iResolution.xy;
    vec2 sp = xy*vec2(iResolution.x/iResolution.y,1.0);

    //--------------------------

    // animate    
    float camid = floor((0.0+iTime-0.0)/9.0);
    float time = 16.5 + (0.0+iTime-0.0)*0.1 + 20.0*iMouse.x/iResolution.x + 72.1*camid + 19.0*max(0.0,camid-1.0);

    // camera    
	float cr = 0.18*sin(-0.1*time);
	vec3  ro = camPath( time + 0.0 );
	vec3  ta = camPath( time + 3.0 );
    ro.y = terrainLow( ro.xz ) + 60.0 + 30.0*sin(1.0*(time-14.4));
	ta.y = ro.y - 200.0;
    // camera to world transformation
    mat3 cam = setCamera( ro, ta, cr );

    // light      
    vec3 light1 = normalize( vec3(-0.8,0.2,0.5) );

    //--------------------------

    // generate ray
    vec3 rd = cam * normalize(vec3(sp.xy,1.5));

    // background    
    vec3 bgcol = dome( rd, light1 );

    // raymarch
    float tmin = 10.0;
    float tmax = 4500.0;

#ifdef USE_BOUND_PLANE
    // intersect boundg plane
    float maxh = 130.0;
    float tp = (maxh-ro.y)/rd.y;
    if( tp>0.0 )
    {
        if( ro.y>maxh ) tmin = max( tmin, tp );
        else            tmax = min( tmax, tp );
    }
#endif

	float sundotc = clamp( dot(rd,light1), 0.0, 1.0 );
    vec3  col = bgcol;

    vec2 res = interesct( ro, rd, tmin, tmax, time );
    if( res.x>tmax )
    {
        // sky		
		col += 0.2*0.12*vec3(1.0,0.5,0.1)*pow( sundotc,5.0 );
		col += 0.2*0.12*vec3(1.0,0.6,0.1)*pow( sundotc,64.0 );
		col += 0.2*0.12*vec3(2.0,0.4,0.1)*pow( sundotc,512.0 );

        // clouds
  		vec2 sc = ro.xz + rd.xz*(1000.0-ro.y)/rd.y;
		col = mix( col, 0.25*vec3(0.5,0.9,1.0), 0.4*smoothstep(0.0,1.0,texture(iChannel0,0.000005*sc).x) );

        // sun scatter
        col += 0.2*0.2*vec3(1.5,0.7,0.4)*pow( sundotc, 4.0 );
    }
	else
	{
        // mountains		
        float t = res.x;
		vec3 pos = ro + t*rd;
        vec3 nor = calcNormalHigh( pos, t, time );
        vec3 sor = calcNormalMed( pos, t );
        vec3 ref = reflect( rd, nor );

        // rock
        col = vec3(0.07,0.06,0.05);
        col *= 0.2 + sqrt( texture( iChannel0, 0.01*pos.xy*vec2(0.5,1.0) ).x *
                           texture( iChannel0, 0.01*pos.zy*vec2(0.5,1.0) ).x );
        vec3 col2 = vec3(1.0,0.2,0.1)*0.01;
        col = mix( col, col2, 0.5*res.y );

        // grass
        float s = smoothstep(0.6,0.7,nor.y - 0.01*(pos.y-20.0));        
        s *= smoothstep( 0.15,0.2,0.01*nor.x+texture(iChannel0, 0.001*pos.zx).x);
        vec3 gcol = 0.13*vec3(0.22,0.23,0.04);
        gcol *= 0.3+texture( iChannel1, 0.03*pos.xz ).x*1.4;
        col = mix( col, gcol, s );
        //col *= texture( iChannel0, 0.3*pos.xz ).x*3.2;
        nor = mix( nor, sor, 0.3*s );
        vec3 ptnor = nor;

        // trees
        s = smoothstep(0.9,0.95,nor.y - 0.01*(pos.y-20.0));        
        s *= smoothstep( 0.1,0.13,-0.17+texture(iChannel0, 0.001*pos.zx).x);
        vec3 tor = -1.0 + 2.0*texture( iChannel1, 0.015*pos.xz ).xyz;
        tor.y = 1.5;
        tor = normalize(tor);
        col = mix( col, 0.11*vec3(0.22,0.25,0.02)*1.0, s );
        nor = mix( nor, tor, 0.7*s );

		// snow
        s = ptnor.y + 0.008*pos.y - 0.2 + 0.2*(texture(iChannel1,0.00015*pos.xz+0.0*sor.y).x-0.5);
        float sf = fwidth(s) * 1.5;
        s = smoothstep(0.84-sf, 0.84+sf, s );
        col = mix( col, 0.15*vec3(0.42,0.6,0.8), s);
        nor = mix( nor, sor, 0.5*smoothstep(0.9, 0.95, s ) );

        // lighting		
        float amb = clamp( nor.y,0.0,1.0);
		float dif = clamp( dot( light1, nor ), 0.0, 1.0 );
		float bac = clamp( dot( normalize( vec3(-light1.x, 0.0, light1.z ) ), nor ), 0.0, 1.0 );
		float sha = mix( calcShadow( pos, light1 ), 1.0, res.y );
        float spe = pow( clamp( dot(ref,light1), 0.0, 1.0 ), 4.0 ) * dif;

		vec3 lin  = vec3(0.0);
        lin += dif*vec3(11.0,6.00,3.00)*vec3( sha, sha*sha*0.5+0.5*sha, sha*sha*0.8+0.2*sha );
		lin += amb*vec3(0.25,0.30,0.40);
        lin += bac*vec3(0.35,0.40,0.50);
        lin += spe*vec3(4.00,4.00,4.00)*res.y;

        col *= lin;

        // fog
        col = mix( col, 0.25*mix(vec3(0.4,0.75,1.0),vec3(0.3,0.3,0.3), sundotc*sundotc), 1.0-exp(-0.0000008*t*t) );

        // sun scatter
        col += 0.15*vec3(1.0,0.8,0.3)*pow( sundotc, 8.0 )*(1.0-exp(-0.003*t));

        // background
        col = mix( col, bgcol, 1.0-exp(-0.00000004*t*t) );
    }

    // gamma
	col = pow( col, vec3(0.45) );

    // color grading    
    col = col*1.4*vec3(1.0,1.0,1.02) + vec3(0.0,0.0,0.11);
    col = clamp(col,0.0,1.0);
    col = col*col*(3.0-2.0*col);
    col = mix( col, vec3(dot(col,vec3(0.333))), 0.25 );

    // vignetting	
 	col *= 0.5 + 0.5*pow( (xy.x+1.0)*(xy.y+1.0)*(xy.x-1.0)*(xy.y-1.0), 0.1 );

    // camera fade
    col *= smoothstep( 0.0, 0.1, 2.0*abs(fract(0.5+iTime/9.0)-0.5) );

	fragColor = vec4( col, 1.0 );
}

#define SHADOWS
#define REFLECTION
#define SECOND_BOUNCE
//#define OCULUS
#define SAILS
#define MBLUR_SAMPLES 4.0

#define FOG_D 80.0

#define L_COUNT 1.0
#define M_COUNT 1.0
#define N_COUNT 3.0
#define I_COUNT 3.0
#define J_COUNT 3.0

struct tSphere {
	vec3 center;
	float radius;
	vec3 color;
	float spec;
};

// return ray p,d distance to triangle v0,v1,v2
// returns -1.0 if no intersection
// writes UV value to the uv vector
float rayIntersectsTriangle(vec3 p, vec3 d, vec3 v0, vec3 v1, vec3 v2, inout vec2 uv)
{
	vec3 e1,e2,h,s,q;
	float a,f,u,v,t;
	e1 = v1-v0;
	e2 = v2-v0;

	h = cross(d,e2);
	a = dot(e1,h);

	f = 1.0 / a;
	s = p-v0;
	u = f * dot(s,h);

	q = cross(s,e1);
	v = f * dot(d,q);

	uv = vec2(u,v);

	if (u < 0.0 || u > 1.0 || v < 0.0 || u+v > 1.0) {
		return -1.0;
	}

	// at this stage we can compute t to find out where
	// the intersection point is on the line
	t = f * dot(e2,q);

	float m = float(t <= 0.00001 || (a > -0.00001 && a < 0.00001));

	t = mix(t, -1.0, m);

	return t;

}


float raySphereDet(vec3 ray, vec3 dir, vec3 center, float radius, float closestHit, inout float b)
{
	vec3 rc = ray-center;  // 1
	float c = dot(rc, rc); // 1
	c -= radius*radius;    // 2?
	b = dot(dir, rc);      // 1
	return b*b - c;        // 2?
}

bool rayBV(vec3 ray, vec3 dir, vec3 center, float radius, float closestHit)
{
	float b;
	float d = raySphereDet(ray, dir, center, radius, closestHit, b);
	if (d < 0.0) {
		return true;
	};
	float t = -b - sqrt(d);
	return (t < -(2.0*radius) || t > closestHit+(2.0*radius));
}

float rayIntersectsSphere(vec3 ray, vec3 dir, vec3 center, float radius, float closestHit)
{
	float b;
	float d = raySphereDet(ray, dir, center, radius, closestHit, b); // 7
	if (d < 0.0) { // 1
		return -1.0;
	}
	float t = -b - sqrt(d); // 3
	if (t < 0.0 || t > closestHit) { // 2
		return -1.0;
	} else {
		return t;
	}
}

// rotate position around axis
vec2 rotate(vec2 p, float a)
{
	return vec2(p.x * cos(a) - p.y * sin(a), p.x * sin(a) + p.y * cos(a));
}

float intersect(float time, inout vec3 ray, vec3 dir, inout vec3 nml, inout tSphere sphere, inout float doAA, inout float pick)
{
	float dist = 5000.0;

	sphere.radius = doAA = -2.0;
	sphere.center = ray+dist*dir;
	nml = -dir;
	pick = -1.0;

	float b,k,d;
	float aaBorder = 1.15;

	float l=0.0,m=0.0,n=0.0;
	float tl = l+time;
	vec3 mContrib = vec3(0.0, 0.0, 0.0);

	for (float n=0.0; n<N_COUNT; n++) {
		float r = min(n, 1.0)*10.0;
		float tn = time + (n*6.28/9.0);
		vec3 nContrib = mContrib + (vec3(sin(tn), -cos(tn), -cos(tn))*r);
		if (rayBV(ray, dir, nContrib, 14.0, dist)) {
			continue;
		}

		doAA = max(doAA, -0.5);

		for (float i=0.0; i<I_COUNT; i++) {
			float ikOff = sin(time+i)*5.0;
			float sikOff = cos(time+i)*5.0;
			vec3 iContrib = nContrib + vec3(ikOff, sikOff, ikOff);
			if (rayBV(ray, dir, iContrib, 7.0, dist)) {
				continue;
			}

			doAA = max(doAA, -0.3);
			for (float j=0.0; j<J_COUNT; j++) {
				float kOff = + cos(time*5.0+j)*2.5;
				float skOff = - sin(time*5.0+j)*2.5;
				k = l*(M_COUNT*N_COUNT*I_COUNT*J_COUNT) + m*(N_COUNT*I_COUNT*J_COUNT) + n*(I_COUNT*J_COUNT) + i*J_COUNT + j;
				r = 0.8+0.5*cos(k);
				vec3 cen = iContrib + vec3(kOff, skOff, kOff);

	#ifdef SAILS
				// Do sails first since they're occluded by the sphere.				
				// BV intersect, let's switch on 4x sampling and check for sail intersect
				if (!rayBV(ray, dir, cen, r*2.5, dist) && rayBV(ray, dir, cen, r*1.25, dist)) {
					float a = 8.0*time*(1.0+i+j)+k+j*i;
					vec3 px = vec3(sin(a), cos(a), 0.0);
					vec3 py = vec3(cos(a), -sin(a), 0.0);
					vec3 pz = vec3(0.0, 0.0, 1.0);

					vec3 p1 = cen - py*5.25*r - px*3.2*r;
					vec3 p2 = cen + py*5.25*r - px*3.2*r;
					vec3 p3 = cen + py*0.0*r + px*0.7*r;
					vec2 uv;
					float t = rayIntersectsTriangle(ray, dir, p2, p3, p1, uv);
					if (t > 0.0 && t < dist) {
						dist = t;
						sphere.radius = 100.0;
						nml = pz;
						if(dot(nml, dir) < 0.0) {
							nml = -nml;
						}
						sphere.center = p1;
						sphere.color = vec3(0.5, 0.1, 0.05);
						sphere.spec = 8.0;
						pick = k;
					}
				}
	#endif

				d = raySphereDet(ray, dir, cen, r*aaBorder, dist, b);
				// BV intersect, let's switch on 4x sampling and check for sphere intersect
				if (d > 0.0 && -b - sqrt(d) > -r*aaBorder) {
					doAA = max(doAA, 0.0);
					// d = b*b - dot(rc,rc) + (r*r)*(aaBorder*aaBorder)
					// eliminate aaBorder^2 by
					// d_r = b^2 - rc^2 + r^2 * aaBorder^2 - r^2 * aaBorder^2 + r^2
					//     = b^2 - rc^2 + r^2
					// rewrite -r^2 * aaBorder^2 + r^2 = -r^2 * (aaBorder^2 - 1)
					d = d - (r*r)*(aaBorder*aaBorder - 1.0);
					if (d > 0.0) {
						float t = -b - sqrt(d);
						if (t > 0.0 && t < dist) {
							dist = t;
							sphere.radius = r;
							sphere.center = cen;
							nml = normalize(sphere.center - ray - dist*dir);
							float odd = mod(n+j, 2.0);
							float ay = abs(nml.y);
							float fy = float(ay < 0.05 || (ay > 0.75 && ay < 0.78));
							sphere.color = mix(vec3(0.1), mix(vec3(0.95, 0.8, 0.7), vec3(0.2), fy), odd);
							// Switch off AA for points inside the sphere
							sphere.spec = mix(64.0, 8.0, odd);
							pick = k;
						}
					}
				}
			}
		}
	}

	if (sphere.radius > 0.0 && sphere.radius < 100.0) {
		doAA = mix(doAA, -0.1, float(dot(nml, dir) > 0.4));
	}

	ray += dist*dir;
	return dist;
}

float pickIntersect(float time, vec3 ray, vec3 dir)
{
	tSphere sphere;
	vec3 nml;
	float doAA, pick;
	float t = intersect(time, ray, dir, nml, sphere, doAA, pick);
	return pick;
}




vec3 shadeBg(vec3 dir, vec3 nml)
{

    vec3 lightPos_ = vec3(
        -cos(iTime*0.1)*-8.5, 
        3.5+sin(iTime*0.05)*3.0, 
        -(sin(iTime*0.1)*4.0-5.4)
    );
    vec3 bgLight = normalize(lightPos_);
    vec3 lightPos = bgLight * 9999.0;
    vec3 sun = vec3(5.0, 3.5, 2.0)*4.0;
	vec3 bgCol = vec3(0.2, 0.15, 0.1);
	float bgDiff = dot(nml, vec3(0.0, 1.0, 0.0));
	float sunPow = dot(nml, bgLight);
	bgCol += 0.1*sun*pow( max(sunPow, 0.0), 2.0);
	bgCol += 2.0*bgCol*pow( max(-sunPow, 0.0), 2.0);
	bgCol += bgDiff*vec3(0.25, 0.5, 0.5);
	bgCol += sun*pow( max(sunPow, 0.0), abs(bgLight.y)*256.0);
	bgCol += bgCol*pow( max(sunPow, 0.0), abs(bgLight.y)*128.0);
	return max(vec3(0.0), bgCol);
}

vec3 shade(vec3 ray, vec3 dir, vec3 nml, float dist, tSphere sphere, inout float doAA)
{
	vec3 col = vec3(0.0, 0.0, 0.0);
	vec3 bgCol = shadeBg(dir, dir);
	if (sphere.radius > 0.0) {
		float a = 1.0 - abs(dot(dir, nml));
		if (sphere.spec < 0.0) {
			col += a * vec3(2.5, 1.6, 1.3);
		}

		float fog = clamp(dist / FOG_D, 0.0, 1.0);
		fog *= fog;
		col = (1.0-fog)*col + fog*bgCol;
	} else {
		col = bgCol;
	}

	return col;
}

#ifdef OCULUS
vec2 hmdWarp(vec2 texIn) {
	vec2 u_lensCenter = vec2(0.0, 0.0);
	vec2 u_scaleIn = vec2(0.45, 0.7);
	vec2 u_scale = vec2(0.7);
	vec4 u_hmdWarpParam = vec4(0.7);
	vec2 u_screenCenter = vec2(0.0);
	if (texIn.x > 0.0) {
		u_lensCenter = vec2(-0.05, 0.0);
		texIn.x = (0.85 - texIn.x);
	} else {
		u_lensCenter = vec2(0.05, 0.0);
		texIn.x = (-0.85 - texIn.x);
	}
	vec2 theta = (texIn - u_lensCenter) * u_scaleIn;
	float rSq = theta.x * theta.x + theta.y * theta.y;
	vec2 theta1 = theta * (u_hmdWarpParam.x + u_hmdWarpParam.y * rSq + 
		u_hmdWarpParam.z * rSq * rSq + u_hmdWarpParam.w * rSq * rSq * rSq);
	return u_lensCenter + u_scale * theta1;
}
#endif

vec3 getDir(vec2 fragCoord)
{
	vec2 pixelAspect = vec2(iResolution.x / iResolution.y, 1.0);
	vec2 pixelRatio = vec2(2.0) / iResolution.xy;
	vec2 uv = (fragCoord*pixelRatio - 1.0)*pixelAspect;

#ifdef OCULUS	
	vec2 tc = hmdWarp(uv);
	uv = tc;
	uv.x *= iResolution.x / iResolution.y;
#endif

	vec3 dir = vec3(uv, 1.0);
	dir.xy = rotate(dir.xy, cos(iTime*0.1));
	dir.xz = rotate(dir.xz, sin(iTime*0.15));
	return normalize(dir);
}

vec3 doReflections(vec2 fragCoord, float time, vec3 oray, vec3 dir, vec3 onml, tSphere oSphere, float odist, float picked)
{
	vec3 ncol = vec3(0.0);
	vec3 xvec = normalize(cross(onml, vec3(1.0, 0.0, 0.0)));
	vec3 yvec = normalize(cross(onml, vec3(0.0, 1.0, 0.0)));
	float doAA_, target, k=0.0;
	//for (float ix=0.0; ix<3.0; ix++) 
	//{
		float ix = 0.0;
		k++;
		float xx = floor(ix / 2.0);
		float yy = ix-(xx*2.0);
		vec4 tex = texture(iChannel0, mod(fragCoord.xy*2.0+vec2(xx,yy), 256.0)/256.0);
		vec3 nml = onml;
		vec3 ray = oray;
		vec3 diff = oSphere.color;
		float dist = odist;
		tSphere sphere = oSphere;
		vec3 v = normalize(pow(tex.rgb, vec3(2.0)));
		vec3 ref = reflect(dir, normalize(nml + v*(2.0/sphere.spec)) );

		// reflection
#ifdef REFLECTION
		float fog = clamp(dist / FOG_D, 0.0, 1.0);
		fog *= fog;
		dist = intersect(time, ray, ref, nml, sphere, doAA_, target);
		sphere.spec *= min(1.0, mix(-1.0, 1.0, abs(picked-target)));
		ncol += (1.0-fog)*diff * shade(ray, ref, nml, dist, sphere, doAA_);
		fog = clamp(dist / FOG_D, 0.0, 1.0);
		fog *= fog;
		// second reflection
#endif
		//if (oSphere.radius / odist < 1.0) {
		//	break;
		//}
#ifdef SECOND_BOUNCE
		if (sphere.radius > 0.0) {
			diff *= sphere.color;
			ref = reflect(ref, nml);
			dist = intersect(time, ray, ref, nml, sphere, doAA_, target);
			sphere.spec *= min(1.0, mix(-1.0, 1.0, abs(picked-target)));
			ncol += (1.0-fog)*diff * shade(ray, ref, nml, dist, sphere, doAA_);
		}
#endif
	//}
	return ncol / k;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float xOff = 0.0;
#ifdef OCULUS
	xOff = 1.0;
	if (fragCoord.x < iResolution.x*0.5) xOff = -1.0;
#endif

	vec3 eye = vec3(-xOff*0.5, -0.0, -30.0);

	vec3 mdir;
	mdir = getDir(iMouse.zw);

	vec3 nml = vec3(0.0);		
	vec3 col = vec3(0.0);

	tSphere sphere;
	float doAA, doAA_;

	float shutterSpeed = 1.0 / 30.0;
	float iso = 100.0;
	float exposureCompensation = +1.0;

	// Auto ISO
	iso = 1.0 / shutterSpeed;

	float picked = -2.0;
	float k = 0.0;
	const float mblur_sample_count = MBLUR_SAMPLES;
	float box_size = ceil(sqrt(MBLUR_SAMPLES));
	for (float dt = 0.0; dt < mblur_sample_count; dt++) {
		k++;
		float tx = floor(dt/box_size);
		float ty = dt - tx*box_size;
		vec4 tex = texture(iChannel0, mod(fragCoord.xy*box_size+vec2(tx,ty), 256.0)/256.0);
		float time = (iTime + (tex.r*2.0-dt/mblur_sample_count)*shutterSpeed) + 450.0 ;

		float i = 0.0;
		vec3 ray = eye;
		float x = floor(0.5*i);
		float y = 1.0-x;
		vec3 dir = getDir(fragCoord.xy+(vec2(x,y)*0.5));

		float target = -1.0;
		float dist = intersect(time, ray, dir, nml, sphere, doAA, target);

		if (picked == -2.0 && target >= 0.0 && iMouse.z > 1.0) { 
			picked = pickIntersect(time, eye, mdir);
		}

		sphere.spec *= min(1.0, mix(-1.0, 1.0, abs(picked-target)));
		col += shade(ray, dir, nml, dist, sphere, doAA_);

		if ( sphere.radius > 0.0 )
		{
			col += doReflections(fragCoord, time, ray, dir, nml, sphere, dist, picked);
		}
		if (doAA == -2.0) {
			break;
		}
	}
	col = 1.0 - exp((-col/k) * iso * shutterSpeed * pow(2.0, exposureCompensation));
	fragColor = vec4(col, 1.0);
}

