// very simple ambient occlusion
float getAO(vec3 rs,vec3 rd)
{
    // move away along the normal
    float d = .3;
    float k = 1.0;
    float ret = 1.0;
    for (int i = 1;i<5;++i) {
        float fi = float(i);
        k *= .5;
        float distBack = scene(rs + rd*(d*fi));
        ret -= k*(fi*d - distBack);
    }
    return ret;
    //return 1.0;
    //return distBack/k;
    //return k/distBack;
}

vec3 march(vec3 rs,vec3 rd,out bool hit)
{
    const float conserve = .87; // compensate for errors in distance function esp. rotate box repeat
    const int steps = 180;
    float eps = .001;
    float toobig = 1000.0;
	vec3 col = vec3(0.0,0.0,0.0);
    hit = false;
    for (int i=0;i<steps;++i) {
        float d = scene(rs);
        if (d > toobig) { // bail early if clearly can't hit anything
            hit = false;
            break;
        }
        if (d < eps) {
            if (rs.y >= 0.0) { // hit above ground
                //col += getTex(rs); // normal color
                //col += getNormal(rs)*.5 + vec3(.5); // normal color
    			//col += float(i)*(10.0/256.0); // brighter if more iterations


                vec3 norm = getNormal(rs);
 				// normal color, very simple lighting .5 ambient, .5 directional
 				// normal in opposite direction of light gets the light
                //col += norm*.5 + vec3(.5); // normal color
                //col += max(-norm.x,0.0)*.5+.5; // dir +1x
                //col += max(norm.y,0.0)*.5+.5; // dir -1y
                //col += max(-norm.z,0.0)*.5+.5; // dir +1z
                col += shade(vec3(1.),rs,norm,rd)*.95;
                //col += -norm.z; // dir +1z, no ambient all directional

                col += sceneCol*.05;
                //col += getTex(rs)*.25; // texture color
                //col += norm*.5 + vec3(.5); // normal color 0 to 1
                //col += norm; // raw normal color -1 to 1
    			//col += float(i)*(10.0/256.0); // brighter if more iterations
                float ao = getAO(rs,norm);
                col *= ao;
                hit = true;
            }
            break;
        }
        rs += rd*d*conserve; // compensate for the slight error from rotating boxes distance modulo function
    }

    return col;
}

//// main
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    bool roll = ReadKeyBool(k1,true);
    bool planeaxis = !ReadKeyBool(k2,true);
    bool movexy = ReadKeyBool(k3,true);

    // ndc
    vec2 pos = ndc(fragCoord.xy);
    vec2 mouse;// = vec2(0.0,0.0);

    if (iMouse.x == 0.0) // hack for mouse not yet clicked
        mouse = vec2(0.0,0.0);
    else
    	mouse = ndc(iMouse.xy);

    // keyboard color, for circle (press 1,2,3)
    vec3 keycolor = vec3(ReadKeyFloat(kRed,true),
             ReadKeyFloat(kGreen,true),
             ReadKeyFloat(kBlue,true));

    //// camera
    const float zoom = 2.0;
    // make a ray, left handed coords
    vec3 rs; // ray start
    vec3 rd = vec3(pos,1.0); // ray direction to the pixel
    vec3 nrd;
    vec3 rc = vec3(0.0,0.0,1.0); // ray direction from center of camera
    // zoom factor
    rd.xy /= zoom;
    vec3 cd = vec3(0.0,0.0,1.0); // camera direction (center, look at)
    if (movexy) { // mouse move camera in xy, always facing forward
	    rs = vec3(0.0,6.0,-5.0) + vec3(mouse*5.99,0.0); // ray start
        nrd = normalize(rd);
    } else { // mouse rotates camera 2 different ways
	    rs = vec3(0.0,.4,0.0); // ray start
        rd.yz *= rot(mouse.y*3.14 ); // pitch
        rc.yz *= rot(mouse.y*3.14 ); // pitch
        if (roll) {
            rd.xy *= rot(mouse.x*3.14 ); // roll
            rc.xy *= rot(mouse.x*3.14 ); // roll
        } else {
            rd.xz *= rot(mouse.x*3.14 ); // yaw
            rc.xz *= rot(mouse.x*3.14 ); // yaw
        }
        nrd = normalize(rd); // normalized for the marching, unnormalized for the sky gradient
        rs += rc * -4.0;
    }



    // cursor color
    vec3 circlecolor = keycolor;
    // is inside circle
    vec2 del =  pos - mouse;
    float d2 = dot(del,del);
    float rad = 1.0/30.0;
    bool iscursor = d2<rad*rad;

    // sky plane color
    //rs -= nrd * 2.3;
    //rs.z += nrd.z;
    vec3 skyplanecolor = skyPlane(rs,rd,planeaxis); // do this with an unnormalized ray

    // march color
    bool hit; // did hit something
    vec3 marchcol = march(rs,nrd,hit);


    // put it all together
    vec3 comp;
    if (iscursor)
        comp = circlecolor;
    else if (hit)
		comp = marchcol;
	else
        comp = skyplanecolor;
	fragColor = vec4(comp,1.0);
}

float time;

float smoothTri(vec2 p, vec2 p0, vec2 p1, vec2 p2, float smoothness)
{
    vec3 e0, e1, e2;

    e0.xy = normalize(p1 - p0).yx * vec2(+1.0, -1.0);
    e1.xy = normalize(p2 - p1).yx * vec2(+1.0, -1.0);
    e2.xy = normalize(p0 - p2).yx * vec2(+1.0, -1.0);

    e0.z = dot(e0.xy, p0) - smoothness;
    e1.z = dot(e1.xy, p1) - smoothness;
    e2.z = dot(e2.xy, p2) - smoothness;

    float a = max(0.0, dot(e0.xy, p) - e0.z);
    float b = max(0.0, dot(e1.xy, p) - e1.z);
    float c = max(0.0, dot(e2.xy, p) - e2.z);

    return smoothstep(smoothness * 2.0, 1e-7, length(vec3(a, b, c)));
}

float layer(vec2 p, float smoothness, float index)
{
    float a = 0.0;
    for(int i = 0; i < 8; i += 1)
    {
        float u0 = (float(i + 0) / 8.0 * 2.0 - 1.0) * 3.0;
        float u1 = (float(i + 1) / 8.0 * 2.0 - 1.0) * 3.0;

        float y = 0.0 + abs(cos(u0 * 130.0 + index)) * 0.9;

        vec2 o = vec2(index * 0.1 - 4.0, abs(cos(time * 3.0 + index + u0)));

        vec2 p0 = vec2(u0, 0.0) + o;
        vec2 p1 = vec2(u1, 0.0) + o;
        vec2 p2 = vec2((u0 + u1) * 0.5, y) + o;

        a += smoothTri(p, p0, p1, p2, smoothness);
    }
    return a;
}

float vignet(vec2 t)
{
    return max(0.0,1.0 - (t.x * t.x * t.x * t.x + t.y * t.y * t.y * t.y));
}

float vignet2(vec2 t)
{
    return max(0.0,t.x * t.x + t.y * t.y);
}

vec3 scene(vec2 p)
{
    vec3 col = vec3(0.0);
    for(int i = 0; i < 5; i += 1)
    {
        float z = float(5 - i);
        float z2 = abs((float(5 - i)) - 0.0) * 2.0;
        float c = (10.0 - z2) * 0.15;
        float f = z2 * z2 * 0.002 * (1.0 + vignet2(p) * 10.0) * 0.5;
        float index = float(i) * 16.0;
        vec2 o = vec2(cos(time), 0.7);
        float a0 = clamp(layer(p * z + o, f, index), 0.0, 1.0);
        float a1 = clamp(layer(p * z + o, f * 30.0, index), 0.0, 1.0);

        vec3 cc = vec3(0.1, 0.4, 1.0);

        col = mix(col, vec3(c) * cc, a0) + mix(col, vec3(c) * cc, a1) * 0.2;
    }
    return col;
}

float jitter()
{
    return fract(gl_FragCoord.x * 0.1943244 + gl_FragCoord.y * 0.525634);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor.rgb = vec3(0.0);
    float j = jitter();
    vec2 p = (fragCoord.xy / iResolution.xy - vec2(0.5)) * 2.6;
    p.y *= iResolution.y / iResolution.x;
    for(int i = 0; i < 2; i += 1)
    {
        time = iTime + (float(i) + j) / 40.0;
        fragColor.rgb += scene(p);
    }
    fragColor.rgb = sqrt(fragColor.rgb * 0.5 * vignet(p)) + jitter() / 255.0;
    fragColor.a = 1.0;
}

