// One of the faces of the shadow box
float shadowPlane(vec2 p)
{
    //return aastep(0.5, alahambra_pattern(p));

    vec2 p_abs = abs(p);
    float p_abs_min = min(p_abs.x, p_abs.y);
    float d = length(p);

    float c0 = min(1.0, abs(d - 0.35) * 80.0);
    float c1 = min(1.0, abs(d - 0.48) * 80.0);
    float c2 = min(1.0, abs(circle_curves(p, 0.55, 40.0)) * 120.0);
    float c3 = min(1.0, abs(circle_curves(p, 0.59, 40.0)) * 80.0);
    float s0 = min(1.0, abs(square(p, 0.595)) * 90.0);
    float s1 = min(1.0, abs(square(p, 0.8)) * 90.0);
    float s2 = min(1.0, abs(square(p, 1.0)) * 10.0);
    /*s2 *= s2;
    s2 *= s2;*/

    float lines = min(c0, min(c1, min(c2, min(c3, min(s0, min(s1, s2))))));


    vec2 p45 = vec2(p.x-p.y,p.x+p.y) * 0.7071;

    float g0 = min(1.0, abs(square(fract(p45 * 40.0) * 2.0 - 1.0, 1.0)) * 2.0);
    g0 = mix(1.0, g0, step(0.0, square(p, 0.8)));
    float g1 = alahambra_pattern(p);
    g1 = mix(1.0, g1, step(d - 0.35, 0.0));

    float patterns = min(g0, g1);

    return aastep(0.5, min(lines, patterns));
}

// Box in the middle of the room that creates the shadows
float shadowBox(vec3 l)
{
    vec2 uv;
    vec3 labs = abs(l);
    float lmax = max(labs.x, max(labs.y, labs.z));
    if (labs.x == lmax) uv = l.yz;
    if (labs.y == lmax) uv = l.xz;
    if (labs.z == lmax) uv = l.xy;

	uv /= lmax;

    return shadowPlane(uv);

    /*uv = floor(uv * 5.0);
    return mod(uv.x+uv.y, 2.0) * 0.5 + 0.5;
    return length(uv);*/
}

float mapLamp(vec3 p)
{
    float l = 2.0;
    vec3 d = abs(p) - l;
  	return min(max(d.x,max(d.y,d.z)),0.0)+length(max(d,0.0));
}

float raymarchLamp(vec3 ro, vec3 rd)
{
	float maxd = 30.0;
    float h = 1.0;
    float t = 0.1;
    for( int i=0; i<160; i++ )
    {
        if( h<(0.001*t)||t>maxd ) break;
	    h = mapLamp( ro+rd*t );
        t += h;
    }

    if( t>maxd ) t=-1.0;
    return t;
}

// Simplest lighting possible
float lighting(vec3 p, vec3 n)
{
    vec3 l = -normalize(p);
    float c = dot(n, l);
    float shadow = shadowBox(-l);
    c *= shadow;
    return c;
}

// Transform from spherical to cartesian coordinates
vec3 sphe2cart(vec2 p)
{
    vec2 s  = sin(p);
    vec2 c  = cos(p);
    return normalize(vec3(c.x * s.y, c.y, s.x * s.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

#if MOUSE_INPUT

#if MOUSE_INPUT_MODE == 0 
    // Rotate camera around the center
    vec2 camSphe = vec2(0.0, 1.175) + iMouse.xy / iResolution.xy * vec2(5.0, 0.7);
    vec3 co = sphe2cart(camSphe) * 9.0;
    vec3 cd = vec3(0.0, 0.0, 0.0);
#else
    // Rotate the camera from the corner
    vec3 co = vec3(8.5, -2.0, 8.5);
    vec2 camSphe = vec2(0.0, 1.57) + (vec2(iMouse.x, -iMouse.y) / iResolution.xy - vec2(0.5)) * vec2(3.0, 1.5);
    vec3 cd = co + sphe2cart(vec2(-2.4, 1.3) + camSphe);
#endif

#else

    vec2 camSphe;
    if (iMouse.z < 1.0)
    {
        camSphe = vec2(iTime * 0.1, sin(iTime * 0.2) * 0.2 + 1.5);
    }
    else
    {
        camSphe = vec2(0.0, 1.175) + iMouse.xy / iResolution.xy * vec2(5.0, 0.7);
    }

    vec3 co = sphe2cart(camSphe) * 9.0;
    vec3 cd = vec3(0.0, 0.0, 0.0);

#endif

    // Camera
    vec3 cf = normalize(cd - co);
    vec3 cu = vec3(0.0,1.0,0.0);
    vec3 cr = normalize(cross(cf, cu));
    cu = cross(cr, cf);

    // Raymarch
    uv -= vec2(0.5);
    uv.x *= iResolution.x / iResolution.y;
    uv *= 1.0;
    vec3 rd = normalize(cf + cr * uv.x + cu * uv.y);
    float a = raymarch(co, rd);
    vec3 p0 = co + rd * a;
    vec3 n = calcNormal(p0);

    // Profit!
    vec3 colour = lighting(p0, n) * vec3(0.85, 0.75, 0.6);

    // Raymarch lamp
    float a1 = raymarchLamp(co, rd);
    if (a1 > 0.0)
    {
    	vec3 p1 = co + rd * a1;
        colour = mix(vec3(0.1), colour, shadowBox(p1));
    }

	fragColor = vec4(vec3(colour),1.0);
}

#define alpha 0.
#define beta 10.

#define PI 3.14159

// Thanks to Lance for the insight on making more than 1 spiral branch
#define NUM_BRANCHES 4.

#define pulse_period 2.5
#define twist_period (5.)
#define amplitude 10.

vec4 getCornerColors(vec2 coord)
{
    vec2 halfRes = iResolution.xy * 0.5;
    float totalArea = iResolution.x * iResolution.y;

    vec3 cornerColors[4];

    cornerColors[0] = vec3(1.0, 0.0, 0.0);
    cornerColors[1] = vec3(0.0, 0.0, 1.0);
    cornerColors[2] = vec3(0.0, 1.0, 0.0);
    cornerColors[3] = vec3(1.0, 1.0, 0.0);

    vec2 cornerCoords[4];

    cornerCoords[0] = vec2(-1.) * halfRes;
    cornerCoords[1] = vec2(1., -1.) * halfRes;
    cornerCoords[2] = vec2(1.) * halfRes;
    cornerCoords[3] = vec2(-1., 1.) * halfRes;

	vec3 result = vec3(0.0);

	for(int i = 0; i < 4; i++)
	{
		vec2 cCoord = cornerCoords[i] * iResolution.xy;

		vec2 d = cornerCoords[i] - coord;

        float a = abs(d.x * d.y);

		float w = a / totalArea;

		result += w * cornerColors[i];
    }

	return vec4(result, 1.0);
}

vec4 spiral4(vec2 coord)
{	
	float alpha_t = alpha - iTime * 50.0;

	float x = coord.x;
	float y = coord.y;

	float r = sqrt(dot(coord, coord));

	float phi = atan(y, x);

	float phi_r = (r - alpha_t) / beta;

	float r_phi = alpha_t + (beta * phi);

	float remainder = abs(cos(phi) - cos(phi_r));

	if (remainder < 0.5)
	{
		return vec4(vec3(0), 1.0);
	}
	else
	{
		return vec4(vec3(remainder), 1.0);
	}
}

vec4 spiral5(vec2 coord)
{
	float alpha_t = alpha - iTime * 50.;

	float x = coord.x;
	float y = coord.y;

	float r = sqrt(x * x + y * y);

	float phi = atan(y, x) * NUM_BRANCHES;

	float phi_r = (r - alpha_t) / beta;

	float r_phi = alpha_t + (beta * phi);

	float remainder = abs(sin(phi) - sin(phi_r));

	remainder += abs(cos(phi) - cos(phi_r));

	if (remainder < 1.0)
	{
		vec4 c1 = vec4(1, 0, 0, 1);
		vec4 c2 = vec4(0, 0, 1, 1);

		float t = (phi / (2. * PI)) + 0.5;

		return mix(c1, c2, remainder);
	}
	else
	{
        vec4 c1 = vec4(1, 0, 0, 1);
		vec4 c2 = vec4(0, 0, 1, 1);
        return mix(c1, c2, remainder);
	}
}

vec4 spiral6(vec2 coord)
{
	float alpha_t = alpha - iTime * 50.;

	float x = coord.x;
	float y = coord.y;

	float r = sqrt(x * x + y * y);

	float phi = atan(y, x) * -NUM_BRANCHES;

	float phi_r = (r - alpha_t) / beta;

	float r_phi = alpha_t + (beta * phi);

	float remainder = abs(sin(phi) - sin(phi_r));

	remainder += abs(cos(phi) - cos(phi_r));

	if (remainder < 1.0)
	{
		vec4 c1 = vec4(1, 0, 0, 1);
		vec4 c2 = vec4(0, 0, 1, 1);

		float t = (phi / (2. * PI)) + 0.5;

		return mix(c1, c2, remainder);
	}
	else
	{
        vec4 c1 = vec4(1, 0, 0, 1);
		vec4 c2 = vec4(0, 0, 1, 1);
        return mix(c1, c2, remainder);
	}
}

vec2 twist2(vec2 coord)
{
	vec2 diff = coord;

	float l = length(diff);

	float twistRadius = length(iResolution.xy * 0.5);

	if (l < twistRadius)
	{
		float adjustedCurrTime = iTime;

		float k = floor(adjustedCurrTime / twist_period);

        float dt = mod(iTime, twist_period) / twist_period;

		float rad = PI * 0.1;

        //rad = rad * (sin(l / 20. + iTime));

        rad *= sin(l / 20.) + (l / length(iResolution.xy) * 0.5);// * sin(iTime);


		vec2 unitDiff = normalize(diff);

		float diffRad = atan(unitDiff.y, unitDiff.x);

		float newRad = diffRad + rad;

		vec2 newDiff = vec2(cos(newRad), sin(newRad)) * l;

		vec2 newCoord = newDiff;

		return newCoord;
	}
	else
	{
		return coord;
	}
}

vec2 pulse3(vec2 coord)
{
	// this indicates how many periods have passed
	float k = floor(iTime / pulse_period);

	// currTime - (k * period) basically gives us the remainder of the above division, so if we divide
	// that by the period we have a representation of time passed relative to the period,  parameterized
	// to be between 0 and 1
	float dt = (iTime - (k * pulse_period)) / pulse_period;

	dt = iTime / pulse_period;

	vec2 diff = coord;

	// Length of diff comes in handy for a few things
	float l = length(diff);

	// Same trick as the above. Use amplitude as indicator of how many periods away we are and flip the offset direction based on evenness
	int i = int(floor(l / amplitude));

	//bool isEven = mod(i, 2) == 0.;
    bool isEven = true;

	//float sign = isEven ? 1.0 : -1.0;

	float sign = sin(l / 20.);

	float offset =  amplitude * sin(dt * PI * 2.0);

	float newL = l + (sign * offset);
	//float newL = l + offset;

	vec2 normDiff = normalize(diff);
	vec2 newDiff = normDiff * newL;

	return newDiff;
}

vec4 distLight(vec2 coord)
{
    float dist = length(coord);

    float t = dist / length(iResolution.xy * 0.5);

    t = 1.0 - t;

    float v = mix(0., 0.8, t);

    return vec4(vec3(v), 1.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy - (iResolution.xy * 0.5);

    uv = pulse3(uv);
    uv = twist2(uv);

    //fragColor = getCornerColors(uv);


    fragColor = 
        (spiral6(uv) 
        * spiral5(uv))
    	* distLight(uv);
        //* getCornerColors(uv);

}

float randomNoise(vec2 p) {
  return fract(sin(47.*p.x+p.y*9973.) + iTime * 0.5);
}

float smoothNoise(vec2 p) {
  vec2 nn = vec2(p.x, p.y+1.);
  vec2 ee = vec2(p.x+1., p.y);
  vec2 ss = vec2(p.x, p.y-1.);
  vec2 ww = vec2(p.x-1., p.y);
  vec2 cc = vec2(p.x, p.y);

  float sum = 0.;
  sum += randomNoise(nn)/8.;
  sum += randomNoise(ee)/8.;
  sum += randomNoise(ss)/8.;
  sum += randomNoise(ww)/8.;
  sum += randomNoise(cc)/2.;

  return sum;
}

float interpolatedNoise(vec2 p) {
  float q11 = smoothNoise(vec2(floor(p.x), floor(p.y)));
  float q12 = smoothNoise(vec2(floor(p.x), ceil(p.y)));
  float q21 = smoothNoise(vec2(ceil(p.x), floor(p.y)));
  float q22 = smoothNoise(vec2(ceil(p.x), ceil(p.y)));

  float r1 = mix(q11, q21, fract(p.x));
  float r2 = mix(q12, q22, fract(p.x));

  return mix (r1, r2, fract(p.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.yy;



    float tiles = 4.;
    q = floor(q * tiles);
  	// Six components of noise in a fractal sum

	float x = interpolatedNoise(q + 2.);
    float y = interpolatedNoise(q + 4. );
    float z = interpolatedNoise(q + 8. );
    vec3 col = vec3(x,y,z);
    float lum = (0.2126 * col.x) + (0.7152 * col.y) + (0.0722 * col.z); 
	fragColor = vec4(col , 1.0);

}

