#define CAMERA_ANIMATION
#define CAMERA_ANIMATION_ROTATE
#define CAMERA_ANIMATION_ZOOM
#define RAINBOW_TRAIL_QUANTIZATION
#define RAINBOW_TRAIL_FADING

#define SUPERSAMPLING
#define AA	4.

#define SZ 12.                 // Stars size
#define RP 8.5                 // Rainbow wave length
#define RS 13.5                // Rainbow wave speed
#define RA 0.013               // Rainbow wave amplitude
#define R6 vec4(.5, .0, .9, 1) // Rainbow wave color 6
#define R5 vec4(.0, .4, .9, 1) // Rainbow wave color 5
#define R4 vec4(.0, .9, .0, 1) // Rainbow wave color 4
#define R3 vec4(.9, .9, .0, 1) // Rainbow wave color 3
#define R2 vec4(.9, .6, .0, 1) // Rainbow wave color 2
#define R1 vec4(.9, .0, .0, 1) // Rainbow wave color 1
#define BG vec3(.0, .2, .4567) // Background color
#define SS 0.15625             // Size of sprite segment
#define SC 6.0                 // Number of sprite segments
#define SU RS                  // Animation speed
#define BT iTime         // Base time

vec3 background(vec2 p, inout vec4 fragColor)
{
    fragColor = vec4(BG, 1);
    return BG;
}

vec2 grid(vec2 p, vec2 sz)
{
    return floor(p * sz);
}

#define _ 0. // Color Palette Index 0
#define B 1. // Color Palette Index 1
#define D 2. // Color Palette Index 2
#define O 3. // Color Palette Index 3
#define Q(i,a,b,c,d,e,f,g,h) if(y==i)m=(a+4.*(b+4.*(c+4.*(d+4.*(e+4.*(f+4.*(g+h*4.)))))));

// Star sprite
void star(vec2 p, inout vec4 col)
{
    p = p * iResolution.xy;

    vec2 scale = vec2(.1);

    vec3 res = BG;

   vec2 gv = grid(p, scale); // The grid guide

   // if (gv.x >= 0. && gv.y >= 0. &&
   //     gv.x <= 15. && gv.y <= 15.) {
    {   
        	res = vec3(mod(gv.x + gv.y, 2.) * .1 + BG);

        // Indexing is upside down.
        int y = 1 - int(scale.y - gv.y + 10.);

    	float m = 0.;
		Q(0, _,_,_,_,_,B,B,B)
		Q(1, _,_,_,B,B,B,D,O)
		Q(2, _,_,B,B,D,D,D,O)
		Q(3, _,B,B,O,D,D,O,O)
		Q(4, _,B,D,O,O,O,O,O)
		Q(5, B,B,D,D,O,O,D,D)
		Q(6, B,D,D,D,O,D,D,D)

    	float ldx = 15. - gv.x; // Calculate the left  bit index
        float rdx = gv.x;       // Calculate the right bit index
        float bit = 0.;

        if (gv.x >= 8.)	bit = mod(m / pow(4., ldx), 4.); // Decode
        else            bit = mod(m / pow(4., rdx), 4.); // Mirror
    	bit = floor(bit);                                // Sharpen    

        // Colorize
             if (bit > 2.) res = vec3(1,0,0);
        else if (bit > 1.) res = vec3(1);
        else if (bit > 0.) res = vec3(0);
    }

    col = vec4(res, 1);
}


void rainbow(vec2 p, inout vec4 fragColor)
{
    float x = 
#ifdef RAINBOW_TRAIL_QUANTIZATION
    	floor(RP * p.x);
#else
        p.x;
#endif

    // Transformation
    p.y += sin(x * RP + RS * BT) * RA; // Wavy line
    p.y = p.y * 1.2 - .22;             // Scale

    // Bounds test
    if (p.y >= .7 || p.y <= 0.1 || p.x > .33) return;
    else if (p.y > .6) fragColor = R6; // Color 6
    else if (p.y > .5) fragColor = R5; // Color 5
    else if (p.y > .4) fragColor = R4; // Color 4
    else if (p.y > .3) fragColor = R3; // Color 3
    else if (p.y > .2) fragColor = R2; // Color 2
	else               fragColor = R1; // Color 1

#ifdef RAINBOW_TRAIL_FADING
    fragColor.xyz = abs(mix(BG, fragColor.xyz,
                               pow(clamp(1.2 + p.x, 0., 1.), 1.4)));
#endif
}

void nyan(vec2 p, inout vec4 fragColor)
{
    p.x *=0.7; 
    if (p.x >= 1. || p.x <= 0. || p.y >= 1. || p.y <= 0. ) return;
    float x = (floor(mod(BT * SU, SC)) + p.x) * SS;
	vec4 s = texture(iChannel0, vec2(x, p.y));
	if (s.w > 0.) fragColor = s;
}

void draw(vec2 sp, vec2 p, inout vec4 fragColor)
{
    // Draw elements using the painter's algorithm.
    background(p, fragColor);
  //  star(sp, fragColor);
    rainbow(p, fragColor);
    nyan(p, fragColor);
}

vec2 camera(vec2 p)
{
#ifdef CAMERA_ANIMATION_ZOOM
    p *= (3.5 + 0.5 * cos(RP + BT * 0.42 + 0.92));
#else
    p *= 3.;
#endif

    p -= vec2(1.2 + .3 * cos(1.29 + BT), 1. + sin(0.3 * BT) * .6);

#ifdef CAMERA_ANIMATION_ROTATE
    float bt = 0.1 * cos(BT);
    float st = sin(bt);
    float ct = cos(bt);
    p = vec2(p.x * ct - p.y * st, p.y * ct + p.x * st);
#endif

    return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = vec2(
        fragCoord.x / iResolution.y, 
        1.0 - fragCoord.y / iResolution.y);

    vec2 p = uv;

#ifdef CAMERA_ANIMATION
    p = camera(uv);
#else
    p = p * 3. - vec2(1.2, 1.);
#endif

#ifdef SUPERSAMPLING
    // Antialiasing via supersampling
    vec3 col = vec3(0);    
    float e = 1. / min(iResolution.y , iResolution.x);    
    for (float i = -AA; i < AA; ++i) {
        for (float j = -AA; j < AA; ++j) {

            draw(uv, p + vec2(i, j) * (e/AA), fragColor);

            col += fragColor.xyz / (4.*AA*AA);
        }
    }

    fragColor = vec4(col, 1);
#else
    draw(p);
#endif /* SUPERSAMPLING */

}

float spiral(float angle, float radius, float frequency, float rate)
{
	return fract(0.3 * radius * frequency + rate * iTime + angle);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 pos = fragCoord.xy - (iResolution.xy * 0.5);
    float angle = atan(pos.y, pos.x) / (2.0 * 3.14159);
    float radius = sqrt(dot(pos, pos));
    float spiral1 = spiral(angle, radius, 10.0 + 0.1 * sin(0.5 * iTime), 2.0);
    float spiral2 = spiral(angle, radius, 20.0 + 0.05 * cos(0.7 * iTime), -1.9);
    float spiral3 = spiral(angle, radius, 15.0, 0.5 * sin(0.84 * iTime));
	fragColor = vec4(spiral1, spiral2, (1.0 - spiral1 - spiral2), 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0*(fragCoord.xy/iResolution.xy) - 1.0;
    float i,j,k = 0.0;
    float t = iTime;
    float vol = texture(iChannel0, vec2(abs(sin(t) * 100.0), 1.0)).x;

    i += 1.0 - abs(
        (2.4 * uv.x +
         sin(0.1 * (t + (vol*10.0)) + (uv.y * (1.0 + (vol*7.0))) +
         sin(t + (uv.y * 13.0)))) * (1.0 + (vol * 50.0)));

    j += abs(sin(t * 10.0 * uv.x * uv.y)) * 10.0;

    k += sin(uv.x + t);

    k += uv.x + vol;

    fragColor = vec4(i,j+k,k,1.0);
}

