// Point in Quad test. Note that it works for selfintersecting quads. No square roots
// required. Derived form this shader: https://www.shadertoy.com/view/lsBSDm

float cross2d( in vec2 a, in vec2 b ) { return a.x*b.y - a.y*b.x; }

float pointInQuad( in vec2 p, in vec2 a, in vec2 b, in vec2 c, in vec2 d )
{
    vec2 e = b-a;
    vec2 f = a-d;
    vec2 g = a-b+c-d;
    vec2 h = p-a;

    float k0 = cross2d( f, g );
    float k1 = cross2d( e, g );
    float k2 = cross2d( f, e );
    float k3 = cross2d( g, h );
    float k4 = cross2d( e, h );
    float k5 = cross2d( f, h );

    float l0 = k2 - k3 + k0;
    float l1 = k2 + k3 + k1;
    float m0 = l0*l0 + k0*(2.0*k4 - l0);
    float m1 = l1*l1 + k1*(2.0*k5 - l1);
    float n0 = m0    + k0*(2.0*k4 + k3 - k2);
    float n1 = m1    + k1*(2.0*k5 - k3 - k2);

    float b0 = step( m0*m0, l0*l0*n0 );
    float b1 = step( m1*m1, l1*l1*n1 );

    float res = (m0>0.0) ? ((m1>0.0) ? b1*b0 : 
                                       b0) : 
                           ((m1>0.0) ? b1 : 
                                       b1 + b0 - b1*b0);

    if( l0*l1 < 0.0 )  res -= b1*b0;

    return res;
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
	vec2 pa = p - a;
	vec2 ba = b - a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;

    vec2 a = cos( 1.11*iTime + vec2(0.1,4.0) );
    vec2 b = cos( 1.13*iTime + vec2(1.0,3.0) );
    vec2 c = cos( 1.17*iTime + vec2(2.0,2.0) );
    vec2 d = cos( 1.15*iTime + vec2(3.0,1.0) );

    float isQuad = pointInQuad( p, a, b, c, d );

    vec3 col = vec3( isQuad*0.5 );

    float h = 2.0/iResolution.y;
    col = mix( col, vec3(1.0), 1.0-smoothstep(h,2.0*h,sdSegment(p,a,b)));
    col = mix( col, vec3(1.0), 1.0-smoothstep(h,2.0*h,sdSegment(p,b,c)));
    col = mix( col, vec3(1.0), 1.0-smoothstep(h,2.0*h,sdSegment(p,c,d)));
    col = mix( col, vec3(1.0), 1.0-smoothstep(h,2.0*h,sdSegment(p,d,a)));

	fragColor = vec4( col,1.0);
}

#define time iTime*0.5

float grid(vec2 p) {
  vec2 orient = normalize(vec2(1.0,3.0));
  vec2 perp = vec2(orient.y, -orient.x);
  float g = mod(floor(1. * dot(p, orient)) + floor(1. * dot(p, perp)), 2.);
  return g;
}

#define samp 30.
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 p = fragCoord.xy / 50. + vec2(-time, time);
  vec2 q = (fragCoord.xy - (iResolution.xy / 2.)) / iResolution.x / 1.5 ;
  vec4 c = vec4(grid(p));
  if (q.x + 0.1 * q.y > 100.) {
    fragColor = c;
  }
  else {
    vec4 cc = vec4(0.0);
    float total = 0.0;

    float radius = length(q) * 100.;
    for (float t = -samp; t <= samp; t++) {
      float percent = t / samp;
      float weight = 1.0 - abs(percent);
	  float u = t / 100.;
      vec2 dir = vec2(fract(sin(537.3 * (u + 0.5)) ) , fract(sin(523.7 * (u + 0.25)) ));
      dir = normalize(dir) * 0.01;
      float skew = percent * radius;
      vec4 samplev = vec4(
          grid(vec2(0.03,0.) + p +  dir * skew),
          grid(radius * vec2(0.005,0.00) + p +  dir * skew),
          grid(radius * vec2(0.007,0.00) + p +  dir * skew),
          1.0);
      cc += samplev * weight;
      total += weight;
    }


    fragColor = cc / total - length(q ) * vec4(1.,1.,1.,1.) * 1.5;
  }
}

#define alpha 0.0
#define beta 10.0

vec4 getCornerColors(vec2 coord)
{
    vec4 cornerColors[4];

    cornerColors[0] = vec4(1.0, 0, 0, 1.0);
    cornerColors[1] = vec4(0, 0, 1.0, 1.0);
    cornerColors[2] = vec4(0, 1.0, 0.0, 1.0);
    cornerColors[3] = vec4(1.0, 1.0, 0.0, 1.0);

    vec2 cornerCoords[4];
    cornerCoords[0] = vec2(0);
    cornerCoords[1] = vec2(1, 0);
    cornerCoords[2] = vec2(1);
    cornerCoords[3] = vec2(0, 1);


	vec4 result = vec4(0.0);

	float totalArea = dot(iResolution.xy, iResolution.xy);

	for(int i = 0; i < 4; i++)
	{
		vec2 cCoord = cornerCoords[i] * iResolution.xy;

		vec2 diff = coord - cCoord;

		float area = dot(diff, diff);

		result += ((totalArea - area) / totalArea) * cornerColors[i];
	}

	return result;
}

vec4 spiral4(vec2 coord)
{	
	float alpha_t = alpha - iTime * 50.0;

	float x = coord.x;
	float y = coord.y;

	float r = sqrt(dot(coord, coord));

	float phi = atan(y, x);

	float phi_r = (r - alpha_t) / beta;

	float r_phi = alpha_t + (beta * phi);

	float remainder = abs(cos(phi) - cos(phi_r));

	if (remainder < 0.5)
	{
		return vec4(vec3(0), 1.0);
	}
	else
	{
		return vec4(vec3(remainder), 1.0);
	}
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy - (iResolution.xy * 0.5);
	//fragColor = spiral4(uv) * vec4(uv,0.5+0.5*sin(iTime),1.0);
    fragColor = spiral4(uv) * (getCornerColors(fragCoord.xy) * 0.5);
}

const int HIST_NUM_BUCKETS = 32;
const int HIST_ITER_PER_BUCKET = 1024;
const float HIST_NUM_BUCKETS_F = float(HIST_NUM_BUCKETS);
const float HIST_ITER_PER_BUCKET_F = float(HIST_ITER_PER_BUCKET);
const float HIST_SCALE = 5.0;

const int VAR_NUM_AVG = 256;
const float VAR_NUM_AVG_F = float(VAR_NUM_AVG);
const float VAR_SCALE = 3.0;

//note: uniform pdf rand [0;1[
float hash12n(vec2 p)
{
	p  = fract(p * vec2(5.3987, 5.4421));
    p += dot(p.yx, p.xy + vec2(21.5351, 14.3137));
	return fract(p.x * p.y * 95.4307);
}

//note: uniform pdf rand [0;1[
vec3 hash32n(vec2 p)
{
	p  = fract(p * vec2(5.3987, 5.4421));
    p += dot(p.yx, p.xy +  vec2(21.5351, 14.3137));
	return fract(vec3(p.x * p.y * 95.4307, p.x * p.y * 97.5901, p.x * p.y * 93.8369));
}

//note: triangular pdf rand [0;2[
vec3 hash32t(vec2 p)
{
	p = fract(p * vec2(5.3987, 5.4421));
	p += dot(p.yx, p.xy + vec2(21.5351, 14.3137));
	float pxy = p.x * p.y;
	return fract(pxy * vec3(95.43070, 97.59010, 93.83690))
		 + fract(pxy * vec3(75.04909, 75.04957, 75.04961));
}


//note: remaps v to [0;1] in interval [a;b]
float remap( float a, float b, float v )
{
	return clamp( (v-a) / (b-a), 0.0, 1.0 );
}
vec2 remap( vec2 a, vec2 b, vec2 v )
{
	return clamp( (v-a) / (b-a), vec2(0.0), vec2(1.0) );
}

//note: quantizes in l levels
float truncate( float a, float l )
{
	return floor(a*l)/l;
}

// ====

vec3 n1rand( vec2 n )
{
	float t = fract( iTime );
    return vec3( hash12n( n + 1337.0*t ) );
}

vec3 n1rand_rgb( vec2 n )
{
	float t = fract( iTime );
	return hash32n( n + 1337.0*t );
}

vec3 n2rand( vec2 n )
{
	float t = fract( iTime );
    return 0.5 * hash32t( n + 1337.0*t ).xxx;
}

vec3 n2rand_rgb( vec2 n )
{
	float t = fract( iTime );
    return 0.5 * hash32t( n + 1337.0*t );
}


float lum( vec3 c )
{
    if ( iMouse.z < 1.0 )
    {
        //relative rgb-luminance weighted
        //return sqrt( 0.299*c.r*c.r + 0.587*c.g*c.g + 0.114*c.b*c.b ); //perceived luminance... skewed?
        return 0.299 * c.r + 0.587 * c.g + 0.114 * c.b;
		//return dot( c, vec3(0.22, 0.707, 0.071) );
    }
    else
    	return dot( c, vec3(0.33333) ); //average
}

vec3 eval( int iter, float t, float seed )
{
    if ( iter == 0 )
        return n1rand( vec2(t, 0.5) + seed );
    else if ( iter == 1 )
        return n2rand( vec2(t, 0.5) + seed );
    else if ( iter == 2 )
        return n1rand_rgb( vec2(t,0.5) + seed );
	else
        return n2rand_rgb( vec2(t,0.5) + seed );
}

float histogram( int iter, vec2 luv )
{
	float t = luv.x;
	vec2 bucket = vec2( truncate(t,HIST_NUM_BUCKETS_F), truncate(t,HIST_NUM_BUCKETS_F)+1.0/HIST_NUM_BUCKETS_F);
	float bucketval = 0.0;
    float var = 0.0;
	for ( int i=0;i<HIST_ITER_PER_BUCKET;++i)
	{
		float seed = float(i)/HIST_ITER_PER_BUCKET_F;

		vec3 r = eval( iter, t, seed );

		bucketval += step(bucket.x,lum(r)) * step(lum(r),bucket.y);
	}
	bucketval /= HIST_ITER_PER_BUCKET_F;
	bucketval *= HIST_SCALE;
	return step( bucketval, luv.y );
}


float variance( int iter, vec2 luv )
{
    vec3 sum = vec3(0.0);
    for ( int i=0; i<VAR_NUM_AVG; ++i )
    {
        float seed = float(i) / VAR_NUM_AVG_F;
        vec3 r = eval( iter, luv.x, seed );
        sum += r;
    }
    vec3 mean = sum / VAR_NUM_AVG_F;

    float var = 0.0;
	for ( int i=0; i<VAR_NUM_AVG; ++i )
	{
		float seed = float(i)/VAR_NUM_AVG_F;
		vec3 r = eval( iter, luv.x, seed );

        vec3 diff = r - mean;
        var += dot( diff, diff );
	}
    var /= float(VAR_NUM_AVG-1);
    var *= VAR_SCALE;
    float oc = step( var, luv.y );
    return step( luv.y, oc );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	vec3 o = vec3(0.0);
    if ( uv.x < 1.0/4.0 )
    {
		o = eval( 0, uv.x, uv.y );
		if ( uv.y < 1.0 / 4.0 )
        {
            vec2 luv = remap( vec2(0.0/4.0, 0.0/4.0), vec2(1.0/4.0, 2.0/4.0), uv );
			o = vec3( 0.85 - 0.7 * histogram( 0, luv ) );
        }
        else if ( uv.y < 2.0/4.0 )
        {
            vec2 luv = remap( vec2(0.0/4.0, 1.0/4.0), vec2(1.0/4.0, 2.0/4.0), uv );
            o = vec3( 0.85 - 0.7 * variance( 0, luv ) );
        }
    }
	else if ( uv.x < 2.0/4.0 )
	{
		o = eval( 1, uv.x, uv.y );
		if ( uv.y < 1.0 / 4.0 )
        {
            vec2 luv = remap( vec2(1.0/4.0, 0.0/4.0), vec2(2.0/4.0, 2.0/4.0), uv );
			o = vec3( 0.85 - 0.7 * histogram( 1, luv ) );
        }
        else if ( uv.y < 2.0/4.0 )
        {
            vec2 luv = remap( vec2(1.0/4.0, 1.0/4.0), vec2(2.0/4.0, 2.0/4.0), uv );
            o = vec3( 0.85 - 0.7 * variance( 1, luv ) );
        }
	}
	else if ( uv.x < 3.0 / 4.0 )
	{
        o = eval( 2, uv.x, uv.y );
		if ( uv.y < 1.0 / 4.0 )
        {
            vec2 luv = remap( vec2(2.0/4.0, 0.0/4.0), vec2(3.0/4.0, 2.0/4.0), uv );
			o = vec3( 0.85 - 0.7 * histogram( 2, luv ) );
        }
        else if ( uv.y < 2.0/4.0 )
        {
            vec2 luv = remap( vec2(2.0/4.0, 1.0/4.0), vec2(3.0/4.0, 2.0/4.0), uv );
            o = vec3( 0.85 - 0.7 * variance( 2, luv ) );
        }
	}
    else
    {
        o = eval( 3, uv.x, uv.y );
		if ( uv.y < 1.0 / 4.0 )
        {
            vec2 luv = remap( vec2(3.0/4.0, 0.0/4.0), vec2(4.0/4.0, 2.0/4.0), uv );
			o = vec3( 0.85 - 0.7 * histogram( 3, luv ) );
        }
        else if ( uv.y < 2.0/4.0 )
        {
            vec2 luv = remap( vec2(3.0/4.0, 1.0/4.0), vec2(4.0/4.0, 2.0/4.0), uv );
            o = vec3( 0.85 - 0.7 * variance( 3, luv ) );
        }

    }

	//display lines
    if ( abs(uv.x - 1.0/4.0) < 0.002 ) o = vec3( 0.0 );
	if ( abs(uv.x - 2.0/4.0) < 0.002 ) o = vec3( 0.0 );
    if ( abs(uv.x - 3.0/4.0) < 0.002 ) o = vec3( 0.0 );

	fragColor = vec4( o, 1.0 );
}

#define A  0.0
#define B  (iMouse.z <= 0.0 ? (sin(iTime*1.5) * 0.5) : iMouse.y / iResolution.y - 0.5)
#define C  0.2

#define EDGE   0.005
#define SMOOTH 0.0025

#define PI 3.14159265359

// F(x,y) = F(x) - y
float F ( in vec2 coords )
{
    // convert time from 0-1 to 0-pi/2
    float T = coords.x * PI * 0.5;

    // pre-calculate sin and cosine
    float snt = sin(T);  // sin 0 = 0.  sin pi/2 = 1
    float cst = cos(T);  // cos 0 = 1.  cos pi/2 = 0

    // interpolating spline, not an exterpolating spline, so should pass through the end control points
    // but not the middle control points.
	// basis - google: graph y = (0.5*sin(x)*(sin(x)+1.0)), y = (0.5*cos(x)*(cos(x)+1.0)), y = (0.5*sin(x)*(sin(x)-1.0))+(0.5*cos(x)*(cos(x)-1.0)) from 0 to pi/2
    // basis sum - google: graph y = (0.5*cos(x)*(cos(x)-1.0)) + (0.5*sin(x)*(sin(x)+1.0)) + (0.5*cos(x)*(cos(x)+1.0)) + (0.5*sin(x)*(sin(x)-1.0)) 
    return
        C *  (0.5*snt*(snt+1.0)) + 
        B * -(0.5*snt*(snt-1.0)) +        
        B * -(0.5*cst*(cst-1.0)) +         
        A *  (0.5*cst*(cst+1.0)) - 
        coords.y;
}

// gradiant function for finding G for a generic function when you can't
// get it analytically using partial derivatives.  We could do
// partial derivatives of F above, but I'm being lazy.
vec2 Grad( in vec2 coords )
{
    vec2 h = vec2( 0.01, 0.0 );
    return vec2( F(coords+h.xy) - F(coords-h.xy),
                 F(coords+h.yx) - F(coords-h.yx) ) / (2.0*h.x);
}

// signed distance function for F(x,y)
float SDF( in vec2 coords )
{
    float v = F(coords);
    vec2  g = Grad(coords);
    return abs(v)/length(g);
}

// signed distance function for Circle, for control points
float SDFCircle( in vec2 coords, in vec2 offset )
{
    coords -= offset;
    float v = coords.x * coords.x + coords.y * coords.y - EDGE*EDGE;
    vec2  g = vec2(2.0 * coords.x, 2.0 * coords.y);
    return v/length(g); 
}

//-----------------------------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    float aspectRatio = iResolution.x / iResolution.y;
    vec2 percent = ((fragCoord.xy / iResolution.xy) - vec2(0.25,0.5));
    percent.x *= aspectRatio;

    vec3 color = vec3(1.0,1.0,1.0);
    float dist = SDFCircle(percent, vec2(0.0,A));
	if (dist < EDGE + SMOOTH)
    {
        dist = max(dist, 0.0);
        dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
        color *= mix(vec3(1.0,0.0,0.0),vec3(1.0,1.0,1.0),dist);
    }

    dist = SDFCircle(percent, vec2(0.5,B));
	if (dist < EDGE + SMOOTH)
    {
        dist = max(dist, 0.0);
        dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
        color *= mix(vec3(0.0,1.0,0.0),vec3(1.0,1.0,1.0),dist);
    }    

    dist = SDFCircle(percent, vec2(1.0,C));
	if (dist < EDGE + SMOOTH)
    {
        dist = max(dist, 0.0);
        dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
        color *= mix(vec3(1.0,1.0,0.0),vec3(1.0,1.0,1.0),dist);
    }    

    dist = SDF(percent);
    if (dist < EDGE + SMOOTH)
    {
        dist = smoothstep(EDGE - SMOOTH,EDGE + SMOOTH,dist);
        color *= (percent.x >= 0.0 && percent.x <= 1.0) ? vec3(dist) : vec3(0.95);
    }

	fragColor = vec4(color,1.0);
}

#define GLOW 1.0

float	dSphere(vec2 pCenter, float pRayon, vec2 fragCoord)
{
    return length(fragCoord.xy - pCenter) - pRayon;
}

vec3	sGlow(vec3 pColor, float ld)
{
    return pColor * exp(-(ld/25.0)*0.5) * GLOW;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float	lt1 = sin(iTime);
    float	lt2 = cos(iTime);
    float	ld1 = dSphere(vec2(iResolution.xy*0.5) - vec2(50.0*lt1, 200.0*lt2), 10.0,fragCoord);
    float	ld2 = dSphere(vec2(iResolution.xy*0.5), 10.0,fragCoord);
    float	ld3 = dSphere(vec2(iResolution.xy*0.5) + vec2(200.0*lt1, 50.0*lt2), 10.0,fragCoord);
    vec3	lColor = sGlow(vec3(1.0, 0.0, 0.0), ld1);
    lColor += sGlow(vec3(0.0, 1.0, 0.0), ld2);
    lColor += sGlow(vec3(0.0, 0.0, 1.0), ld3);
    fragColor = vec4(lColor, 1.0);
}

vec2 fold = vec2(-0.5, -0.5);
vec2 translate = vec2(1.5);
float scale = 1.25;

vec3 hsv(float h,float s,float v) {
	return mix(vec3(1.),clamp((abs(fract(h+vec3(3.,2.,1.)/3.)*6.-3.)-1.),0.,1.),s)*v;
}

vec2 rotate(vec2 p, float a){
	return vec2(p.x*cos(a)-p.y*sin(a), p.x*sin(a)+p.y*cos(a));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 p = -1.0 + 2.0*fragCoord.xy/iResolution.xy;
	p.x *= iResolution.x/iResolution.y;
	p *= 0.02;
	float x = p.y;
	p = abs(mod(p, 2.0) - 1.);
	for(int i = 0; i < 32; i++){
		p = abs(p - fold) + fold;
		p = p*scale - translate;
		p = rotate(p, 3.14159/(0.10+sin(iTime*0.0022+float(i)*0.251)*0.50001+0.501));
	}
	float i = x*500.0 + atan(p.y, p.x) + iTime*0.003;
	float h = floor(i*6.0)/5.0 + 0.1;
	h += smoothstep(0.0, 0.4, mod(i*6.0/5.0, 1.0/5.0)*5.10)/5.10 - 1.1;
	fragColor=vec4(hsv(h, 1.0, smoothstep(-1.0, 3.0, length(p))), 30);
}

//
// Scene variables
//

#define NUM_SPHERES 4
#define MAX_BOUNCES 3
#define NUM_LIGHTS  1
#define DRAW_ROOM
#define DRAW_SPHERES

vec2 mouse;
vec2 uv;
float hit = 0.0;

//
// Scene constants
//

#define NUM_PLANES 5
#define PI 3.14159265358979323846264
#define INFINITY 1e32
#define EPSILON  1e-3

vec3 ambLight = vec3(0.2);


//
// Material struct
//

struct mat_t
{
    vec3 ka;  // ambient color
	vec3 kd;  // diffuse color
    vec3 ks;  // specular color
    float ns; // specular exponent (shininess)
} mats;

//
// Ray struct
//

struct ray_t
{
	vec3 o; // origin
    vec3 d; // direction
} ray;


//
// Point light struct
//

struct pLight_t
{
	vec3 pos; // position of the light
    vec3 col; // color (intensity) of the light
} lights[ NUM_LIGHTS ];

//
// Intersection struct
//

struct intersection_t
{
    mat_t mat;   // material at intersection point
    vec3 hitpt;  // position in world space
    vec3 iray;   // incoming ray direction
    vec3 normal; // normal vector
	float t;     // direction along iray from ray origin
} ist;

vec3 intersection_shade( intersection_t hit )
{
	vec3 endCol = vec3(0);

    // Ambient
    endCol += hit.mat.ka * ambLight;

    for( int i = 0; i < NUM_LIGHTS; i++ )
    {
        // Diffuse
        vec3 l = normalize( lights[ i ].pos - hit.hitpt );
        vec3 n = hit.normal;
    	endCol += hit.mat.kd * max( dot( n, l ), 0.0 ) * lights[ i ].col;

        // Specular
        vec3 r = reflect( -l, n);
    	vec3 v = -hit.iray;
    	float s =  pow( max( dot( r, v ), 0.0) , hit.mat.ns );
    	endCol += hit.mat.ks * s * lights[ i ].col;
    }

    return endCol;
}

//
// Plane struct
//

struct plane_t
{
    mat_t mat;   // material
    vec3 center; // location
	vec3 normal; // normal defining plane
    vec3 up;     // up vector defining orientation
    vec2 dims;   // dimensions along up and normal x up

} planes[ NUM_PLANES ];

intersection_t plane_intersect( plane_t p, ray_t r)
{
	intersection_t ist;
    ist.t = INFINITY;

    float t = dot( ( p.center - r.o ), p.normal ) / dot( r.d, p.normal );
    if( t < 0.0 ) { return ist; }

    vec3 pt = (r.d * t) + r.o;
    vec3 rad = pt - p.center;
    if( ( abs( dot( rad, p.up ) ) < p.dims.y ) && ( abs( dot( rad, cross( p.up, p.normal ) ) ) < p.dims.x ) )
    {
    	ist.t = t;
        ist.iray = r.d;
        ist.hitpt = pt;
        ist.normal = p.normal;
        ist.mat = p.mat;
    }

    return ist;
}

//
// Sphere struct
//

struct sphere_t
{
    mat_t mat;
	vec3 pos;
    float r;
} spheres[ NUM_SPHERES ];

intersection_t sphere_intersect( sphere_t s, ray_t r )
{
    intersection_t intersection;

    float a = dot( r.d, r.d );
    float b  = 2.0 * dot( r.o - s.pos, r.d );
    float c  = - (s.r * s.r) + dot( r.o  - s.pos, r.o - s.pos );
    float d  = b*b - 4.0*a*c;
    if( d < 0.0 ) { ist.t = INFINITY; return ist; }
    ist.t = ( - b - sqrt( d ) ) / (2.0 * a);
    if( ist.t < 0.0 ) { ist.t = INFINITY; return ist; }
    ist.iray = r.d;
    ist.hitpt = (r.d * ist.t) + r.o;
    ist.normal = normalize( ist.hitpt - s.pos );
    ist.mat = s.mat;

    return ist;
}

//
// Camera struct
//

struct camera_t
{
	vec3 pos;

} cam;

ray_t camera_getRay( camera_t c, vec2 uv )
{
    ray_t ray;
    ray.o = c.pos;

    // Rotate camera according to mouse position
    float ca = cos(mouse.x), sa = sin(mouse.x);
    mat3 rotX = mat3(ca, 0.0, sa, 0.0, 1.0, 0.0, -sa, 0.0, ca);
    ca = cos(mouse.y), sa = sin(mouse.y);
    mat3 rotY = mat3(1.0, 0.0, 0.0, 0.0, ca, -sa, 0.0, sa, ca);
    mat3 rotM = rotX * rotY;

	ray.o = rotM*c.pos;
    ray.d = rotM*normalize( vec3( uv, -1.0 ) ); // should be -1! facing into scene

	return ray;
}

//
// Scene functions
//

void init_scene( void )
{
    // Initialize lights
    float w = float(NUM_LIGHTS)*8.0;
    float lr = w*0.5;
    for( int i = 0; i < NUM_LIGHTS; i++)
    {
    	lights[ i ].pos = vec3( -lr + w*float( i + 1 ) / float( NUM_LIGHTS + 1 ), 2, 3 );
        lights[ i ].col = vec3( 0.8 );
    }

    // Initialize camera
    cam.pos = vec3( 0.0, 0.0, 3.9 );

    // Initialize spheres
    float t = iTime * 0.5;
#ifdef DRAW_SPHERES
    for(int i = 0; i < NUM_SPHERES; i++)
    {
        float ifrc = float(i)/float(NUM_SPHERES)*2.0*PI;
        float r = 1.0;
        spheres[ i ].r = r;
        r *= 2.0;
        float ipi = float( i ) * 2.0 * PI / float( NUM_SPHERES );
        spheres[ i ].pos = vec3( r * sin( ipi + t ), r * cos( ipi + t ), -0.0 );
        spheres[ i ].mat.kd = vec3( 0.4 ) + 0.4 * vec3( sin(ifrc + t), sin(ifrc + t + 2.0*PI/3.0), sin(ifrc + t + 2.0*2.0*PI/3.0));      
        spheres[ i ].mat.ka = spheres[ i ].mat.kd;
        spheres[ i ].mat.ks = vec3(0.7);
        spheres[ i ].mat.ns = 128.0;
    }
#endif
#ifdef DRAW_ROOM
    float br = 4.0;
    for( int i = 0; i < NUM_PLANES; i++)
    {
        float fiPI = float(i)*0.5*PI;
        planes[ i ].center = vec3(cos(fiPI)*br,0,-sin(fiPI)*br);
        planes[ i ].normal = -normalize( planes[ i ].center );
        planes[ i ].up     = vec3(0,1,0);
        planes[ i ].dims   = vec2(br);
        planes[ i ].mat.ka = vec3(0.1);
	    planes[ i ].mat.ks = vec3(0.3);
    	planes[ i ].mat.ns = 128.0;

    }
    planes[ 3 ].center = vec3(0,br-EPSILON,0);
    planes[ 3 ].normal = vec3(0,-1,0);
    planes[ 3 ].up     = vec3(0,0,1);
    planes[ 3 ].dims   = vec2(br);
    planes[ 3 ].mat.ka = vec3(0.1);
    planes[ 3 ].mat.ks = vec3(0.0);
    planes[ 3 ].mat.ns = 128.0;
    planes[ 4 ].center = vec3(0,-br+EPSILON,0);
    planes[ 4 ].normal = vec3(0,1,0);
    planes[ 4 ].up     = vec3(0,0,1);
    planes[ 4 ].dims   = vec2(br);
    planes[ 4 ].mat.ka = vec3(0.1);
    planes[ 4 ].mat.ks = vec3(0.0);
    planes[ 4 ].mat.ns = 128.0;
    planes[ 0 ].mat.kd = vec3(0.9,0.1,0.1);
    planes[ 1 ].mat.kd = vec3(0.8,0.8,0.8);
    planes[ 2 ].mat.kd = vec3(0.1,0.9,0.1);
    planes[ 3 ].mat.kd = vec3(0.8,0.8,0.8);
    planes[ 4 ].mat.kd = vec3(0.8,0.8,0.8);
#endif
}

vec3 intersect_scene( ray_t ray )
{
    vec3 endCol = vec3(0);
    vec3 specMod = vec3(1);
    intersection_t bestI;
    intersection_t ht;
    ray_t r = ray;
    for( int j = 0; j < MAX_BOUNCES; j++)
    {
        bestI.t = INFINITY;
        // Intersect geometry, finding closest point
#ifdef DRAW_SPHERES
        for(int i = 0; i < NUM_SPHERES; i++)
        {
            ht = sphere_intersect( spheres[ i ], r );
            if( ht.t < bestI.t ) { bestI = ht; }
        }
#endif
#ifdef DRAW_ROOM
        for( int i = 0; i < NUM_PLANES; i++)
        {
            ht = plane_intersect( planes[ i ], r );
            if( ht.t < bestI.t ) { bestI = ht; }
        }
#endif
        // Quit if we don't hit anything
        if( bestI.t == INFINITY ) { break; }
     	hit = 1.0;

        // Shade
        endCol += specMod*intersection_shade( bestI );
        specMod *= bestI.mat.ks; // Keep track of specular intensity of each reflection

        // Reflect ray about normal
        r.o = bestI.hitpt + bestI.normal * EPSILON;
        r.d = reflect( r.d, bestI.normal );        
    }

    return endCol;
}

//
// Main loop
//

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Get screen coordinate
	uv = -1.0 + 2.0 * ( fragCoord.xy / iResolution.xy );
    uv.x *= (iResolution.x/iResolution.y);
    mouse = -1.0 + 2.0 * ( iMouse.xy / iResolution.xy );
    mouse *= 2.0*PI;

    // Initialize scene
    init_scene();

    // Intersect scene
    ray_t ray = camera_getRay( cam, uv );
    vec3 col = intersect_scene( ray );

    // Add background
    col = hit*col + (1.0-hit)*(1.0-0.2*length(uv))*vec3(1,1,0);
    fragColor = vec4( col, 1);
}

// Created by Edd Biddulph
// License for this shader: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//
// This is a recreation of the castle fly-by sequence from the original Unreal, released in 1998 and
// developed by Epic MegaGames. http://epicgames.com/
//
// This was mostly inspired by P_Malin's amazing Doom shader - https://www.shadertoy.com/view/lsSXzD
//
// I made a custom tool to convert the geometry from the original files from the game, but the textures
// were made 'by hand'. Unreal's levels are composed of convex shapes called 'brushes'. The brushes
// are combined using CSG operations to produce the level geometry. My tool detects cuboid-shaped brushes
// from which a distance field is simple to create. Most of the non-cuboid-shaped brushes can be created
// by cutting a cuboid with less than 4 oriented planes so my tool first generates a cuboid and adds
// those planes into the distance field code.


#define time (iTime-6.0)
float flagTime=0.0;

float hash(float n)
{
    return fract(sin(n)*43758.5453);
}

float smoothNoise2(vec2 p)
{
    p=fract(p/256.0);
    return textureLod(iChannel0, p, 0.0).r;
}

// Distorted texture coordinates for the rippling flag.
vec2 flagTC(vec2 p)
{
    float tm=-flagTime*1.4;
    return vec2(p.x+p.x*(cos(p.x*7.0+tm*6.0))*0.02+cos(p.y*6.0+tm*7.5)*0.01*p.x,p.y+p.x*cos(p.x*4.0+tm*5.0)*0.01);
}

vec3 flagTexture2(vec2 p)
{
    p.x+=0.05;
    p.y+=0.05;
    float d=1e2;
    d=min(d,length(p-vec2(-0.85,-0.13+p.x*-0.4))-0.5);
    d=min(d,length(p-vec2(-0.8,-1.2))-0.9);
    d=min(d,length(max(vec2(0.0),abs(p-vec2(0.4,-0.63))-vec2(0.5,0.3))));
    d=min(d,length(max(vec2(0.0),abs(p-vec2(0.9,0.0))-vec2(0.3,0.8))));
    d=min(d,length(max(vec2(0.0),abs(p-vec2(-0.9,0.0))-vec2(0.3,0.8))));
    vec2 p2=p+vec2(0.0,-0.07);
    d=min(d,length((p2-vec2(0.1,0.2))*vec2(1.0,0.9))+0.1-p2.y*1.3);
    d=max(d,-length(p-vec2(0.1,0.5))+0.15);
    vec2 p3=p+vec2(0.0,0.1);
    d=min(d,max(p.x, max(-(length((p3-vec2(0.1,0.2))*vec2(1.0,0.9))+0.1-p3.y*1.1), dot(p-vec2(0.0,0.12),normalize(vec2(-0.75,-1.0))))));
    return mix(vec3(0.2,0.1,0.1),vec3(1.0,0.2,0.1)*0.9,(0.3+(1.0-smoothstep(0.0,0.02,d))))*
        mix(0.7,1.0,smoothNoise2(p*40.0)*0.25+smoothNoise2(p*20.0)*0.25);
}

vec4 flagTexture(vec2 p2)
{
    vec2 p=p2.xy*0.7+vec2(0.5);
    vec2 c=flagTC(p);
    vec2 e=vec2(1e-2,0.0);
    float g=max(length((flagTC(p+e.xy)-c)/e.x),length((flagTC(p+e.yx)-c)/e.x));

    float b=step(abs(c.y-0.5),0.3)*step(c.x,0.8);
    float nb=-1.0+abs(c.y-0.5)*3.3;
    return vec4(flagTexture2((c*2.0-vec2(1.0))*vec2(-1.0,1.0))*(1.0-smoothstep(0.0,2.0,g)),
                step(abs(c.y-0.5)*2.0,0.7)*step(abs(c.x-0.5)*2.0,1.0)*smoothstep(nb+0.199,nb+0.2,smoothNoise2(c*vec2(32.0,0.5))));
}


// Brick pattern for tex_wmbrrw.
float brickt3(vec2 p2)
{
    vec2 fp2=fract(p2);
    float brick=length(max(vec2(0.0),abs(fp2-vec2(0.5))-vec2(0.4,0.36)));
    return 1.0-smoothstep(0.01,0.2,brick)-smoothNoise2(p2*vec2(7.0,4.0)*3.0)*0.2;
}

// Brick pattern for tex_nfloor6.
float brickt2(vec2 p2)
{
    vec2 fp2=fract(p2);
    float brick=length(max(vec2(0.0),abs(fp2-vec2(0.5))-vec2(0.465,0.465)));
    return 1.0-smoothstep(0.0,0.04,brick)+smoothNoise2(p2*vec2(4.0,4.0)*3.0)*0.0;
}

// Brick pattern for tex_wmcs.
float brickt(vec2 p2)
{
    vec2 fp2=fract(p2);
    float brick=length(max(vec2(0.0),abs(fp2-vec2(0.5))-vec2(0.44,0.32))*vec2(11.0/4.0,1.0));
    return 1.0-smoothstep(0.0,0.18,brick)+smoothNoise2(p2*vec2(8.0,4.0)*3.0)*0.4;
}

// Roof texture.
vec3 tex_Roof(vec2 p)
{
    vec2 tc0=floor(p*vec2(6.0,3.0));
    p.y+=0.5/3.0;
    vec2 tc=floor(p*vec2(6.0,3.0));
    vec2 tt=fract(p*vec2(6.0,3.0));

    tt.x+=smoothNoise2(p*10.0)*0.05+smoothNoise2(p*30.0)*0.02;

    float spike=smoothstep(0.8,1.0,smoothNoise2(vec2(p.x*100.0,tc.y*7.0+tc.x*1.0)))*0.4;
    float j=smoothNoise2(vec2(p.x*8.0,tc.y*3.0+tc.x*8.0))*0.1;
    float sh=1.0-(smoothstep(-spike,0.05,tt.y-0.5+j)-sqrt(smoothstep(0.05,0.7,tt.y-0.5+j)));
    sh*=smoothstep(0.0,0.1,tt.x)-smoothstep(0.9,1.0,tt.x+cos(tc0.x*9.0+tc0.y*2.0)*0.1);   

    vec3 col=mix(vec3(1.4,0.9,0.5)*0.5,vec3(1.0,0.7,0.5),smoothNoise2(p*vec2(128.0,8.0)))*mix(0.1,1.0,sh)*0.5;
    col*=vec3(1.0)+3.0*vec3(0.5,0.5,0.7)*pow(smoothNoise2(p+tc0),4.0);
    col*=sqrt(smoothNoise2(p*8.0-tc0*4.0)*0.5+smoothNoise2(p*64.0-tc0*1.0)*0.2);

    return col;
}

vec3 tex_wrck(vec2 p)
{
    p+=smoothNoise2(p)*0.2;
    vec3 col=mix(vec3(0.1,0.22,0.1)*0.3,vec3(0.3,0.4,0.25),smoothNoise2(p*2.0)*0.5+smoothNoise2(p*8.0)*0.25+smoothNoise2(p*32.0)*0.125);
    col=mix(vec3(0.4,0.3,0.1)*0.4,col*0.8,sqrt(smoothNoise2(p*64.0)*0.5+smoothNoise2(p*13.0)*0.125));
    vec2 p2=p+smoothNoise2(p*4.0)*0.3;
    col+=3.0*vec3(0.2,0.3,0.3)*pow(smoothNoise2(p2*4.0)*0.5,4.0);

    float ly=fract(p.y*12.0)-0.5+smoothNoise2(vec2(p.x*14.0,floor(p.y)))*0.2+smoothNoise2(vec2(p.x*5.0,floor(p.y*12.0)))*0.68;
    float lines=1.0-(smoothstep(0.0,0.1,ly)-smoothstep(0.1,0.8,ly))*0.3;

    col=mix(col,col*lines*(1.0+(smoothstep(-0.05,0.0,ly)-smoothstep(0.0,0.05,ly))*0.5),smoothstep(0.0,0.5,smoothNoise2(p*4.0)));
    col*=mix(0.7,1.0,smoothNoise2(p*16.0));
    col=mix(col,vec3(dot(col,vec3(1.0/3.0))),0.75);

    return col*0.8;
}

vec3 tex_wmbrrw(vec2 p)
{
    float s=mix(0.5,1.2,sqrt(smoothNoise2(p*3.0))+smoothNoise2(p*80.0));
    vec3 col=mix(mix(vec3(0.1),vec3(0.7,0.25,0.05),0.4)*0.4,
                 mix(vec3(0.1),vec3(0.3,0.2,0.09),0.4)*0.7*s,
                 sqrt(max(0.0,smoothNoise2(p*8.0)+0.15*smoothNoise2(p*32.0))))*0.7;
    vec2 p2=p*vec2(5.0,12.0)+vec2(smoothNoise2(p*24.0),smoothNoise2(p*24.0+vec2(2.0)))*0.05;
    p2.x+=floor(p2.y)*0.5;

    float bh=pow(0.5+0.5*cos(floor(p2.y)*14.0)*cos(floor(p2.x)*1.0),2.0)*0.5;

    float brick=brickt3(p2);
    vec3 bn=normalize(vec3(brickt3(p2+vec2(1e-3,0.0))-brick,brickt3(p2+vec2(0.0,1e-3))-brick,0.04));

    col*=mix(vec3(2.0),vec3(0.3+bh*1.0),brick);

    col.rgb*=vec3(0.5)+vec3(0.5)*dot(bn,normalize(vec3(1.0,-1.0,1.0)));

    vec2 shadp=fract(p*2.0-vec2(0.12,0.04));
    float shadow=mix(0.2,1.0,1.0-smoothstep(0.05,0.3,length(max(vec2(0.0),abs(shadp-vec2(0.5))-vec2(0.2,0.21)))));
    col*=shadow;
    vec2 beamp=fract(p*2.0);
    float hbeammask=1.0-smoothstep(0.07,0.08,abs(beamp.y-0.1));
    float vbeammask=1.0-smoothstep(0.07,0.08,abs(beamp.x-0.1));

    col*=1.0-2.0*vec3(0.4,0.4,0.5)*smoothstep(0.5,1.0,pow(max(0.0,smoothNoise2((p.yx*20.0)*vec2(1.0,1.0))),2.0));

    col=mix(col,0.3*mix(vec3(1.0,0.6,0.4)*0.5,vec3(1.0,0.7,0.4)*0.3,
                        smoothNoise2(beamp*vec2(5.0,128.0))+smoothNoise2(beamp*vec2(4.0,256.0)*2.0)*0.75
                       ),hbeammask);

    col*=smoothstep(0.05,0.16,abs(beamp.x-0.1))*smoothstep(0.05,0.16,abs(beamp.x-1.1));

    col=mix(col,0.3*mix(vec3(1.0,0.6,0.4)*0.5,vec3(1.0,0.7,0.4)*0.3,
                        smoothNoise2(beamp*vec2(128.0,5.0))+smoothNoise2(beamp*vec2(256.0,4.0)*2.0)*0.75
                       ),vbeammask);

    vec2 nn=vec2(smoothNoise2(p*1.0),smoothNoise2(p*10.0+vec2(23.0)))*2.0;
    col*=1.0+vec3(0.4,0.4,0.5)*smoothstep(0.5,1.0,smoothNoise2((p.yx*20.0+nn)*vec2(1.0,2.0))+
                                          smoothNoise2(p*8.0)*0.2)+smoothNoise2(p.yx*128.0)*0.3;


    col*=1.0+vec3(0.4)*smoothstep(0.5,1.0,smoothNoise2(p.yx*2.0+nn)+smoothNoise2(p*7.0)*0.2)+smoothNoise2(p.yx*128.0)*0.3;

    return col*1.75;
}

vec3 tex_nfloor6(vec2 p)
{
    p.x+=floor(hash(floor(p.y*2.0))*10.0);
    p.y+=floor(hash(floor(p.x*2.0))*10.0);
    float s=mix(0.5,1.2,sqrt(smoothNoise2(p*3.0))+smoothNoise2(p*80.0));
    vec3 col=mix(mix(vec3(0.1),vec3(0.18,0.25,0.05),0.4)*0.4,
                 mix(vec3(0.1),vec3(0.2,0.175,0.09),0.4)*0.7*s,
                 sqrt(max(0.0,smoothNoise2(p*8.0)+0.15*smoothNoise2(p*32.0))))*0.7;
    vec2 p2=p*vec2(2.0,2.0)+vec2(smoothNoise2(p*24.0),smoothNoise2(p*24.0+vec2(2.0)))*0.02;

    float bh=pow(0.5+0.5*cos(floor(p2.y)*14.0)*cos(floor(p2.x)*1.0),2.0)*0.5;

    float brick=brickt2(p2);
    vec3 bn=normalize(vec3(brickt2(p2+vec2(1e-3,0.0))-brick,brickt2(p2+vec2(0.0,1e-3))-brick,0.04));

    col*=mix(1.1,1.1+bh*1.0,brick);
    vec2 nn=vec2(smoothNoise2(p*4.0),smoothNoise2(p*10.0+vec2(23.0)))*4.0;
    col*=1.0+2.0*vec3(0.25,0.33,0.5)*smoothstep(0.5,1.0,smoothNoise2(p.yx*20.0+nn)+smoothNoise2(p*7.0)*0.2)+smoothNoise2(p.yx*128.0)*0.3;

    col*=1.0+1.0*vec3(0.4)*smoothstep(0.5,1.0,smoothNoise2(p.yx*2.0+nn)+smoothNoise2(p*7.0)*0.2)+smoothNoise2(p.yx*128.0)*0.3;

    col+=vec3(0.1)*smoothstep(0.4,0.9,smoothNoise2(p.yx*40.0))*smoothstep(0.8,0.9,smoothNoise2(p*50.0));
    col*=1.2;

    col.rgb*=vec3(0.6)+vec3(0.3)*dot(bn,normalize(vec3(1.0,-1.0,1.0)));

    col.rgb*=vec3(1.0)+0.5*vec3(2.0,0.75,0.5)*max(0.0,1.0-abs(brick-0.6)*2.0)*1.0;

    return col;
}

vec3 tex_wmcs(vec2 p)
{
    float s=mix(0.2,1.2,sqrt(smoothNoise2(p*3.0)));
    vec3 col=mix(vec3(0.15,0.2,0.2)*0.4,vec3(0.2,0.15,0.11)*0.7*s,sqrt(max(0.0,smoothNoise2(p*8.0)+0.15*smoothNoise2(p*32.0))))*0.7;
    vec2 p2=p*vec2(4.0,10.0)+vec2(smoothNoise2(p*24.0),smoothNoise2(p*24.0+vec2(2.0)))*0.02;
    p2.x+=floor(p2.y)*0.5;

    float bh=pow(0.5+0.5*cos(floor(p2.y)*14.0)*cos(floor(p2.x)*1.0),2.0);

    float brick=brickt(p2);
    vec3 bn=normalize(vec3(brickt(p2+vec2(1e-3,0.0))-brick,brickt(p2+vec2(0.0,1e-3))-brick,0.04));

    col*=mix(0.9,1.4+bh*1.0,brick);
    col*=1.0+0.4*smoothstep(0.8,0.9,smoothNoise2(p.yx*20.0)+smoothNoise2(p*70.0)*0.2)+smoothNoise2(p.yx*128.0)*0.4;
    col*=1.0+0.5*smoothstep(0.8,0.9,smoothNoise2(p.yx*60.0)+smoothNoise2(p*7.0)*0.15)+smoothNoise2(p.yx*64.0)*0.2;
    col*=1.1;
    col+=vec3(0.2)*smoothstep(0.4,0.9,smoothNoise2(p.yx*20.0))*smoothstep(0.8,0.9,smoothNoise2(p*40.0));

    col.rgb*=0.5+0.5*dot(bn,normalize(vec3(1.0,-1.0,1.0)));

    col.rgb*=1.0+max(0.0,1.0-abs(brick-0.7)*2.0);

    return col;
}

vec2 rotate(float angle, vec2 v)
{
    return vec2(cos(angle) * v.x + sin(angle) * v.y, cos(angle) * v.y - sin(angle) * v.x);
}

// Distance to a (non-rounded) cuboid.
float cuboid(vec3 p,vec3 a,vec3 b)
{
    vec3 d=abs(p-(a+b))-(b-a);
    return max(d.x,max(d.y,d.z));
}

float smallerTower0(vec3 p)
{
    float d=1e5;
    p.xz=abs(p.xz);

    {
        float d2=dot(p.xz,normalize(vec2(1.0)))-(-1285.0 - -1680.0);
        d=min(d,max(d2,p.y-(4257.0-2880.0)));
    }

    {
        float d2=dot(p-(vec3(normalize(vec2(1.0))*(-1285.0- -1680.0),4257.0 - 2880.0).xzy),
                     normalize(vec3(normalize(vec2(1.0))*(4371.0-4257.0),-(-1088.0 - -1285.0)).xzy));
        d=min(d,max(d2,p.y-(4371.0-2880.0)));
    }

    {
        float d2=dot(p-(vec3(normalize(vec2(1.0))*0.0,5404.0 - 2880.0).xzy),
                     normalize(vec3(normalize(vec2(1.0))*-(4371.0 - 5404.0),(-1088.0 - -1680.0)).xzy));
        d=min(d,max(d2,abs(p.y-((4371.0+5404.0)*0.5-2880.0))-(5404.0-4371.0)*0.5));
    }

    return d;
}

float smallerTower(vec3 p)
{
    p-=vec3(-1680, 2880, -448);
    vec3 p2=vec3(dot(normalize(vec2(1.0)),p.xz),p.y,dot(normalize(vec2(1.0,-1.0)),p.xz));

    return max(smallerTower0(p),smallerTower0(p2));
}

float tower0(vec3 p)
{
    float d=1e5;
