const float EPS = 0.01;
const int MAXI = 100;

mat4 transpose( mat4 m )
{
	mat4 t = mat4( vec4( m[0][0], m[1][0], m[2][0], m[3][0] ),
				   vec4( m[0][1], m[1][1], m[2][1], m[3][1] ),
				   vec4( m[0][2], m[1][2], m[2][2], m[3][2] ),
				   vec4( m[0][3], m[1][3], m[2][3], m[3][3] ) );
	return t;
}

mat4 invert( mat4 mat )
{
	vec3 right = 	vec3( mat[0][0], mat[0][1], mat[0][2] );  
	vec3 up = 		vec3( mat[1][0], mat[1][1], mat[1][2] );
	vec3 fwd = 		vec3( mat[2][0], mat[2][1], mat[2][2] );
	vec3 pos = 		vec3( mat[3][0], mat[3][1], mat[3][2] );

	mat4 t = transpose( mat );

	t[0][3] = -dot(right, pos);
	t[1][3] = -dot(right, pos);
	t[2][3] = -dot(right, pos);

	return t;
}

float smin( float a, float b, float k )
{
    float res = exp( -k*a ) + exp( -k*b );
    return -log( res )/k;
}

float cube( vec3 ray, float r, mat4 transform )
{
	vec3 rayPrime = vec3(transpose( transform ) * vec4(ray,1));
	float d = length(max(abs(rayPrime)-vec3(0.5,0.5,0.5),0.0))-r;

	return d;
}

float sphere( vec3 ray, float r, mat4 transform )
{
	vec3 rayPrime = vec3(invert( transform ) * vec4(ray,1));
	float d = length(rayPrime)-r;

	return d;
}

float torus( vec3 ray, vec2 t, mat4 transform )
{
    vec3 rayPrime = vec3(invert( transform ) * vec4(ray,1));
	vec2 q = vec2(length(rayPrime.xz)-t.x,rayPrime.y);
	return length(q)-t.y;
}

float SceneDist( vec3 ray )
{	
	float t = iTime;
	float c = cos( t );
	float s = sin( t );

	mat4 rotX = mat4(      vec4(1,0,0,0),
						   vec4(0,c,-s,0),
						   vec4(0,s,c,0),
						   vec4(0,0,0,1) );

	mat4 rotY = mat4(      vec4(c,0,-s,0),
						   vec4(0,1,0,0),
						   vec4(s,0,c,0),
						   vec4(0,0,0,1) );

	mat4 rotZ = mat4(      vec4(c,s,0,0),
						   vec4(-s,c,0,0),
						   vec4(0,0,1,0),
						   vec4(0,0,0,1) );

	mat4 pos = mat4(       vec4(1,0,0,s*1.0),
						   vec4(0,1,0,0),
						   vec4(0,0,1,c*1.0),
						   vec4(0,0,0,1) );

	mat4 transform = pos * rotZ * rotY * rotY;

	float d1 = cube( ray, 0.15, transform );
	//float d2 = sphere( ray, 0.5, transform );
    float d2 = torus( ray, vec2(0.7, 0.1), transform);
	float tVal = sin(iTime)*0.5+0.5;
	float final = mix(d1,d2, tVal);

	return final;
}

vec3 getNormal(vec3 pos){ // this gets the normal
						  // if you understand, good. if not, not much I can do.
	vec2 eps = vec2(0.0, EPS);
	return normalize(vec3( // always normalise directions
			SceneDist(pos + eps.yxx) - SceneDist(pos - eps.yxx),
			SceneDist(pos + eps.xyx) - SceneDist(pos - eps.xyx),
			SceneDist(pos + eps.xxy) - SceneDist(pos - eps.xxy)));
}

vec3 hsv(float h,float s,float v) { // this is just a hue/saturation/luminance to RGB conversion
	return mix(vec3(1.),clamp((abs(fract(h+vec3(3.,2.,1.)/3.)*6.-3.)-1.),0.,1.),s)*v;
}

vec3 Lighting( vec3 camPos, vec3 pos, vec3 normal, vec3 diffuseColor )
{
	vec3 lightPos = vec3(0,0,10);
	vec3 lightDir = normalize( lightPos - pos );
	vec3 viewDir = normalize( camPos - pos );
	vec3 lightAmbientColor = vec3(0.1,0.1,0.1);
	vec3 lightSpecularColor = vec3(1,1,1);

	vec3 halfDir = normalize(viewDir + lightDir);
	//float lightSpecularIntensity = pow( clamp(dot( normal, halfDir ), 0.0, 1.0), 2.0 );
	float lightSpecularIntensity = pow( clamp(dot( normal, reflect(lightDir, normal )), 0.0, 1.0), 80.0 );

	float lightDiffuseIntensity = clamp( dot( -normalize(pos), lightDir ), 0.00, 1.0);


	return lightAmbientColor + (lightDiffuseIntensity * diffuseColor) + (lightSpecularIntensity * lightSpecularColor);

}

vec3 RenderScene( vec2 uv, vec2 fragCoord )
{
	vec3 color = vec3(1,0,0);

	//vec3 camPos = vec3( sin(iTime) * 3.0, 0.0, cos(iTime) * 3.0);
	vec3 camPos = vec3(0,0,-3);
	vec3 camTarget = vec3(0.0, 0.0, 0.0);
	vec3 camUp = vec3(0,1.0,0);
	vec3 camFwd = normalize( camTarget - camPos );
	vec3 camRight = normalize( cross( camUp, camFwd ) );
	camUp = normalize( cross( camRight, camFwd ) );

	float dist = SceneDist( camPos );
	float total = dist;
	vec3 rayDir = vec3( normalize( camFwd + camRight * uv.x + camUp * uv.y ) );

	for(int i=0; i < MAXI; ++i)
	{
		dist = SceneDist( camPos + rayDir * total );
		total += dist;

		if( dist <= EPS )
		{
			break;
		}
	}

	vec3 dest = camPos + rayDir * total;
	if( dist <= EPS )
	{
        float t = sin(iTime)*0.5+0.5;
        vec3 diffuse = mix( vec3(1.0,0.0,0.0), vec3(1.0,1.0,0.0), t);
		color = Lighting( camPos, dest, getNormal( dest ), diffuse );
	}
	else	
	{
        vec2 p = floor( iTime + fragCoord.xy/iResolution.x*10.0 );
        float s = mod( p.x + p.y, 2.0 );
        vec3 bg0 = vec3(s,s,s);

        vec3 bg1 = hsv(.55,smoothstep(-1.5,1.,uv.y),1.);

        float t = sin(iTime) * 0.5 + 0.5;
        color = mix( bg0, bg1, t);
	}

	return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ((fragCoord.xy / iResolution.xy) - 0.5) * vec2(2.0, 2.0 * iResolution.y / iResolution.x);
	vec3 color = RenderScene( uv,fragCoord );

	// Scan line
	color -= mod(fragCoord.y, 2.0) < 1.0 ? 0.5 : 0.0;

	fragColor = vec4(color, 1.0);
}

mat3 worldRotation() {
	float xang = iTime * 0.5;
    float yang = iTime * 0.0;
    float zang = iTime * 0.5;

	mat3 zrot = mat3(
    	cos(zang), -sin(zang), 0.0,
        sin(zang), cos(zang), 0.0,
        0.0, 0.0, 1.0);

    mat3 yrot = mat3(
        cos(yang), 0.0, -sin(yang),
        0.0, 1.0, 0.0,
        sin(yang), 0.0, cos(yang));

    mat3 xrot = mat3(
        1.0, 0.0, 0.0,
        0.0, cos(xang), -sin(xang),
        0.0, sin(xang), cos(xang));

    return zrot * yrot * xrot;
}

vec3 viewToWorld(vec3 pos) {
	vec3 world = worldRotation() * pos;
    world.z += iTime * 10.0;
    return world;
}

vec3 worldToView(vec3 pos) {
	vec3 view = pos;
    view.z -= iTime * 10.0;
	return view * worldRotation();
}

vec2 dist(vec3 pos) {
	pos = viewToWorld(pos);

    float len = length(pos.xy);
    float tun = len - 6.0;

    float theta = atan(pos.y, pos.x) * 20.0;
    float phi =  (pos.z + theta) * 0.5;

    float disp = sin(phi) * sin(pos.z);

    float damp = pow(1.0 / (1.0 + abs(pos.z) * 0.1), 1.0);

    tun += disp * (1.0 - damp);

    return vec2(tun,disp);
}

vec2 trace(vec3 ray) {
    float t = 0.0;
    vec2 disp;
    for(int i = 0; i < 16; ++i){
        disp = dist(ray * t);
        t += abs(disp.x) * 0.5;
    }
    return vec2(t, disp.y);
}

vec3 normal(vec2 coord) {
	vec3 ray = vec3(coord.xy, 1.0);
    float delta = 1.0 / iResolution.x;

    vec3 rhl = normalize(ray + vec3(-delta, 0.0, 0.0));
    vec3 hl = rhl * trace(rhl).x;

    vec3 rhr = normalize(ray + vec3(delta, 0.0, 0.0));
    vec3 hr = rhr * trace(rhr).x;

    vec3 rvl = normalize(ray + vec3(0.0, -delta, 0.0));
    vec3 vl = rvl * trace(rvl).x;

    vec3 rvr = normalize(ray + vec3(0.0, delta, 0.0));
    vec3 vr = rvr * trace(rvr).x;

    return normalize(cross(hr-hl, vr-vl));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 coord = (fragCoord.xy + 0.5) / iResolution.xy * 2.0 - 1.0;
    coord.y *= iResolution.y / iResolution.x;
    vec3 eye = normalize(vec3(coord.xy, 1.0));

    vec2 alpha = trace(eye);
    vec3 norm = normal(coord);

    vec3 view = eye * alpha.x;
    vec3 world = viewToWorld(view);

    vec3 lighting = vec3(0.1,0.1,0.1);
    vec3 glow = vec3(0.0,0.0,0.0);

    for (int i=-1; i<2; ++i) {
        float lang = world.z * 0.5 + iTime * 3.0;
        vec3 lpos = vec3(cos(lang)*sin(lang*2.0), sin(lang), 0.0) * 3.0;
        float lightz = (floor(world.z/50.0)+float(i))* 50.0;
        vec3 light = vec3(lpos.x, lpos.y, lightz);
        vec3 surfaceToLight = light - world;
        float lightDist = dot(surfaceToLight, surfaceToLight);
        surfaceToLight = normalize(surfaceToLight);
        float shine = 1.0 / (1.0 + lightDist * 0.01);
        vec3 ref = surfaceToLight - 2.0 * norm * dot(surfaceToLight, norm);

		vec3 lcol = vec3(cos(world.z+iTime), sin(world.z), cos(world.z)) * 0.5 + 0.5;

        float spec = pow(0.3 + max(dot(eye, -ref),0.0), 8.0);
        vec3 diff = vec3(sin(world.x), cos(world.x), sin(world.x)) * 0.5 + 0.5;
        diff *= alpha.y * 0.5 + 0.5;    

        lighting += (diff + spec) * lcol * shine;

        vec3 viewLight = worldToView(light);
        if (viewLight.z > 0.0) {
            viewLight /= viewLight.z;
            vec2 orbdiff = coord - viewLight.xy;
        	float dist = dot(orbdiff, orbdiff);
        	float orb = 1.0 / (1.0 + dist * 100.0);
            orb = pow(0.1 + orb, 4.0);
        	glow += orb * lcol;
        }
    }

    float fog = 1.0 / (1.0 + alpha.x * 0.1);

    vec3 col = clamp(lighting * fog + glow, 0.0, 1.0);

	fragColor = vec4(col, 1.0);
}

const float radius = 8.0;
const float lightingConstant = 16.0;
float sphereRad = sin(iTime*0.4)*0.5+4.0;

float intersectSphere(vec3 oc, vec3 dir)
{
    oc = oc - vec3(0.5,0.0,0.5);
    float b = 2.0 * dot(dir, oc);
    float c = dot(oc, oc) - sphereRad*sphereRad;
    float disc = b * b - 4.0 * c;

    if (disc < 0.0)
        return -1.0;

    // compute q as described above
    float q;
    if (b < 0.0)
        q = (-b - sqrt(disc))/2.0;
    else
        q = (-b + sqrt(disc))/2.0;

    float t0 = q;
    float t1 = c / q;

    // make sure t0 is smaller than t1
    if (t0 > t1) {
        // if t0 is bigger than t1 swap them around
        float temp = t0;
        t0 = t1;
        t1 = temp;
    }

    // if t1 is less than zero, the object is in the ray's negative direction
    // and consequently the ray misses the sphere
    if (t1 < 0.0)
        return -1.0;

    // if t0 is less than zero, the intersection point is at t1
    if (t0 < 0.0) {
        return t1;
    } else {
        return t0; 
    }
}
vec4 realColorForRay(vec3 origin, vec3 direction, bool isSpherePass) {
    vec3 skyCol = clamp(vec3(direction.y*0.5),0.0,1.0) + vec3(0.0,0.05,0.3);
    vec4 col = vec4(skyCol,99999999999.0);
    for (int i=0; i<=2; i++) {
        for (float j=-radius; j<=radius; j+= 1.0) {
            highp float dist = (j-origin[i])/direction[i];
            if (dist > 0.0 && dist < col[3]) {
                vec3 impactLocation = origin+direction*dist;
                if (abs(impactLocation.x-0.5)>0.6 || abs(impactLocation.y-0.5)>0.6 || isSpherePass)
                if (abs(impactLocation.x)<=9.0 && abs(impactLocation.y)<=9.0 && abs(impactLocation.z)<=9.0)
                if ((i==0 || mod(impactLocation[0],2.0) < 1.0) && (i==1 || mod(impactLocation[1],2.0) < 1.0) && (i==2 || mod(impactLocation[2],2.0) < 1.0)) {
                    col = vec4(vec3(0.8-length(impactLocation)/lightingConstant)+vec3(0.1,0.1,0.1),dist);
                }
            }
        }
    }
    return col;
}
vec3 colorForRay(vec3 origin, vec3 direction) {
    vec4 col = realColorForRay(origin,direction,false);

    float sphereHitDist = intersectSphere(origin,direction);
    if ((sphereHitDist < col[3]) && sphereHitDist != -1.0) {
        col = vec4(vec3(0.07*sin(-iTime*0.5)+0.07)+realColorForRay(origin+direction*(sphereHitDist-0.1),refract(direction,normalize(origin+direction*sphereHitDist),sin(iTime*0.5)/4.0+0.75),true).rgb,sphereHitDist);
    }

    return vec3(col.rgb)+vec3(0.1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    highp vec3 cameraOrigin = vec3(-sin(sin(iTime*2.1)*0.2)*9.0+0.5,0.5+cos(iTime*2.1)*1.0,-18.0+sin(iTime*0.04)*10.0);
    highp vec3 cameraDirection  = normalize(vec3( (fragCoord.x/iResolution.x*512.0-256.0),(fragCoord.y/iResolution.x*512.0-(iResolution.y/iResolution.x*256.0)), 300.0));


	fragColor = vec4(colorForRay(cameraOrigin,cameraDirection),1.0);
}

float SPHERE_DISTANCE = 12.0;

mat3 rotationMatrix(vec3 axis, float angle)
{
    axis = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float oc = 1.0 - c;

    return mat3(oc * axis.x * axis.x + c,           oc * axis.x * axis.y - axis.z * s,  oc * axis.z * axis.x + axis.y * s,
                oc * axis.x * axis.y + axis.z * s,  oc * axis.y * axis.y + c,           oc * axis.y * axis.z - axis.x * s,
                oc * axis.z * axis.x - axis.y * s,  oc * axis.y * axis.z + axis.x * s,  oc * axis.z * axis.z + c);
}

// distance from pos to the closest sphere's outline.
float sphereMap( in vec3 pos ) {
    vec3 actualPos = mod(pos, SPHERE_DISTANCE);
    float spherePos = SPHERE_DISTANCE/2.0;// + 2.0 * cos((floor(pos.z / SPHERE_DISTANCE) + 1.0) * iTime / 4.0);
    float radius = (1.0 +cos(iTime + pos.x / 60.0));
    return length(actualPos - vec3(spherePos)) - radius;
}

float floorMap( in vec3 pos ) {
    return pos.z + 10.0;
}

float map( in vec3 pos ) {
    return min(sphereMap(pos), floorMap(pos));
    // return sphereMap(pos);
}

vec3 normal( in vec3 pos, in float t) {
    float e = 0.001 * t;
    vec3  eps = vec3(e,0.0,0.0);
    vec3 nor;
    nor.x = map(pos+eps.xyy) - map(pos-eps.xyy);
    nor.y = map(pos+eps.yxy) - map(pos-eps.yxy);
    nor.z = map(pos+eps.yyx) - map(pos-eps.yyx);
    return normalize(nor);
}

vec3 camPos( float t ) {
    /*return vec3(0.3 * cos(t * 0.17),
                -40.0 + 0.4 * sin(t * 0.4),
                -12.0 + 1.9 * cos(t * 0.23)
                );*/
    float angle = iMouse.x / iResolution.x * 6.2831;
    mat3 rotMatrix = rotationMatrix( vec3(0, 0.0, 1.0), angle);
    return rotMatrix * vec3(60.0, 20.0, 0.0);
}

bool raycast( in vec3 rO, in vec3 rD, out float t ) {
    bool hit = false;
    for(int i = 0; i < 100; i++) {
        float dist = map(rO + t * rD);
        if(dist < 0.0) { // move outward in the normal's direction
            vec3 norm = normal(rO+t*rD, t);

        }
        if(dist < 0.01) {
            hit = true;
            break;
        }
        t += max(0.1, dist);
    }

	return true;
}

float accumulateShadowFactor( in vec3 rO, in vec3 rD ) {
    float strongestShadow = 0.0;
    float t = 2.01 + 6.0;
    for(int i = 0; i < 100; i++) {
        float dist = map(rO + t*rD);
        if(dist < 0.01) {
            strongestShadow = max(strongestShadow, 100.0 / t);
            break;
        }
        strongestShadow = max(strongestShadow, (1.0 / dist) / t);
        t += max(0.1, dist);
    }
    return strongestShadow;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float fovX = radians(60.0),
          fovY = fovX * iResolution.y / iResolution.x;
	vec2 xy = fragCoord.xy / iResolution.xy * 2.0 - 1.0;

    vec3 camPosition = camPos(iTime);
    vec3 camTarget = vec3(0.0, 0.0, iMouse.y / iResolution.y * 250.0 - 125.0);
    vec3 up = vec3(0.0, .0, 1.0);

    vec3 camDirection = normalize(camTarget - camPosition),
         camRight = cross(camDirection, up),
    	 camUp = cross(camRight, camDirection);

    vec3 rO = camPosition;

    float angleX = -mix(0.0, fovX, xy.x);
    float angleY = -mix(0.0, fovY, xy.y);
    mat3 rotAroundUp = rotationMatrix(camUp, angleX);
    // rotate camDirection to the correct X angle
    vec3 rD = rotAroundUp * camDirection;
    vec3 rotatedCamRight = rotAroundUp * camRight;
    mat3 rotAroundRight = rotationMatrix(rotatedCamRight, angleY);
    rD = rotAroundRight * rD;

    float t = 0.0;
	bool hit = raycast( rO, rD, t);

    vec3 dif1Dir = normalize(vec3(1.0, -1.0, 1.0));
    vec3 ambientLight = vec3(0.2, 0.17, 0.14);

    vec3 fogColor = vec3(0.38, 0.40, 0.45);
    float fogFactor = exp(-t / 200.0);

    vec3 col = vec3(0.0);

    if( hit ) {
        vec3 norm = normal( rO + t*rD, t );
        float dif1Comp = clamp(dot(dif1Dir, norm), 0.0, 1.0);
        float shadowFactor = 0.0; //accumulateShadowFactor( rO + t*rD, dif1Dir);

    	col = ambientLight;
        col += vec3(0.7, 0.75, 0.79) * dif1Comp * (1.0 - shadowFactor);
        col = mix(col, fogColor, 1.0 - fogFactor);

        // col = vec3(clamp(abs(map(rO+t*rD)) * 100.0, 0.0, 1.0));
    } else {
        col = fogColor;
    }

    fragColor = vec4(col, 1.0);
}

vec4 noise(float p){return texture(iChannel0,vec2(p/iChannelResolution[0].x,.0));}
vec4 noise(vec2 p){return texture(iChannel0,p/iChannelResolution[0].xy);}
vec4 noise(vec3 p){float m = mod(p.z,1.0);float s = p.z-m; float sprev = s-1.0;if (mod(s,2.0)==1.0) { s--; sprev++; m = 1.0-m; };return mix(texture(iChannel0,p.xy/iChannelResolution[0].xy + noise(sprev).yz*21.421),texture(iChannel0,p.xy/iChannelResolution[0].xy + noise(s).yz*14.751),m);}
vec4 noise(vec4 p){float m = mod(p.w,1.0);float s = p.w-m; float sprev = s-1.0;if (mod(s,2.0)==1.0) { s--; sprev++; m = 1.0-m; };return mix(noise(p.xyz+noise(sprev).wyx*3531.123420),	noise(p.xyz+noise(s).wyx*4521.5314),	m);}
vec4 noise(float p, float lod){return texture(iChannel0,vec2(p/iChannelResolution[0].x,.0),lod);}
vec4 noise(vec2 p, float lod){return texture(iChannel0,p/iChannelResolution[0].xy,lod);}
vec4 noise(vec3 p, float lod){float m = mod(p.z,1.0);float s = p.z-m; float sprev = s-1.0;if (mod(s,2.0)==1.0) { s--; sprev++; m = 1.0-m; };return mix(texture(iChannel0,p.xy/iChannelResolution[0].xy + noise(sprev,lod).yz,lod*21.421),texture(iChannel0,p.xy/iChannelResolution[0].xy + noise(s,lod).yz,lod*14.751),m);}

#define t iTime

vec3 flare(vec2 uv, vec2 pos, float seed, float size)
{
	vec4 gn = noise(seed-1.0);
	gn.x = size;
	vec3 c = vec3(.0);
	vec2 p = pos;
	vec2 d = uv-p;


	c += (0.01+gn.x*.2)/(length(d));

	c += vec3(noise(atan(d.x,d.y)*256.9+pos.x*2.0).y*.25)*c;

	float fltr = length(uv);
	fltr = (fltr*fltr)*.5+.5;
	fltr = min(fltr,1.0);

	for (float i=.0; i<20.; i++)
	{
		vec4 n = noise(seed+i);
		vec4 n2 = noise(seed+i*2.1);
		vec4 nc = noise (seed+i*3.3);
		nc+=vec4(length(nc));
		nc*=.65;

		for (int i=0; i<3; i++)
		{
			float ip = n.x*3.0+float(i)*.1*n2.y*n2.y*n2.y;
			float is = n.y*n.y*4.5*gn.x+.1;
			float ia = (n.z*4.0-2.0)*n2.x*n.y;
			vec2 iuv = (uv*(mix(1.0,length(uv),n.w*n.w)))*mat2(cos(ia),sin(ia),-sin(ia),cos(ia));
			vec2 id = mix(iuv-p,iuv+p,ip);
			c[i] += pow(max(.0,is-(length(id))),.45)/is*.1*gn.x*nc[i]*fltr;
		}

	}


	return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy -.5;
	uv.x*= iResolution.x/ iResolution.y;
