    vec2 pos;
    if (iMouse.z < .5)
    {
        pos = vec2(sin(t),cos(t*.7));
    }

    else pos = mouse.xy;

		color += flare(uv,pos,t-mod(t,2.00),0.15)*vec3(1.9,1.9,2.4);


	color+=noise(fragCoord.xy).xyz*.01;


	fragColor = vec4(color,1.0);
}

