vec2 cinv( vec2 z ) { float d = dot(z,z);return vec2(z.x,-z.y)/d; }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = 1.75*(-iResolution.xy+2.0*fragCoord.xy)/iResolution.y;

	vec2 z = vec2(1.);
	float f = 4.;
	float g = 4.;


	for( int i=0; i<128; i++ ) 
	{
		float w = float(i)*.7+iTime*.5;
		vec2 z1 = 2.*vec2(cos(w),sin(w));
		z = cinv(z+p) - cinv(z-p) ;
		f = min( f, dot(z+z1,z+z1));
		g = min( g, dot(z-z1,z-z1));

	}

	f = 1.0+log(f)/15.0;
	g = 1.0+log(g)/15.0;

	fragColor = abs(vec4(g,f*.7,0.,1.0));
}

#define PI 3.1415926535897932384626433832795

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t =0.2*iTime;
    vec2  s =iResolution.xy/60.+abs(mod(t,8.)-4.);
    vec2  p =fragCoord.xy/iResolution.x*s-s/2.;
    float v =.0+sin((p.x+3.*t))+sin((p.y+6.*t)/2.)+sin((p.x+p.y+2.*t)/2.);
    p += s/2.*vec2(sin(t/3.),cos(t/2.));
    v += sin(sqrt(p.x*p.x+p.y*p.y+1.)+t);
    float R =sin(.2*PI*v),G =cos(.75*PI*v),B =sin(.9*PI*v);

    // restrict the color palette
    R = ceil(R*255. /  8.) *  8. / 256.;
    G = ceil(G*255. / 16.) * 16. / 256.;
    B = ceil(B*255. /  8.) *  8. / 256.;

    if(mod(R,16.) < 1.) R =G*.5+.5; // less red (more green, blue & yellow)
    vec3 col =vec3(R,G,B);

    fragColor = vec4(col,1.);
}

#define time iTime/1.5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    uv.y -= .5;

    vec3 col = vec3(1.);

    for (int i = 0; i <8; ++i)
    {
        float y = cos(uv.x + time * 3. + float(i)/4.) / 2.8;
        float f = smoothstep(.05, .1, abs(uv.y - y));
        col.g *= f;

        float g = smoothstep(.06, .101, abs(uv.y - y));
        col.g = mix(col.g, g, .22);  
    }


	fragColor = vec4(col, 1.0);
}

struct Ray
{
	vec3 origin;
    vec3 dir;
};

struct Plane
{
	vec3 p;
    vec3 n;
};


struct Light
{

    float specular;
    vec3 position;
