float tri(in float x)
{
    return abs(fract(x)-0.5);
}

mat2 m2 = mat2( 0.80,  0.60, -0.60,  0.80 );
float tnoise(in vec2 p)
{
    p*=.008;
    float z=2.;
	float rz = 0.;
	for (float i= 1.;i < TERRAIN_COMPLEXITY;i++ )
	{
        rz+= tri(p.x+tri(p.y*1.))/z;
		z = z*2.;
		p = p*1.8;
        p*= m2;
	}
	return rz*9.;
}

float oct(in vec3 p){ return dot(vec3(0.5773),abs(p));}
vec2 ou( vec2 d1, vec2 d2 ){return (d1.x<d2.x) ? d1 : d2;}

vec3 roty(vec3 p, float a)
{
    float s = sin(a), c = cos(a);
    return vec3(c*p.x + s*p.z, p.y, -s*p.x + c*p.z);
}

vec2 map(vec3 p)
{   
    //terrain
    vec2 d = vec2(6.*tnoise(p.xz)+p.y+20.+(tri(p.z*0.001)-0.4)*22.,1.);
    //xlog(x) seems to work nicely for a valley
    d.x -= abs(p.x*0.5*log(abs(p.x)))*0.05-8.;
    //flat water
    d = ou(d,vec2(p.y+30., 2.));
    //"enemy"
    vec3 enp = enpos();
    enp.z += time*50.;
    d = ou(d,vec2((oct(roty(p-enp, time*2.5))-6.)*0.66,8.));

	return d;
}

vec2 march(in vec3 ro, in vec3 rd)
{
	float precis = .1;
    float h=precis*2.0;
    float d = 0.;
    float c = 1.;
    for( int i=0; i<ITR; i++ )
    {
        if( abs(h)<precis || d>FAR ) break;
        d += h;
	    vec2 res = map(ro+rd*d);
        h = res.x*1.4;
        c = res.y;
    }
	return vec2(d,c);
}

vec3 normal(const in vec3 p)
{  
    vec2 e = vec2(-1., 1.)*.1;
	return normalize(e.yxx*map(p + e.yxx).x + e.xxy*map(p + e.xxy).x + 
					 e.xyx*map(p + e.xyx).x + e.yyy*map(p + e.yyy).x );   
}

//(from eiffie, who thought it was from iq, dont know who actually wrote it)
float segm(vec3 ro, vec3 rd, vec3 p1, vec3 p2)
{
	vec3 p = p1-ro;
	vec3 di = p2-ro-p;
	float proj = dot(rd, di);
	float m = clamp((dot(rd,p)*proj-dot(p,di))/(dot(di,di)-proj*proj), 0., 1.);
	p += di*m;
	p = dot(p, rd)*rd-p;
    return smoothstep(0.9985,.999,1.-dot(p,p));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
	vec2 p = fragCoord.xy/iResolution.xy-0.5;
    vec2 bp = p+0.5;
	p.x*=iResolution.x/iResolution.y;
	vec2 um = vec2(0);
    um.x = 0.5+(smoothstep(-2.,2.,sin(time*.7-0.1))-0.5)*.1;
    um.y = sin(time+1.)*0.02;

    //camera
    vec3 ro = vec3((smoothstep(-2., 2., sin(time*0.7+1.57))-0.5)*50., sin(time)*5.-1., time*50.);
    um.x *= 3.;
    vec3 eye = normalize(vec3(cos(um.x),um.y*5.,sin(um.x)));
    vec3 right = normalize(vec3(cos(um.x+1.5708),0.,sin(um.x+1.5708)));
    mat2 ori = mm2( smoothstep(-.5,.5,sin(time*0.7+0.78))-.5 + smoothfloor(time*0.04,.45)*6.28 );
    right.xy *= ori;
    vec3 up = normalize(cross(right,eye));
	vec3 rd=normalize((p.x*right+p.y*up)*.75+eye);

    vec3 bg = sin(vec3(0.35,0.4,0.48)*11.3*PALETTE)*0.5+.5;
    vec3 col = bg*floor(-rd.y*50.+6.)*0.06;

    //march
	vec2 rz = march(ro,rd);
    if ( rz.x < FAR )
    {
        vec3 pos = ro+rz.x*rd;
        vec3 nor = normal( pos );
        vec3 ligt = normalize(vec3(-.7,0.2, 0.1));
        float dif = clamp(dot(nor, ligt), 0., 1.);
        float fre = pow(clamp(1. + dot(nor, rd), 0., 1.), 2.);
        if (rz.y == 1.)
        {
            float mx = abs(pos.x*.1)-10.;
            mx = smoothstep(-20.,10.,mx);
            col = mix(vec3(0.,0.37,0),vec3(0.2,.17,0.15),mx);
        }
        else
            col = sin(vec3(0.35,0.4,0.48)*rz.y*PALETTE)*0.5+.55;
        col = col*dif + col*0.4 + .3*fre*col;
    }

    //lasers
    vec3 enp =enpos();
    enp.z += time*50.;
    vec3 rn = enp - ro;
    float tgt = dot(eye, normalize(rn));
    if (tgt > .997)
    {
        vec3 ray1 = vec3(0.7, 1., -1);
        vec3 ray2 = vec3(-0.7, 1., -1);
        ray1.xy *= ori; ray2.xy *= ori;
        float lz = segm(ro,rd,ro-ray1,up*0.5+ro+(eye-ray1*0.01)*30.);
        lz += segm(ro,rd,ro-ray2,up*.5+ro+(eye-ray2*0.01)*30.);
        float sw = mod(floor(time*20.),2.);
        lz *= sw;
        col = col*(1.-smoothstep(0.0,1.,lz))+lz*vec3(1.,0.,0.);
        //hit (cant really have explosions since I don't have a function for hit times)
        if (tgt > .999)
        {
            vec2 d = hash2(time);
            rd.xy += d*0.03;
            rn.xy += d*10.;
            float s = sw*smoothstep(0.9998, .9999,dot(rd,normalize(rn)));
            col = col*(1.-smoothstep(0., 1., s))+s*vec3(1.-d.x, .0, 0.1);
        }
    }

    //hud
    float lk = 0.;
    if (tgt > .99)lk = 4.;
    vec3 hud = makeHud(p,tgt);
    col = col*(1.-smoothstep(0., 1., hud.y+hud.x+hud.z))+hud;   
    //scanlines
    col *= (sin(p.y*1.3*iResolution.x)*0.15)*(sin(p.y*10.+time*410.)*0.4)+1.;

	fragColor = vec4( col, 1.0 );
}

#define PI 3.1415926535897932384626433832795

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float pulse = 1.;        // pulse: on/off
    float mag = 80.;

    float gs = iResolution.x / 20.;
    float ar = iResolution.x / iResolution.y;
    float t =0.2*iTime;

    if (pulse != 0.) {
        pulse = sqrt( abs( mod( t, .4 ) - .2 ) ) * 16.;
        mag = 160.;
    }
    vec2 s =iResolution.xy/mag+pulse;

    // correct aspect ratio
    vec2 p =fragCoord.xy/iResolution.x;

    if (pulse != 0.) {
        p *= s-s/2.;         // scale
    }

    // rotate
    float sa =sin(t);
    float ca =clamp(cos(t * 4.),-.9,.9);
    vec2  rp =vec2( p.x*ca - p.y*sa, p.x*sa + p.y*ca );
    if (pulse != 0.) { p = rp; }
    else { p = rp * mag * .1;  }

    // calculate the cumulative value of several sine functions
    float v =sin(p.x+.8*t)+.5*sin(p.y+.8*t)+.5*sin(p.x+p.y+.9*t);
    p += s/2.*vec2(sin(t/.9),cos(t/.6));
    v += sin(sqrt(p.x*p.x+p.y*p.y+1.)+t);

    // color blending
    float R =sin(.2 *PI*v),
          G =cos(.75*PI*v),
          B =sin(.9 *PI*v);

    // restricted color palette (nice effect)
    // code by McRam (https://www.shadertoy.com/view/ld2XRG)
    R = ceil(R*255. /  8.) *  8. / 256.;
    G = ceil(G*255. / 16.) * 16. / 256.;
    B = ceil(B*255. /  8.) *  8. / 256.;

    if(mod(R,16.) < 1.) R =G*.5+.5;
    vec3 col =vec3(R,G,B);

    // grid
    col *= 0.4 * 1./length( sin( 1.*.1 * gs*p.x ) );
    col *= 0.8 * 1./length( sin( ar*.1 * gs*p.y ) );

    // deinterlaced scanlines
    col *= .33 * length( sin( 5. * p.y * gs ) );

    col =clamp(col,vec3(.0),vec3(1.));           // safe range
    fragColor = vec4(col,1.);
}

#define F1 float
#define F2 vec2
#define F3 vec3
#define F4 vec4

//
// TEMPORAL DITHERING TEST IN LINEAR
//

F1 Linear1(F1 c){return(c<=0.04045)?c/12.92:pow((c+0.055)/1.055,2.4);}
F3 Linear3(F3 c){return vec3(Linear1(c.r),Linear1(c.g),Linear1(c.b));}
F1 Srgb1(F1 c){return(c<0.0031308?c*12.92:1.055*pow(c,0.41666)-0.055);}
F3 Srgb3(F3 c){return vec3(Srgb1(c.r),Srgb1(c.g),Srgb1(c.b));}
F3 photoLuma=F3(0.2126,0.7152,0.0722);
F1 PhotoLuma(F3 c){return dot(c,photoLuma);}

F1 Noise(F2 n,F1 x){n+=x;return fract(sin(dot(n.xy,F2(12.9898, 78.233)))*43758.5453)*2.0-1.0;}

// Step 1 in generation of the dither source texture.
F1 Step1(F2 uv,F1 n){
 F1 a=1.0,b=2.0,c=-12.0,t=1.0;   
 return (1.0/(a*4.0+b*4.0-c))*(
  Noise(uv+F2(-1.0,-1.0)*t,n)*a+
  Noise(uv+F2( 0.0,-1.0)*t,n)*b+
  Noise(uv+F2( 1.0,-1.0)*t,n)*a+
  Noise(uv+F2(-1.0, 0.0)*t,n)*b+
  Noise(uv+F2( 0.0, 0.0)*t,n)*c+
  Noise(uv+F2( 1.0, 0.0)*t,n)*b+
  Noise(uv+F2(-1.0, 1.0)*t,n)*a+
  Noise(uv+F2( 0.0, 1.0)*t,n)*b+
  Noise(uv+F2( 1.0, 1.0)*t,n)*a+
 0.0);}

// Step 2 in generation of the dither source texture.
F1 Step2(F2 uv,F1 n){
 F1 a=1.0,b=2.0,c=-2.0,t=1.0;   
 return (1.0/(a*4.0+b*4.0-c))*(
  Step1(uv+F2(-1.0,-1.0)*t,n)*a+
  Step1(uv+F2( 0.0,-1.0)*t,n)*b+
  Step1(uv+F2( 1.0,-1.0)*t,n)*a+
  Step1(uv+F2(-1.0, 0.0)*t,n)*b+
  Step1(uv+F2( 0.0, 0.0)*t,n)*c+
  Step1(uv+F2( 1.0, 0.0)*t,n)*b+
  Step1(uv+F2(-1.0, 1.0)*t,n)*a+
  Step1(uv+F2( 0.0, 1.0)*t,n)*b+
  Step1(uv+F2( 1.0, 1.0)*t,n)*a+
 0.0);}

// Used for stills.
F3 Step3(F2 uv){
 F1 a=Step2(uv,0.07);    
 F1 b=Step2(uv,0.11);    
 F1 c=Step2(uv,0.13);
 #if 0
  // Monochrome can look better on stills.
  return F3(a);
 #else
  return F3(a,b,c);
 #endif
}

// Used for temporal dither.
F3 Step3T(F2 uv){
 F1 a=Step2(uv,0.07*fract(iTime));    
 F1 b=Step2(uv,0.11*fract(iTime));    
 F1 c=Step2(uv,0.13*fract(iTime));
 return F3(a,b,c);}

#define STEPS 8.0

void mainImage( out vec4 fragColor, in vec2 fragCoord ){    
 F2 uv=fragCoord.xy;
 F2 uv2=fragCoord.xy/iResolution.xy;F2 uv3=F2(uv2.x,1.0-uv2.y); 
 F3 color=texture(iChannel0,uv2).rgb;
 F3 color2=texture(iChannel1,uv3*2.0).rgb;
 // BOTTOM: Show bands.
 if(uv2.y<0.1){color=F3(uv2.x);color=floor(color*STEPS+Step3(uv)*4.0)*(1.0/(STEPS-1.0));}   
 else if(uv2.y<0.2){color=F3(uv2.x);color=floor(color*STEPS)*(1.0/(STEPS-1.0));}   
 else if(uv2.y<0.3){color=F3(uv2.x);color=floor(color*STEPS+Step3T(uv)*4.0)*(1.0/(STEPS-1.0));}   
 // Show video and texture.
 else if(uv2.y<0.9){
  if(uv2.x<0.3)color=color2;
  color=Linear3(color);
  // Add grain in linear space.
  #if 0
   // Slow more correct solutions.
   #if 1
    // Too expensive.
    // Helps understand the fast solutions.
    F3 amount=Linear3(Srgb3(color)+(4.0/STEPS))-color;
   #else
    // Less too expensive.
    F1 luma=PhotoLuma(color);
    // Implement this as a texture lookup table.
    F1 amount=Linear1(Srgb1(luma)+(4.0/STEPS))-luma;
   #endif
  #else
   // Fast solutions.
   #if 1
    // Hack 1 (fastest).
    // For HDR need saturate() around luma.
    F1 luma=PhotoLuma(color);
    F1 amount=mix(
     Linear1(4.0/STEPS),
     Linear1((4.0/STEPS)+1.0)-1.0,
     luma);
   #else
    // Hack 2 (slower?).
    // For HDR need saturate() around color in mix().
    F3 amount=mix(
     F3(Linear1(4.0/STEPS)),
     F3(Linear1((4.0/STEPS)+1.0)-1.0),
     color);
   #endif
  #endif
  color+=Step3T(uv)*amount;
  // The following represents hardware linear->sRGB xform
  // which happens on sRGB formatted render targets,
  // except using a lot less bits/pixel.
  color=max(F3(0.0),color);
  color=Srgb3(color);
  color=floor(color*STEPS)*(1.0/(STEPS-1.0));}
 // TOP: Show dither texture.
 else{color=Step3(uv)*1.0+0.5;}    
 fragColor=F4(color,1.0);}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.yy;
    vec2 center = vec2(0.5,0.5);
    float amt = 1.99;
   	float r = length(uv-center);
    vec2 centered = uv-center;
    float tanny = atan(centered.x,centered.y)*1.0;
    tanny+=iTime+.05*sin(r*130.)+2.*r;

    vec4 clr =   vec4(sin(tanny*1.0),
                        sin(tanny*1.0+amt),
                      	sin(tanny*1.0+2.0*amt),
                        1.0
                       );
    clr*=1.0-1.0*sqrt(dot(centered,centered));

	fragColor =clr;
}

const float PI =3.14159265;
const float PI2=6.28318531;

float obj_ball(vec3 p, vec3 center, float radius){
    return length(p-center)-radius;
}
float obj_box(vec3 p, vec3 center, vec3 size, float roundness){
    vec3 d = abs(p-center)-size;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0)) - roundness;
}
float obj_cylinder(vec3 p, vec3 center, vec2 size, float roundness){
    vec3 tp = p-center;
    vec2 d = abs(vec2(length(tp.xz),tp.y)) - size;
    return min(max(d.x,d.y)+roundness,0.0) + length(max(d,0.0))-roundness;
}
float obj_planeX(vec3 p, float planeX){
    return p.x-planeX;
}
float obj_planeY(vec3 p, float planeY){
    return p.y-planeY;
}
float obj_planeZ(vec3 p, float planeZ){
    return p.z-planeZ;
}

float obj_cylForeverZ(vec2 p, float middleY, float radius){
    return abs(length(vec2(p.x,p.y-middleY))) - radius;
}

float distSmooth( float a, float b, float k ){
    float h = clamp(0.5+0.5*(b-a)/k,0.0,1.0);
    return mix(b,a,h) - k*h*(1.0-h);
}

void hardAdd(inout float curD, inout int curMaterial, float newD, int newMaterial){
    if (newD < curD) {
        curD = newD;
        curMaterial = newMaterial;
    }
}
void hardSubtract(inout float curD, float newD) {
    curD = max( -newD, curD );
}
void smoothAdd(inout float curD, float newD, float blendPower){//blend colors too?
    curD = distSmooth( newD, curD, blendPower );
}
void smoothSubtract(inout float curD, float newD, float blendPower){
    curD = -distSmooth( newD , -curD , blendPower );
}

const float cylMidZ = -1.5;

vec3 rotMod(vec3 p, vec2 middle, float modFrac){
    vec2 cylRel = vec2(p.x-middle.x,p.z-middle.y);
    float cylAng = atan(cylRel.y,cylRel.x);
    float cylDist = length(cylRel);
    float modSlice = PI2/modFrac;
    float newAng = (mod((cylAng+modSlice*0.5),(modSlice)))-(modSlice*0.5);
    return vec3(cos(newAng)*cylDist,p.y,sin(newAng)*cylDist);

}

float middleMod(float val,float modDist){
    return mod(val+modDist*0.5,modDist)-modDist*0.5;
}

float room(vec3 p, out int material){






    float distance = 9999.9;
    material = 0;

    const float pillarGapX = 2.5;
    const float pillarGapZ = 0.5;

    const float floorTileGap = 0.5;
    const float floorTileGrout = 0.07;

    const float ceilY = 9.0;

    const float mirrorY = 0.5;

    vec3 pillarP = vec3(mod(p.x,pillarGapX*2.0),-abs(p.y-mirrorY)+mirrorY,mod(p.z,pillarGapZ*2.0));

    // ceiling flat
    hardAdd(distance,material,
            -obj_planeY(p ,ceilY),
            3);

    // ceiling groove
    hardSubtract(distance,
                 obj_cylForeverZ(vec2(middleMod(p.x,5.0),p.y),ceilY,2.3));

    // base
    hardAdd(distance,material,
            obj_box(pillarP,vec3(pillarGapX,-1.4,pillarGapZ),vec3(0.4,0.15,0.4),0.0),
            1);

    // base blend
    smoothAdd(distance,
              obj_cylinder(pillarP,vec3(pillarGapX,-1.15,pillarGapZ),vec2(0.3,0.1),0.0),
              0.11);

    // floor
    hardAdd(distance,material,
            obj_box(vec3(mod(p.x,floorTileGap*2.0),p.y,mod(p.z,floorTileGap*2.0)),vec3(floorTileGap,-1.5-floorTileGap,floorTileGap),vec3(floorTileGap-floorTileGrout),floorTileGrout),
            2);

    //column
    hardAdd(distance,material,
            obj_cylinder(pillarP,vec3(pillarGapX,mirrorY,pillarGapZ),vec2(0.25,2.0),0.0),
            1);







    return distance;

}







void mainVR( out vec4 fragColor, in vec2 fragCoord, in vec3 fragRayOri, in vec3 fragRayDir ) {


    vec3 cameraPos = fragRayOri;
    vec3 curCameraRayUnit = fragRayDir;



    //scene movement
    float fwdDist = iTime*0.2;
    cameraPos.z -= fwdDist;



    // Raymarching.
    const vec3 e=vec3(0.00007,0,0);
    const float maxd=40.0; //Max depth
    vec3 p;

    float f=0.0;
    float d=0.01;
    int surfaceMaterial = 0;
    for(int i=0;i<96;i++){
        if ((abs(d) < .001) || (f > maxd)) break;
        f+=d;
        p=cameraPos+curCameraRayUnit*f;
        d = room(p,surfaceMaterial);
    }


    vec3 color;
    int dummyMaterial;

    if (f < maxd){

        vec3 surfaceColor;
        float specA, specP;
        float difP = 1.0;
        vec3 normalCheat = vec3(0.0,0.0,0.0);//generally not advisable in stereo, but it's used very shallowly here

        if (surfaceMaterial == 1){

            vec3 marbleP = p;

            marbleP.x += sin(p.y*20.0)*0.12;
            marbleP.z += sin(p.y*22.0)*0.1;
            marbleP.y += sin(p.x*25.0)*0.13;
            marbleP.y += sin(p.z*23.0)*0.14;

            marbleP.y += sin(p.x*1.3)*0.5;
            marbleP.y += sin(p.z*1.5)*0.6;

            marbleP.x += sin(p.y*150.0)*0.011;
            marbleP.z += sin(p.y*162.0)*0.013;
            marbleP.y += sin(p.x*145.0)*0.012;
            marbleP.y += sin(p.z*153.0)*0.015;

            marbleP.x *= 20.0;
            marbleP.z *= 20.0;
            marbleP.y *= 10.0;

            float marbleAmtA = abs(sin(marbleP.x)+sin(marbleP.y)+sin(marbleP.z))/3.0;
            marbleAmtA = pow(1.0-marbleAmtA,5.0);

            marbleP = p;

            marbleP.x += sin(p.y*21.0)*0.12;
            marbleP.z += sin(p.y*23.0)*0.1;
            marbleP.y += sin(p.x*22.0)*0.13;
            marbleP.y += sin(p.z*24.0)*0.14;

            marbleP.y += sin(p.x*1.2)*0.5;
            marbleP.y += sin(p.z*1.4)*0.6;

            marbleP.x += sin(p.y*150.0)*0.011;
            marbleP.z += sin(p.y*162.0)*0.013;
            marbleP.y += sin(p.x*145.0)*0.012;
            marbleP.y += sin(p.z*153.0)*0.015;

            marbleP.x *= 22.0;
            marbleP.z *= 23.0;
            marbleP.y *= 11.0;

            float marbleAmtB = abs(sin(marbleP.x)+sin(marbleP.y)+sin(marbleP.z))/3.0;
            marbleAmtB = pow(1.0-marbleAmtB,9.0);
            marbleAmtB = 1.0-(1.0-marbleAmtB*0.3);

            float marbleAmt = marbleAmtA + marbleAmtB;
            marbleAmt = clamp(marbleAmt,0.0,1.0);

            surfaceColor = mix(vec3(0.0,0.8,0.5),vec3(0.70),marbleAmt);
            specA = mix(1.0,0.8,marbleAmt);
            specP = mix(16.0,28.0,marbleAmt);

        }
        if (surfaceMaterial == 2) {

            vec3 marbleP = p;
            vec3 intensityP = p;

            float tileSize = 1.0;
            if ( ceil(mod(p.x,tileSize*2.0)/tileSize) == ceil(mod(p.z,tileSize*2.0)/tileSize) ) {
                surfaceColor = vec3(0.45,0.0,0.0);
                marbleP.x *= -1.0;
                intensityP.x *= -1.0;
                marbleP.z += 1.0;
                intensityP.z += 1.0;
            } else {
                surfaceColor = vec3(0.75,0.75,0.6);
            }
            specA = 1.0;
            specP = 16.0;




            marbleP.x += marbleP.z*0.5;
            marbleP.z += marbleP.x*0.4;

            marbleP.x += sin(marbleP.x*  3.8)*0.125;
            marbleP.z += sin(marbleP.z*  3.6)*0.135;

            marbleP.x += sin(marbleP.z* 20.0)*0.025;
            marbleP.z += sin(marbleP.x* 25.0)*0.025;

            marbleP.x += sin(marbleP.z* 40.0)*0.025;
            marbleP.z += sin(marbleP.x* 45.0)*0.025;

            marbleP.x += sin(marbleP.z*150.0)*0.01;
            marbleP.z += sin(marbleP.x*160.0)*0.011;

            marbleP *= 36.0;




            intensityP.z -= 10000.0;

            intensityP.x += intensityP.z*0.3;
            intensityP.z += intensityP.x*0.1;

            intensityP.x += sin(intensityP.x*1.2)*0.36;
            intensityP.z += sin(intensityP.z*1.3)*0.21;

            intensityP.x += sin(intensityP.z*2.2)*0.8;
            intensityP.z += sin(intensityP.x*2.3)*0.9;

            intensityP *= 6.0;


            float intensityAmt = (sin(intensityP.x)*sin(intensityP.z))*0.5+0.5;
            intensityAmt = 1.0-pow(1.0-intensityAmt,0.5);

            float marbleAmt = (sin(marbleP.x)*sin(marbleP.z))*0.5+0.5;


            float marbleGrainAmt = marbleAmt;
            marbleGrainAmt = 1.0-((1.0-pow(marbleGrainAmt,1.5))*(1.0-intensityAmt)*1.125);
            marbleGrainAmt = 1.0-pow(1.0-marbleGrainAmt,5.0);

            surfaceColor *= marbleGrainAmt;
            specA *= marbleGrainAmt;



            float marbleGashAmt = marbleAmt;
            marbleGashAmt *= 0.5 + 18.0*intensityAmt;
            marbleGashAmt += pow(intensityAmt,2.5)*18.0;
            marbleGashAmt = clamp(marbleGashAmt,0.0,1.0);

            float marbleGoldAmt = pow(marbleGashAmt,1.0);
            float marbleShadeAmt = pow(marbleGashAmt,16.0);

            surfaceColor *= marbleShadeAmt;
            specA *= marbleShadeAmt;

            vec3 myNormalCheat = vec3(
                sin( p.x*200.0 + sin(p.z*100.0)*0.5 + sin(p.z*17.0)*(5.0+sin(p.x*20.0)*4.0) )*0.000015,
                0.0,
                0.0
            );

            surfaceColor = mix(vec3(1.0,0.9,0.0),surfaceColor,marbleGoldAmt);
            specP = mix(256.0,specP,marbleGoldAmt);
            specA = mix(1.0,specA,marbleGoldAmt);
            difP = mix(6.0,difP,marbleGoldAmt);
            normalCheat = mix(myNormalCheat,normalCheat,marbleGoldAmt);

        }
        if (surfaceMaterial == 3){

            float splinters =
                pow(abs( ( sin(p.x*100.0)*0.5 + sin(p.y*100.0)*0.5 ) ), 0.1)
                *
                (sin(p.z*2.0+sin(p.x*10.0)*4.0+sin(p.x*27.0)*3.0)*0.5+0.5);

            float waves = sin(
                p.z*10.0 +
                sin(p.z*3.0 + sin(p.x*11.0)*0.5 )*1.0 +
                sin((p.z + sin(p.z*0.5)*5.5)*0.15 + sin(p.x*0.8)*2.0) * 14.0 +
                pow(abs(sin((p.x*1.0 + sin(p.x*3.0)*0.5)*25.0)),0.5) * 0.5
            );

            float grain = splinters * 0.3 + waves * 0.7;
            grain = pow(grain*0.5+0.5,0.25);

            surfaceColor = mix(vec3(0.2,0.1,0.1),vec3(0.4,0.2,0.05),grain);
            specP = mix(30.0,20.0,grain);
            specA = grain;

        }

        vec3 n = vec3(d-room(p-e.xyy,dummyMaterial),
                      d-room(p-e.yxy,dummyMaterial),
                      d-room(p-e.yyx,dummyMaterial));
        n += normalCheat;
        vec3 N = normalize(n);

        vec3 pointLightPos = vec3(0.0,2.0,-4.5-fwdDist);
        vec3 L = normalize(pointLightPos-p);

        float diffuse=max(dot(N,L),0.0);
        vec3 H = normalize(L-curCameraRayUnit);
        float specular = max(dot(H,N),0.0);
        color = pow(diffuse,difP)*surfaceColor + pow(specular,specP)*specA;

        float lightDist = (length(pointLightPos-p)) * 0.04;
        lightDist = max(0.0,min(1.0,lightDist));
        color *= pow(1.0-lightDist, 2.0);

    } else {

        color = vec3(0.0,0.0,0.0);

    }



    fragColor = vec4(color,1.0);

}



void mainImage( out vec4 fragColor, in vec2 fragCoord ){


    float vertFov = 50.0;
    float horizFov = 2.0*atan(tan((vertFov/180.0*PI)/2.0)*(iResolution.x/iResolution.y))*180.0/PI;
    vec4 fovAngsMono = vec4(horizFov/2.0, horizFov/2.0, vertFov/2.0, vertFov/2.0);







    vec2 fragFrac = fragCoord.xy/iResolution.xy;

    vec2 eyeRes = iResolution.xy;
    vec4 fovAngs = fovAngsMono;


/*
    vec3 cameraRight,cameraUp,cameraFwd;
    quatToAxes(headOrientation,cameraRight,cameraUp,cameraFwd);
	cameraFwd *= -1.0;
*/
    float camLookY = sin(PI*0.5+iTime*-0.2)*0.5 - 0.25;
    vec3 cameraRight = vec3(1.0,0.0,0.0);
    vec3 cameraFwd = -vec3(0.0,sin(camLookY),cos(camLookY));
    vec3 cameraUp = cross(cameraRight,cameraFwd);






    // position

    vec3 cameraPos = vec3(0.0);








    float fovL = -fovAngs.x/180.0*PI;
    float fovR =  fovAngs.y/180.0*PI;
    float fovU = -fovAngs.z/180.0*PI;
    float fovD =  fovAngs.w/180.0*PI;

    float fovMiddleX = (fovR + fovL) * 0.5;
    float fovMiddleY = (fovU + fovD) * 0.5;
    float fovHalfX = (fovR - fovL) * 0.5;
    float fovHalfY = (fovD - fovU) * 0.5;



    float scrWorldHalfX = sin(fovHalfX)/sin(PI*0.5 - fovHalfX);
    float scrWorldHalfY = sin(fovHalfY)/sin(PI*0.5 - fovHalfY);


    // determine screen plane size from FOV values, then interpolate to find current pixel's world coord

    vec2 vPos = fragFrac;//0 to 1
    vPos.x -= (-fovL/(fovHalfX*2.0));
    vPos.y -= (-fovU/(fovHalfY*2.0));

    vec3 screenPlaneCenter = cameraPos+cameraFwd;
    vec3 scrCoord = screenPlaneCenter + vPos.x*cameraRight*scrWorldHalfX*2.0 + vPos.y*cameraUp*scrWorldHalfY*2.0;
    vec3 curCameraRayUnit = normalize(scrCoord-cameraPos);


    mainVR(fragColor,fragCoord,cameraPos,curCameraRayUnit);

}

