// fractional brownian motion
// iter is number of octaves

vec3 fbmDerivative(vec2 p, int iter) {

    float f = 0.0;
	float dfx = 0.0;
	float dfy = 0.0;
    float fLacunarity = LACU;

	float amplitude = 0.5;
	float sumAmplitude = 0.0;

	for (int i=0;i<20;i++) {
		vec3 value = noised( p ); 

		f += amplitude * value.x;
		dfx +=  value.y;
		dfy +=  value.z;
		p = p * fLacunarity; 
		sumAmplitude+=amplitude;
		amplitude/=2.0;
		if (i>iter) {break;}
	}
	f/=sumAmplitude;

    return vec3( conv(f), dfx, dfy);
}

// same as above, without derivatives
float fbm(vec2 p, int iter){
	int idx=0;
    float f = 0.0;
	float amplitude = 0.5;
	float sumAmplitude = 0.0;
	for (int i=0;i<20;i++) {
		float value = noise( p ); 
		f += amplitude * value;
		p = p * LACU; 
		sumAmplitude+=amplitude;
		amplitude/=2.0;
		if ( i>iter ) {break;}
	}
	f/=sumAmplitude;
    return conv(f);
}

