struct Plane
{
    vec3 normal;
    vec3 position;
};

struct Ray
{
    vec3 from;
    vec3 direction;
};

const int NUM_PLANES = 1;

//#define USE_TEXTURE 

Plane planes[NUM_PLANES];    

vec4 trace(Ray ray)
{
    for (int i = 0; i < NUM_PLANES; ++i)
    {


        Plane p = planes[i];
        vec3 originalPosition = p.position;

        float a = dot(p.position - ray.from, p.normal);
        float b = dot(ray.direction, p.normal);
        float t = a / b;

        vec3 worldpos = ray.from + ray.direction * t;
        vec2 uv = vec2(worldpos.x, worldpos.z);

        #ifdef USE_TEXTURE
        uv *= 0.16;
        vec4 tex = texture(iChannel0, uv);
        #endif

        if (t > 0.0)
        {
            float c = 0.0;

            if(mod(worldpos.x, 1.0) > 0.5 && mod(worldpos.z, 1.0) > 0.5)
            {
                c = 1.0;
            }

            if(mod(worldpos.x, 1.0) < 0.5 && mod(worldpos.z, 1.0) < 0.5)
            {
                c = 1.0;
            }
            #ifdef USE_TEXTURE
            return tex;
			#else
            return vec4(c, 0.41 * float(i + 1), 0.0, 1.0);
            #endif

        }

    }    

    return vec4(0.0,0.25,_fragCoord.y / iResolution.y, 0.0);
}

vec2 getNormalizedPosition(vec2 screenCoord)
{
    float midx = iResolution.x * 0.5;
    float midy = iResolution.y * 0.5;

    float mx = screenCoord.x - midx;
    float my = screenCoord.y - midy;
    return vec2(mx / midx, my / midy);
}


Ray getRay(vec2 screenCoord)
{
    vec3 p = vec3(getNormalizedPosition(screenCoord), 0.0);

    Ray r;
    r.from = lookFrom;

    vec3 look = lookAt - lookFrom;

    p.z = look.z;
    p.z /= length(look);
    p = normalize(p);
    r.direction = p;
    r.direction *= rot;

    return r;
}


void lookat(vec3 to)
{
    lookAt = to;
    vec3 fwd = normalize(lookFrom - to);
    vec3 up = vec3(0.0, 1.0, 0.0);

    vec3 v = normalize(cross(up, fwd));
    vec3 w = normalize(cross(fwd, v));

    rot[0] = v;
    rot[1] = w;
    rot[2] = fwd;


}

vec4 drawPlanes()
{

    vec4 totalCol = vec4(0.0, 0.0, 0.0, 0.0);    
	const int kernel = 11;
    const float filterOffset = 1.0 / float(kernel);

    for (int x = -kernel; x < kernel; x+=2)
    {
        for (int y= -kernel; y < kernel; y+=2)
        {
            vec2 offset = vec2(x, y) * filterOffset;

            vec2 pos = _fragCoord.xy + offset;
		    Ray r = getRay(pos);
		    totalCol += trace(r);
        }
    }
    vec4 col = totalCol / float (kernel * kernel);
    return col;
}

vec3 rotateX(float angle, vec3 point)
{
    mat3 rotx;
    rotx[0] = vec3(1.0, 0.0, 0.0);
    rotx[1] = vec3(0.0, cos(angle), -sin(angle));
    rotx[2] = vec3(0.0, sin(angle), cos(angle));
    return rotx * point;
}

vec3 rotateZ(float angle, vec3 point)
{
    mat3 rotz;
    rotz[0] = vec3(cos(angle), -sin(angle), 0.0);
    rotz[1] = vec3(sin(angle), cos(angle), 0.0);
    rotz[2] = vec3(0.0, 0.0, 1.0);
    return rotz * point;
}


void rotateCamZ(float angle)
{
    mat3 rotz;
    rotz[0] = vec3(cos(angle), -sin(angle), 0.0);
    rotz[1] = vec3(sin(angle), cos(angle), 0.0);
    rotz[2] = vec3(0.0, 0.0, 1.0);

    rot *= rotz;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    _fragCoord = fragCoord;
    float move = iTime;
    lookFrom = vec3(0.0, -0.0, -move * 2.0);

