#define PI  3.14159265359
#define EPS .001

#define CIRCLE_RADIUS .5
#define CIRCLE_GLOW   .1
#define SAMPLE_SCALE  .2

float hash(vec2 p)
{
    return fract(sin(dot(p,vec2(127.1,311.7))) * 43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // 1. Calculate the uv coordinates in [-1, 1] xy space.
	vec2  uv = fragCoord.xy / iResolution.xy * 2. - 1.;

    // 2. Sample the audio stream.
    // Calculate the polar coordinates based on uv value.
    // Atan will return a value in the range of [-1, 1] so
    // we need to transform back to [0, 1] before sampling.
    float x = atan(-uv.x, -uv.y) / PI;
    // The samples are not going to seamlessly merge on the edges
    // so what we are going to do to make it nicer is display them
    // twice and invert the sampling order for the second repetition.
    x = (x < 0. ? 1. + x : 1. - x);
    vec3  s = texture(iChannel0, vec2(x, .25)).xyz * SAMPLE_SCALE;

    // 3. Define the geometry.
    // Correct the uv coordinates with regards to the aspect
    // ratio to calculate correct circle radius.
    vec2  cv = uv * vec2(iResolution.x / iResolution.y, 1.);
    float ds = length(cv);

    // 4. Calculate the pixel color.
    float sr = (ds - s.x) / CIRCLE_RADIUS;
    float cl = (1. - sqrt(abs(1. - sr))) / sr + CIRCLE_GLOW;

    // 5. PostFX
    vec3 col = cl * vec3(abs(cos(iTime)), .5 + uv.x * uv.y, ds - uv.y);
    float grain = hash(hash(uv) * cv * iTime) * .05;
    float fade  = smoothstep(EPS, 2., iTime);

    fragColor = vec4((col + grain) * fade, 1);
}

#define alpha 0.0
#define beta 10.0

// Thanks to Lance for the insight on making more than 1 spiral branch
#define NUM_BRANCHES 1.

#define PI 3.14159

vec4 getCornerColors(vec2 coord)
{
    vec4 cornerColors[4];

    cornerColors[0] = vec4(1.0, 0, 0, 1.0);
    cornerColors[1] = vec4(0, 0, 1.0, 1.0);
    cornerColors[2] = vec4(0, 1.0, 0.0, 1.0);
    cornerColors[3] = vec4(1.0, 1.0, 0.0, 1.0);

    vec2 cornerCoords[4];
    cornerCoords[0] = vec2(0);
    cornerCoords[1] = vec2(1, 0);
    cornerCoords[2] = vec2(1);
    cornerCoords[3] = vec2(0, 1);


	vec4 result = vec4(0.0);

	float totalArea = dot(iResolution.xy, iResolution.xy);

	for(int i = 0; i < 4; i++)
	{
		vec2 cCoord = cornerCoords[i] * iResolution.xy;

		vec2 diff = coord - cCoord;

		float area = dot(diff, diff);

		result += ((totalArea - area) / totalArea) * cornerColors[i];
	}

	return result;
}

vec4 spiral4(vec2 coord)
{	
	float alpha_t = alpha - iTime * 50.0;

	float x = coord.x;
	float y = coord.y;

	float r = sqrt(dot(coord, coord));

	float phi = atan(y, x);

	float phi_r = (r - alpha_t) / beta;

	float r_phi = alpha_t + (beta * phi);

	float remainder = abs(cos(phi) - cos(phi_r));

	if (remainder < 0.5)
	{
		return vec4(vec3(0), 1.0);
	}
	else
	{
		return vec4(vec3(remainder), 1.0);
	}
}

vec4 spiral5(vec2 coord)
{
	float alpha_t = alpha - iTime * 50.;

	//coord = coord / fres;

	//coord = (2.0 * coord) - vec2(1.0);

	//coord = coord - (iResolution.xy * 0.5);

	float x = coord.x;
	float y = coord.y;

	float r = sqrt(x * x + y * y);

	float phi = atan(y, x) * NUM_BRANCHES;

	float phi_r = (r - alpha_t) / beta;

	float r_phi = alpha_t + (beta * phi);

	//float remainder = abs(cos(phi) - cos(phi_r));

	float remainder = abs(sin(phi) - sin(phi_r));

	remainder += abs(cos(phi) - cos(phi_r));

	if (remainder < 1.0)
	{
		vec4 c1 = vec4(1, 0, 0, 1);
		vec4 c2 = vec4(0, 0, 1, 1);

		float t = (phi / (2. * PI)) + 0.5;

		return mix(c1, c2, remainder);
	}
	else
	{
        vec4 c1 = vec4(1, 0, 0, 1);
		vec4 c2 = vec4(0, 0, 1, 1);
        return mix(c1, c2, remainder);
	}
}

vec4 spiral6(vec2 coord)
{
	float alpha_t = alpha - iTime * 50.;

	//coord = coord / fres;

	//coord = (2.0 * coord) - vec2(1.0);

	//coord = coord - (iResolution.xy * 0.5);

	float x = coord.x;
	float y = coord.y;

	float r = sqrt(x * x + y * y);

	float phi = atan(y, x) * -NUM_BRANCHES;

	float phi_r = (r - alpha_t) / beta;

	float r_phi = alpha_t + (beta * phi);

	//float remainder = abs(cos(phi) - cos(phi_r));

	float remainder = abs(sin(phi) - sin(phi_r));

	remainder += abs(cos(phi) - cos(phi_r));

	if (remainder < 1.0)
	{
		vec4 c1 = vec4(1, 0, 0, 1);
		vec4 c2 = vec4(0, 0, 1, 1);

		float t = (phi / (2. * PI)) + 0.5;

		return mix(c1, c2, remainder);
	}
	else
	{
        vec4 c1 = vec4(1, 0, 0, 1);
		vec4 c2 = vec4(0, 0, 1, 1);
        return mix(c1, c2, remainder);
	}
}

