#define A  0.0
#define B  (iMouse.z <= 0.0 ? 0.4 : iMouse.y / iResolution.y - 0.5)
#define C  (sin(iTime*1.5) * 0.5)
#define D  0.2

#define EDGE   0.005
#define SMOOTH 0.0025

#define PI 3.14159265359

// F(x,y) = F(x) - y
float F ( in vec2 coords )
{
    // convert time from 0-1 to 0-pi/2
    float T = coords.x * PI * 0.5;

    // pre-calculate sin and cosine
    float snt = sin(T);  // sin 0 = 0.  sin pi/2 = 1
    float cst = cos(T);  // cos 0 = 1.  cos pi/2 = 0

    // interpolating spline, not an exterpolating spline, so should pass through the end control points
    // but not the middle control points.
	// basis - google: graph y = (0.5*cos(x)*(cos(x)-1.0)), y = (0.5*sin(x)*(sin(x)+1.0)), y = (0.5*cos(x)*(cos(x)+1.0)), y = (0.5*sin(x)*(sin(x)-1.0)) from 0 to pi/2
    // basis sum - google: graph y = (0.5*cos(x)*(cos(x)-1.0)) + (0.5*sin(x)*(sin(x)+1.0)) + (0.5*cos(x)*(cos(x)+1.0)) + (0.5*sin(x)*(sin(x)-1.0)) 
    return
        D *  (0.5*snt*(snt+1.0)) + 
        B * -(0.5*snt*(snt-1.0)) +        
        C * -(0.5*cst*(cst-1.0)) +         
        A *  (0.5*cst*(cst+1.0)) - 
        coords.y;
}

// gradiant function for finding G for a generic function when you can't
// get it analytically using partial derivatives.  We could do
// partial derivatives of F above, but I'm being lazy.
vec2 Grad( in vec2 coords )
{
    vec2 h = vec2( 0.01, 0.0 );
    return vec2( F(coords+h.xy) - F(coords-h.xy),
                 F(coords+h.yx) - F(coords-h.yx) ) / (2.0*h.x);
}

// signed distance function for F(x,y)
float SDF( in vec2 coords )
{
    float v = F(coords);
    vec2  g = Grad(coords);
    return abs(v)/length(g);
}

// signed distance function for Circle, for control points
float SDFCircle( in vec2 coords, in vec2 offset )
{
    coords -= offset;
    float v = coords.x * coords.x + coords.y * coords.y - EDGE*EDGE;
    vec2  g = vec2(2.0 * coords.x, 2.0 * coords.y);
    return v/length(g); 
}

